INSERT INTO [t13057] VALUES (1,'لوددت أني غودرت مع أصحاب نحص الجبل يعني: سفح الجبل','1','0','0');
INSERT INTO [t13057] VALUES (2,'إني قضيت أنهم إليها لا يرجعون','2','0','0');
INSERT INTO [t13057] VALUES (3,'تمن علي ما شئت، قال: رب، تردني إلى الدنيا حتى أقتل فيك وفي نبيك عليه السلام مرة أخرى،','3','0','0');
INSERT INTO [t13057] VALUES (4,'تمن علي عبدي ما شئت أعطكه. قال: يا رب ما عبدتك حق عبادتك أتمنى عليك أن تردني إلى الدنيا','4','0','0');
INSERT INTO [t13057] VALUES (5,'ولا تحسبن الذين قتلوا في سبيل الله أمواتا بل أحياء عند ربهم يرزقون} [آل عمران: ١٦٩] فقال:','5','0','0');
INSERT INTO [t13057] VALUES (6,'سل، وتمنه، فيقول: ما أسأل، ولا أتمنى إلا أن تردني إلى الدنيا، فأقتل في سبيلك عشر مرار لما','6','0','0');
INSERT INTO [t13057] VALUES (7,'ما على الأرض من نفس تموت ولها عند الله خير تحب أن ترجع إليكم ولا. . . . . . . . . . .','7','0','0');
INSERT INTO [t13057] VALUES (8,'ما أحد يدخل الجنة، يحب أن يرجع إلى الدنيا، وله ما على الأرض من شيء، إلا الشهيد، فإنه','8','0','0');
INSERT INTO [t13057] VALUES (9,'طوبى لك يا طير، ما أنعمك على هذه الشجرة، تأكل من هذه الثمرة، ثم تموت، ثم لا تكون شيئا','9','0','0');
INSERT INTO [t13057] VALUES (10,'يا ليتني شجرة تعضد ثم تؤكل','10','0','0');
INSERT INTO [t13057] VALUES (11,'ليتني كنت خضرة تأكلني الدواب','11','0','0');
INSERT INTO [t13057] VALUES (12,'يا ليتني مثل هذه التبنة، ليت أمي لم تلدني، ليتني لم أك شيئا، ليتني كنت نسيا','12','0','0');
INSERT INTO [t13057] VALUES (13,'لو أن لي ما على الأرض لافتديت به من هول المطلع','13','0','0');
INSERT INTO [t13057] VALUES (14,'لو كان لي ما طلعت عليه الشمس لافتديت به من كرب ساعة - يعني بذلك الموت - فكيف ولم أرد','14','0','0');
INSERT INTO [t13057] VALUES (15,'والله لو أن لي الدنيا وما فيها لافتديت به من هول ما أمامي قبل أن أعلم ما','15','0','0');
INSERT INTO [t13057] VALUES (16,'لو أن لي الدنيا وما فيها لافتديت بها من النار، وإن لم أرها','16','0','0');
INSERT INTO [t13057] VALUES (17,'لوددت أني تركت كفافا، لا لي ولا علي','17','0','0');
INSERT INTO [t13057] VALUES (18,'إن من غررتموه لمغرور لوددت أني خرجت منها كما دخلت فيها، لو كان لي اليوم ما طلعت عليه','18','0','0');
INSERT INTO [t13057] VALUES (19,'وددت أن الله غفر لي خطيئة من خطاياي، وأنه لم يعرف نسبي','19','0','0');
INSERT INTO [t13057] VALUES (20,'وددت أني إذا أنا مت لم أبعث','20','0','0');
INSERT INTO [t13057] VALUES (21,'لو وقفت بين الجنة والنار، فخيرت بينهما، أيهما منزلي، أو أكون ترابا، لاخترت أن أكون','21','0','0');
INSERT INTO [t13057] VALUES (22,'يا ليتني كبشا، فذبحني أهلي، فأكلوا لحمي، وحسوا مرقي','22','0','0');
INSERT INTO [t13057] VALUES (23,'يا ليتني رمادا تذريني الريح','23','0','0');
INSERT INTO [t13057] VALUES (24,'وددت أني بمنزلة أصحاب الأعراف','24','0','0');
INSERT INTO [t13057] VALUES (25,'وددت أن الله عز وجل، خلقني يوم خلقني شجرة تعضد','25','0','0');
INSERT INTO [t13057] VALUES (26,'والله لوددت أني عبد لعبد حبشي مجدع، وأني أنجو من شر يوم القيامة','26','0','0');
INSERT INTO [t13057] VALUES (27,'يا ليتني إذا مت كنت نسيا منسيا','27','0','0');
INSERT INTO [t13057] VALUES (28,'يا ليتني كنت شجرة','28','0','0');
INSERT INTO [t13057] VALUES (29,'يا ليتني كنت عصا رطبا','29','0','0');
INSERT INTO [t13057] VALUES (30,'ألا ليتني لم أك شيئا مذكورا، ألا ليتني كهذا الماء الجاري، أو كنابتة من الأرض، أو كراعي','30','0','0');
INSERT INTO [t13057] VALUES (31,'لوددت أني كنت عبدا حبشيا لشر أهل المدينة ملكة، أرعى عليهم غنمهم، وأني لم أكن فيما كنت','31','0','0');
INSERT INTO [t13057] VALUES (32,'لوددت أني كنت نسيا منسيا','32','0','0');
INSERT INTO [t13057] VALUES (33,'وددت أني كبش أهلي، فذبحوني، ثم طبخوني، ثم أكلوني','33','0','0');
INSERT INTO [t13057] VALUES (34,'لتمنيت أنا أن يكون لي في الآخرة خص من قصب، وأروى من الماء، وأنجو من','34','0','0');
INSERT INTO [t13057] VALUES (35,'وددت أن الله إذا جمع الخلائق يوم القيامة يقول لي: يا مالك، فأقول: لبيك، فيأذن لي أن أسجد','35','0','0');
INSERT INTO [t13057] VALUES (36,'يا ليتني لم أخلق وليتني إذ خلقت لم أوقف، وليتني إذ وقفت لم أحاسب، وليتني إذ حوسبت لم','36','0','0');
INSERT INTO [t13057] VALUES (37,'يا ليتنا لم نخلق، ويا ليتنا إن حوسبنا لم نعذ‍ب، ويا ليتنا إن عذبنا لم','37','0','0');
INSERT INTO [t13057] VALUES (38,'يا ليتني كنت لبنة من هذا اللبن لا علي ولا لي','38','0','0');
INSERT INTO [t13057] VALUES (39,'أيسرك أنك شجرة من هذه الشجر، أكلتك هذه الراحلة، فقذفتك بعرا، فاتخذت جلة؟ قال: لا والله،','39','0','0');
INSERT INTO [t13057] VALUES (40,'ما منكم أحمر ولا أسود، يفضلني بتقى، إلا وددت أني في مسلاخه','40','0','0');
INSERT INTO [t13057] VALUES (41,'أتمنى بيتا مملوءا رجالا مثل أبي عبيدة','41','0','0');
INSERT INTO [t13057] VALUES (42,'ما الموت إلا سلام جبرا، قال: ذلك الذي أردت','42','0','0');
INSERT INTO [t13057] VALUES (43,'يا طير ما أنعمك، لا حساب عليك ولا عذاب، يا ليتني مثلك','43','0','0');
INSERT INTO [t13057] VALUES (44,'تمن على الله ما شئت قال: أتمنى سيفا صارما، وجنة حصينة، فأقاتل في سبيل الله حتى','44','0','0');
INSERT INTO [t13057] VALUES (45,'لو كان الرماد يدخل حلقي لأكلته','45','0','0');
INSERT INTO [t13057] VALUES (46,'الله قد كفانا مؤونة الدنيا، وأمرنا بطلب الآخرة، فليت الله كفانا مؤونة الآخرة، وأمرنا بطلب','46','0','0');
INSERT INTO [t13057] VALUES (47,'تمنى عبد الملك بن مروان الخلافة، وتمنى مصعب بن الزبير، سكينة بنت الحسين، وعائشة بنت طلحة،','47','0','0');
INSERT INTO [t13057] VALUES (48,'من يأخذها مني بما فيها؟ يا ليته كان بعرا ثم أمر بالحرس فأحاطوا بقصره قال بنوه: ما هذا؟','48','0','0');
INSERT INTO [t13057] VALUES (49,'إني لوددت أن كل لقمة آكلها في فم، أبغض الناس إلي','49','0','0');
INSERT INTO [t13057] VALUES (50,'ليتك كنت بي عقيما إن لبنيك في القبر حبسا طويلا','50','0','0');
INSERT INTO [t13057] VALUES (51,'تذهب بهذا الدرهم الستوق فتلقيه في هذه الدراهم الجياد، فلعل إنسانا يتجاوز','51','0','0');
INSERT INTO [t13057] VALUES (52,'ليتني مت قبل هذا اليوم بكذا وكذا','52','0','0');
INSERT INTO [t13057] VALUES (53,'فيا ليتني لاقيت في الرحم الردى ... ولم تبتدرني بالأكف القوابل ولم أسكن الدنيا إلى','53','0','0');
INSERT INTO [t13057] VALUES (54,'ليت أمي لم تلدني فتقول له امرأته: يا أبا ميسرة، أليس قد أحسن الله إليك؟ هداك للإسلام،','54','0','0');
INSERT INTO [t13057] VALUES (55,'ليت أمي لم تلدني أخبرت أني وارد النار، ولم أخبر أني صادر عنها','55','0','0');
INSERT INTO [t13057] VALUES (56,'ولوددت أني كنت عبدا مملوكا لبني فلان، من بني كنانة أشقى أهل بيت من كنانة وأني لم أل من','56','0','0');
INSERT INTO [t13057] VALUES (57,'يا ليتها لم تقل لنا','57','0','0');
INSERT INTO [t13057] VALUES (58,'أعييش ويحك إن حبي قد ثوى ... فأبوك مهيوض الجناح كسير يا ليتني من قبل مهلك صاحبي ... غيبت','58','0','0');
INSERT INTO [t13057] VALUES (59,'ليت السماء تفطرت أكنافها وتناثرت منها نجوم تلمع لما رأيت الناس هد جميعهم صوت ينادي بالنعي','59','0','0');
INSERT INTO [t13057] VALUES (60,'يمنونني الأجر العظيم وليتني ... نجوت كفافا لا علي ولا ليا','60','0','0');
INSERT INTO [t13057] VALUES (61,'ما ليت وما لك، والسبيل قد أصالك','61','0','0');
INSERT INTO [t13057] VALUES (62,'والمرء مرتهن بسوف وليتني ... وهلاكه في السوف والليت','62','0','0');
INSERT INTO [t13057] VALUES (63,'ما تمنيت شيئا قط قلنا له: وكيف ذلك؟ قال: إذا عرض لي شيء من ذاك، سألته','63','0','0');
INSERT INTO [t13057] VALUES (64,'من استعمل التسويف والمنى لم ينبعث في العمل وكان يقال: من أقلقه الخوف، ترك أرجو، وسوف،','64','0','0');
INSERT INTO [t13057] VALUES (65,'الأماني تنقص العقل','65','0','0');
INSERT INTO [t13057] VALUES (66,'والله لوددت أني كنت شجرة، ووالله لوددت أني كنت مدرة، ووالله لوددت أن الله لم يخلقني','66','0','0');
INSERT INTO [t13057] VALUES (67,'لوددت أني كنت ثكلت عشرة كلهم مثل عبد الرحمن بن الحارث بن هشام، وأني لم أسر مسيري الذي','67','0','0');
INSERT INTO [t13057] VALUES (68,'لأن أكون جلست عن مسيري، أحب إلي من أن يكون لي عشرة من رسول الله صلى الله عليه وسلم، مثل','68','0','0');
INSERT INTO [t13057] VALUES (69,'أشتهي أن ينفرج لي عن صدري، فأنظر إلى قلبي، ماذا صنع القرآن فيه وما نكأ، وكان عيسى إذا قرأ','69','0','0');
INSERT INTO [t13057] VALUES (70,'أشتهي والله يا أبا بشر، أن أكون رمادا، لا يجتمع منه سفه أبدا في الدنيا ولا في الآخرة،','70','0','0');
INSERT INTO [t13057] VALUES (71,'أشتهي أن أبكي حتى لا أقدر على أن أبكي، قال: فكان يبكي الليل والنهار، وكانت دموعه سائله','71','0','0');
INSERT INTO [t13057] VALUES (72,'وددت والله أنه كما قلت، ومن لعمر بالذي قلت رحمك الله','72','0','0');
INSERT INTO [t13057] VALUES (73,'ليت تلك الشجرة لم تخلق','73','0','0');
INSERT INTO [t13057] VALUES (74,'يا ليتني لم أخلق، فإذا خلقت مت صغيرا، ويا ليتني إذ لم أمت صغيرا، عمرت حتى أعمل في خلاص','74','0','0');
INSERT INTO [t13057] VALUES (75,'لو وقفت بين الجنة والنار، فخيرت بين أن أصير رمادا، أو أخير إلى أي الدارين أصير، لاخترت أن','75','0','0');
INSERT INTO [t13057] VALUES (76,'لكني وددت أن لي مثل أحد ذهبا، أحصي عدده وأؤدي زكاته','76','0','0');
INSERT INTO [t13057] VALUES (77,'ما يسرني أن لي من الجسر إلى خراسان ببعرة وربما قال: بنواة قال: وما يسرني أن لي من الخيل','77','0','0');
INSERT INTO [t13057] VALUES (78,'ما يسرني أن لي جميع ما حوت البصرة، من الأموال والثمرة بفلسين','78','0','0');
INSERT INTO [t13057] VALUES (79,'لئن تندرا - وأشار إلى عينيه - أحب إلي من ذلك','79','0','0');
INSERT INTO [t13057] VALUES (80,'لوددت أني حرقت بها، ثم أخرجت، ثم أحرقت، ثم أخرجت، ثم أحرقت، وأني لم','80','0','0');
INSERT INTO [t13057] VALUES (81,'ليت أني بزهد الحسن، وورع ابن سيرين، وفقه سعيد بن المسيب، وعبادة عامر بن عبد قيس، قال روح:','81','0','0');
INSERT INTO [t13057] VALUES (82,'ما يسرني أن لي كل عقدة، كل يعطى بملء زبيل من تراب','82','0','0');
INSERT INTO [t13057] VALUES (83,'إنما قوتي في الدنيا نصف مد في اليوم، وإنما لباسي ما ستر عورتي، وإنما بيتي ما أكن رأسي،','83','0','0');
INSERT INTO [t13057] VALUES (84,'لو أن الدنيا بحذافيرها، عرضت علي حلالا، لا أحاسب بها في الآخرة، لمكثت أتقذرها، كما يتقذر','84','0','0');
INSERT INTO [t13057] VALUES (85,'لو أدخلني النار، فصرت فيها ما يئسته','85','0','0');
INSERT INTO [t13057] VALUES (86,'لو خيرت بين أن أموت فأري القيامة، وأهوالها، والبعث، والحساب، ثم أدخل الجنة، وبين أن أكون','86','0','0');
INSERT INTO [t13057] VALUES (87,'لو أن الدنيا، عرضت علي منذ يوم خلقت، إلى أن تفنى، أتنعم فيها حلالا لا أسأل عنه يوم','87','0','0');
INSERT INTO [t13057] VALUES (88,'ما أحب أن ألقى الله بصحيفة أحد، إلا بصحيفة هذا المسجى','88','0','0');
INSERT INTO [t13057] VALUES (89,'وددت أني شعرة في صدر أبى بكر - رضي الله عنهما -','89','0','0');
INSERT INTO [t13057] VALUES (90,'لوددت أني من الجنة، حيث أرى أبا بكر رضي الله عنه','90','0','0');
INSERT INTO [t13057] VALUES (91,'لوددت أني شعرة، في صدر أبي بكر رضي الله عنهما','91','0','0');
INSERT INTO [t13057] VALUES (92,'ليت أنها قطعت من ها هنا - يعني يديه من المرفقين - وأني لم أكن شهدت','92','0','0');
INSERT INTO [t13057] VALUES (93,'لو أتاني آت من ربي، يخبرني بأن يخيرني في الجنة أنا أو في النار، وبين أن أصير ترابا،','93','0','0');
INSERT INTO [t13057] VALUES (94,'ولمن خاف مقام ربه جنتان} [الرحمن: ٤٦]','94','0','0');
INSERT INTO [t13057] VALUES (95,'طوبى لك يا طائر، تأكل الثمر، وتقع على الشجر، وددت أني ثمرة ينقرها','95','0','0');
INSERT INTO [t13057] VALUES (96,'تمنوا وتمنوا، فلما فاتهم جدوا','96','0','0');
INSERT INTO [t13057] VALUES (97,'والله لكأن جنبي في تخت وكأني أتنفس من سم إبرة، وكأن غصن شوك يجر به من قدمي إلى هامتي ثم','97','0','0');
INSERT INTO [t13057] VALUES (98,'وددت أني قرأت القرآن، ثم وقفت، ولم ألق أحدا أرضاه إلا قال ذلك','98','0','0');
INSERT INTO [t13057] VALUES (99,'وددت أني أفلت من هذا الأمر، لا لي، ولا علي','99','0','0');
INSERT INTO [t13057] VALUES (100,'يا ليتني كنت نسيا منسيا، قبل الذي كان من شأن عثمان رضي الله عنه، والله ما أحببت أن ينتهك','100','0','0');
INSERT INTO [t13057] VALUES (101,'ليتني مت قبل هذا اليوم بعشرين سنة','101','0','0');
INSERT INTO [t13057] VALUES (102,'وددت أني مت قبل هذا اليوم بكذا وكذا','102','0','0');
INSERT INTO [t13057] VALUES (103,'لوددت أني بحيث صيد هذا الطير، لا أكلم بشرا، ولا يكلمني، حتى ألقى الله عز','103','0','0');
INSERT INTO [t13057] VALUES (104,'لوددت أن لي إنسانا، يكون في مالي، ثم أغلق علي بابا، فلا يدخل علي أحد، حتى ألحق بالله عز','104','0','0');
INSERT INTO [t13057] VALUES (105,'ليت ما فات من شبابي يعود ... كيف والشيب كل يوم يزيد من هموم طوارق تعتريني ... وهنات يشيب','105','0','0');
INSERT INTO [t13057] VALUES (106,'ما سألت الله الجنة قط، إلا وأنا مستحي منه، ولوددت أني أنجو من النار، وأصير رمادا، وكان','106','0','0');
INSERT INTO [t13057] VALUES (107,'وددت أني لم أكن كتبت من هذا العلم حرفا واحدا، إلا ما لا بد للرجل منه قال: ثم بكى، ثم قال:','107','0','0');
INSERT INTO [t13057] VALUES (108,'ليأتين على الناس زمان، يأتي الرجل القبر يتمرغ عليه، كما تتمرغ الدابة، ويتمنى أن يكون','108','0','0');
INSERT INTO [t13057] VALUES (109,'اسكت أتريد أن أوقف يوم القيامة حتى أسأل عن كل مجلس جلسته، وعن كل حديث حدثته: أي شيء أردت','109','0','0');
INSERT INTO [t13057] VALUES (110,'لا يتمنى المؤمن الموت من ضر أصابه، فإن كان لا بد فاعلا، فليقل: اللهم أحيني ما كانت الحياة','110','0','0');
INSERT INTO [t13057] VALUES (111,'لا يتمن أحد الموت، إلا من وثق بعمله','111','0','0');
INSERT INTO [t13057] VALUES (112,'إنك ميت، ولكن سل الله العافية','112','0','0');
INSERT INTO [t13057] VALUES (113,'يوشك ابن أخي إن أخر أجلك يكون الخفيف الحاذ، أغبط من أبي عشرة، كلهم رب بيت، ويوشك ابن أخي','113','0','0');
INSERT INTO [t13057] VALUES (114,'لا يتمن أحد الموت فقال عطاء: إنما يريد الحياة من يزداد خيرا، فأما من يزداد شرا، فما يصنع','114','0','0');
INSERT INTO [t13057] VALUES (115,'ويل عطاء ليت أم عطاء، لم تلده فوالله ما زال كذلك، حتى نظرنا إلى الشمس، قد طفلت للغروب،','115','0','0');
INSERT INTO [t13057] VALUES (116,'مررت بكلب ميت، فقلت: استرحت ليس عليك حساب','116','0','0');
INSERT INTO [t13057] VALUES (117,'استراحت الطير في السماء، والحيتان في البحار، والوحش في القفار، وأنا مرتهن','117','0','0');
INSERT INTO [t13057] VALUES (118,'ليتني مثل هذا وبكى، ثم بكى','118','0','0');
INSERT INTO [t13057] VALUES (119,'أصبحنا في أمنية المتمنين، الموتى يتمنون أنهم في مثل عافيتنا، والمشاغيل يتمنون','119','0','0');
INSERT INTO [t13057] VALUES (120,'لو قيل لي: إنك من أهل النار، ما تركت العمل، لئلا تلومني نفسي، تقول: ألا صنعت ألا','120','0','0');
INSERT INTO [t13057] VALUES (121,'طوبى لك يا طير، تأكل من الثمر، وتستظل بالشجر، وتصير إلى غير حساب، يا ليت أبا بكر','121','0','0');
INSERT INTO [t13057] VALUES (122,'لوددت أني أنجو من الإمارة كفافا، لا لي، ولا علي','122','0','0');
INSERT INTO [t13057] VALUES (123,'يا جبل، تصير هباء منثورا، وتصير كذا، وتصير كذا، ويبقى على يزيد الحساب، قال: ثم يبكي، فما','123','0','0');
INSERT INTO [t13057] VALUES (124,'لو خيرت بين أن لا أكون شيئا، وبين حالي التي أنا عليها، لاخترت أن لا أكون كنت شيئا، ولا','124','0','0');
INSERT INTO [t13057] VALUES (125,'لو خيرت بين دخول الجنة، وبين أن أكون لبنة من هذا الحائط، لاخترت أن أكون لبنة منه، متى','125','0','0');
INSERT INTO [t13057] VALUES (126,'لوددت أني كنت غصنا رطبا، وأني لم أسري في هذا الأمر، تعني يوم الجمل','126','0','0');
INSERT INTO [t13057] VALUES (127,'لوددت أني مت، ووالله لوددت أني مت، قالها ثلاثا','127','0','0');
INSERT INTO [t13057] VALUES (128,'ولوددت أني شجرة تعضد، ثم تؤكل','128','0','0');
INSERT INTO [t13057] VALUES (129,'والذي نفسي بيده، لوددت أني شجرة بجانب الطريق، مر بي بعير فأخذني بفيه فلاكني، ثم ألقاني،','129','0','0');
INSERT INTO [t13057] VALUES (130,'لوددت أني كبش رباني أهلي، حتى إذا كنت كأسمن ما يكون، زارهم بعض من يحبونه، فذبحوني، فجعلوا','130','0','0');
INSERT INTO [t13057] VALUES (131,'يا طير ما أنعمك لا حساب عليك، ولا عذاب، يا ليتني مثلك، ليته','131','0','0');
INSERT INTO [t13057] VALUES (132,'لا تقوم الساعة، حتى يمر الرجل بقبر الرجل، فيقول: يا ليته مكان هذا','132','0','0');
INSERT INTO [t13057] VALUES (133,'لا تقوم الساعة، حتى يمر الرجل بقبر أخيه، فيلكزه برجله، ويقول: يا ليتني كنت','133','0','0');
INSERT INTO [t13057] VALUES (134,'ليته لم يكن سيد قومه، كم من باطل قد حققناه، وحق قد أبطلناه','134','0','0');
INSERT INTO [t13057] VALUES (135,'لأن أكون في هذه السحابة، فأقع إلى الأرض، فأنقطع أحب إلي من أن أكون شرعت في دم عثمان','135','0','0');
INSERT INTO [t13057] VALUES (136,'لا تمن الموت، يا عم رسول الله، فإنك إن تبق تزدد خيرا، يكون ذلك خيرا لك، وإن تبق فتستعتب','136','0','0');
INSERT INTO [t13057] VALUES (137,'ولا تتمنوا ما فضل الله به بعضكم على بعض} [النساء: ٣٢] إلى آخر الآية','137','0','0');
INSERT INTO [t13057] VALUES (138,'شهدتها - يعني الجماجم - فما رميت بسهم، ولا طعنت برمح، ولا ضربت بسيف، وددت أن هذه سقطت من','138','0','0');
INSERT INTO [t13057] VALUES (139,'وددت أنها في قلب كل مؤمن من أمتي تبارك الذي بيده الملك','139','0','0');
INSERT INTO [t13057] VALUES (140,'لوددت أني في حيز من حديد، ومعي ما يصلحني، لا أكلم الناس ولا يكلموني، حتى ألقى الله عز','140','0','0');
INSERT INTO [t13057] VALUES (141,'وددت أني طير في منكبي الريش','141','0','0');
INSERT INTO [t13057] VALUES (142,'لولا أن أضع جبيني لله ساجدا، أو أجالس أقواما يلتقطون طيب الكلام، كما يلتقط طيب التمر','142','0','0');
INSERT INTO [t13057] VALUES (143,'لو أن لي ما في الأرض، لافتديت به من هول المطلع','143','0','0');
INSERT INTO [t13057] VALUES (144,'لكني والله لوددت أنه سلم لي صحبتي مع رسول الله صلى الله عليه وسلم، وأني انفلت من عملي هذا','144','0','0');
INSERT INTO [t13057] VALUES (145,'لقد شهدتم - يعني قتال الجماجم - فما رميت بسهم، ولا طعنت برمح، ولا ضربت بسيف، ولوددت أن','145','0','0');
INSERT INTO [t13057] VALUES (146,'لو وقفت بين الجنة والنار، فقيل لي: أيما أحب إليك: أن أخيرك أيهما تكون دارك، أو تكون رمادا','146','0','0');
INSERT INTO [t13057] VALUES (147,'فمروا برجل مجلود، يقول: أنا والله مظلوم، فقال: يا هذه، لمثل هذا كان زوجك يتمنى','147','0','0');
INSERT INTO [t13057] VALUES (148,'ينبغي لك لو أنك لم تعص الله طرفة عين، أن تمنى أنك لم تخلق','148','0','0');
INSERT INTO [t13057] VALUES (149,'لو أني أعلم أنكم تبقون على حالكم هذه، لتمنيت أن أعيش، فذكر عشرين سنة','149','0','0');
INSERT INTO [t13057] VALUES (150,'كان من دعاء لي، أو من دعائي أن لا أموت فجأة، فأما اليوم فوددت أنه قد','150','0','0');
INSERT INTO [t13057] VALUES (151,'لأنا إلى من في بطنها أشوق مني إلى من على ظهرها','151','0','0');
INSERT INTO [t13057] VALUES (152,'إذا دخل أهل الجنة الجنة، وأهل النار النار، دعي برجل من أهل الجنة، فيقال له: كيف منزلك','152','0','0');
INSERT INTO [t13057] VALUES (153,'ولوددت أني كنت عبدا مملوكا لبني فلان من كنانة، أسقيهم الماء، وأني لم آل من هذا العمل شيئا','153','0','0');
INSERT INTO [t13057] VALUES (154,'لا تقوم الساعة حتى يسود كل قبيلة منافقوها، فلذلك اشتهيت أن أموت قبل ذلك','154','0','0');
INSERT INTO [t13057] VALUES (155,'إنما كنت أريدهما لأنظر بهما إلى رسول الله صلى الله عليه وسلم، فأما إذ قبض الله عز وجل','155','0','0');
INSERT INTO [t13057] VALUES (156,'ترى الجنازة يمر بها على القوم، فيقول القائل: يا ليتني على أعوادك، فيقول: إنك لا تدري ما','156','0','0');
INSERT INTO [t13057] VALUES (157,'إذا تمنى أحدكم، فلينظر ما يتمنى، فإنه لا يدري ما يكتب الله له من','157','0','0');
INSERT INTO [t13057] VALUES (158,'إذا تمنيت شيئا فأعطيته، فقل: أسأل الله الجنة','158','0','0');
INSERT INTO [t13057] VALUES (159,'قد سمعت ما قلتم، والله لوددت أنها بقيت هاهنا أبدا، لا أدري ما أبشر به','159','0','0');
INSERT INTO [t13057] VALUES (160,'أتمنى أن يكون مثل هذه الدار رجالا مثل أبي عبيدة بن الجراح','160','0','0');
INSERT INTO [t13057] VALUES (161,'ود أبوك أنه مات قبل هذا اليوم بعشرين عاما','161','0','0');
INSERT INTO [t13057] VALUES (162,'وددت أني كنت جلة لأهلي فأحرقوني','162','0','0');
INSERT INTO [t13057] VALUES (163,'وددت أني كنت كبشا لأهلي، فذبحوني، فشووني، وأكلوا لحمي','163','0','0');
INSERT INTO [t13057] VALUES (164,'أصبحتم في منى ناس كثير','164','0','0');
INSERT INTO [t13057] VALUES (165,'الحمد لله الذي جعلهم يتمنون أنهم مثلنا عند الموت، ولا نتمنى أنا مثلهم عند الموت، ما','165','0','0');
INSERT INTO [t13057] VALUES (166,'ليت حظي من الفتيا الكفاف','166','0','0');
