INSERT INTO [t13059] VALUES (9,'الراحلون الذين جمعوا بين الأقطار','133','0','0');
INSERT INTO [t13059] VALUES (10,'الطبقة الأولى عبد الله بن المبارك، جمع بين اليمن والعراق ومصر والجزيرة والشام زيد بن الحباب، جمع بين العراق، وخراسان، ومصر والشام أبو داود الطيالسي، جمع بين العراق والري والجزيرة','134','0','0');
INSERT INTO [t13059] VALUES (11,'الطبقة الثانية أسد بن موسى، جمع بين العراق ومصر والشام المعلى بن منصور، جمع بين العراق ومصر والشام آدم بن أبي إياس، جمع بين العراق والشام يحيى بن حسان، جمع بين العراق واليمن والشام','135','0','0');
INSERT INTO [t13059] VALUES (12,'الطبقة الثالثة أحمد بن حنبل، جمع بين العراق واليمن والجزيرة والشام إسحاق بن راهويه، جمع بين العراق واليمن والجزيرة والشام يحيى بن معين، جمع بين العراق والجزيرة ومصر والشام علي بن بحر البري، جمع بين العراق واليمن، وأحسبه دخل الشام نعيم بن حماد، جمع','136','0','0');
INSERT INTO [t13059] VALUES (13,'الطبقة الرابعة محمد بن يحيى النيسابوري، جمع بين العراق ومصر واليمن والشام أبو زرعة الرازي وأبو حاتم، جمعا بين العراق والحجاز والجزيرة والشام أحمد بن الفرات الأصبهاني، وأحمد بن منصور الرمادي، جمعا بين العراق واليمن ومصر والشام يعقوب بن سفيان، جمع بين','137','0','0');
INSERT INTO [t13059] VALUES (14,'الطبقة الخامسة','138','0','0');
INSERT INTO [t13059] VALUES (15,'الذين جمعوا بين الأقطار موسى بن هارون، المعمري، الفريابي، الحسين بن إسحاق، عبدان، الحسن بن سفيان، محمد بن خزيمة، ابن صاعد، أبو عبد الرحمن النسائي، أبو عروبة الحسين بن أبي معشر الحراني، ابن أبي داود، زكرياء بن يحيى الساجي، محمد بن جرير، عبد','139','0','0');
INSERT INTO [t13059] VALUES (16,'الذين قصدوا ناحية واحدة للقاء من بها رحل ابن شهاب إلى الشام، إلى عطاء بن يزيد، وابن محيريز وابن حيوة رحل يحيى بن أبي كثير إلى المدينة للقاء من بها من أولاد الصحابة رحل محمد بن سيرين يعني إلى الكوفة، فلقي بها عبيدة وعلقمة وعبد الرحمن بن أبي ليلى رحل','140','0','0');
INSERT INTO [t13059] VALUES (17,'الراحلون من البصرة إلى الكوفة محمد بن سيرين إلى علقمة وعبيدة، حميد بن هلال إلى أبي الأحوص سليمان التيمي لقي بها جماعة من التابعين عبد الله بن عون إلى أبي وائل والشعبي والنخعي ومسلم البطين شعبة بن الحجاج وأبو عوانة وعبد الواحد بن زياد إلى الأعمش وأبي','142','0','0');
INSERT INTO [t13059] VALUES (18,'الراحلون من الكوفة إلى البصرة سفيان الثوري، ثم رحل عنها إلى اليمن، شريك بن عبد الله، حفص بن غياث، ثم رحل عنها إلى المدينة','143','0','0');
INSERT INTO [t13059] VALUES (19,'ومن أهل واسط الذين رحلوا إلى البصرة هشيم بن بشير، ويزيد بن هارون، ومحمد بن الحسن المزني','144','0','0');
INSERT INTO [t13059] VALUES (20,'الراحلون من خراسان إلى العراق إبراهيم بن طهمان، وأبو حمزة السكري، وخارجة بن مصعب، وأبو تميلة يحيى بن واضح، والفضل بن موسى السيناني','145','0','0');
INSERT INTO [t13059] VALUES (21,'من لا يرى الرحلة والتعالي في الإسناد إذا حصل له الحديث مسموعا','146','0','0');
INSERT INTO [t13059] VALUES (22,'القول في فضل من جمع بين الرواية والدراية','156','0','0');
INSERT INTO [t13059] VALUES (23,'فأما المعرفون بأجدادهم المنسوبون إليهم دون آبائهم كابن أبجر، وابن جريج، وبني أبي شيبة فهم كثيرون: فأما ابن أبجر فإنما هو عبد الملك بن حيان بن أبجر وابن جريج إنما هو عبد الملك بن عبد العزيز بن جريج وبنو أبي شيبة إنما هم بنو محمد بن أبي شيبة، وهم عثمان','191','0','0');
INSERT INTO [t13059] VALUES (24,'ومن أصحاب النبي صلى الله عليه وسلم ممن يعرف بجده وينسب إليه أحمر بن جزء، وهو ابن سواء بن جزء وحمل بن النابغة، وهو حمل بن مالك بن النابغة هذلي ومجمع بن جارية، وهو مجمع بن يزيد بن جارية','192','0','0');
INSERT INTO [t13059] VALUES (25,'ثم من يعرف بكنية جده وينسب إليه ابن أبي الحسين المكي، هو عبد الله بن عبد الرحمن بن أبي الحسين وابن أبي عمار، وهو عبد الرحمن بن عبد الله بن أبي عمار وابن أبي لبيبة، وهو محمد بن عبد الرحمن بن أبي لبيبة وابن أبي ذباب، وهو الحارث بن عبد الرحمن بن أبي','193','0','0');
INSERT INTO [t13059] VALUES (26,'ثم المنتسبون إلى أمهاتهم فابن علية، وهو إسماعيل بن إبراهيم، وعلية أمه، وكان يكره أن يدعى ابن علية وابن عائشة، وهو محمد بن حفص بن عمر بن موسى بن عبد الله بن معمر، وعائشة أمه، وهي بنت عبيد الله بن عبد الله بن معمر','194','0','0');
INSERT INTO [t13059] VALUES (27,'وفي أصحاب النبي صلى الله عليه وسلم عدة ينسبون إلى أمهاتهم منهم: شرحبيل بن عبد الله بن المطاع بن عمرو من كندة، وأمه حسنة مولاة معمر بن حبيب الجمحي، وأخبرنا أبو خليفة عن الجهم، عن الجمحي، قال: هو شرحبيل بن عبد الله بن المطاع بن عمرو من كندة، وأمه','195','0','0');
INSERT INTO [t13059] VALUES (28,'المعروفون بغير أسمائهم إما بلقب أو بنعت أو معنى منهم: الأحلج الكندي، وهو يحيى بن عبد الله بن حسان بن حجر بن وهب بن ربيعة بن الحارث بن معاوية بن ثور، حدثني عبد الله بن علي، عن أبي سعيد الأشج، بهذا الاسم والنسب خاقان الأهتم، اسمه عبد الله بن عبد الله','198','0','0');
INSERT INTO [t13059] VALUES (29,'ومن أصحاب النبي صلى الله عليه وسلم ممن يعرف بلقبه أو نعته الجارود العبدي، وهو بشر بن عمرو، قال شباب: الجارود لقب أشج عبد القيس، وهو قيس بن النعمان، ويقال اسمه المنذر، الأقرع بن حابس، اسمه فراس آبي اللحم عبد الله بن عبد مالك، ويقال اسمه خلف بن عبد','199','0','0');
INSERT INTO [t13059] VALUES (30,'ثم الملقبون الآباء سلمة بن الأكوع، اسم الأكوع سنان بن عبد الله الأسلمي سلمة بن المحبق، اسم المحبق صخر بن عبيد من هذيل عتبة بن فرقد هو عتبة بن يربوع بن حبيب بن مالك حذيفة بن اليمان اسم اليمان حسل بن جابر شداد بن الهاد، واسم الهاد عمرو بن عبد الله من','201','0','0');
INSERT INTO [t13059] VALUES (31,'الأسامي والكنى المشكلة الصور التي يجمعها عصر واحد حدثني محمد بن محمد بن يحيى القراب السجستاني بمدينة سابور، ثنا عثمان بن سعيد الدرامي السمسار قال: كنا عند سعيد بن أبي مريم بمصر، فأتاه رجل فسأله كتابا ينظر فيه، أو سأله أن يحدثه بأحاديث، فامتنع عليه،','202','0','0');
INSERT INTO [t13059] VALUES (32,'ومن المشكل جزي بن بكير بالزاي معجمة، وهو من أهل الكوفة، روى عن حذيفة، وجري بن كليب من أهل البصرة من بني سدوس بالراء غير معجمة، وهو أيضا من أهل الكوفة روى عن علي هذا قول البرديجي وجزي النهدي كوفي، روى عن علي رضي الله عنه وعايش بن أنس بالياء والشين','205','0','0');
INSERT INTO [t13059] VALUES (33,'المتفقة أسماؤهم وعصورهم ورواتهم من أصحاب النبي صلى الله عليه وسلم والرواة عنهم ومن المشكل أيضا أسام، وكنى متفقة يجمعها عصر واحد، تشترك في أكثر من روت عنه وروى عنها، وربما جمعهما بلد واحد، تأتي بهما الآثار مفردة غير منسوبة، وذلك مثل: إبراهيم بن يزيد','209','0','0');
INSERT INTO [t13059] VALUES (34,'المتفقة كناهم وعصورهم منهم المكنون بأبي صالح، عدة منهم اشتركوا في الرواية، عن أبي هريرة، عشرون أو نحوها منهم: أبو صالح السمان، أبو سهيل بن أبي صالح، وروى عنه الأعمش والحكم وأبو حصين، وأبو إسحاق، وحبيب بن أبي ثابت، واسمه ذكوان وأبو صالح مولى','215','0','0');
INSERT INTO [t13059] VALUES (35,'المكنون بأبي حازم قال القاضي: قال لنا الحسن بن المثنى: وجدت على ظهر كتاب لي وهو من كلام علي بن المديني - وكان أصحابنا يذكرون أنه عنه علق، وأبى الحسن أن يسنده إليه -: أبو حازم الأشجعي، واسمه سلمان صاحب أبي هريرة، قال شباب: أبو حازم الأشجعي هو أبو','222','0','0');
INSERT INTO [t13059] VALUES (36,'المكنون أبا مريم قال القاضي: قال الحسن بن المثنى فيما ذكر أنه وجد على ظهر كتابه أن منهم: أبا مريم صاحب علي الذي روى عنه نعيم بن حكيم، وروى هو، عن علي وأبي الدرداء، واسمه قيس وأبو مريم، الذي روى عن ابن مسعود، وروى عنه أشعث بن سليم، اسمه عبد الله','224','0','0');
INSERT INTO [t13059] VALUES (37,'المكنون أبا العنبس منهم أبو العنبس صاحب إبراهيم، روى عن أبيه، اسمه عمرو بن مرزوق وأبو العنبس الذي روى عنه عبد الملك بن عمير، لا يعرف اسمه وأبو العنبس صاحب زاذان اسمه سعيد بن كثير بن عبيد، وكثير بن عبيد هو أبو سعيد الذي يقال له رضيع عائشة، روى عنه','225','0','0');
INSERT INTO [t13059] VALUES (38,'المكنون أبا بكر غير مسمين أبو بكر بن عبد الرحمن بن الحارث بن هشام أحد الفقهاء السبعة بالمدينة، اسمه كنيته، وأبو بكر بن محمد بن عمرو بن حزم الأنصاري، اسمه كنيته، وأبو بكر بن أبي جهم بن حذيفة، اسمه كنيته، وأبو بكر بن أبي موسى الأشعري، اسمه كنيته،','227','0','0');
INSERT INTO [t13059] VALUES (39,'المكنون أبا نعامة قال شباب: أبو نعامة العدوي عمر بن قيس وأبو نعامة الضبي شيبة بن نعامة وأبو نعامة السعدي عبد ربه هؤلاء طبقة','229','0','0');
INSERT INTO [t13059] VALUES (40,'المكنون أبا غالب هما اثنان: أحدهما روى عن أبي أمامة، اسمه حزور والآخر روى عن أنس، ولم يسم لنا','230','0','0');
INSERT INTO [t13059] VALUES (41,'المكنون أبا الدهماء هما اثنان: أبو الدهماء مالك بن سهم وأبو الدهماء قرفة بن بهيس','232','0','0');
INSERT INTO [t13059] VALUES (42,'المكنون أبا إسحاق أبو إسحاق السبيعي، وهو الهمداني، واسمه عمرو بن عبد الله وأبو إسحاق الشيباني، واسمه سليمان بن ماهان اشتركا في ابن أبي أوفى، وروى عنهما الثوري، وشعبة، وغيرهما','233','0','0');
INSERT INTO [t13059] VALUES (43,'المكنون أبا الزعراء سمعت أحمد بن هارون البرديجي يقول: أبو الزعراء الذي روى عن أبي الأحوص، وروى عنه سفيان الثوري وعبيدة بن حميد، وسفيان بن عيينة، اسمه عمرو بن عمرو، وهو ابن أخي أبي الأحوص، قال: وأبو الزعراء الذي روى عن محل بن خليفة، روى عنه عبد','234','0','0');
INSERT INTO [t13059] VALUES (44,'ومن المشكل أيضا أسام مفردة يغلط بها إلى أشكالها في الصورة، لغموضها وظهور إشكالها تعلى بن عبيد بن تعلى، بالتاء منقوط من فوقه يشتبه بيعلى إلا أن يعلى في الأسامي أكثر وأشهر علبة بالباء مثال قلبة، وهو أبو ذواد بن علبة، يشتبه بعلية المنتسب إليها إسماعيل','236','0','0');
