INSERT INTO [t13061] VALUES (1,'باب الحديث الذي يروى خلافه عن رسول الله صلى الله عليه وسلم','12','0','0');
INSERT INTO [t13061] VALUES (2,'باب أقاويل الصحابة رضي الله عنهم إذا تفرقوا فيها ويستدل به على معرفة الصحابة والتابعين ومن بعدهم من أكابر فقهاء الأمصار','20','0','0');
INSERT INTO [t13061] VALUES (3,'باب من له الفتوى والحكم','128','0','0');
INSERT INTO [t13061] VALUES (4,'باب ما يذكر من ذم الرأي وتكلف القياس في موضع النص قال الله جل ثناؤه فإن تنازعتم في شيء فردوه إلى الله والرسول قال: الشافعي رضي الله عنه فإن تنازعتم يعني: والله أعلم , هم وأمراؤهم الذين أمروا بطاعتهم , فردوه إلى الله والرسول يعني والله أعلم إلى ما قال','143','0','0');
INSERT INTO [t13061] VALUES (5,'باب ترك الحكم بتقليد أمثاله من أهل العلم حتى يعلم مثل علمهم قال الله جل ثناؤه: ولا تقف ما ليس لك به علم','191','0','0');
INSERT INTO [t13061] VALUES (6,'باب تقليد العامي للعالم قال الله جل ثناؤه: فاسألوا أهل الذكر إن كنتم لا تعلمون وقال: أطيعوا الله وأطيعوا الرسول وأولي الأمر منكم','199','0','0');
INSERT INTO [t13061] VALUES (7,'باب من كره المسألة عما لم يكن ولم ينزل به وحي','209','0','0');
INSERT INTO [t13061] VALUES (8,'باب العلم العام الذي لا يسع البالغ العاقل جهله','233','0','0');
INSERT INTO [t13061] VALUES (9,'باب العلم الخاص الذي لم تكلفه العامة وكلف على ذلك من فيه الكفاية للقيام به قال الشافعي رضي الله عنه: هذا مثل ما يكون منهم في الصلاة من سهو يجب به سجود السهو أو لا يجب , وما يفسد الحج ولا يفسده , وما يجب به الفدية ولا يجب , مما يفعل وغير ذلك وقال في موضع','247','0','0');
INSERT INTO [t13061] VALUES (10,'باب فضل العلم قال الشافعي رضي الله عنه: والفضل في هذه الدرجة من العلم لمن قام بها على من عطلها قال البيهقي رضي الله عنه: قال الله عز وجل: يرفع الله الذين آمنوا منكم والذين أوتوا العلم درجات','251','0','0');
INSERT INTO [t13061] VALUES (11,'باب مذاكرة العلم والجلوس مع أهله','321','0','0');
INSERT INTO [t13061] VALUES (12,'باب فضل العلم خير من فضل العبادة','352','0','0');
INSERT INTO [t13061] VALUES (13,'باب كراهية طلب العلم لغير الله وما جاء في الترغيب في العمل بالعلم','376','0','0');
INSERT INTO [t13061] VALUES (14,'باب ما يكره لأهل العلم وغيرهم من التكبر والتجبر وإلزام الناس مخاطبتهم بما يخاطب به الجبابرة والسكوت إليه والسرور به أعاذنا الله من شرور أنفسنا ومن سيئات أعمالنا','433','0','0');
INSERT INTO [t13061] VALUES (15,'باب ما يستحب للعالم من توقي المشتبهات لئلا يغتر به الجاهل فيقع في الحرام','445','0','0');
INSERT INTO [t13061] VALUES (16,'باب كراهية منع العلم وهو علم الكتاب والسنة','467','0','0');
INSERT INTO [t13061] VALUES (17,'باب أداء النصيحة في تنبيه العامة على ما جهلوه','484','0','0');
INSERT INTO [t13061] VALUES (18,'باب تبيين الحديث وترتيله ليفهم عنه','488','0','0');
INSERT INTO [t13061] VALUES (19,'باب من أعاد الحديث ثلاثا ليفهم عنه','494','0','0');
