INSERT INTO [t13061] VALUES (21,'باب لا تحدث قوما حديثا لا تبلغه عقولهم','507','0','0');
INSERT INTO [t13061] VALUES (22,'باب من قال: من إضاعة العلم أن تحدث به غير أهله','511','0','0');
INSERT INTO [t13061] VALUES (23,'باب تقريب الفتيان من طلاب العلم وترغيبهم في التعلم','519','0','0');
INSERT INTO [t13061] VALUES (24,'باب توقير العالم والعلم - قال الله جل ثناؤه: يا أيها الذين آمنوا لا ترفعوا أصواتكم فوق صوت النبي ولا تجهروا له بالقول كجهر بعضكم لبعض - وقال: لا تجعلوا دعاء الرسول بينكم كدعاء بعضكم بعضا. ولا درجة بعد النبوة أفضل من درجة العلم','547','0','0');
INSERT INTO [t13061] VALUES (25,'باب ما يذكر في القيام لأهل العلم وغيرهم على وجه الإكرام','595','0','0');
INSERT INTO [t13061] VALUES (26,'باب من كره أن يقام على وجه التعظيم مخافة الكبر','603','0','0');
INSERT INTO [t13061] VALUES (27,'باب من رخص في كتابة العلم وأحسبه حين أمن من اختلاطه بكتاب الله جل ثناؤه','625','0','0');
INSERT INTO [t13061] VALUES (28,'باب استعمال الصدق في العلم وفي كل شيء','656','0','0');
INSERT INTO [t13061] VALUES (29,'باب التوقي عن الفتيا والتثبت فيها','661','0','0');
INSERT INTO [t13061] VALUES (30,'باب ما يخشى من زلة العالم في العلم أو العمل','701','0','0');
INSERT INTO [t13061] VALUES (31,'باب ما يخشى من رفع العلم وظهور الجهل','716','0','0');
