INSERT INTO [t13064] VALUES (246,'إن الله ليبتلي عبده بالسقم حتى يكفر عنه بذلك ذنبه كله','246','0','0');
INSERT INTO [t13064] VALUES (247,'إن المريض إذا جزع فأذنب قال الملك الذي على اليمين للملك الذي على الشمال لا','247','0','0');
INSERT INTO [t13064] VALUES (248,'ما أراه إلا بذنب وما يعفو الله أكثر وتلا: {وما أصابكم من مصيبة فبما كسبت أيديكم} [الشورى:','248','0','0');
INSERT INTO [t13064] VALUES (249,'ما أصاب رجلا من المسلمين نكبة فما فوقها حتى ذكر الشوكة إلا لإحدى خصلتين إلا ليغفر الله له','249','0','0');
INSERT INTO [t13064] VALUES (250,'أذهب البأس رب الناس واشف أنت الشافي لا شافي إلا أنت','250','0','0');
INSERT INTO [t13064] VALUES (251,'من كتم حمى يوم أصابه أخرجه الله من ذنوبه كيوم ولدته أمه وكتب له براءة من النار وستر عليه','251','0','0');
INSERT INTO [t13064] VALUES (252,'إن شئت دعوت الله أن يعافيك وإن شئت صبرت ثلاثا فتخرج منها كيوم ولدتك أمك قال: بل أصبر يا','252','0','0');
INSERT INTO [t13064] VALUES (253,'لا خير في جسد لا يبتلى ولا خير في مال لا يرزأ منه إن الله إذا أحب عبدا ابتلاه وإذا ابتلاه','253','0','0');
INSERT INTO [t13064] VALUES (254,'ما يصاب المسلم من شوكة فما فوقها إلا كانت كفارة له','254','0','0');
INSERT INTO [t13064] VALUES (255,'ما من مريض يقول سبحان الملك القدوس الرحمن الملك الديان لا إله إلا أنت، مسكن العروق','255','0','0');
INSERT INTO [t13064] VALUES (256,'لما خلق الله الداء والدواء فالداء ثلاثة، والدواء ثلاثة فالداء المرة، والدم، والبلغم فدواء','256','0','0');
INSERT INTO [t13064] VALUES (257,'إياك ومجالسة الثقيل فإنا نجد في كتاب الطب أن مجالسة الثقيل حمى الروح ثم أنشد عبد الرحمن','257','0','0');
INSERT INTO [t13064] VALUES (258,'إنها سألت على قدرها فنعطيها على قدر النعمة علينا','258','0','0');
INSERT INTO [t13064] VALUES (259,'فكيف يتيسر حزني على مصيبتي فيما سلف من ذنوبي والشاهد لله علي قال: وغشي','259','0','0');
INSERT INTO [t13064] VALUES (260,'إذا أنا مت فأحرقوني فاذروا نصفي في البر ونصفي في البحر فوالله لئن أخذني الله عز وجل','260','0','0');
INSERT INTO [t13064] VALUES (261,'إن هذه بيدي منذ كذا وكذا لم آلو أن أحكم صنعتها فجئت بها إلى هذا البزاز فأخرج علي فيها','261','0','0');
