INSERT INTO [t13067] VALUES (31,'النساء " ومن يأكل أموال اليتامى ظلما فإنما يأكل في بطنه نارا وسوف يصلى سعيرا "، وفي قراءة عبد الله: (كتاب الله عليكم أحل لكم) بغير واو، وفي قراءة عبد الله: (وسيؤتي الله المؤمنين) ، (أو يغلب نؤته أجرا عظيما) ، وفي قراءة عبد الله: (بيت مبيت','200','0','0');
