INSERT INTO [t13067] VALUES (33,'الأنعام (ما كان فتنتهم) نصب. وفي قراءة عبد الله: (الموت يتوفاه رسلنا) ، وفي قراءة عبد الله: (يقضي بالحق وهو خير الفاصلين) ، وفي قراءة عبد الله: (يا ليتنا نرد ولا نكذب بآيات ربنا) ، وفي قراءة عبد الله: (كالذي استهواه الشيطان) ، وفي قراءة','202','0','0');
INSERT INTO [t13067] VALUES (34,'الأعراف وفي قراءة عبد الله: (وقد تركوك أن يعبدوك وآلهتك) ، (قالوا ربنا إلا تغفر لنا وترحمنا) ، (إن الذين استمسكوا بالكتاب)','203','0','0');
INSERT INTO [t13067] VALUES (35,'الأنفال وفي قراءة عبد الله: (والله مع المؤمنين) ، (ولا يحسب الذين كفروا سبقوا) يحسب بالياء بغير نون','204','0','0');
INSERT INTO [t13067] VALUES (36,'براءة (أن تتقبل منهم نفقاتهم) ، وفي قراءة عبد الله: (قل أذن خير ورحمة لكم) ، (ولو قطعت قلوبهم) , (أولم تر أنهم يفتنون) ، (من بعد ما زاغت قلوب طائفة)','205','0','0');
