INSERT INTO [t13067] VALUES (111,'من سورة البقرة كتبوا فباؤ بغضب بغير ألف، بئسما اشتروا به أنفسهم موصول، ولبئس ما شروا مقطوع، واذكروا نعمت الله بالتاء، يرجون رحمت الله بالتاء، لا انفصام لها بالألف، أولياؤهم الطغوت بغير الألف، وكتبوا في جميع القرآن " الربوا " بالواو والألف، إلا','421','0','0');
INSERT INTO [t13067] VALUES (112,'ومن سورة آل عمران ومن اتبعن بغير ياء، والأمين بياء واحدة، والنبين كذلك، فاتبعوني بإثبات الياء، إذ قالت امرأت عمران بالتاء، فنجعل لعنت الله بالتاء، واذكروا نعمت الله بالتاء، ففي رحمة الله بالهاء، تقاة بالألف، لكيلا تحزنوا موصولة، أين ما ثقفوا','422','0','0');
INSERT INTO [t13067] VALUES (113,'ومن سورة النساء والذان كتبوا بلام واحدة، أم من يكون عليهم وكيلا مقطوعة، أينما تكونوا موصولة، إن امرؤا هلك بالألف','423','0','0');
INSERT INTO [t13067] VALUES (114,'ومن سورة المائدة اذكروا نعمت الله عليكم بالتاء، وكتبوا في هذه السورة قبل هذه الآية بالهاء، يعني في نعمة، ألا تعدلوا بغير نون، والصبئون بغير ألف وياء، إلى الحوارين بياء واحدة، لبئس ما قدمت لهم مقطوعة، لبئس ما كانوا يعملون مقطوعة','424','0','0');
INSERT INTO [t13067] VALUES (115,'ومن سورة الأنعام وتمت كلمة ربك بالهاء، إن ما توعدون لآت مقطوعة، ليس في القرآن غيرها، إن الذين فرقوا دينهم بغير ألف، بالغدوة والعشي بالواو، وقد هدين بالياء، ولقد جاءك من نبإي بالياء، وما بالياء غير هذا، قل لا أجد في ما أوحي مقطوعة','425','0','0');
INSERT INTO [t13067] VALUES (116,'ومن سورة الأعراف إن لنا لأجرا بغير ياء، وكتبوا ابن أم مقطوعة، وإن شك فيه أبو بكر، وكتبوا إن رحمت الله بالتاء، وتمت كلمت ربك الحسنى بالتاء، فلما عتوا عن ما نهوا عنه مقطوعة، ليس في القرآن غيرها، أن لا يقولوا على الله، على أن لا أقول بالنون، (','426','0','0');
INSERT INTO [t13067] VALUES (117,'ومن سورة الأنفال فقد مضت سنت الأولين بالتاء','427','0','0');
INSERT INTO [t13067] VALUES (118,'ومن سورة التوبة أم من أسس بنيانه مقطوعة، ولا أوضعوا بالألف، وآخر سيئا بياءين','428','0','0');
INSERT INTO [t13067] VALUES (119,'ومن سورة يونس حقت كلمت ربك بالتاء، من تلقاءي نفسي بالياء، ننج المؤمنين ليس في القرآن غيره، لتلفتنا عن ما وجدنا يعني مقطوعة','429','0','0');
INSERT INTO [t13067] VALUES (120,'ومن سورة هود فإلم يستجيبوا لكم بغير نون، ليس في القرآن غيره، أن لا تعبدوا إلا الله بالنون، رحمت الله وبركته بالتاء، وآتني رحمة من عنده بالياء، وآتيني منه رحمة بالياء','430','0','0');
INSERT INTO [t13067] VALUES (121,'ومن سورة يوسف في غيابت الجب بالتاء، قالت امرأت العزيز بالتاء، وقال نسوة في المدينة امرأت العزيز بالتاء، لا تايئسوا من روح الله إنه لا يايئس من روح الله بالألف جميعا، يا أبت بالتاء، فنجي من نشاء بنون واحدة','431','0','0');
INSERT INTO [t13067] VALUES (122,'ومن سورة الرعد أفلم يايئس الذين آمنوا بالألف، وإن ما نرينك مقطوعة، ليس في القرآن غيره','432','0','0');
INSERT INTO [t13067] VALUES (123,'ومن سورة إبراهيم وإن تعدوا نعمت الله بالتاء، بدلوا نعمت الله بالتاء، وقد هدينا سبلنا بالياء','433','0','0');
INSERT INTO [t13067] VALUES (124,'ومن سورة الحجر وإن كان أصحاب الأيكة بالألف، وقد خلت سنت الأولين بالتاء، جزء مقسوم بغير واو','434','0','0');
INSERT INTO [t13067] VALUES (125,'ومن سورة النحل أفبنعمة الله يجحدون بالهاء هكذا عنده، يعرفون نعمت الله، واشكروا نعمت الله، وبنعمت الله هم يكفرون بالتاء، لكي لا مقطوعة، وعلى قول بعضهم لكيلا يعلم موصول','435','0','0');
INSERT INTO [t13067] VALUES (126,'ومن سورة بني إسرائيل الأقصا الذي بالألف','436','0','0');
INSERT INTO [t13067] VALUES (127,'ومن سورة مريم ذكر رحمت ربك بالتاء، ثلاث في جميع القرآن كلها بلا ألف، أين ما كنت مقطوعة، وأوصيني بالصلوة بالياء','437','0','0');
INSERT INTO [t13067] VALUES (128,'ومن سورة طه وأنا اخترتك بغير ألف، ومن أنإي الليل بالياء، فاتبعون، ألا تتبعن بغير ياء','438','0','0');
INSERT INTO [t13067] VALUES (129,'ومن سورة الأنبياء وحرم على قرية بغير ألف، وضياء وذكرا بالألف، ليس في القرآن غيره، وكذلك نجي المؤمنين بنون واحدة، وكان أبو عبيد يقول: (ننج) بغير ياء على قراءة عاصم، وهم في ما اشتهت يعني مقطوعة، ألا إله إلا أنت بغير نون','439','0','0');
INSERT INTO [t13067] VALUES (130,'ومن سورة الحج أن لا تشرك بالنون، يكادون يسطون بالسين، أنه من تولاه، لكيلا يعلم موصولة، وأن ما يدعون من دونه مقطوعة','440','0','0');
INSERT INTO [t13067] VALUES (131,'ومن سورة المؤمنين الذين هم في صلاتهم خاشعون بغير واو، وفي الآية الثانية والذين هم على صلواتهم بإثبات الواو، وكتبوا في الآية الأولى: قال الملؤا بالواو والألف، الحمد لله الذي نجينا بالياء','441','0','0');
INSERT INTO [t13067] VALUES (132,'ومن سورة النور والخامسة أن لعنت الله بالتاء، كل قد علم صلاته بلا واو','442','0','0');
INSERT INTO [t13067] VALUES (133,'ومن سورة الفرقان وعتو عتوا كبيرا بغير ألف، يعني في الأولى','443','0','0');
INSERT INTO [t13067] VALUES (134,'ومن سورة الشعراء وقيل لهم أين ما كنتم مقطوعة، أصحاب لئيكة بغير ألف','444','0','0');
INSERT INTO [t13067] VALUES (135,'ومن سورة النمل قالت يا أيها الملؤا بالواو والألف، يا أيها الملؤا أيكم مثله، فما آتين الله بالياء، أئنا لمخرجون بالياء، أتمدونن بغير ياء وبنونين','445','0','0');
INSERT INTO [t13067] VALUES (136,'ومن سورة القصص وقالت امرأت فرعون قرت عين لي بالتاء، أن يهديني سواء السبيل بإثبات الياء، يا أيها الملأ بغير واو','446','0','0');
INSERT INTO [t13067] VALUES (137,'ومن سورة العنكبوت إنكم لتأتون الفاحشة بغير ياء، أئنكم لتأتون الرجال بإثبات الياء','447','0','0');
INSERT INTO [t13067] VALUES (138,'ومن سورة الروم هل لكم من ما ملكت أيمانكم مقطوعة بإثبات النون، فانظر إلى آثار رحمت الله بالتاء، فطرت الله التي فطر بإثبات التاء، في ما رزقناكم مقطوعة','448','0','0');
INSERT INTO [t13067] VALUES (139,'ومن سورة لقمان ألم تر أن الفلك تجري في البحر بنعمت الله يعني بالتاء','449','0','0');
INSERT INTO [t13067] VALUES (140,'ومن سورة الأحزاب زوجناكها لكي لا يكون مقطوعة، وما ملكت أيمانهم لكيلا موصول، أين ما ثقفوا مقطوع، لآتوها بإثبات الألف، الظنونا والرسولا والسبيلا','450','0','0');
INSERT INTO [t13067] VALUES (141,'وفي سورة سبأ علم الغيب بغير ألف','451','0','0');
INSERT INTO [t13067] VALUES (142,'ومن سورة الملائكة يا أيها الناس اذكروا نعمت الله بالتاء، ولؤلؤ بغير ألف، سنت الله في الذين بالتاء، ولن تجد لسنت الله بالتاء','452','0','0');
INSERT INTO [t13067] VALUES (143,'ومن سورة يس وأن اعبدون بلا ياء، أن لا تعبدوا الشيطان بإثبات النون','453','0','0');
INSERT INTO [t13067] VALUES (144,'ومن سورة الصافات أم من خلقنا مقطوع، أئنا لتاركو آلهتنا بالياء والنون، إن هذا لهو البلؤ المبين بالواو، ولولا نعمت ربي بالتاء','454','0','0');
INSERT INTO [t13067] VALUES (145,'ومن سورة ص ولات حين مناص مقطوع، لئيكة بغير ألف، ذكرى الدار بالياء، أم عندهم خزائن رحمت ربك بالتاء، وانطلق الملأ منهم بغير واو وبغير ألف، هذا عطاؤنا بالواو','455','0','0');
INSERT INTO [t13067] VALUES (146,'ومن سورة الزمر لا تقنطوا من رحمة الله يعني بالهاء، لو أن الله هدايني بالياء','456','0','0');
INSERT INTO [t13067] VALUES (147,'ومن سورة المؤمن أين ما كنتم مقطوعة، سنت الله التي قد خلت بالتاء، وكذلك حقت كلمت ربك بالتاء، يوم هم بارزون مقطوع، ومن تقي السيئات بياء واحدة، لدى الحناجر بالياء، يا قوم اتبعون يعني بغير ياء','457','0','0');
INSERT INTO [t13067] VALUES (148,'ومن سورة حم السجدة أم من يأتي آمنا مقطوعة، وما تخرج من ثمرات بتاء','458','0','0');
INSERT INTO [t13067] VALUES (149,'ومن سورة عسق ويعف عن كثير يعني بغير واو، ويمح الله الباطل بغير واو، فبما كسبت أيديكم ويعفوا عن كثير بالواو والألف، أو من وراءي حجاب بالياء، ليس في القرآن غيرها','459','0','0');
INSERT INTO [t13067] VALUES (150,'ومن سورة الزخرف أهم يقسمون رحمت ربك، بالتاء، ورحمت ربك خير مما يجمعون بالتاء، ثم تذكروا نعمة ربكم بالهاء، أيه الساحر بغير ألف وجعلوا الملائكة الذين هم عبد الرحمن بغير ألف','460','0','0');
INSERT INTO [t13067] VALUES (151,'ومن سورة الدخان ما فيه بلؤا يعني بواو وألف، إن شجرت الزقوم بالتاء','461','0','0');
