INSERT INTO [t13067] VALUES (155,'ومن سورة الذاريات والسماء بنيناها بأييد بياءين','465','0','0');
INSERT INTO [t13067] VALUES (156,'ومن سورة الطور فما أنت بنعمت ربك بالتاء','466','0','0');
INSERT INTO [t13067] VALUES (157,'ومن سورة النجم وثمودا فما أبقى بالألف، ما كذب الفؤاد ما رأى بالياء والألف، لقد رأى من آيات ربه الكبرى يعني بالياء، ليس في القرآن غيره إلا هذين الحرفين، فأعرض عمن موصول، ومنوة الثالثة بالواو، أزفت الآزفت بالتاء','467','0','0');
INSERT INTO [t13067] VALUES (158,'ومن سورة القمر فما تغن النذر بغير ياء، يوم يدع الداع بغير ياء، إلى الداع بغير ياء','468','0','0');
INSERT INTO [t13067] VALUES (159,'ومن سورة الرحمن تعالى أيه الثقلان بغير ألف','469','0','0');
