INSERT INTO [t13073] VALUES (1,'باب الترغيب في تعلم الناسخ والمنسوخ','1','0','0');
INSERT INTO [t13073] VALUES (2,'باب اختلاف العلماء في الذي ينسخ القرآن والسنة للعلماء في هذا خمسة أقوال فمنهم من يقول: ينسخ القرآن القرآن والسنة وهذا قول الكوفيين ومنهم من يقول: ينسخ القرآن القرآن، ولا يجوز أن تنسخه السنة وهذا قول الشافعي في جماعة معه وقال قوم: ينسخ السنة القرآن','9','0','0');
INSERT INTO [t13073] VALUES (3,'باب أصل النسخ واشتقاقه " اشتقاق النسخ من شيئين أحدهما أنه يقال: نسخت الشمس الظل إذا أزالته وحلت محله، ونظير هذا فينسخ الله ما يلقي الشيطان والآخر من نسخت الكتاب إذا نقلته من نسخة، وعلى هذا الناسخ والمنسوخ، وأصله أن يكون الشيء حلالا إلى مدة ثم ينسخ','13','0','0');
INSERT INTO [t13073] VALUES (4,'باب النسخ على كم يكون من ضرب أكثر النسخ في كتاب الله جل وعز على ما تقدم في الباب الذي قبل هذا أن يزال الحكم بنقل العباد عنه مشتق من نسخت الكتاب ويبقى المنسوخ متلوا','14','0','0');
INSERT INTO [t13073] VALUES (5,'باب الفرق بين النسخ والبداء الفرق بين النسخ والبداء أن النسخ تحويل العباد من شيء قد كان حلالا فيحرم أو كان حراما فيحلل أو كان مطلقا فيحظر أو كان محظورا فيطلق أو كان مباحا فيمنع أو ممنوعا فيباح إرادة الصلاح للعباد وقد علم الله جل وعز العاقبة في ذلك وعلم','20','0','0');
INSERT INTO [t13073] VALUES (6,'باب ذكر بعض هذه الأحاديث','22','0','0');
INSERT INTO [t13073] VALUES (7,'باب السور التي فيها الناسخ والمنسوخ','25','6','0');
INSERT INTO [t13073] VALUES (8,'فأول ذلك السورة التي تذكر فيها البقرة','26','0','0');
INSERT INTO [t13073] VALUES (9,'باب ذكر الآية الثانية من هذه السورة قال الله جل وعز ولله المشرق والمغرب فأينما تولوا فثم وجه الله إن الله واسع عليم فللعلماء في هذه الآية ستة أقوال: قال قتادة: " هي منسوخة "، وذهب إلى أن المعنى صلوا كيف شئتم فإن المشرق والمغرب لله فحيث استقبلتم فثم','33','8','0');
INSERT INTO [t13073] VALUES (10,'باب ذكر الآية الثالثة من هذه السورة قال الله جل وعز حافظوا على الصلوات والصلاة الوسطى وقوموا لله قانتين قال أبو جعفر: أما ما ذكر في الحديث " والصلاة الوسطى وصلاة العصر " فيقال: إن هذا نسخ أي: رفع ويقال: إن هذه قراءة على التفسير أي حافظوا على الصلوات','38','8','0');
INSERT INTO [t13073] VALUES (11,'باب ذكر الآية الرابعة يا أيها الذين آمنوا كتب عليكم القصاص في القتلى الحر بالحر والعبد بالعبد والأنثى بالأنثى فمن عفي له من أخيه شيء فاتباع بالمعروف وأداء إليه بإحسان ذلك تخفيف من ربكم ورحمة فمن اعتدى بعد ذلك فله عذاب أليم في هذه الآية موضعان أحدهما الحر','41','8','0');
INSERT INTO [t13073] VALUES (12,'باب ذكر الآية الخامسة قال الله جل وعز كتب عليكم إذا حضر أحدكم الموت إن ترك خيرا الوصية للوالدين والأقربين بالمعروف حقا على المتقين في هذه الآية خمسة أقوال: فمن قال: إن القرآن يجوز أن ينسخ بالسنة، قال: نسخها: " لا وصية لوارث " ومن قال من الفقهاء: لا','46','8','0');
INSERT INTO [t13073] VALUES (13,'باب ذكر قوله جل وعز كتب عليكم الصيام كما كتب على الذين من قبلكم لعلكم تتقون وهي الآية السادسة قال أبو جعفر: في هذه الآية خمسة أقوال: قال جابر بن سمرة: " هي ناسخة لصوم يوم عاشوراء "، يذهب إلى أن النبي صلى الله عليه وسلم أمر بصوم يوم عاشوراء فلما فرض','48','8','0');
INSERT INTO [t13073] VALUES (14,'باب ذكر الآية السابعة قال جل وعز وعلى الذين يطيقونه فدية طعام مسكين فمن تطوع خيرا فهو خير له وأن تصوموا خير لكم إن كنتم تعلمون قال أبو جعفر: في هذه الآية أقوال أصحها أنها منسوخة، سياق الآية يدل على ذلك والنظر والتوقيف من رجلين من أصحاب رسول الله صلى','51','8','0');
INSERT INTO [t13073] VALUES (15,'باب ذكر الآية الثامنة قال الله جل وعز أحل لكم ليلة الصيام الرفث إلى نسائكم الآية قال أبو العالية، وعطاء: هي ناسخة لقوله جل وعز كما كتب على الذين من قبلكم وقال غيرهما: هي ناسخة لفعلهم الذي كانوا عليه','54','8','0');
INSERT INTO [t13073] VALUES (16,'باب ذكر ما في الآية التاسعة قال جل وعز وقولوا للناس حسنا','56','8','0');
INSERT INTO [t13073] VALUES (17,'باب ذكر الآية العاشرة قال جل وعز يا أيها الذين آمنوا لا تقولوا راعنا وقولوا انظرنا','58','8','0');
INSERT INTO [t13073] VALUES (18,'باب ذكر الآية الحادية عشرة قال الله جل وعز ود كثير من أهل الكتاب لو يردونكم من بعد إيمانكم كفارا حسدا من عند أنفسهم من بعد ما تبين لهم الحق فاعفوا واصفحوا حتى يأتي الله بأمره إن الله على كل شيء قدير','60','8','0');
INSERT INTO [t13073] VALUES (19,'باب ذكر الآية الثانية عشرة قال جل وعز وقاتلوا في سبيل الله الذين يقاتلونكم ولا تعتدوا إن الله لا يحب المعتدين قال ابن زيد: " هي منسوخة نسخها وقاتلوا المشركين كافة كما يقاتلونكم كافة " وعن ابن عباس: " أنها محكمة " روى عنه ابن أبي طلحة وقاتلوا في سبيل','62','8','0');
INSERT INTO [t13073] VALUES (20,'باب ذكر الآية الثالثة عشرة قال جل وعز ولا تقاتلوهم عند المسجد الحرام حتى يقاتلوكم فيه فإن قاتلوكم فاقتلوهم كذلك جزاء الكافرين هذه الآية من أصعب ما في الناسخ والمنسوخ فزعم جماعة من العلماء أنها غير منسوخة واحتجوا بها وبأشياء من السنن وزعم جماعة أنها','64','8','0');
INSERT INTO [t13073] VALUES (21,'باب ذكر الآية الرابعة عشرة قال جل وعز الشهر الحرام بالشهر الحرام والحرمات قصاص فمن اعتدى عليكم فاعتدوا عليه بمثل ما اعتدى عليكم','70','8','0');
INSERT INTO [t13073] VALUES (22,'باب ذكر الآية الخامسة عشرة قال جل وعز: كتب عليكم القتال وهو كره لكم فقال قوم: هي ناسخة لحظر القتال عليهم ولما أمروا به من الصفح والعفو بمكة وقال قوم: هي منسوخة وكذا قالوا في قوله جل وعز انفروا خفافا وثقالا والناسخة وما كان المؤمنون لينفروا كافة فلولا','72','8','0');
INSERT INTO [t13073] VALUES (23,'باب ذكر الآية السادسة عشرة قال جل وعز: يسألونك عن الشهر الحرام قتال فيه قل قتال فيه كبير الآية العلماء على أن هذه الآية منسوخة وأن قتال المشركين في الأشهر الحرم مباح غير عطاء فإنه قال: " الآية محكمة ولا يجوز القتال في الأشهر الحرم "','76','8','0');
INSERT INTO [t13073] VALUES (24,'باب ذكر الآية السابعة عشرة قال الله جل وعز وأتموا الحج والعمرة لله الآية، وقد صح عن رسول الله صلى الله عليه وسلم أنه أمر أصحابه بعد أن أحرموا بالحج ففسخوه وجعلوه عمرة واختلف العلماء في فسخ أصحاب رسول الله صلى الله عليه وسلم الحج بعد أن أهلوا به إلى','80','8','0');
INSERT INTO [t13073] VALUES (25,'باب ذكر الآية الثامنة عشرة قال جل وعز يسألونك عن الخمر والميسر قل فيهما إثم كبير ومنافع للناس وإثمهما أكبر من نفعهما قال جماعة من العلماء: هذه الآية ناسخة لما كان مباحا من شرب الخمر وقال آخرون: هي منسوخة بتحريم الخمر في قوله جل وعز فاجتنبوه قال أبو','97','8','0');
INSERT INTO [t13073] VALUES (26,'باب ذكر الآية التاسعة عشرة قال جل وعز قل العفو فيه ثلاثة أقوال من العلماء من قال: إنها منسوخة بالزكاة المفروضة، ومنهم من قال: هي الزكاة، ومنهم من قال: هو شيء أمر به غير الزكاة لم ينسخ','146','8','0');
INSERT INTO [t13073] VALUES (27,'باب ذكر الآية التي هي تتمة العشرين قال الله جل وعز ولا تنكحوا المشركين حتى يؤمنوا فيها ثلاثة أقوال من العلماء من قال هي منسوخة ومنهم من قال هي ناسخة ومنهم من قال هي محكمة لا ناسخة ولا منسوخة فممن قال إنها منسوخة ابن عباس','151','8','0');
INSERT INTO [t13073] VALUES (28,'باب ذكر الآية الحادية والعشرين قال جل وعز: ويسألونك عن المحيض قل هو أذى فاعتزلوا النساء في المحيض ولا تقربوهن حتى يطهرن فإذا تطهرن قال أبو جعفر: أدخلت هذه الآية في الناسخ والمنسوخ لأنه معروف من شريعة بني إسرائيل أن لا يجتمعوا مع الحائض في بيت ولا','159','8','0');
INSERT INTO [t13073] VALUES (29,'باب ذكر الآية الثانية والعشرين قال جل وعز: والمطلقات يتربصن بأنفسهن ثلاثة قروء الآية قال أبو جعفر: فممن جعلها في الناسخ والمنسوخ الضحاك عن ابن عباس، وقتادة إلا أن لفظ ابن عباس أن قال: استثنى، ولفظ قتادة نسخ قال: قال الله تبارك وتعالى: والمطلقات','166','8','0');
INSERT INTO [t13073] VALUES (30,'باب ذكر الآية الثالثة والعشرين قال الله جل وعز الطلاق مرتان الآية فمن العلماء من يقول هي ناسخة لما كانوا عليه لأنهم كانوا في الجاهلية مدة، وفي أول الإسلام برهة يطلق الرجل امرأته ما شاء من الطلاق فإذا كادت تحل من طلاقها راجعها ما شاء فنسخ الله ذلك بأنه','183','8','0');
INSERT INTO [t13073] VALUES (31,'باب ذكر الآية الرابعة والعشرين قال الله جل وعز: وعلى الوارث مثل ذلك في هذه الآية للعلماء أقوال فمنهم من قال هي منسوخة ومنهم من قال إنها محكمة والذين قالوا إنها محكمة لهم فيها ستة أجوبة فمنهم من قال: وعلى الوارث مثل ذلك ألا يضار، ومنهم من قال: الوارث','191','8','0');
INSERT INTO [t13073] VALUES (32,'باب ذكر الآية الخامسة والعشرين قال جل وعز والذين يتوفون منكم ويذرون أزواجا يتربصن بأنفسهن أربعة أشهر وعشرا الآية، أكثر العلماء على أن هذه الآية ناسخة لقوله جل وعز: والذين يتوفون منكم ويذرون أزواجا وصية لأزواجهم متاعا إلى الحول غير إخراج لأن الناس','198','8','0');
INSERT INTO [t13073] VALUES (33,'باب ذكر الآية السادسة والعشرين قال جل وعز: لا جناح عليكم إن طلقتم النساء ما لم تمسوهن أو تفرضوا لهن فريضة ومتعوهن على الموسع قدره وعلى المقتر قدره متاعا بالمعروف حقا على المحسنين فممن قال بظاهر الآية وأنه واجب على كل مطلق المتعة للمطلقة كما قال الله جل','214','8','0');
INSERT INTO [t13073] VALUES (34,'باب ذكر الآية السابعة والعشرين قال جل وعز: لا إكراه في الدين فمن العلماء من قال: هي منسوخة لأن رسول الله صلى الله عليه وسلم قد أكره العرب على دين الإسلام وقاتلهم ولم يرض منهم إلا بالإسلام فممن قال بذلك سليمان بن موسى قال: نسخها يا أيها النبي جاهد','218','8','0');
INSERT INTO [t13073] VALUES (35,'باب ذكر الآية الثامنة والعشرين قال جل وعز وإن كان ذو عسرة فنظرة إلى ميسرة فمن قال إنها ناسخة احتج بأن الإنسان في أول الإسلام كان إذا أعسر من دين عليه بيع حتى يستوفي المدين دينه منه فنسخ الله جل وعز ذلك بقوله جل ثناؤه وإن كان ذو عسرة فنظرة إلى ميسرة ويدلك','222','8','0');
INSERT INTO [t13073] VALUES (36,'باب ذكر الآية التاسعة والعشرين قال جل وعز: يا أيها الذين آمنوا إذا تداينتم بدين إلى أجل مسمى فاكتبوه الآية فافترق العلماء فيها على ثلاثة أقوال فمنهم من قال: لا يسع مؤمنا إذا باع بيعا إلى أجل أو اشترى إلا أن يكتب كتابا ويشهد إذا وجد كاتبا ولا يسع مؤمنا','225','8','0');
INSERT INTO [t13073] VALUES (37,'باب ذكر الآية التي هي تتمة ثلاثين آية قال جل وعز وإن تبدوا ما في أنفسكم أو تخفوه يحاسبكم به الله الآية فعن ابن عباس فيها ثلاثة أقوال: إحداهن: أنها منسوخة بقوله تعالى: لا يكلف الله نفسا إلا وسعها لها ما كسبت وعليها ما اكتسبت وسنذكره بإسناده والثاني:','230','8','0');
INSERT INTO [t13073] VALUES (38,'سورة آل عمران قال أبو جعفر: لم نجد في هذه السورة بعد تقص شديد مما ذكر في الناسخ والمنسوخ إلا ثلاث آيات ولولا محبتنا أن يكون الكتاب مشتملا على كل ما ذكر منها لكان القول فيها: إنها ليست بناسخة ولا منسوخة ونحن نبين ذلك إن شاء الله','235','0','0');
INSERT INTO [t13073] VALUES (39,'باب ذكر الآية الأولى من هذه السورة قال جل وعز: قال آيتك ألا تكلم الناس ثلاثة أيام إلا رمزا فزعم بعض أهل العلم أن هذا منسوخ وذلك أن هذه شريعة قد ذكرها الله تعالى فكان لنا أن نستعملها ما لم تنسخ ثم إنها نسخت على لسان رسول الله صلى الله عليه وسلم','236','38','0');
INSERT INTO [t13073] VALUES (40,'باب ذكر الآية الثانية قال جل وعز: يا أيها الذين آمنوا اتقوا الله حق تقاته فمن أجل ما روي في تفسيرها وأصحه','238','38','0');
INSERT INTO [t13073] VALUES (41,'باب ذكر الآية الثالثة قال جل وعز: ليس لك من الأمر شيء أو يتوب عليهم أو يعذبهم فإنهم ظالمون فزعم بعض الكوفيين أن هذه الآية ناسخة للقنوت الذي كان النبي صلى الله عليه وسلم يفعله بعد الركوع في الركعة الأخيرة من الصبح واحتج بحديث','243','38','0');
INSERT INTO [t13073] VALUES (42,'سورة النساء قال جل وعز: وإن خفتم ألا تقسطوا في اليتامى فانكحوا ما طاب لكم من النساء مثنى وثلاث ورباع فإن خفتم ألا تعدلوا فواحدة أو ما ملكت أيمانكم قال أبو جعفر: في هذه الآية إشكال وتفسير ونحو، وقد ذكرنا ما فيها إلا ما كان من النسخ فإنها على مذهب جماعة','250','0','0');
INSERT INTO [t13073] VALUES (43,'باب ذكر الآية الثانية من هذه السورة قال جل وعز مخاطبا للأوصياء في أموال اليتامى ومن كان غنيا فليستعفف ومن كان فقيرا فليأكل بالمعروف فمنع جماعة من أهل العلم الوصي من أخذ شيء من مال اليتيم فحكى بشر بن الوليد عن أبي يوسف، قال: لا أدري لعل هذه الآية منسوخة','254','42','0');
INSERT INTO [t13073] VALUES (44,'باب ذكر الآية الثالثة قال جل وعز: وإذا حضر القسمة أولو القربى واليتامى والمساكين فارزقوهم منه وقولوا لهم قولا معروفا للعلماء فيها ثلاثة أقوال فمنهم من قال إنها منسوخة ومنهم من قال: هي محكمة واجبة ومنهم من قال: هي محكمة على الندب والترغيب والحض، فممن','266','42','0');
INSERT INTO [t13073] VALUES (45,'باب ذكر الآية الرابعة والخامسة قال جل وعز: واللاتي يأتين الفاحشة من نسائكم فاستشهدوا عليهن أربعة منكم فإن شهدوا فأمسكوهن في البيوت حتى يتوفاهن الموت أو يجعل الله لهن سبيلا واللذان يأتيانها منكم فآذوهما إلى آخر الآية','272','42','0');
INSERT INTO [t13073] VALUES (46,'باب ذكر الآية السادسة قال جل وعز: وأحل لكم ما وراء ذلكم وفيها فما استمتعتم به منهن فآتوهن أجورهن فريضة فقوله جل وعز: وأحل لكم ما وراء ذلكم، لولا ما جاء فيه من النسخ لم يكن تحريم سوى ما في الآية وحرم الله جل وعز على لسان رسوله صلى الله عليه وسلم من لم','280','42','0');
INSERT INTO [t13073] VALUES (47,'باب ذكر الآية السابعة قال الله جل وعز والذين عقدت أيمانكم فآتوهم نصيبهم فمن أصح ما روي في هذه الآية إسنادا وأجله قائلا','294','42','0');
INSERT INTO [t13073] VALUES (48,'باب ذكر الآية الثامنة قال جل وعز يا أيها الذين آمنوا لا تقربوا الصلاة وأنتم سكارى حتى تعلموا ما تقولون أكثر العلماء على أنها منسوخة غير أنهم يختلفون في الناسخ لها','301','42','0');
INSERT INTO [t13073] VALUES (49,'باب ذكر الآية التاسعة قال جل وعز: إلا الذين يصلون إلى قوم بينكم وبينهم ميثاق أو جاءوكم حصرت صدورهم أن يقاتلوكم أو يقاتلوا قومهم ولو شاء الله لسلطهم عليكم فلقاتلوكم فإن اعتزلوكم فلم يقاتلوكم وألقوا إليكم السلم فما جعل الله لكم عليهم سبيلا أهل التأويل على','306','42','0');
INSERT INTO [t13073] VALUES (50,'باب ذكر الآية العاشرة قال جل وعز: ومن يقتل مؤمنا متعمدا فجزاؤه جهنم خالدا فيها وغضب الله عليه ولعنه وأعد له عذابا عظيما فمن العلماء من قال: لا توبة لمن قتل مؤمنا متعمدا وبعض من قال هذا قال الآية التي في الفرقان منسوخة بالآية التي في النساء فهذا قول ومن','310','42','0');
INSERT INTO [t13073] VALUES (51,'سورة المائدة اختلف العلماء في هذه السورة فمنهم من قال لم ينسخ منها شيء ومنهم من احتج بأنها آخر سورة نزلت فلا يجوز أن يكون فيها منسوخ','321','0','0');
INSERT INTO [t13073] VALUES (52,'باب ذكر الآية الأولى من هذه السورة قال جل وعز: يا أيها الذين آمنوا لا تحلوا شعائر الله ولا الشهر الحرام ولا الهدي ولا القلائد ولا آمين البيت الحرام ذهب جماعة من العلماء إلى أن هذه الأحكام الخمسة منسوخة وذهب بعضهم إلى أن فيها منسوخا وذهب بعضهم إلى أنها','325','51','0');
INSERT INTO [t13073] VALUES (53,'باب ذكر الآية الثانية قال الله جل وعز: اليوم أحل لكم الطيبات وطعام الذين أوتوا الكتاب حل لكم وطعامكم حل لهم فقالوا فيها ثلاثة أقوال فمنهم من قال: أحل لنا طعام أهل الكتاب وإن ذكروا عليه غير اسم الله فكان هذا ناسخا لقوله تعالى: ولا تأكلوا مما لم يذكر','329','51','0');
INSERT INTO [t13073] VALUES (54,'باب ذكر الآية الثالثة قال جل وعز: يا أيها الذين آمنوا إذا قمتم إلى الصلاة فاغسلوا وجوهكم وأيديكم إلى المرافق الآية فيها سبعة أقوال: فمن العلماء من قال: هي ناسخة لقوله جل وعز لا تقربوا الصلاة وأنتم سكارى، ومنهم من قال: هي ناسخة لما كانوا عليه لأن','336','51','0');
INSERT INTO [t13073] VALUES (55,'باب ذكر الآية الرابعة قال الله جل وعز: فاعف عنهم واصفح من العلماء من قال إنما كان العفو والصفح قبل الأمر بالقتال ثم نسخ ذلك بالأمر بالقتال','347','51','0');
INSERT INTO [t13073] VALUES (56,'باب ذكر الآية الخامسة قال جل وعز: إنما جزاء الذين يحاربون الله ورسوله ويسعون في الأرض فسادا أن يقتلوا أو يصلبوا أو تقطع أيديهم وأرجلهم من خلاف أو ينفوا من الأرض فقال قوم: هذه ناسخة لما كان رسول الله صلى الله عليه وسلم فعله في أمر العرنيين من التمثيل','349','51','0');
INSERT INTO [t13073] VALUES (57,'باب ذكر الآية السادسة قال جل وعز: فإن جاءوك فاحكم بينهم أو أعرض عنهم من العلماء من قال هذه الآية محكمة والإمام مخير إذا تحاكم إليه أهل الكتاب إن شاء حكم بينهم وإن شاء أعرض عنهم وردهم إلى حكامهم هذا قول الشعبي، وإبراهيم النخعي','358','51','0');
INSERT INTO [t13073] VALUES (58,'باب ذكر الآية السابعة قال الله جل وعز يا أيها الذين آمنوا شهادة بينكم إذا حضر أحدكم الموت حين الوصية اثنان ذوا عدل منكم أو آخران من غيركم إن أنتم ضربتم الآية للصحابة والتابعين والفقهاء في هذه الآية خمسة أقوال منها أن شهادة أهل الكتاب على المسلمين جائزة','363','51','0');
INSERT INTO [t13073] VALUES (59,'سورة الأنعام','368','0','0');
INSERT INTO [t13073] VALUES (60,'باب ذكر الآية الثانية قال الله عز وجل وما على الذين يتقون من حسابهم من شيء','371','59','0');
INSERT INTO [t13073] VALUES (61,'باب ذكر الآية الثالثة قال جل وعز: وذر الذين اتخذوا دينهم لعبا ولهوا','373','59','0');
INSERT INTO [t13073] VALUES (62,'باب ذكر الآية الرابعة قال جل وعز: وهو الذي أنشأ جنات معروشات وغير معروشات والنخل والزرع مختلفا أكله والزيتون والرمان متشابها وغير متشابه كلوا من ثمره إذا أثمر وآتوا حقه يوم حصاده ولا تسرفوا إنه لا يحب المسرفين للصحابة والتابعين والفقهاء في هذه الآية','375','59','0');
INSERT INTO [t13073] VALUES (63,'باب ذكر الآية الخامسة قال جل وعز: قل لا أجد في ما أوحي إلي محرما على طاعم يطعمه إلا أن يكون ميتة أو دما مسفوحا أو لحم خنزير فإنه رجس أو فسقا أهل لغير الله به في هذه الآية خمسة أقوال، قالت طائفة هي منسوخة لأنه وجب منها ألا يحرم ألا ما فيها فلما حرم رسول','386','59','0');
INSERT INTO [t13073] VALUES (64,'سورة الأعراف','398','0','0');
INSERT INTO [t13073] VALUES (65,'سورة الأنفال','405','0','0');
INSERT INTO [t13073] VALUES (66,'باب ذكر الآية الثانية قال الله جل وعز ومن يولهم يومئذ دبره إلا متحرفا لقتال أو متحيزا إلى فئة فقد باء بغضب من الله ومأواه جهنم وبئس المصير للعلماء في هذه الآية ثلاثة أقوال منهم من قال: هي منسوخة ومنهم من قال: هي مخصوصة لأهل بدر لأنها فيهم نزلت، ومنهم','416','65','0');
INSERT INTO [t13073] VALUES (67,'باب ذكر الآية الثالثة قال جل وعز وما كان الله ليعذبهم وأنت فيهم وما كان الله معذبهم وهم يستغفرون للعلماء في هذه الآية خمسة أقوال: قال الحسن: نسخ وما كان الله معذبهم وهم يستغفرون قوله وما لهم ألا يعذبهم الله قال أبو جعفر: النسخ هاهنا محال لأنه خبر خبر','421','65','0');
INSERT INTO [t13073] VALUES (68,'باب ذكر الآية الرابعة قال جل وعز وإن جنحوا للسلم فاجنح لها','427','65','0');
INSERT INTO [t13073] VALUES (69,'باب ذكر الآية الخامسة قال جل وعز: يا أيها النبي حرض المؤمنين على القتال إن يكن منكم عشرون صابرون يغلبوا مائتين وإن يكن منكم مائة يغلبوا ألفا من الذين كفروا','429','65','0');
INSERT INTO [t13073] VALUES (70,'باب ذكر الآية السادسة قال جل وعز: ما كان لنبي أن يكون له أسرى حتى يثخن في الأرض','432','65','0');
INSERT INTO [t13073] VALUES (71,'باب ذكر الآية السابعة قال جل وعز: فكلوا مما غنمتم حلالا طيبا فكان هذا ناسخا لما تقدم من حكم الله جل وعز في حظر الغنائم لأنها لم تحل لأحد قبل أمة محمد عليه السلام وإنما كانت تنزل نار من السماء فتأكلها والدليل على صحة هذا قول النبي صلى الله عليه وسلم " لم','434','65','0');
INSERT INTO [t13073] VALUES (72,'باب ذكر الآية الثامنة قال جل وعز: والذين آمنوا ولم يهاجروا ما لكم من ولايتهم من شيء حتى يهاجروا','435','65','0');
INSERT INTO [t13073] VALUES (73,'سورة براءة قال أبو جعفر: لا أعلم خلافا أنها من آخر ما نزل بالمدينة ولذلك قل المنسوخ فيها ويدلك على ذلك','438','0','0');
INSERT INTO [t13073] VALUES (74,'باب ذكر الآية الأولى منها قال جل وعز: براءة من الله ورسوله إلى الذين عاهدتم من المشركين فسيحوا في الأرض أربعة أشهر للعلماء في هذه الآية سبعة أقوال','448','73','0');
INSERT INTO [t13073] VALUES (75,'باب ذكر الآية الثانية قال جل وعز: فإذا انسلخ الأشهر الحرم فاقتلوا المشركين حيث وجدتموهم الآية للعلماء في هذه الآية ثلاثة أقوال فمنهم من قال: هي منسوخة وقال لا يحل قتل أسير صبرا وإنما يمن عليه أو يفادى وقالوا الناسخ لها قوله فإما منا بعد وإما فداء فممن','457','73','0');
INSERT INTO [t13073] VALUES (76,'باب ذكر الآية الثالثة قال تعالى: إنما المشركون نجس فلا يقربوا المسجد الحرام بعد عامهم هذا فكانت هذه الآية ناسخة لما كان رسول الله صلى الله عليه وسلم صالح عليه المشركين أن لا يمنع من البيت أحد وقد قال تعالى: ولا تقاتلوهم عند المسجد الحرام حتى يقاتلوكم','463','73','0');
INSERT INTO [t13073] VALUES (77,'باب ذكر الآية الرابعة قال جل ذكره: قاتلوا الذين لا يؤمنون بالله ولا باليوم الآخر الآية فمن العلماء من يقول هذه الآية ناسخة للعفو عن المشركين لأنه كان قتالهم ممنوعا منه فنسخ الله تعالى ذلك','465','73','0');
INSERT INTO [t13073] VALUES (78,'باب ذكر الآية الخامسة قال جل وعز: إلا تنفروا يعذبكم عذابا أليما','467','73','0');
INSERT INTO [t13073] VALUES (79,'باب ذكر الآية السادسة من هذه السورة','469','73','0');
INSERT INTO [t13073] VALUES (80,'باب ذكر الآية السابعة قال جل وعز: إنما الصدقات للفقراء والمساكين أدخلت في الناسخ والمنسوخ لأنها نسخت كل صدقة في القرآن','473','73','0');
INSERT INTO [t13073] VALUES (81,'باب ذكر الآية الثامنة قال الله جل وعز: استغفر لهم أو لا تستغفر لهم إن تستغفر لهم من العلماء من قال: هي منسوخة بقوله تعالى: ولا تصل على أحد منهم مات أبدا الآية','479','73','0');
INSERT INTO [t13073] VALUES (82,'باب ذكر الآية التاسعة قال الله جل وعز: ما كان لأهل المدينة ومن حولهم من الأعراب أن يتخلفوا عن رسول الله ولا يرغبوا بأنفسهم عن نفسه مذهب ابن زيد أنه نسخها وما كان المؤمنون لينفروا كافة ومذهب غيره أنه ليس هاهنا ناسخ ولا منسوخ وأن الآية الأولى توجب إذا نفر','483','73','0');
INSERT INTO [t13073] VALUES (83,'سورة يونس صلى الله عليه وسلم','484','0','0');
INSERT INTO [t13073] VALUES (84,'سورة هود','486','0','0');
INSERT INTO [t13073] VALUES (85,'سورة يوسف صلى الله عليه وسلم','489','0','0');
INSERT INTO [t13073] VALUES (86,'سورة الرعد','493','0','0');
INSERT INTO [t13073] VALUES (87,'سورة إبراهيم صلى الله عليه وسلم','498','0','0');
INSERT INTO [t13073] VALUES (88,'سورة الحجر','500','0','0');
INSERT INTO [t13073] VALUES (89,'سورة النحل','502','0','0');
INSERT INTO [t13073] VALUES (90,'سورة بني إسرائيل','506','0','0');
INSERT INTO [t13073] VALUES (91,'باب ذكر الآية الثانية قال الله جل وعز: ولا تقربوا مال اليتيم إلا بالتي هي أحسن حتى يبلغ أشده','512','90','0');
INSERT INTO [t13073] VALUES (92,'باب ذكر الآية الثالثة قال الله جل وعز: ولا تجهر بصلاتك ولا تخافت بها وابتغ بين ذلك سبيلا، فيها ثلاثة أقوال في رواية الضحاك عن ابن عباس: " نسختها الآية في سورة الأعراف واذكر ربك في نفسك تضرعا وخيفة ودون الجهر من القول، قال دون العلانية من القراءة','514','90','0');
INSERT INTO [t13073] VALUES (93,'سورة الكهف، ومريم، وطه، والأنبياء عليهم السلام','518','0','0');
INSERT INTO [t13073] VALUES (94,'سورة الحج','526','0','0');
INSERT INTO [t13073] VALUES (95,'باب ذكر الآية الثانية قال جل وعز: أذن للذين يقاتلون بأنهم ظلموا','534','94','0');
INSERT INTO [t13073] VALUES (96,'باب ذكر الآية الثالثة قال جل وعز: وما أرسلنا من قبلك من رسول ولا نبي إلا إذا تمنى ألقى الشيطان في أمنيته فينسخ الله ما يلقي الشيطان','536','94','0');
INSERT INTO [t13073] VALUES (97,'باب ذكر الآية الرابعة قال جل وعز: وجاهدوا في الله حق جهاده من جعلها منسوخة قال: هي مثل قوله تعالى اتقوا الله حق تقاته فنسخها عنده فاتقوا الله ما استطعتم قال أبو جعفر: وهذا لا نسخ فيه وقد بيناه في سورة آل عمران','541','94','0');
INSERT INTO [t13073] VALUES (98,'سورة المؤمنون','542','0','0');
INSERT INTO [t13073] VALUES (99,'سورة النور','545','0','0');
INSERT INTO [t13073] VALUES (100,'باب ذكر الآية الثانية قال تعالى: يا أيها الذين آمنوا لا تدخلوا بيوتا غير بيوتكم حتى تستأنسوا وتسلموا على أهلها ذلكم خير لكم لعلكم تذكرون للعلماء في هذه الآية قولان فمنهم من قال: لما قال: لا تدخلوا بيوتا غير بيوتكم حتى تستأنسوا وتسلموا على أهلها كان','554','99','0');
INSERT INTO [t13073] VALUES (101,'باب في ذكر الآية الثالثة قال جل وعز: يا أيها الذين آمنوا ليستأذنكم الذين ملكت أيمانكم والذين لم يبلغوا الحلم منكم ثلاث مرات من قبل صلاة الفجر وحين تضعون ثيابكم من الظهيرة ومن بعد صلاة العشاء ثلاث عورات لكم للعلماء في هذه الآية ست أقوال منهم من قال: هي','562','99','0');
INSERT INTO [t13073] VALUES (102,'باب ذكر الآية الرابعة قال تعالى: ليس على الأعمى حرج ولا على الأعرج حرج ولا على المريض حرج الآية للعلماء فيها ستة أقوال منهم من قال في قوله تعالى ولا على أنفسكم إلى آخر الآية إنه منسوخ ومنهم من قال في الآية إنها ناسخة لما قال الله تعالى يا أيها الذين','571','99','0');
INSERT INTO [t13073] VALUES (103,'سورة الفرقان','581','0','0');
INSERT INTO [t13073] VALUES (104,'سورة الشعراء','585','0','0');
INSERT INTO [t13073] VALUES (105,'سورة النمل، والقصص، والعنكبوت، والروم','590','0','0');
INSERT INTO [t13073] VALUES (106,'سورة لقمان والم السجدة','594','0','0');
INSERT INTO [t13073] VALUES (107,'سورة الأحزاب','597','0','0');
INSERT INTO [t13073] VALUES (108,'باب ذكر الآية الأولى منها قال الله جل وعز ادعوهم لآبائهم هو أقسط عند الله فإن لم تعلموا آباءهم فإخوانكم في الدين ومواليكم فكان هذا ناسخا لما كانوا عليه من التبني وكان رسول الله صلى الله عليه وسلم قد تبنى زيد بن حارثة فنسخ التبني وأمر أن يدعوا من دعوا إلى','599','107','0');
INSERT INTO [t13073] VALUES (109,'باب ذكر الآية الثانية قال جل وعز: لا يحل لك النساء من بعد ولا أن تبدل بهن من أزواج ولو أعجبك حسنهن إلا ما ملكت يمينك للعلماء في هذه الآية ثمانية أقوال منهم من قال: هي منسوخة بالسنة ومنهم من قال: هي منسوخة بآية أخرى وكان الله عز وجل قد حظر عليه التزوج','601','107','0');
INSERT INTO [t13073] VALUES (110,'سورة سبأ وفاطر، ويس والصافات','605','0','0');
INSERT INTO [t13073] VALUES (111,'سورة ص والزمر','608','0','0');
INSERT INTO [t13073] VALUES (112,'الحواميم السبع','612','111','0');
INSERT INTO [t13073] VALUES (113,'باب ذكر الموضع الأول منها قال جل وعز: والملائكة يسبحون بحمد ربهم ويستغفرون لمن في الأرض','614','111','0');
INSERT INTO [t13073] VALUES (114,'باب ذكر الموضع الثاني قال جل وعز إخبارا: لنا أعمالنا ولكم أعمالكم لا حجة بيننا وبينكم فيها قولان محتملان فمن ذلك','617','111','0');
INSERT INTO [t13073] VALUES (115,'باب ذكر الموضع الثالث قال جل وعز من كان يريد حرث الآخرة نزد له في حرثه ومن كان يريد حرث الدنيا نؤته منها وما له في الآخرة من نصيب فيه قولان من ذلك','620','111','0');
INSERT INTO [t13073] VALUES (116,'باب ذكر الموضع الرابع قال جل وعز قل لا أسألكم عليه أجرا إلا المودة في القربى في هذه الآية أربعة أقوال، فمن ذلك','622','111','0');
INSERT INTO [t13073] VALUES (117,'باب ذكر الموضع الخامس قال الله جل وعز: والذين إذا أصابهم البغي هم ينتصرون زعم ابن زيد أنها منسوخة قال: " المسلمون ينتصرون من المشركين ثم نسخها أمرهم بالجهاد " وقال غيره هي محكمة والانتصار من الظالم بالحق تقويم له محمود ممدوح صاحبه كان الظالم مسلما أو','627','111','0');
INSERT INTO [t13073] VALUES (118,'باب ذكر الموضع الذي في الزخرف قال الله جل وعز فاصفح عنهم وقل سلام فسوف يعلمون جماعة من العلماء يقولون إنها منسوخة بالأمر بالقتال فمن ذلك','630','111','0');
INSERT INTO [t13073] VALUES (119,'باب ذكر الموضع الذي في الجاثية قال الله جل وعز قل للذين آمنوا يغفروا للذين لا يرجون أيام الله ليجزي قوما بما كانوا يكسبون قال جماعة من العلماء هي منسوخة فمن ذلك','633','111','0');
INSERT INTO [t13073] VALUES (120,'باب ذكر الآية التي في الأحقاف قال جل وعز: قل ما كنت بدعا من الرسل وما أدري ما يفعل بي ولا بكم','636','111','0');
INSERT INTO [t13073] VALUES (121,'سورة محمد صلى الله عليه وسلم','641','0','0');
INSERT INTO [t13073] VALUES (122,'باب ذكر الموضع الأول منها قال عز وجل فإذا لقيتم الذين كفروا فضرب الرقاب حتى إذا أثخنتموهم فشدوا الوثاق فإما منا بعد وإما فداء حتى تضع الحرب أوزارها في هذه الآية خمسة أقوال، من العلماء من قال: هي منسوخة وهي في أهل الأوثان لا يجوز أن يفادوا ولا يمن عليهم','643','121','0');
INSERT INTO [t13073] VALUES (123,'باب ذكر الموضع الثاني قال الله جل وعز: فلا تهنوا وتدعوا إلى السلم وأنتم الأعلون قال أبو جعفر: من قال: هذه ناسخة لقوله عز وجل وإن جنحوا للسلم فاجنح لها احتج بأن في هذه المنع من الميل إلى الصلح إذا لم يكن بالمسلمين حاجة إلى الصلح وقد قيل: هاتان الآيتان','649','121','0');
INSERT INTO [t13073] VALUES (124,'سورة الفتح، والحجرات','650','0','0');
