INSERT INTO [t13073] VALUES (136,'باب ذكر الموضع الأول قال الله جل وعز ومن الليل فاسجد له وسبحه ليلا طويلا قال ابن زيد: " كان هذا أول شيء فريضة، ثم خففها الله تعالى فقال جل وعز: ومن الليل فتهجد به نافلة لك "','721','135','0');
INSERT INTO [t13073] VALUES (137,'باب ذكر الموضع الثاني قال الله جل وعز قد أفلح من تزكى وذكر اسم ربه فصلى تكلم العلماء في هذه الآية بأجوبة فروي عن ابن عباس، أنه قال: " من تزكى من الشرك " وروي عنه أنه قال: " أخرجوا زكاة الفطر قبل صلاة العيد " وعن أبي مالك، " من تزكى: من آمن " وعن','722','135','0');
INSERT INTO [t13073] VALUES (138,'سورة القدر إلى آخر القرآن','731','0','0');
