INSERT INTO [t13077] VALUES (3,'التخويف','5','0','0');
INSERT INTO [t13077] VALUES (4,'الاجتهاد في العمل','7','0','0');
INSERT INTO [t13077] VALUES (5,'الجد والحذر','9','0','0');
INSERT INTO [t13077] VALUES (6,'الرجاء لا التجرؤ','11','0','0');
INSERT INTO [t13077] VALUES (7,'حديث أنطونس السائح ومواعظه وأمثاله','13','0','0');
INSERT INTO [t13077] VALUES (8,'موضوع كتاب أنطونيوس السائح قال أبو بكر بن أبي الدنيا: ثم إنا وجدنا فيما وضع الأولون من حكمهم وضربوا من أمثالهم كتابا فيه حكم وأمثال تحدو ذا اللب على رفض العاجلة وتحثه على الأخذ بالوثيقة في العمل للآجلة وهو الكتاب الذي ينسب إلى أنطونس السائح فقال فيما','14','0','0');
INSERT INTO [t13077] VALUES (9,'وصية ملك كان ملك بعد زمان المسيح عليه السلام يقال له أنطونس عاش ثلاثمائة سنة وعشرين سنة، فلما حضرته الوفاة بعث إلى ثلاثة نفر من عظماء أهل ملته وأفاضلهم فقال لهم: فقد نزل بي ما ترون، وأنتم رءوس أهل مملكتكم وأفاضلهم، ولا أعرف أحدا أولى بتدبير رعيتكم','15','0','0');
INSERT INTO [t13077] VALUES (10,'اختلاف على الملك فدب أولئك الثلاثة نفر إلى الستة الذين جعل إليهم اختيار الملك، فصار كل رجلين من الستة يدعوان إلى رجل من الثلاثة، فلما رأى ذلك حكماؤهم وأهل الرأي منهم قالوا: يا معشر الستة الذين جعل إليهم الاختيار، قد افترقت كلمتكم، واختلف رأيكم،','17','0','0');
INSERT INTO [t13077] VALUES (11,'مشاورة أنطونيوس السائح وكان في جبل بحضرتهم رجل سائح يقال له أنطونس في غار معروف مكانه، قد تخلى من الدنيا وأهلها، فاجتمعت كلمتهم بالرضا بمن أشار إليه السائح من الثلاثة نفر، فوكلوا بالمملكة رجلا من الستة، وانطلق الثلاثة نفر إلى ذلك السائح، فاقتصوا','18','0','0');
INSERT INTO [t13077] VALUES (12,'اغتنام العمر والتوثق بالعمل فأخبروني: هل عرفتم مداكم من الملك، وغايتكم من العمر؟ قالوا: لا ندري، لعل ذلك لا يكون إلا طرفة عين قال: فلم تخاطرون بهذه الغرة؟ قالوا: رجاء طول المدة قال: كم أتت عليكم من سنة؟ قالوا: أصغرنا ابن خمس وثلاثين سنة،','21','0','0');
INSERT INTO [t13077] VALUES (13,'التفكر في هلاك العالم قالوا: أخبرنا كيف أول معرفتك للأمور من قبل الفكر؟ قال: تفكرت في هلاك العالم فإذا ذاك من قبل أربعة أشياء جعلت فيهن اللذات، وهي أبواب مركبة في الجسد، منها ثلاثة في الرأس، وواحد في البطن، فأما أبواب الرأس فالعينان، والمنخران،','24','0','0');
INSERT INTO [t13077] VALUES (14,'قطع اللذات قالوا: وما اللذات؟ وبماذا قطعتهن؟ قال: اللذات: المال، والبنون، والأزواج، والسلطان، فقطعتهن بالهموم، والأحزان، والخوف، وبذكر الموت المنغص للذات، وقطعت ذلك أجمع بالعزلة وترك الاهتمام بأمور الدنيا، فلا أحزن على أحد هلك فيها، ولا','26','0','0');
INSERT INTO [t13077] VALUES (15,'قصة صاحب الحية واشتد عجبي من أهل العقول ما يمنعهم من النظر في سلامة أبدانهم؟ فإنهم يريدون أن يهلكوا أنفسهم كما هلك صاحب الحية، قالوا: أخبرنا كيف كان مثل صاحب الحية؟ قال: زعموا أنه كان في دار رجل من الناس حية ساكنة في جحر قد عرفوا مكانها، وكانت تلك','28','0','0');
INSERT INTO [t13077] VALUES (16,'قصة صاحب الكرم ويل لأصحاب المعرفة الذين لو قصرت عنهم عقولهم لكان أعذر لهم، ويل لهم، ويل لهم، لو قد أصابهم ما أصاب صاحب الكرم. قالوا: وكيف كان مثل صاحب الكرم؟ قال أنطونس: زعموا أنه كان رجل له كرم واسع، كثير العنب، متصل الشجر، مثمر، فاستأجر لكسح','32','0','0');
INSERT INTO [t13077] VALUES (17,'قصة صاحب السفينة قال أنطونس: ولقد عجبت لأهل الأمل وطمعهم في طول العمر، فوجدت أعدى الناس للناس الأولاد لآبائهم، عمل آباؤهم في الاستكثار لهم، وأتعبوا أبدانهم في إصلاح معايش غيرهم بهلاك أنفسهم، وشاركهم في اللذة غيرهم، فأفردوا بالسؤال عما كدحوا كصاحب','35','0','0');
INSERT INTO [t13077] VALUES (18,'قصة صاحب الحوت ولقد عجبت للمدخر عن نفسه، والمؤثر لغيره، فويحك، قابل همومك بخفة المال، وتبلغ بالكفاف تبلغ المنزل، وادخر الفضل لنفسك، ولا تؤثر غيرك فتلق ما لقي صاحب الحوت. قالوا: وما الذي لقي صاحب الحوت؟ قال أنطونس: زعموا أن صياد سمك أصاب في صيده','39','0','0');
INSERT INTO [t13077] VALUES (19,'قصة هلاك اليهودي والنصراني ولقد عجبت لهذا الشغل الذي غر أهل العقل والجهل حتى هلكوا جميعا بالرجاء والطمع، كما هلك اليهودي والنصراني. قالوا: أخبرنا كيف كان ذلك؟ . قال أنطونس: اصطحب رجلان يهودي ونصراني إلى أرض يبتاعان الجوهر، فسارا في عمران من الأرض','41','0','0');
INSERT INTO [t13077] VALUES (20,'قصة صاحب الدير ولقد عجبت لأهل الأعمال السيئة واستتارهم من العباد بقبيح أعمالهم، ولا يستترون ممن يلي عقوبتهم، ولا يراقبونه وهو الذي يثيب على الحسن، ويجزي بالسيء، كيف أمنوا أن يصيبهم ما أصاب صاحب الدير. قالوا: وما الذي أصاب صاحب الدير؟ قال أنطونس:','43','0','0');
INSERT INTO [t13077] VALUES (21,'قصة الأعمى في مصيبته ولقد عجبت لأهل المصائب، كيف لا يستعينون على مصائبهم بالصبر، ويذكرون ما يؤملون من الثواب، فإنه سيأتي على صاحب المصيبة يوم يتمنى فيه ما تمنى الأعمى في مصيبته. قالوا: وما تمنى الأعمى في مصيبته؟ قال أنطونس: زعموا أن تاجرا دفن مائة','48','0','0');
INSERT INTO [t13077] VALUES (22,'قصة صاحب المسيل ولقد عجبت لنفاذ عقولهم، كيف لا يعملون بما يعلمون، كأنهم يريدون أن يهلكوا كما هلك صاحب المسيل. قالوا: وكيف كان ذلك؟ قال أنطونس: زعموا أن رجلا نزل بطن مسيل، فقيل له: تحول عن هذا المنزل، فإنه منزل خطر. فقال: قد عملت، ولكن يعجبني','49','0','0');
INSERT INTO [t13077] VALUES (23,'قصة أصحاب أفرولية قال أنطونس: فلو أخذنا بالحزم كنا كأصحاب أفرولية. قيل: وكيف كان ذلك؟ قال بعث ملك أسقولية بعثا إلى أفرولية، وكان المسير إليها في البحر ستين ليلة، لا يجدون من الزاد والماء إلا ما حملوه معهم، وكان مع صاحب أسقولية كاهنان، فقال أحدهما','50','0','0');
INSERT INTO [t13077] VALUES (24,'الوصية الأخيرة قال النفر الستة لأنطونس: ما أحسن قولك، وأبلغ موعظتك. قال: أما إن حلاوة عظتي لا تجاوز آذانكم، ألم تعلموا أن فيما جاء به موسى في الناموس، وفيما جاء به داود من الزبور، والمسيح من الإنجيل، وفي كتب جميع الأنبياء: إنما تجزون بما كنتم','52','0','0');
