INSERT INTO [t13086] VALUES (21,'قبر سعد بن أبي وقاص رضي الله عنه','356','0','0');
INSERT INTO [t13086] VALUES (22,'قبر أبي النبي صلى الله عليه وسلم','358','0','0');
INSERT INTO [t13086] VALUES (23,'قبر آمنة أم رسول الله صلى الله عليه وسلم','360','0','0');
INSERT INTO [t13086] VALUES (24,'قبر أم حبيبة زوج النبي صلى الله عليه وسلم رضي الله عنها','368','0','0');
INSERT INTO [t13086] VALUES (25,'قبر أم سلمة زوج النبي صلى الله عليه وسلم رضي الله عنها حدثنا محمد بن يحيى قال: سمعت من، يذكر، أن قبر أم سلمة رضي الله عنها بالبقيع، حيث دفن محمد بن زيد بن علي، قريبا من موضع فاطمة بنت رسول الله صلى الله عليه وسلم، وأنه كان حفر فوجد على ثماني أذرع','370','0','0');
INSERT INTO [t13086] VALUES (26,'قبر سعد بن معاذ رضي الله عنه قال عبد العزيز: أصيب سعد رضي الله عنه يوم الخندق، فدعا، فحبس الله عنه الدم حتى حكم في بني قريظة، ثم انفجر كله، فمات في منزله في بني عبد الأشهل، فصلى عليه رسول الله صلى الله عليه وسلم، ولحد له في طرف الزقاق الذي بلزق','376','0','0');
INSERT INTO [t13086] VALUES (27,'قبر حمزة بن عبد المطلب رضي الله عنه','377','0','0');
INSERT INTO [t13086] VALUES (28,'قبر صفية بنت عبد المطلب رضي الله عنها قال عبد العزيز: توفيت صفية فدفنت في آخر الزقاق الذي يخرج إلى البقيع عند باب الدار التي يقال لها: دار المغيرة بن شعبة التي أقطعه عثمان بن عفان رضي الله عنهما، لازقا بجدار الدار قال عبد العزيز: فبلغني أن الزبير بن','379','0','0');
INSERT INTO [t13086] VALUES (29,'قبر العباس بن عبد المطلب رضي الله عنه قال عبد العزيز: دفن العباس بن عبد المطلب عند قبر فاطمة بنت أسد بن هاشم في أول مقابر بني هاشم التي في دار عقيل. فيقال: إن ذلك المسجد بني قبالة قبره. قال: وقد سمعت من يقول: دفن في موقع من البقيع متوسطا','380','0','0');
INSERT INTO [t13086] VALUES (30,'قبور بني هاشم','381','0','0');
INSERT INTO [t13086] VALUES (31,'قبر أبي سفيان بن الحارث رضي الله عنه قال عبد العزيز: بلغني أن عقيل بن أبي طالب رأى أبا سفيان بن الحارث رضي الله عنه يجول بين المقابر فقال له: يا ابن عم، ما لي أراك هاهنا؟ قال: أطلب موضع قبر، فأدخله داره، وأمر بقبر فحفر في قاعتها، فقعد عليه أبو','382','0','0');
INSERT INTO [t13086] VALUES (32,'قبر عمرو بن الجموح وعبد الله بن عمرو بن حرام رضي الله عنهما','383','0','0');
