INSERT INTO [t13086] VALUES (34,'ما جاء في الحربة التي يمشى بها في العيدين بين يدي الولاة','417','0','0');
INSERT INTO [t13086] VALUES (35,'ما كان يفعله النبي صلى الله عليه وسلم في صلاة الاستسقاء','434','0','0');
INSERT INTO [t13086] VALUES (36,'ذكر بئر رومة، وهي في العقيق','459','0','0');
INSERT INTO [t13086] VALUES (37,'ما جاء في النقيع','467','0','0');
