INSERT INTO [t13086] VALUES (39,'ما جاء في أسماء المدينة','497','0','0');
INSERT INTO [t13086] VALUES (40,'ذكر أودية المدينة وما حولها وحدودها ومجتمع مياهها ومغايضها','512','0','0');
INSERT INTO [t13086] VALUES (41,'بطحان','516','0','0');
INSERT INTO [t13086] VALUES (42,'ذكر آبار المدينة قال أبو غسان: ومن آبار المدينة: بئر بالحرانية يقال لها: الحفير، يصب فيها سيل مذينب، وربما صرف إليها سيل مهزور إذا طغى وخيف على المدينة، فيصب فيها هو ومذينب. وبئر يقال لها البويرمة، لبني الحارث بن الخزرج. وبئر يقال لها الهجير','518','0','0');
INSERT INTO [t13086] VALUES (43,'ما جاء في أموال النبي صلى الله عليه وسلم وصدقاته ونفقاته بالمدينة وأعراضها','525','0','0');
INSERT INTO [t13086] VALUES (44,'أمر خيبر','534','0','0');
INSERT INTO [t13086] VALUES (45,'خبر فدك','571','0','0');
INSERT INTO [t13086] VALUES (46,'ذكر فاطمة والعباس وعلي رضي الله عنه، وطلب ميراثهم من تركة النبي صلى الله عليه وسلم','578','0','0');
INSERT INTO [t13086] VALUES (47,'خصومة علي والعباس رضي الله عنهما إلى عمر رضي الله عنه','595','0','0');
INSERT INTO [t13086] VALUES (48,'ذكر صدقات أصحاب رسول الله صلى الله عليه وسلم من المهاجرين وغيرهم','618','0','0');
INSERT INTO [t13086] VALUES (49,'صدقة العباس بن عبد المطلب رضي الله عنه قال أبو غسان: تصدق العباس بن عبد المطلب رضي الله عنه بحل له كان بينبع على عين يقال لها: عين جساس، على شراب زمزم، فذلك الحق يقال له: السقاية؛ لأنه تصدق به على زمزم، وهو الثمن من تلك العين، وهو اليوم بيد','619','0','0');
INSERT INTO [t13086] VALUES (50,'صدقة عبد الله بن العباس بن عبد المطلب رضي الله عنهما وتصدق عبد الله بن العباس بن عبد المطلب رضي الله عنهما بمال بالصهوة، وهو موضع بين معن وبير حوزة على ليلة من المدينة، وتلك الصدقة بيد الخليفة يوكل بها','620','0','0');
INSERT INTO [t13086] VALUES (51,'صدقات علي بن أبي طالب رضي الله عنه','621','0','0');
INSERT INTO [t13086] VALUES (52,'دور عبد بن قصي اتخذ طليب بن كثير بن عبد بن قصي دارا في زقاق الصفارين، فورثها أبو كثير بن زيد بن كثير بن عبد بن قصي، ثم خرجت من أيديهم','641','0','0');
INSERT INTO [t13086] VALUES (53,'دور بني زهرة اتخذ عبد الرحمن بن عوف دورا، فدخل منها في المسجد ثلاث آدر، كن يدعين القرائن، وسمعت من يذكر أن القرائن ثلاث جنابذ لعبد الرحمن بن عوف، وللقرائن يقول أبو قطيفة: ألا ليت شعري هل تغير بعدنا جنوب المصلى أم كعهدي القرائن ودخل في المسجد أيضا','642','0','0');
INSERT INTO [t13086] VALUES (54,'دور بني تيم اتخذ أبو بكر رضي الله عنه دارا إلى زقاق البقيع، قبالة دار عثمان رضي الله عنه الصغرى. واتخذ أبو بكر رضي الله عنه أيضا منزلا آخر عند المسجد، وهو المنزل الذي قال فيه رسول الله صلى الله عليه وسلم: " سدوا عني هذه الأبواب إلا ما كان من باب أبي','655','0','0');
INSERT INTO [t13086] VALUES (55,'دور بني مخزوم اتخذ خالد بن الوليد بن المغيرة رضي الله عنه داره التي كانت بالبطحاء، وهي اليوم الدار التي بين دار أسماء بنت حسين، وبين الخط الذي في دار عمرو بن العاص، وهي بأيدي بني أيوب بن سلمة من ولد الوليد بن المغيرة','658','0','0');
INSERT INTO [t13086] VALUES (56,'دور بني عدي بن كعب واتخذ عبد الله بن عمر بن الخطاب رضي الله عنهما داره التي في بني عمرو بن مبذول التي يقال لها: دار الجنابذ، بابها شارع في بني عمرو بن مبذول، على يمين الذاهب إلى مسجدهم، توفي عبد الله رضي الله عنه وتركها ميراثا، فتجاوزها ولده من بعده','664','0','0');
INSERT INTO [t13086] VALUES (57,'دور بني جمح اتخذ عمير بن وهب داره التي في الصفارين، وهي دار المغيرة بن الأخنس، ثم ناقل بها عمير المغيرة إلى الدار التي للمغيرة بالمصلى، التي تدعى اليوم دار ابن صفوان، فهي اليوم بأيدي آل صفوان بن أمية بن خلف. واتخذ محمد بن حاطب الدار التي تدعى دار','669','0','0');
INSERT INTO [t13086] VALUES (58,'دور بني سهم اتخذ عمرو بن العاص رضي الله عنه داره التي بالبلاط بين دار خالد بن الوليد، وبين الكتاب الذي يقال له: كتاب ابن الخصيب، فتصدق بها على ولده، فهي بأيديهم صدقة. وقد كان بعض ولده عمر فيها، حدث عبد الله بن عبيد الله بن عباس، عن ابن أبي فديك','671','0','0');
INSERT INTO [t13086] VALUES (59,'دور بني عامر بن لؤي اتخذ عبد الله بن مخرمة داره التي بالبلاط الشارع بابها قبالة دار عبد الله بن عوف التي فيها بنو نوفل بن مساحق بن عبد الله بن مخرمة، فبأيدي ولده بعضها، وقد خرج منهم بعضها، والذي خرج بأيدي ورثة عمر بن بزيع مولى أمير المؤمنين. واتخذ','672','0','0');
INSERT INTO [t13086] VALUES (60,'دور بني محارب بن فهر اتخذت فاطمة بنت قيس بن وهب بن خالد بن وائلة بن ثعلبة بن سفيان بن محارب بن فهر، أخت الضحاك بن قيس، دارا بين دار أنس بن مالك، وبين زقاق جمل، باعها ورثتها، فهي اليوم بيد إبراهيم بن محمد بن عبد الله بن محمد بن علي بن عبد الله بن','676','0','0');
INSERT INTO [t13086] VALUES (61,'دور أحلاف قريش اتخذ أبو هريرة الدوسي، صاحب رسول الله صلى الله عليه وسلم ورضي عنه، دارا بالبلاط بين الزقاق الذي فيه دار عبد الرحمن بن الحارث بن هشام، وبين خط البلاط الأعظم، فباعها ولده من عمر بن بزيع، وكان يسكنها موالي أبي هريرة فخرجوا منها وأرضاهم','677','0','0');
INSERT INTO [t13086] VALUES (62,'ذكر الدور الشوارع على مسجد النبي صلى الله عليه وسلم اليوم منها دار عبد الله بن مكمل الشارعة في رحبة القضاء، وهي مما يتشاءم به؛ وذلك مما نشأ عن بنائها. ومن تلك الدور دار عبد الله بن عمر رضي الله عنهما في القبلة، وقد ذكرنا لها قصة في دور بني عدي. ثم','680','0','0');
INSERT INTO [t13086] VALUES (63,'محال القبائل من المهاجرين نزل بنو غفار بن مليل بن ضمرة بن بكر بن عبد مناف بن كنانة القطيعة التي قطع لهم النبي صلى الله عليه وسلم، وهي ما بين دار كثير بن الصلت التي تعرف بدار الحجارة بالسوق، إلى زقاق ابن حبين، إلى دار أبي سبرة التي صارت لخالد مولى عبيد','685','0','0');
INSERT INTO [t13086] VALUES (64,'منازل أسلم ومالك ابني أفصى نزل بنو أسلم ومالك ابني أفصى بن حارثة بن عمرو بن عامر منزلين: فنزلت بنو مالك بن أفصى وأمية وسهم ابني أسلم، ما بين خط زقاق ابن حبين مولى العباس بن عبد المطلب، الشامي من زاوية يقصان التي بالسوق إلى خط جهينة، إلى شامي ثنية','689','0','0');
INSERT INTO [t13086] VALUES (65,'منازل مزينة ومن حل معها من قيس ونزل بنو هدبة بن لاطم بن عثمان بن عمرو، إلا بني عامر بن ثور بن ثعلبة بن لاطم بن عثمان، وعثمان نفسه الذي يقال له: مزينة، وهي أم مزنة بنت خالد بن خالد بن وبرة، ما بين زاوية بيت القروي المطل على بطحان الغربية، إلى زاوية','690','0','0');
INSERT INTO [t13086] VALUES (66,'منازل جهينة وبلي نزلت جهينة بن زيد بن السود بن أسلم بن الحارث بن قضاعة، وبلي بن عمرو بن الحاف بن قضاعة، ما بين خط أسلم الذي بين أسلم وجهينة، إلى دار حرام بن عثمان السلمي الأنصاري التي في بني سلمة، إلى الجبل الذي يقال له: جبل جهينة، إلى يماني ثنية','693','0','0');
INSERT INTO [t13086] VALUES (67,'منازل قيس نزلت أشجع بن ريث بن غطفان بن سعد بن قيس بن غيلان الشعب الذي يقال له: شعب أشجع، وهو ما بين سائلة أشجع، إلى ثنية الوداع، إلى جوف شعب سلع، " وخرج إليهم النبي صلى الله عليه وسلم بأحمال التمر فنثره لهم "','695','0','0');
INSERT INTO [t13086] VALUES (68,'منازل بني كعب بن عمرو وإخوانهم من بني المصطلق ونزل بنو كعب بن عمرو بن عدي بن عمرو بن عامر، ما بين يماني بني ليث بن بكر، إلى دار شريح العدوي عدي بن عمرو، إلى موضع التمارين بالسوق، إلى زقاق الجلادين الشارع على المصلى يمنة ويسرة إلى بطحان، إلى زقاق','698','0','0');
INSERT INTO [t13086] VALUES (69,'ما جاء في ثنية الوداع وسبب ما سميت به قال أبو غسان: حدثني عبد العزيز بن عمران، عن عامر عن جابر قال: كان لا يدخل المدينة أحد إلا عن طريق واحد من ثنية الوداع، فإن لم يعشر بها مات قبل أن يخرج منها، فإذا وقف على الثنية قيل: قد ودع، فسميت ثنية الوداع،','699','0','0');
INSERT INTO [t13086] VALUES (70,'ذكر دار هشام بن عبد الملك التي كان بنى، وقصر خل، وقصر بني جديلة قال أبو غسان: كان الذي هاج هشام بن عبد الملك على بناء داره التي كانت بالسوق، أن إبراهيم بن هشام بن إسماعيل كان خال هشام بن عبد الملك، وكان ولاه المدينة، فكتب إليه إبراهيم فذكر أن','701','0','0');
INSERT INTO [t13086] VALUES (71,'ما جاء فيما يخرج أهل المدينة منها','706','0','0');
INSERT INTO [t13086] VALUES (72,'ما قيل في المدينة من الشعر يتشوق إليها وغير ذلك قال عبد الله بن عامر بن كريز، وركب البحر غائبا فاشتاق رفيق له إلى المدينة فقال: بكى صاحبي لما رأى الفلك قد مضت تهادى بنا فوق ذي لجج خضر وحن إلى أهل المدينة حنه لمصر وهيهات المدينة من مصر فقلت له لا تبك','734','0','0');
