INSERT INTO [t13086] VALUES (95,'أخبار عمر بن الخطاب رضي الله عنه هو عمر بن نفيل بن عبد العزى بن رياح بن عبد الله بن قرط بن رزاح بن عدي بن كعب، ويكنى أبا حفص، وأمه حنتمة بنت هاشم بن المغيرة بن عبد الله بن عمر بن مخزوم. وكان لعمر من الولد عبد الله، وعبد الرحمن، وحفصة، وأمهم زينب','1250','0','0');
