INSERT INTO [t13090] VALUES (8,'القول في البيان عما في هذه الأخبار من الغريب فمن ذلك قول النبي صلى الله عليه وسلم في خطبته بمكة حين ذكر الحرم: " لا يعضد شجره "، يعني بقوله عليه السلام: " لا يعضد شجره "، لا يفسد ولا يقطع، وإنما ذلك مثل، وأصله من: عضد الرجل الرجل، إذا أصاب عضده','55','1','0');
