INSERT INTO [t13090] VALUES (50,'القول في البيان عما في هذه الأخبار من الغريب فمن ذلك قول النبي صلى الله عليه وسلم إذ سئل عمن قدم شيئا من نسكه قبل شيء: " لا حرج "، يعني صلى الله عليه وسلم بقوله: " لا حرج "، لا ضيق في فعل ذلك، أي أن ذلك واسع له في الدين، مطلق غير مضيق فيه. وأصل','448','1','0');
