INSERT INTO [t13090] VALUES (120,'القول في البيان عما في هذه الأخبار من الغريب فمن ذلك قول أبي سعيد الخدري أنه قال: قيل لرسول الله صلى الله عليه وسلم: إنه يستقى لك من بئر بضاعة، فإنه يلقى فيها ما ينجي الناس والمحايض، يعني بقوله: وإنه يلقى فيها ما ينجي الناس، يعني ما يحدثون من القذر','1246','1','0');
INSERT INTO [t13090] VALUES (121,'ذكر خبر آخر من أخبار سماك بن حرب، عن عكرمة، عن ابن عباس، عن النبي صلى الله عليه وسلم','1254','1','0');
INSERT INTO [t13090] VALUES (122,'ذكر من حدث هذا الحديث عن عكرمة فأرسله، ولم يجعل بينه وبين النبي صلى الله عليه وسلم أحدا','1256','1','0');
INSERT INTO [t13090] VALUES (123,'ذكر من حضرنا ذكره ممن سلك من ذلك سبيل ما روي عن النبي صلى الله عليه وسلم من فعله فيه','1258','1','0');
INSERT INTO [t13090] VALUES (124,'ذكر من قال ذلك أو حكم به','1269','1','0');
INSERT INTO [t13090] VALUES (125,'ذكر من روي عنه أنه روى عن النبي صلى الله عليه وسلم قبول شهادة عدلين في ذلك','1278','1','0');
INSERT INTO [t13090] VALUES (126,'ذكر خبر آخر من أخبار سماك، عن عكرمة، عن ابن عباس، عن النبي صلى الله عليه وسلم','1284','1','0');
INSERT INTO [t13090] VALUES (127,'ذكر من حدث هذا الحديث فقال فيه: عن سماك، عن عكرمة، عن النبي صلى الله عليه وسلم، فأرسله ولم يدخل فيه بين عكرمة، والنبي عليه السلام أحدا','1290','1','0');
INSERT INTO [t13090] VALUES (128,'ذكر من حدث هذا الحديث عن عكرمة فقال فيه: عنه، عن أبي هريرة، عن النبي صلى الله عليه وسلم','1293','1','0');
