INSERT INTO [t13090] VALUES (132,'ذكر من حدث هذا الحديث، عن سماك فقال فيه: عن عكرمة، عن ابن عباس، عن سودة','1322','1','0');
INSERT INTO [t13090] VALUES (133,'ذكر ذلك حدثنا أبو كريب محمد بن العلاء قال: حدثنا عبيد الله، عن إسرائيل، عن جابر، عن ابن عباس، وعن عكرمة، عن عبد الله بن عباس، عن سودة بنت زمعة، قالت: كانت لنا شاة، فذكرت نحو حديث أبي كريب، عن عبيد الله، عن إسرائيل، عن سماك','1324','1','0');
INSERT INTO [t13090] VALUES (134,'ذكر من روى هذا الحديث عن سماك، فقال فيه: عن عكرمة، عن سودة، ولم يدخل بين عكرمة وسودة أحدا','1327','1','0');
