INSERT INTO [t13091] VALUES (1,'مسند علي بن أبي طالب','1','0','0');
INSERT INTO [t13091] VALUES (2,'ذكر ما لم يمض ذكره من أخبار ثعلبة بن يزيد الحماني، عن علي رضوان الله عليه، عن النبي صلى الله عليه وسلم','2','1','0');
INSERT INTO [t13091] VALUES (3,'القول في علل هذا الخبر وهذا خبر عندنا صحيح سنده، وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح، وذلك أنه خبر لا يعرف له مخرج عن علي عن النبي صلى الله عليه وسلم إلا من هذا الوجه، والخبر إذا انفرد به عندهم منفرد وجب التثبت فيه، وقد حدث هذا الحديث عن','4','1','0');
INSERT INTO [t13091] VALUES (4,'القول في البيان عما في هذا الخبر من الفقه والذي فيه من ذلك: الإبانة من النبي صلى الله عليه وسلم عن إبطال ما كان أهل الجاهلية يتواصون به بينهم، ويستعملونه في جاهليتهم من التطير، واتقاء مخالطة ذي الداء حذارا من أن يعديهم داؤه في المؤاكلة، والمشاربة،','41','1','0');
INSERT INTO [t13091] VALUES (5,'ذكر من قال ذلك أو روي عنه أنه أكل مع ذي العاهة خوفا أن يكون في تركه الأكل معه دخول منه في معنى ما أبطله النبي صلى الله عليه وسلم من العدوى، ونهى عنه من التطير','75','1','0');
INSERT INTO [t13091] VALUES (6,'ذكر الخبر الوارد بذلك','90','1','0');
INSERT INTO [t13091] VALUES (7,'ذكر من قال ذلك ممن لم يمض ذكره','93','1','0');
INSERT INTO [t13091] VALUES (8,'القول في البيان عما في هذه الأخبار من الغريب فمن ذلك قول النبي صلى الله عليه وسلم: " لا عدوى " يعني بقوله لا عدوى لا يعدو داء ذي الداء إلى غيره بدنوه منه وقربه، وذلك أن أهل الجاهلية كانوا يتحامون مجالسة أهل الأدواء، ومؤاكلتهم، ومشاربتهم، ويزعمون أن','97','1','0');
INSERT INTO [t13091] VALUES (9,'ذكر خبر آخر من أخبار ثعلبة بن يزيد عن علي بن أبي طالب عن النبي صلى الله عليه وسلم','108','1','0');
INSERT INTO [t13091] VALUES (10,'القول في علل هذا الخبر وهذا خبر عندنا صحيح سنده، وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح، وذلك: أنه خبر لا يعرف لبعض ما فيه مخرج عن علي، عن النبي صلى الله عليه وسلم يصح إلا من هذا الوجه، وأخرى: أن في إسناده شكا فيمن حدث عن علي رحمة الله','110','1','0');
INSERT INTO [t13091] VALUES (11,'ذكر من روى ذلك عن علي','111','1','0');
INSERT INTO [t13091] VALUES (12,'القول في البيان عما في هذه الأخبار من المعاني إن قال لنا قائل: ما معنى هذه الأخبار، وما وجهها؟ قيل: ذلك هو ما دل عليه ظاهره، وذلك قوله صلى الله عليه وسلم: " التاجر فاجر، إلا من اتقى ربه، وبر وصدق "، فمن كذب في ثمن ما اشترى عند البيع، ومدحه بغير','120','1','0');
INSERT INTO [t13091] VALUES (13,'ذكر من قال ذلك','124','1','0');
INSERT INTO [t13091] VALUES (14,'ذكر ما صح سنده من ذلك','131','1','0');
INSERT INTO [t13091] VALUES (15,'القول في البيان عما في هذه الأخبار من الغريب فمن ذلك قول رفاعة: " فمدوا أعناقهم واشرأبوا، يعني بقوله: واشرأبوا: تشوفوا، وتطلعوا، وتأهبوا للاستماع والنظر، ومنه قول النبي صلى الله عليه وسلم: " يؤتى بالموت يوم القيامة، فيوقف بين الجنة والنار،','146','1','0');
INSERT INTO [t13091] VALUES (16,'ذكر خبر آخر من أخبار علي بن أبي طالب رضوان الله عليه، عن النبي صلى الله عليه وسلم','147','1','0');
INSERT INTO [t13091] VALUES (17,'القول في علل هذا الخبر وهذا خبر عندنا صحيح سنده، وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح، لعلل: إحداها: ما ذكرنا من اضطراب الرواة فيه على الأعمش، فيرويه شريك عنه، عن المنهال، عن عباد، عن علي، ويرويه أبو بكر بن عياش عنه، عن عمرو بن مرة،','150','1','0');
INSERT INTO [t13091] VALUES (18,'ذكر من روى هذا الحديث عن المنهال بن عمرو فقال فيه: عنه، عن عبد الله بن الحارث، عن ابن عباس، عن علي، عن النبي صلى الله عليه وسلم، وخالف فيه الأعمش','152','1','0');
INSERT INTO [t13091] VALUES (19,'ذكر الرواية عمن قال: إنما قضى ديون رسول الله صلى الله عليه وسلم بعد وفاته ومواعيده أبو بكر رحمة الله عليه','154','1','0');
INSERT INTO [t13091] VALUES (20,'القول فيما في هذا الخبر من الفقه وفي معنى بعض ما فيه إن قال لنا قائل: قد قلت: إن الخبر الذي رويته عن علي، عن النبي صلى الله عليه وسلم أنه قال: " من يضمن عني ديني، ويقضي عداتي، ويكون معي في الجنة "، صحيح، فإن كان صحيحا، فما بالك تركت القول به؟','158','1','0');
INSERT INTO [t13091] VALUES (21,'ذكر من قال في ذلك نحو قولنا فيه، فأبطل الضمان إذا لم يكن المضمون من المال معلوم المقدار','159','1','0');
INSERT INTO [t13091] VALUES (22,'ذكر من قال: " جائز ضمان الضامن مالا مجهول المبلغ " قال أبو حنيفة، وأبو يوسف، ومحمد: إذا قال الرجل لرجل: " بايع فلانا، فما بعته به من شيء فهو علي فهو جائز، وإن لم يوقت لذلك وقتا " قالوا: وإن باعه بألف درهم أو أكثر أو أقل فهو جائز، قالوا: وكذلك','163','1','0');
INSERT INTO [t13091] VALUES (23,'ذكر ما لم يمض ذكره من أخبار أبي يحيى حكيم بن سعد، عن علي رضوان الله عليه، عن النبي صلى الله عليه وسلم، مما صح عندنا سنده عنه ذكر خبر من ذلك','167','1','0');
INSERT INTO [t13091] VALUES (24,'القول في علل هذا الخبر وهذا خبر عندنا صحيح سنده، وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح، لعلل: إحداها: أنه خبر لا يعرف له مخرج عن علي، عن النبي صلى الله عليه وسلم يصح إلا من هذا الوجه، والخبر إذا انفرد به عندهم منفرد وجب التثبت فيه.','169','1','0');
INSERT INTO [t13091] VALUES (25,'ذكر الرواية الواردة عن رسول الله صلى الله عليه وسلم أنه أمر بصلب الذي أعطي جعلا على الفتك به','171','1','0');
INSERT INTO [t13091] VALUES (26,'ذكر من قال: إن الذي جعل له الجعل على قتل رسول الله صلى الله عليه وسلم أسلم، ولم يقتل ولم يصلب','174','1','0');
INSERT INTO [t13091] VALUES (27,'ذكر من قال: إن عليا إنما أمر بقتل قاتله ولم يأمر بإحراقه، ونهى عن المثلة به، وأن الذي أحرق قاتله قوم من العامة','176','1','0');
INSERT INTO [t13091] VALUES (28,'ذكر ما في هذا الخبر، أعنى خبر علي رضوان الله عليه عن النبي صلى الله عليه وسلم الذي ذكرناه قبل، من الفقه والذي فيه من ذلك، الإبانة عن صحة قول القائلين بإطلاق إحراق جيفة المشركين، ومن كان سبيله سبيلهم، ممن قتل بحق وهو مقيم على الكفر، أو الردة عن','178','1','0');
INSERT INTO [t13091] VALUES (29,'ذكر الأخبار الواردة بذلك','180','1','0');
INSERT INTO [t13091] VALUES (30,'القول في البيان عما في هذه الأخبار من الغريب فمن ذلك قول عمير بن وهب لرسول الله صلى الله عليه وسلم وأصحابه: انعموا صباحا، يعني بذلك: نعمتم عند الصباح، وهي تحية كان أهل الجاهلية يحيون بها ملوكهم، وفيها لغتان: إحداهما انعم صباحا، والآخر: عم صباحا،','193','1','0');
INSERT INTO [t13091] VALUES (31,'ذكر خبر آخر من أخبار أبي يحيى، عن علي بن أبي طالب رضوان الله عليه، عن النبي صلى الله عليه وسلم','198','1','0');
INSERT INTO [t13091] VALUES (32,'القول في علل هذا الخبر وهذا خبر عندنا صحيح سنده، وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح لعلتين إحداهما: أنه خبر لا يعرف له مخرج من وجه يصح عن علي عن النبي صلى الله عليه وسلم، إلا من هذا الوجه، والثانية: " أن المعروف عن رسول الله صلى الله','200','1','0');
INSERT INTO [t13091] VALUES (33,'ذكر الرواية الواردة عن رسول الله صلى الله عليه وسلم: " أنه كان يقول بعض ما في خبر علي هذا، عن رسول الله صلى الله عليه وسلم، إذا كان في حرب','201','1','0');
INSERT INTO [t13091] VALUES (34,'ذكر الأخبار الواردة عن رسول الله صلى الله عليه وسلم بما كان يقوله إذا أراد السفر وقد روي عن رسول الله صلى الله عليه وسلم في ذلك أشياء نذكر ما حضرنا من ذلك ذكره، فمن ذلك ما','205','1','0');
INSERT INTO [t13091] VALUES (35,'القول في البيان عما في هذه الأخبار من الغريب فمن ذلك قول النبي صلى الله عليه وسلم: " اللهم إني بك أصول "، يعني صلى الله عليه وسلم بقوله: " بك أصول "، بك أسطو على أعدائك، يقال للفحل من الإبل إذا عدا على آخر، واثبا عليه بالعض: صال عليه، ومنه قول','223','1','0');
INSERT INTO [t13091] VALUES (36,'ذكر خبر آخر من أخبار علي رضوان الله عليه عن النبي صلى الله عليه وسلم وعلى آله','225','1','0');
INSERT INTO [t13091] VALUES (37,'ذكر خبر آخر من أخبار علي رضوان الله عليه عن النبي صلى الله عليه وسلم وعلى آله','228','1','0');
INSERT INTO [t13091] VALUES (38,'القول في علل هذا الخبر وهذا خبر عندنا صحيح سنده، وقد وافق عبد الله بن شداد في رواية هذا الخبر عن علي، عن رسول الله صلى الله عليه وسلم غيره، نذكر ما صح من ذلك عندنا سنده، ثم نتبع جميعه البيان إن شاء الله','233','1','0');
INSERT INTO [t13091] VALUES (39,'القول في البيان عما في هذه الأخبار من الغريب " فمن ذلك قول النبي صلى الله عليه وسلم لسعد " ارم، فداك أبي وأمي، أيها الغلام الحزور ". والحزور من الغلمان هو الذي قد قوي واشتد وخدم، يجمع: حزاورة، وحزورين، ومنه قول أبي النجم العجلي: لم يبعثوا شيخا','247','1','0');
INSERT INTO [t13091] VALUES (40,'ذكر خبر آخر من أخبار علي رضوان الله عليه، عن النبي صلى الله عليه وسلم وعلى آله','250','1','0');
INSERT INTO [t13091] VALUES (41,'ذكر من روى هذا الخبر عن علي، فوقفه عليه , ولم يرفعه إلى رسول الله صلى الله عليه وسلم','252','1','0');
INSERT INTO [t13091] VALUES (42,'ذكر من روى هذا الخبر عن أبي إسحاق فقال فيه: عنه، عن سعيد، عن رجل، عن علي، ولم يقل: عن سعيد بن ذي حدان، عن علي','257','1','0');
INSERT INTO [t13091] VALUES (43,'القول في البيان عن معاني هذه الأخبار " إن قال لنا قائل: أخبرنا عن هذه الأخبار التي ذكرت عن رسول الله صلى الله عليه وسلم من قيله: " الحرب خدعة "، وأن الكذب فيها وفي المعنيين الآخرين اللذين رويت عنه أنه رخص فيهما الكذب، أسقيمة أم صحيحة؟ فإن كانت سقيمة','288','1','0');
INSERT INTO [t13091] VALUES (44,'ذكر بعض من روي ذلك عنه','296','1','0');
INSERT INTO [t13091] VALUES (45,'ذكر من قال ذلك','304','1','0');
INSERT INTO [t13091] VALUES (46,'ذكر من قال ذلك','311','1','0');
INSERT INTO [t13091] VALUES (47,'ذكر من قال ذلك','320','1','0');
INSERT INTO [t13091] VALUES (48,'القول في البيان عما في هذه الأخبار من الغريب " فمن ذلك قول النبي صلى الله عليه وسلم في الخبر الذي رواه النواس بن سمعان عنه: " ما لكم تتهافتون في الكذب كما يتهافت الفراش في النار "؟ يعني بقوله صلى الله عليه وسلم " تتهافتون ": تتساقطون، يقال منه:','329','1','0');
INSERT INTO [t13091] VALUES (49,'ذكر خبر آخر من أخبار علي رحمة الله عليه، عن النبي صلى الله عليه وسلم','334','1','0');
INSERT INTO [t13091] VALUES (50,'القول في علل هذا الخبر وهذا خبر عندنا صحيح سنده، وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح، لعلل: أحدها: أنه خبر لا يعرف له مخرج عن علي , عن النبي صلى الله عليه وسلم إلا من هذا الوجه، والخبر إذا انفرد به عندهم منفرد وجب التثبت فيه. والثانية:','339','1','0');
INSERT INTO [t13091] VALUES (51,'ذكر من روى هذا الخبر فجعل هذا الكلام من كلام علي ولم يرفعه إلى النبي صلى الله عليه وسلم','340','1','0');
INSERT INTO [t13091] VALUES (52,'ذكر خبر آخر من أخبار علي رضوان الله عليه، عن رسول الله صلى الله عليه وسلم','342','1','0');
INSERT INTO [t13091] VALUES (53,'القول في علل هذا الخبر هذا الخبر عندنا صحيح سنده، وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح، لعلل: إحداها: أنه خبر لا يعرف له عن رسول الله صلى الله عليه وسلم مخرج يصح إلا من هذا الوجه، والخبر إذا انفرد به عندهم منفرد وجب التثبت فيه. والثانية','344','1','0');
INSERT INTO [t13091] VALUES (54,'ذكر خبر آخر من أخبار علي رضوان الله عليه، عن النبي صلى الله عليه وسلم وعلى آله','348','1','0');
INSERT INTO [t13091] VALUES (55,'القول في علل هذا الخبر وهذا خبر عندنا صحيح سنده، وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح، لعلل: إحداها: أنه خبر لا يعرف له مخرج عن علي رحمة الله عليه عن النبي صلى الله عليه وسلم يصح، إلا من هذا الوجه، والخبر إذا انفرد به عندهم منفرد وجب','351','1','0');
INSERT INTO [t13091] VALUES (56,'ذكر ما صح عندنا سنده مما حضرنا من ذلك ذكره','352','1','0');
INSERT INTO [t13091] VALUES (57,'القول في البيان عما في هذه الأخبار من الغريب والذي فيها من قول علي رحمة الله عليه مخبرا عن عبد الله: أنه ارتقى مرة شجرة أراك يجتني لأصحابه قال: رئيته قال: بريرا، يعني بالبرير: ثمر الأراك، غضا كان أو مدركا، فأما الغض منه فإنه يدعى كباثا، وإياه عنى','354','1','0');
INSERT INTO [t13091] VALUES (58,'ذكر خبر آخر من أخبار أم موسى، عن علي رضوان الله عليه، عن النبي صلى الله عليه وسلم وعلى آله','356','1','0');
INSERT INTO [t13091] VALUES (59,'القول في علل هذا الخبر والقول في ذلك نحو القول في الذي قبله. وقد وافق عليا رحمة الله عليه في رواية هذا الخبر عن رسول الله صلى الله عليه وسلم جماعة من أصحابه، نذكر ما صح عندنا مما حضرنا من ذلك سنده.','358','1','0');
INSERT INTO [t13091] VALUES (60,'ذكر خبر آخر من أخبار أم موسى عن علي رضوان الله عليه عن النبي صلى الله عليه وسلم','361','1','0');
INSERT INTO [t13091] VALUES (61,'القول في علل هذا الخبر والقول في علل هذا الخبر نظير القول في علل الذي قبله، وقد مضى قبل ذكر نظائر هذا الخبر، فكرهنا إعادته.','363','1','0');
INSERT INTO [t13091] VALUES (62,'ذكر خبر آخر من أخبار أم موسى، عن علي رحمة الله عليه، عن النبي صلى الله عليه وسلم','364','1','0');
INSERT INTO [t13091] VALUES (63,'ذكر خبر آخر من أخبار علي رحمة الله عليه عن النبي صلى الله عليه وسلم وعلى آله','366','1','0');
INSERT INTO [t13091] VALUES (64,'القول في علل هذا الخبر وهذا خبر عندنا صحيح سنده، وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح، لعلل: إحداها: أنه خبر لا يعرف له مخرج يصح عن علي، عن النبي صلى الله عليه وسلم إلا من هذا الوجه، والخبر إذا انفرد به عندهم منفرد وجب التثبت فيه.','368','1','0');
INSERT INTO [t13091] VALUES (65,'ذكر من وافق عليا رحمة الله عليه في روايته عن رسول الله صلى الله عليه وسلم فيمن غير تخوم الأرض، أو منارها، أو أخذ منها شيئا بغير حق','370','1','0');
INSERT INTO [t13091] VALUES (66,'ذكر من وافق عليا رحمة الله عليه في روايته عن رسول الله صلى الله عليه وسلم في ذم العاق والديه','398','1','0');
INSERT INTO [t13091] VALUES (67,'ذكر من وافق عليا رحمة الله عليه في روايته عن رسول الله صلى الله عليه وسلم، ما روى في ذم من تولى غير مواليه، ومن وافق هانئا مولى علي في روايته ما روى في ذلك عن علي، عن النبي صلى الله عليه وسلم وعلى آله','420','1','0');
INSERT INTO [t13091] VALUES (68,'القول في البيان عما في هذه الأخبار من الغريب " فمن ذلك قول النبي صلى الله عليه وسلم في الخبر الذي ذكرناه عن علي بن أبي طالب عنه: " لعن الله من غير منار الأرض " يعني صلى الله عليه وسلم بالمنار: المعالم، وهو مفعل، من قول القائل: قد نار لي هذا الأمر،','445','1','0');
INSERT INTO [t13091] VALUES (69,'ذكر خبر آخر من أخبار علي رحمة الله عليه عن النبي صلى الله عليه وسلم','447','1','0');
INSERT INTO [t13091] VALUES (70,'القول في علل هذا الخبر وهذا خبر عندنا صحيح سنده، وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح، لعلل: إحداها: أنه خبر لا يعرف له عن رسول الله صلى الله عليه وسلم مخرج يصح، إلا من هذا الوجه، والخبر إذا انفرد به عندهم منفرد وجب التثبت فيه. والثانية','450','1','0');
INSERT INTO [t13091] VALUES (71,'القول في معنى هذا الخبر وفيما فيه من الفقه إن قال لنا قائل: وما معنى الخبر وما وجهه، إن كان صحيحا كما قلت؟ وقد علمت ما','451','1','0');
INSERT INTO [t13091] VALUES (72,'ذكر بعض ما حضرنا ذكره منهم','457','1','0');
INSERT INTO [t13091] VALUES (73,'ذكر خبر آخر من أخبار علي رضوان الله عليه، عن النبي صلى الله عليه وسلم وعلى آله','466','1','0');
INSERT INTO [t13091] VALUES (74,'ذكر خبر آخر من أخبار علي، عن النبي صلى الله عليه وسلم','468','1','0');
INSERT INTO [t13091] VALUES (75,'القول في علل هذا الخبر وهذا خبر عندنا صحيح سنده، وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح، لعلل: إحداها: أن إبراهيم بن مهاجر عندهم لا تثبت به في الدين حجة. والأخرى: أن شريكا عندهم كان يكثر غلطه، فالواجب التوقف في أخباره. والثالثة: أن أبا','470','1','0');
INSERT INTO [t13091] VALUES (76,'ذكر من حرم أكل ذبائحهم','472','1','0');
INSERT INTO [t13091] VALUES (77,'ذكر من قال ذلك','480','1','0');
INSERT INTO [t13091] VALUES (78,'ذكر خبر آخر من أخبار علي رحمة الله عليه، عن رسول الله صلى الله عليه وسلم','494','1','0');
INSERT INTO [t13091] VALUES (79,'القول في علل هذا الخبر وهذا خبر عندنا صحيح سنده، وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح، وذلك أنه خبر لا يعرف له مخرج عن علي، عن النبي صلى الله عليه وسلم يصح، إلا من هذا الوجه. والخبر إذا انفرد به عندهم منفرد وجب التثبت فيه ?','496','1','0');
INSERT INTO [t13091] VALUES (80,'ذكر خبر آخر من أخبار علي رحمة الله عليه، عن النبي صلى الله عليه وسلم','497','1','0');
INSERT INTO [t13091] VALUES (81,'ذكر ما لم يمض ذكره من أخبار أبي مريم، عن علي رحمة الله عليه، عن النبي صلى الله عليه وسلم','499','1','0');
INSERT INTO [t13091] VALUES (82,'القول في علل هذا الخبر وهذا خبر عندنا صحيح سنده، وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح، لعلل: إحداها: أنه خبر لا يعرف له مخرج يصح عن علي، عن رسول الله صلى الله عليه وسلم إلا من هذا الوجه، والخبر إذا انفرد به، عندهم منفرد، وجب التثبت فيه','503','1','0');
INSERT INTO [t13091] VALUES (83,'ذكر ما في هذا الخبر من الفقه والذي فيه من ذلك الدلالة على صحة قول من قال: لا بأس على الرجل المسلم إذا رأى بعض ما يتخذه أهل الكفر وأهل الفسوق والفجور من الأشياء التي يعصى الله بها، مما لا يصلح لغير معصية الله به، وهو بهيئته، وذلك مثل الطنابير والعيدان','504','1','0');
INSERT INTO [t13091] VALUES (84,'ذكر من حضرنا ذكره، ممن فعل ذلك، أو أمر به منهم','507','1','0');
INSERT INTO [t13091] VALUES (85,'ذكر خبر آخر من أخبار أبي مريم، عن علي رضوان الله عليه، عن النبي صلى الله عليه وسلم','517','1','0');
INSERT INTO [t13091] VALUES (86,'ذكر خبر آخر من أخبار علي، عن النبي صلى الله عليه وسلم','520','1','0');
INSERT INTO [t13091] VALUES (87,'القول في علل هذا الخبر وهذا خبر عندنا صحيح سنده، وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح، لعلل: إحداها: أنه خبر لا يعرف له مخرج يصح عن رسول الله صلى الله عليه وسلم إلا من هذا الوجه. والثانية: أنه قد حدث به عن أبي إسحاق غير الثوري، فقال فيه','522','1','0');
INSERT INTO [t13091] VALUES (88,'القول في ما في هذا الخبر من الفقه والذي فيه من ذلك الدلالة على أن من رمى بشيء في طريق من الطرق متعمدا رميه به، أو تركه كذلك في منزل نزله، على غير عزم منه على ألا يعود لأخذه والرجوع في تملكه، ولكن على العزم منه على العودة لأخذه واسترجاعه ممن وجده معه','524','1','0');
INSERT INTO [t13091] VALUES (89,'ذكر من قال ذلك','527','1','0');
INSERT INTO [t13091] VALUES (90,'ذكر من قال: ذلك','533','1','0');
INSERT INTO [t13091] VALUES (91,'ذكر من قال ذلك','535','1','0');
INSERT INTO [t13091] VALUES (92,'ذكر خبر آخر من أخبار علي، عن النبي صلى الله عليه وسلم','537','1','0');
INSERT INTO [t13091] VALUES (93,'القول في علل هذا الخبر وهذا خبر عندنا صحيح سنده، وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح، لعلل: إحداها: أنه خبر حدث به جماعة عن علي، فجعلوا الكلام موقوفا عليه، ولم يرفعوه إلى رسول الله صلى الله عليه وسلم. والثانية: أنه خبر قد روي عن غير','541','1','0');
INSERT INTO [t13091] VALUES (94,'ذكر من قال: الذي نادى بذلك بلال','550','1','0');
INSERT INTO [t13091] VALUES (95,'ذكر من قال: الذي كان ينادي بذلك بديل بن ورقاء','552','1','0');
INSERT INTO [t13091] VALUES (96,'ذكر من قال: الذي نادى بذلك عبد الله بن حذافة','556','1','0');
INSERT INTO [t13091] VALUES (97,'ذكر من قال: كان الذي نادى بذلك بشر بن سحيم، ومن روى هذا الخبر فجعله عنه، عن النبي صلى الله عليه وسلم، ولم يدخل بينه وبين النبي صلى الله عليه وسلم عليا','561','1','0');
INSERT INTO [t13091] VALUES (98,'ذكر من قال: الذي بعثه رسول الله صلى الله عليه وسلم في ذلك: كعب بن مالك، وأوس بن الحدثان','569','1','0');
INSERT INTO [t13091] VALUES (99,'ذكر من قال: بل كان ذلك معاذ بن جبل','571','1','0');
INSERT INTO [t13091] VALUES (100,'ذكر من قال: كان ذلك سعد بن أبي وقاص','573','1','0');
INSERT INTO [t13091] VALUES (101,'ذكر من حدث هذا الحديث، ولم يسم الذي نادى بذلك في حديثه','575','1','0');
INSERT INTO [t13091] VALUES (102,'القول في البيان عن وجه اختلاف نقلة هذه الأخبار في الذي بعثه رسول الله صلى الله عليه وسلم بمنى للنداء بما ذكر فيها إن قال لنا قائل: ما أنت قائل في هذه الأخبار التي رويتها لنا؟ فإن قلت: إنها صحاح، قلنا لك: فما وجه اختلاف رواتها في المنادي الذي نادى','579','1','0');
INSERT INTO [t13091] VALUES (103,'ذكر خبر آخر من أخبار علي، عن رسول الله صلى الله عليه وسلم','581','1','0');
INSERT INTO [t13091] VALUES (104,'القول في علل هذا الخبر وهذا خبر عندنا صحيح سنده، وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح، لعلل: إحداها: أنه خبر لا يعرف له مخرج يصح عن علي، عن رسول الله صلى الله عليه وسلم، إلا من هذا الوجه. والخبر إذا انفرد به عندهم منفرد، وجب التثبت فيه','583','1','0');
INSERT INTO [t13091] VALUES (105,'ذكر من روى هذا الخبر عن علي بن طلق، عن النبي صلى الله عليه وسلم','584','1','0');
INSERT INTO [t13091] VALUES (106,'ذكر خبر آخر من أخبار علي عن النبي صلى الله عليه وسلم','589','1','0');
INSERT INTO [t13091] VALUES (107,'القول في علل هذا الخبر وهذا خبر عندنا صحيح سنده، وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح، لعلل: إحداها: أنه خبر لا يعرف له مخرج يصح عن علي , عن رسول الله صلى الله عليه وسلم، إلا من هذا الوجه. والخبر إذا انفرد به عندهم منفرد وجب التثبت فيه.','592','1','0');
INSERT INTO [t13091] VALUES (108,'القول فيما في هذا الخبر من الفقه والذي فيه من ذلك البيان عن أن المعني بقول الله تعالى ذكره: يا أيها الذين آمنوا لا تقربوا الصلاة وأنتم سكارى حتى تعلموا ما تقولون ولا جنبا إلا عابري سبيل حتى تغتسلوا غسل جميع الجسد في الجنابة، وأن المراد بقوله: وإن كنتم','593','1','0');
INSERT INTO [t13091] VALUES (109,'ذكر من حضرنا ذكره منهم','597','1','0');
INSERT INTO [t13091] VALUES (110,'القول في البيان عما في هذه الأخبار من الغريب فمن ذلك قول أبي البختري: خرج حذيفة وقد طم رأسه، يعني بقوله: وقد طم رأسه، جز شعره واستأصله. وأما قول حذيفة لامرأته: استأصلي شعرك، فإنه يعني به: روي أصوله بالماء في الغسل من الجنابة والحيض، وابلغي','605','1','0');
INSERT INTO [t13091] VALUES (111,'ذكر خبر آخر من أخبار علي رحمة الله عليه، عن رسول الله صلى الله عليه وسلم','606','1','0');
INSERT INTO [t13091] VALUES (112,'القول في علل هذا الخبر وهذا خبر عندنا صحيح سنده، وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح، لعلل إحداها: أن المعروف من رواية أصحاب علي هذا الخبر عن علي، الوقف به على علي، وترك رفعه إلى رسول الله صلى الله عليه وسلم. والثانية: أن حميد بن عبد','608','1','0');
INSERT INTO [t13091] VALUES (113,'ذكر من روى هذا الحديث عن أيوب فقال فيه: عنه، عن ابن سيرين، عن أبي هريرة، عن النبي صلى الله عليه وسلم','614','1','0');
INSERT INTO [t13091] VALUES (114,'القول في البيان عما في هذا الخبر من الفقه والذي فيه من ذلك: الإبانة عن أن الحق على كل مسلم: الاقتصاد في كل شيء من أمره , وترك الإفراط والغلو فيه. وذلك أن التحاب في الله من أفضل أعمال المسلمين، ومما أمر به رسول الله صلى الله عليه وسلم فقال: " لا','619','1','0');
