INSERT INTO [t13092] VALUES (1,'مسند عمر بن الخطاب رضي الله عنه','1','0','0');
INSERT INTO [t13092] VALUES (2,'ذكر ما صح عندنا سنده من حديث أبي سعيد الخدري، عن عمر بن الخطاب، عن رسول الله صلى الله عليه وسلم مما لم نذكره فيما مضى','1','1','0');
INSERT INTO [t13092] VALUES (3,'ذكر علل هذا الخبر وهذا خبر عندنا صحيح سنده، لا سبب يضعفه، ولا علة توهنه؛ لعدالة من بيننا وبين رسول الله صلى الله عليه وسلم من نقلته، وقد يجب أن يكون سقيما غير صحيح؛ لعلل: إحداها: أن هذا الحديث قد حدث به جماعة من الثقات من أصحاب أبي بكر بن عياش،','4','1','0');
INSERT INTO [t13092] VALUES (4,'ذكر من حدث بهذا الحديث عن أبي بكر بن عياش فلم يدخل بين أبي سعيد ورسول الله صلى الله عليه وسلم عمر','6','1','0');
INSERT INTO [t13092] VALUES (5,'ذكر من حدث بهذا الحديث عن الأعمش، فجعله عن غير أبي صالح','8','1','0');
INSERT INTO [t13092] VALUES (6,'ذكر من حدث بهذا الحديث عن أبي سعيد غير من ذكرت، فلم يدخل بينه وبين رسول الله صلى الله عليه وسلم أحدا','10','1','0');
INSERT INTO [t13092] VALUES (7,'ذكر ذلك','14','1','0');
INSERT INTO [t13092] VALUES (8,'ذكر ما في هذه الأخبار من الفقه فمما في ذلك منه: تحريم النبي صلى الله عليه وسلم المسألة، ثم اختلف أهل العلم في المسألة التي حرمها صلى الله عليه وسلم، وفي صفة السائل الذي حرم ذلك عليه. فقال بعضهم: المسألة التي حرمها صلى الله عليه وسلم على من حرمها','76','1','0');
INSERT INTO [t13092] VALUES (9,'ذكر من قال بتحريم النبي صلى الله عليه وسلم المسألة في حديث عمر، وقوله: " إن أحدهم ليخرج بمسألته من عندي متأبطا بها نارا "، إنما هي المسألة التي يسألها السائل من يسأل؛ تكثيرا بها ماله، وهو عنها غني، ولم يحد في الغنى حدا غير الغنى المعروف في العوام','78','1','0');
INSERT INTO [t13092] VALUES (10,'ذكر من أنكر المسألة بكل حال','85','1','0');
INSERT INTO [t13092] VALUES (11,'ذكر بعض من حضرنا ذكره منهم','93','1','0');
INSERT INTO [t13092] VALUES (12,'ذكر الرواية عن نبي الله صلى الله عليه وسلم، وعن بعض من حضرنا ذكره من السلف في ذلك','95','1','0');
INSERT INTO [t13092] VALUES (13,'ذكر من وافق عمر في رواية ذلك عن رسول الله صلى الله عليه وسلم من الصحابة','132','1','0');
INSERT INTO [t13092] VALUES (14,'ذكر ما حضرنا ذكره في ذلك عن السلف من الصحابة والتابعين وغيرهم','168','1','0');
INSERT INTO [t13092] VALUES (15,'القول فيما في هذه الأخبار من الغريب فمن ذلك قول النبي صلى الله عليه وسلم: " من سأل وله ما يغنيه جاء يوم القيامة وفي وجهه خدوش أو كدوح أو خموش "، فالكدوح: آثار الخدوش، ومنه قول الشاعر: هما الغول والسعلاة حلقي منهما مخدش ما بين التراقي مكدح يعني بقوله','208','1','0');
INSERT INTO [t13092] VALUES (16,'ذكر خبر آخر من أخبار أبي سعيد الخدري، عن عمر بن الخطاب، عن رسول الله صلى الله عليه وسلم','221','1','0');
INSERT INTO [t13092] VALUES (17,'القول في علل هذا الخبر وهذا خبر عندنا صحيح سنده، لا علة فيه توهنه، ولا سبب يضعفه، وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح؛ لعلل: إحداها: أنه خبر قد حدث به عن أبي سعيد غير واحد من الرواة عنه، فلم يذكروا فيه الكلام الذي ذكره أبو نضرة في هذا','223','1','0');
INSERT INTO [t13092] VALUES (18,'ذكر من حدث بهذا الحديث بهذا الإسناد، فلم يذكر فيه ما ذكره ابن أبي عدي من قول عمر في الضب: إنما عافه رسول الله صلى الله عليه وسلم','224','1','0');
INSERT INTO [t13092] VALUES (19,'ذكر من حدث بهذا الحديث عن عمر، فجعله عن داود، عن أبي نضرة، عن عمر مرسلا، ولم يدخل بين أبي نضرة وبين عمر أبا سعيد','226','1','0');
INSERT INTO [t13092] VALUES (20,'ذكر من حدث بهذا الحديث عن أبي سعيد، فلم يذكر فيه الكلام الذي ذكره داود، عن أبي نضرة، عنه، عن عمر','228','1','0');
INSERT INTO [t13092] VALUES (21,'ذكر من حدث بهذا الحديث عن داود فوافق - في ترك الكلام الذي ذكره ابن أبي عدي، عن أبي سعيد، عن عمر، عن النبي صلى الله عليه وسلم - قتادة','232','1','0');
INSERT INTO [t13092] VALUES (22,'ذكر ذلك','235','1','0');
INSERT INTO [t13092] VALUES (23,'ذكر ذلك','239','1','0');
INSERT INTO [t13092] VALUES (24,'ذكر بعض من حضرنا ذكره منهم','264','1','0');
INSERT INTO [t13092] VALUES (25,'ذكر من قال بهذا الخبر من متقدمي أهل العلم','296','1','0');
INSERT INTO [t13092] VALUES (26,'ذكر من نهى عن أكله من السلف','305','1','0');
INSERT INTO [t13092] VALUES (27,'ذكر البيان عما في هذه الأخبار من الغريب فمن ذلك قول عمر رحمه الله في لحم الضب: إنما عافه رسول الله صلى الله عليه وسلم. يعني بقوله: عافه: كرهه، يقال منه: عاف فلان هذا الشيء فهو يعافه عيفا، وعيوفا، ومنه قول أعشى بني ثعلبة: لكالثور يوم الورد يضرب','311','1','0');
INSERT INTO [t13092] VALUES (28,'ذكر ما لم يمض من حديث يعلى بن أمية، عن عمر، عن النبي صلى الله عليه وسلم','320','1','0');
INSERT INTO [t13092] VALUES (29,'القول في علل هذا الحديث وهذا الحديث عندنا صحيح سنده، لا علة فيه توهنه، ولا سبب يضعفه، وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح؛ لأنه خبر لا يعرف له مخرج عن عمر، عن رسول الله صلى الله عليه وسلم بهذا اللفظ إلا من هذا الوجه، والخبر إذا انفرد','323','1','0');
INSERT INTO [t13092] VALUES (30,'ذكر ما حضرنا ذكره من ذلك','326','1','0');
INSERT INTO [t13092] VALUES (31,'ذكر ما حضرنا ذكره من ذلك','345','1','0');
INSERT INTO [t13092] VALUES (32,'ذكر بعض من حضرنا ذكره من القائلين بذلك من السلف','381','1','0');
INSERT INTO [t13092] VALUES (33,'ذكر بعض من حضرنا ذكره من قائلي ذلك من السلف','386','1','0');
INSERT INTO [t13092] VALUES (34,'ذكر من قال هذه المقالة من السلف','388','1','0');
INSERT INTO [t13092] VALUES (35,'ذكر من أنكر القصر في حال الأمن، ولم يره إلا في حال خوف فتنة العدو','437','1','0');
INSERT INTO [t13092] VALUES (36,'القول في البيان عما في هذه الأخبار من الغريب فمن ذلك قول الذي سأل ابن عباس فقال له: إنا قدمنا البلد ونحن آمنون خافضون. يعني بقوله: خافضون: ساكنون وادعون لا نحارب أحدا. وأصله من خفض الصوت، وهو سكونه، وترك رفعه. يقال للرجل: اخفض من صوتك: يراد به','441','1','0');
INSERT INTO [t13092] VALUES (37,'القول في علل هذا الخبر وهذا خبر عندنا صحيح سنده، لا علة فيه توهنه، ولا سبب يضعفه، وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح؛ لعلل: إحداها: أن المعروف من رواة هذا الحديث القصور به على جابر، من غير إدخال عمر بينه وبين رسول الله صلى الله عليه','449','1','0');
INSERT INTO [t13092] VALUES (38,'ذكر من حدث بهذا الحديث عن سفيان، فجعله عن أبي الزبير، عن جابر، عن رسول الله صلى الله عليه وسلم، ولم يدخل بين جابر وبين رسول الله صلى الله عليه وسلم أحدا','451','1','0');
INSERT INTO [t13092] VALUES (39,'ذكر من حدث بهذا الحديث عن أبي الزبير، فلم يجعل فيه بين جابر وبين رسول الله صلى الله عليه وسلم أحدا من غير حديث الثوري','453','1','0');
INSERT INTO [t13092] VALUES (40,'القول في البيان عما في هذا الخبر من المعنى إن قال لنا قائل: ما معنى هذا الخبر، وما وجهه؟ أصحيح هو أم سقيم؟ فإن كان صحيحا فقد بطل معنى الخبر الذي رواه سمرة بن جندب، عن النبي صلى الله عليه وسلم الذي','456','1','0');
INSERT INTO [t13092] VALUES (41,'ذكر خبر آخر من أخبار عمر، عن رسول الله صلى الله عليه وسلم، مما روى عنه أبو قتادة الأنصاري','470','1','0');
INSERT INTO [t13092] VALUES (42,'القول في علل هذا الحديث وهذا خبر عندنا صحيح سنده، لا علة فيه توهنه، ولا سبب يضعفه؛ لعدالة من بيننا وبين رسول الله صلى الله عليه وسلم من نقلته، وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح. وذلك أنه خبر قد حدث به عن غيلان بن جرير جماعة، فجعلوه عن','472','1','0');
INSERT INTO [t13092] VALUES (43,'ذكر من حدث به عن غيلان بن جرير، عن عبد الله بن معبد، عن أبي قتادة، عن النبي صلى الله عليه وسلم، ولم يدخل بين أبي قتادة وبين النبي صلى الله عليه وسلم أحدا','473','1','0');
INSERT INTO [t13092] VALUES (44,'ذكر من حدث ببعض هذا الحديث عن أبي قتادة، فوافق في روايته إياه الذين لم يدخلوا بين أبي قتادة وبين رسول الله صلى الله عليه وسلم أحدا','477','1','0');
INSERT INTO [t13092] VALUES (45,'ذكر البيان عن معاني الأخبار التي ذكرناها بنهي النبي صلى الله عليه وسلم عن صوم الدهر وذكر اختلاف السلف في ذلك اختلف العلماء من السلف في صوم الدهر، فقال بعضهم بتصحيح الأخبار التي ذكرناها، وقالوا: غير جائز لأحد صوم الدهر، وإن أفطر الأيام المحرم صومهن.','499','1','0');
INSERT INTO [t13092] VALUES (46,'ذكر من قال بذلك','500','1','0');
INSERT INTO [t13092] VALUES (47,'ذكر من فعل ذلك وقاله','514','1','0');
INSERT INTO [t13092] VALUES (48,'وبنحو الذي قلنا في ذلك قال أيضا جماعة من السلف، ذكر بعض من حضرنا ذكره منهم','530','1','0');
INSERT INTO [t13092] VALUES (49,'ذكر من قال ذلك من السلف وفعله','555','1','0');
INSERT INTO [t13092] VALUES (50,'ذكر من حضرنا ذكره منهم','567','1','0');
INSERT INTO [t13092] VALUES (51,'ذكر القول في البيان عن الأخبار الواردة عن رسول الله صلى الله عليه وسلم في صوم يوم عرفة إن قال لنا قائل: إنك قد ذكرت لنا أن الخبر الذي روي عن عمر - عن رسول الله صلى الله عليه وسلم في صوم يوم عرفة أنه كفارة سنتين - خبر صحيح، فإن كان الأمر في ذلك كما قلت','571','1','0');
INSERT INTO [t13092] VALUES (52,'ذكر من أفطر ذلك اليوم هنالك، ومن اختار الفطر فيه على الصوم من الصحابة والتابعين','594','1','0');
INSERT INTO [t13092] VALUES (53,'ذكر من كره صوم يوم عرفة لكل أحد بكل موضع وذلك ما','605','1','0');
INSERT INTO [t13092] VALUES (54,'ذكر من كان يؤثر صوم يوم عرفة على الإفطار فيه، ومن كان يأمر بذلك من الصحابة والتابعين','614','1','0');
INSERT INTO [t13092] VALUES (55,'ذكر من قال: كان ذلك يوما يصومه أهل الجاهلية، فلما نزل فرض شهر رمضان ترك صومه، فمن شاء صامه ومن شاء أفطره','625','1','0');
INSERT INTO [t13092] VALUES (56,'ذكر من قال: كان يوم عاشوراء يوما يصومه اليهود، فصامه النبي صلى الله عليه وسلم','651','1','0');
INSERT INTO [t13092] VALUES (57,'ذكر من قال: لم يزل رسول الله صلى الله عليه وسلم يصوم عاشوراء، ويحث على صومه حتى مضى لسبيله','657','1','0');
INSERT INTO [t13092] VALUES (58,'ذكر من كان يصومه، ويأمر بصومه منهم','665','1','0');
INSERT INTO [t13092] VALUES (59,'ذكر من كان يكره صومه ولا يصومه','681','1','0');
INSERT INTO [t13092] VALUES (60,'القول في البيان عما في هذه الأخبار من الغريب فمن ذلك قول رسول الله صلى الله عليه وسلم لعبد الله بن عمرو: " فإنك إذا فعلت ذلك هجمت له العين، ونفهت له النفس "، يعني بقوله صلى الله عليه وسلم: " هجمت له العين ": غارت في الحجاج، ودخلت في عظمه. ومنه قيل','688','1','0');
INSERT INTO [t13092] VALUES (61,'ذكر ما لم يمض ذكره من حديث أنس بن مالك، عن عمر بن الخطاب، عن رسول الله صلى الله عليه وسلم','694','1','0');
INSERT INTO [t13092] VALUES (62,'القول في علل هذا الخبر وهذا خبر عندنا صحيح سنده، لا علة فيه توهنه، ولا سبب يضعفه، وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح؛ وذلك أنه خبر لا يعرف له مخرج عن عمر إلا من هذا الوجه، والخبر إذا انفرد به عندهم منفرد وجب التثبت فيه','697','1','0');
INSERT INTO [t13092] VALUES (63,'ذكر ما في هذا الخبر من فائدة العلم والذي في هذا الخبر من فائدة العلم الدلالة على أن أصح القراءتين في قوله: واتخذوا من مقام إبراهيم مصلى كسر الخاء من قوله: واتخذوا على وجه الأمر؛ لإخبار عمر عن تنزيل الله إياه على رسوله صلى الله عليه وسلم، أمرا منه له','698','1','0');
INSERT INTO [t13092] VALUES (64,'ذكر الأخبار عن بعض من كان يفعل ذلك','709','1','0');
INSERT INTO [t13092] VALUES (65,'ذكر ذلك','717','1','0');
INSERT INTO [t13092] VALUES (66,'القول فيما في هذه الأخبار من الغريب فمن ذلك في خبر إياس بن عبد الله بن أبي ذباب أنه قيل لرسول الله صلى الله عليه وسلم: ذئر النساء على أزواجهن: يعني بذلك أنهن اجترأن عليهم وتنكرن لهم عما كن عليه لهم من، الطاعة إلى الخلاف عليهم، يقال منه: هي امرأة','730','1','0');
INSERT INTO [t13092] VALUES (67,'ذكر خبر آخر من حديث أنس بن مالك، عن عمر، عن النبي صلى الله عليه وسلم','739','1','0');
INSERT INTO [t13092] VALUES (68,'القول في علل هذا الحديث وهذا خبر عندنا صحيح سنده، لا علة فيه توهنه، ولا سبب يضعفه لعدالة من بيننا وبين رسول الله صلى الله عليه وسلم من نقلته، وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح، وذلك أنه خبر لا يعرف له عن عمر، عن رسول الله صلى الله عليه','741','1','0');
INSERT INTO [t13092] VALUES (69,'ذكر ذلك','742','1','0');
INSERT INTO [t13092] VALUES (70,'ذكر الرواية عمن وافق عمر في رواية هذا الخبر عن رسول الله صلى الله عليه وسلم','744','1','0');
INSERT INTO [t13092] VALUES (71,'القول في معاني هذه الأخبار اختلف السلف من علماء الأمة في معاني هذه الأخبار، فقال جماعة يكثر عددها بتصحيحها، وتصحيح القول بظاهرها وعمومها، وقالوا: الميت بعد موته يسمع كلام الأحياء، ولذلك قال النبي صلى الله عليه وسلم لأهل القليب بعدما ألقوا فيه ما قال','749','1','0');
INSERT INTO [t13092] VALUES (72,'ذكر بعض ما حضرنا ذكره، مما صح من ذلك سنده عن رسول الله صلى الله عليه وسلم','750','1','0');
INSERT INTO [t13092] VALUES (73,'ذكر من قال بتصحيح هذه الأخبار من السلف، وقالوا: إن الموتى يسمعون كلام الأحياء، ويتكلمون ويعلمون','762','1','0');
INSERT INTO [t13092] VALUES (74,'ذكر من لم نذكر من ذلك','774','1','0');
INSERT INTO [t13092] VALUES (75,'ذكر الأخبار الواردة بذلك','779','1','0');
INSERT INTO [t13092] VALUES (76,'ذكر بعض ما حضرنا ذكره من ذلك، مما صح عندنا سنده','789','1','0');
INSERT INTO [t13092] VALUES (77,'ذكر بعض ما حضرنا ذكره من ذلك','803','1','0');
INSERT INTO [t13092] VALUES (78,'ذكر بعض ما حضرنا ذكره منهم','810','1','0');
INSERT INTO [t13092] VALUES (79,'ذكر ما حضرنا ذكره من ذلك','817','1','0');
INSERT INTO [t13092] VALUES (80,'ذكر قول القائلين: لا يجلس من تبعها بعد أن يصلى عليها حتى يوضع صاحبها في القبر','824','1','0');
INSERT INTO [t13092] VALUES (81,'ذكر الأخبار التي بها اعتل هؤلاء','832','1','0');
INSERT INTO [t13092] VALUES (82,'ذكر من كان يرى الجلوس قبل أن توضع الجنازة','838','1','0');
INSERT INTO [t13092] VALUES (83,'ذكر اختلاف الرواة في ذلك','865','1','0');
INSERT INTO [t13092] VALUES (84,'ذكر ما صح عندنا من ذلك سنده','875','1','0');
INSERT INTO [t13092] VALUES (85,'القول في البيان عما في هذه الأخبار التي ذكرناها من الغريب فمن ذلك قول البراء بن عازب: فانتهينا إلى القبر ولما يلحد، واللحد: هو القبر يحفر للموت معترضا في جانب، وفيه لغتان: لحد، بفتح اللام: هي لغة تميم، ولحد بضمها: وهي لغة أهل العالية، وكل مائل','932','1','0');
INSERT INTO [t13092] VALUES (86,'ذكر ما صح عندنا سنده من حديث عمرو بن حريث، عن عمر بن الخطاب، عن النبي صلى الله عليه وسلم','940','1','0');
INSERT INTO [t13092] VALUES (87,'القول في علل هذا الخبر وهذا خبر عندنا صحيح سنده، لا علة فيه توهنه، ولا سبب يضعفه، وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح لعلتين: إحداهما: أنه قد حدث به عن إسماعيل بن أبي خالد جماعة، ولم يرفعوه إلى النبي صلى الله عليه وسلم، بل وقفوه على','942','1','0');
INSERT INTO [t13092] VALUES (88,'ذكر من حدث بهذا الحديث عن إسماعيل، عن عمرو بن حريث، عن عمر، فجعله من كلام عمر، ولم يرفعه إلى النبي صلى الله عليه وسلم','943','1','0');
INSERT INTO [t13092] VALUES (89,'ذكر من وافق عمر في رواية هذا الخبر عن رسول الله صلى الله عليه وسلم من أصحابه','945','1','0');
INSERT INTO [t13092] VALUES (90,'ذكر الأخبار التي ادعى قائلو هذه المقالة أنها للأخبار التي تقدم ذكرنا لها معارضة','954','1','0');
INSERT INTO [t13092] VALUES (91,'ذكر بعض الأخبار الواردة بمعنى ما قلنا قبل','998','1','0');
INSERT INTO [t13092] VALUES (92,'ذكر بعض ما حضرنا ذكره ممن روى، أو قال الشعر من الصحابة والتابعين والسلف الصالحين، ومن كان منهم يسمعه ويأمر بروايته أو قيله','1011','1','0');
INSERT INTO [t13092] VALUES (93,'القول في البيان عما في هذه الأخبار من الغريب فمن ذلك قول النبي صلى الله عليه وسلم: " لأن يمتلئ جوف أحدكم قيحا حتى يريه "، يعني بقوله صلى الله عليه وسلم: " حتى يريه "، حتى يدوى جوفه ويأكله القيح، يقال فيه: ورى القيح جوف فلان، فهو يريه وريا، والجوف','1035','1','0');
INSERT INTO [t13092] VALUES (94,'حديث النعمان بن بشير، عن عمر بن الخطاب، عن النبي صلى الله عليه وسلم','1040','1','0');
INSERT INTO [t13092] VALUES (95,'القول في علل هذا الخبر وهذا خبر عندنا صحيح سنده، لا علة فيه توهنه، ولا سبب يضعفه، وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح لعلتين: إحداهما: أنه لا يعرف له عن النعمان بن عمر، عن النبي صلى الله عليه وسلم مخرج إلا من هذا الوجه. والأخرى: أنه','1042','1','0');
INSERT INTO [t13092] VALUES (96,'ذكر من روى هذا الحديث عن سماك، فجعله عن النعمان، عن رسول الله صلى الله عليه وسلم، ولم يدخل بينه وبين رسول الله صلى الله عليه وسلم أحدا','1043','1','0');
INSERT INTO [t13092] VALUES (97,'القول في البيان عن معاني هذه الأخبار إن قال لنا قائل: وما وجه هذه الأخبار ومعانيها، وقد علمت صحة الأخبار عن رسول الله صلى الله عليه وسلم، أنه كان يرفع ما أفاء الله عليه من النضير وفدك قوته وقوت عياله لسنة، ثم يسلف ما فضل عن ذلك في الكراع والسلاح عدة','1072','1','0');
INSERT INTO [t13092] VALUES (98,'ذكر ما حضرنا ذكره من ذلك','1077','1','0');
INSERT INTO [t13092] VALUES (99,'ذكر بعض من حضرنا ذكره ممن سلك في ذلك سبيل رسول الله صلى الله عليه وسلم','1082','1','0');
INSERT INTO [t13092] VALUES (100,'القول في البيان عما في هذه الأخبار من الغريب فمن ذلك قول أبي هريرة في الخبر الذي ذكره عن رسول الله صلى الله عليه وسلم عند مصيره إلى منزل أبي الهيثم بن التيهان: " فجاء بقربته يرعبها "، يعني بقوله: يرعبها، يملؤها ماء. يقال منه: رعب فلان الحوض فهو','1099','1','0');
INSERT INTO [t13092] VALUES (101,'ذكر ما لم يمض ذكره من حديث مالك بن أوس بن الحدثان النصري، عن عمر بن الخطاب، عن رسول الله صلى الله عليه وسلم','1101','1','0');
INSERT INTO [t13092] VALUES (102,'القول في علل هذا الخبر وهذا خبر عندنا صحيح سنده لا علة فيه توهنه، ولا سبب يضعفه، وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح لعلتين: إحداهما: أنه خبر قد حدث بهذا الحديث عن عمر من غير حديث مالك بن أوس بن الحدثان، فجعل هذا الكلام موقوفا على عمر،','1108','1','0');
INSERT INTO [t13092] VALUES (103,'ذكر من روى هذا الكلام عن عمر فوقفه عليه ولم يرفعه','1109','1','0');
INSERT INTO [t13092] VALUES (104,'القول في معاني هذه الأخبار اختلف أهل العلم في معنى قول النبي صلى الله عليه وسلم: " الذهب بالذهب ربا إلا هاء وهاء، والفضة بالفضة ربا إلا هاء وهاء "، وفي معنى قول أبي سعيد الخدري: " سمعت رسول الله صلى الله عليه وسلم يقول: " لا تبيعوا الذهب بالذهب إلا','1135','1','0');
INSERT INTO [t13092] VALUES (105,'ذكر من قال هذا القول، وقال: لا يجوز بيع الذهب بالذهب، ولا الورق بالورق، وما أشبه ذلك نساء، ولا يجوز افتراق متبايعي شيء من ذلك إلا عن تقابض','1137','1','0');
INSERT INTO [t13092] VALUES (106,'ذكر ما دل عليه الخبر الذي ذكرناه عن عمر، عن النبي صلى الله عليه وسلم في الصرف من الأحكام فإذ كان صحيحا ما قلنا من أنه غير جائز للمتصارفين أن يفترقا بأبدانهما عن المجلس الذي تصارفا فيه إلا عن تقابض منهما ما تصارفاه، إذ كان غير جائز بيع أحدهما بالآخر','1146','1','0');
INSERT INTO [t13092] VALUES (107,'ذكر البيان عما في هذه الأخبار من الغريب فمن ذلك قول النبي صلى الله عليه وسلم: " لا تبيعوا الذهب بالذهب إلا مثلا بمثل، ولا تشفوا بعضها على بعض "، يعني صلى الله عليه وسلم بقوله: " لا تشفوا بعضها على بعض "، لا تفضلوا أن تبيعوا إحداهما زائدة على الأخرى','1150','1','0');
INSERT INTO [t13092] VALUES (108,'ذكر ما صح عندنا سنده من حديث عبد الرحمن بن عبد القاري، عن عمر، عن النبي صلى الله عليه وسلم','1155','1','0');
INSERT INTO [t13092] VALUES (109,'القول في علل هذا الخبر وهذا خبر عندنا صحيح سنده، لا علة فيه توهنه، ولا سبب يضعفه، وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح لعلل: إحداها: أنه خبر غير محفوظ عن عمر، عن رسول الله صلى الله عليه وسلم إلا من هذا الوجه، والخبر إذا انفرد به منفرد','1157','1','0');
INSERT INTO [t13092] VALUES (110,'ذكر من روى هذا الخبر عن عبد الرحمن بن عبد القاري، عن عمر، فجعله من كلام عمر، وخالف بلفظه ألفاظه','1159','1','0');
INSERT INTO [t13092] VALUES (111,'ذكر من حدث بهذا الحديث عن عمر من غير حديث عبد الرحمن، فوقف به أيضا على عمر ولم يرفعه','1162','1','0');
INSERT INTO [t13092] VALUES (112,'ذكر ما في هذا الخبر من الفقه وفي هذا الخبر من الفقه أن النبي صلى الله عليه وسلم ندب من كان له حظ من صلاة كان يصليها من الليل فنام عنها، أو شغل، أو نابته نائبة، فلم يصلها من أجل ذلك، أن الذي ينبغي له أن يقضيها، وذلك أن في إعلام النبي صلى الله عليه','1165','1','0');
INSERT INTO [t13092] VALUES (113,'ذكر من روى ذلك عنه من المتقدمين غير عمر، وقد ذكرنا ما روي عن عمر فيه','1167','1','0');
INSERT INTO [t13092] VALUES (114,'ذكر ما روي عن النبي صلى الله عليه وسلم في ذلك','1174','1','0');
INSERT INTO [t13092] VALUES (115,'القول في البيان عما في هذه الأخبار من الغريب فمن ذلك قول النبي صلى الله عليه وسلم: " من نام عن حزبه " , يعني بحزبه: جماعة السور التي كان يقرؤها في صلاته التي كان يصليها من الليل , وكل جماعة اجتمعت مؤتلفة أو مفترقة على شيء فهو حزب , ومن ذلك قيل للأحزاب','1179','1','0');
INSERT INTO [t13092] VALUES (116,'ذكر خبر آخر من حديث عبد الرحمن بن عبد القاري , عن عمر , عن رسول الله صلى الله عليه وسلم','1183','1','0');
INSERT INTO [t13092] VALUES (117,'ذكر خبر آخر من أخبار عمر , عن رسول الله صلى الله عليه وسلم','1185','1','0');
INSERT INTO [t13092] VALUES (118,'القول في علل هذا الخبر وهذا خبر عندنا صحيح سنده , لا علة فيه توهنه , ولا سبب يضعفه. وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح لعلتين: إحداهما: أنه قد حدث بهذا الحديث عن عمر غير واحد من الرواة , فجعل هذا الكلام الذي روي عنه , عن رسول الله صلى','1187','1','0');
INSERT INTO [t13092] VALUES (119,'ذكر من حدث بهذا الحديث عن الزهري فقال فيه: عنه عن عمرو بن واثلة','1188','1','0');
INSERT INTO [t13092] VALUES (120,'ذكر من حدث بهذا الحديث فجعل الكلام الذي فيه عن رسول الله صلى الله عليه وسلم من كلام عمر','1190','1','0');
INSERT INTO [t13092] VALUES (121,'ذكر ما في هذا الحديث من الفقه ومما في هذا الحديث من الفقه: أن عمر لم يستنكر تلقي نافع بن عبد الحارث إياه إلى عسفان من مكة , وفي ذلك الدليل أن للرجل تلقي القادم من سفر , واستقبال من قدم من بلدته إلى بلدة أخرى تكرمة وتعظيما , كالذي فعل من ذلك نافع بعمر.','1192','1','0');
INSERT INTO [t13092] VALUES (122,'ذكر خبر آخر من أخبار عمر عن رسول الله صلى الله عليه وسلم','1194','1','0');
INSERT INTO [t13092] VALUES (123,'القول في علل هذا الخبر وهذا خبر عندنا صحيح سنده , لا علة فيه توهنه , ولا سبب يضعفه لعدالة من بيننا وبين رسول الله صلى الله عليه وسلم من نقلته , وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح لعلتين: إحداهما: أنه خبر لا يعرف له أصل من وجه يصح عن رسول','1196','1','0');
INSERT INTO [t13092] VALUES (124,'القول فيما في هذا الخبر في الفقه والذي في هذا الخبر من الفقه تصحيح قول من قال: كل عامل عمل عملا فهو وإن كان في رأي العين عند من يراه على وجه فإنه فيما بين العامل وبين ربه على ما صرفه إليه بنيته ونواه بقلبه , لا على ما يبدو لعين من يراه. فإن كان ذلك','1197','1','0');
INSERT INTO [t13092] VALUES (125,'ذكر بعض ما حضرنا ذكره من ذلك مما صح سنده','1200','1','0');
INSERT INTO [t13092] VALUES (126,'ذكر ما روي في ذلك عن الصحابة والتابعين','1211','1','0');
INSERT INTO [t13092] VALUES (127,'ذكر بعض من قال ذلك','1228','1','0');
INSERT INTO [t13092] VALUES (128,'القول في البيان عما في هذه الأخبار من الغريب فمن ذلك قول شداد بن أوس: يا نعايا العرب , هكذا رواية المحدثين من رواة الأخبار , إن أخوف ما أخاف على هذه الأمة الرياء والشهوة الخفية. وأما أهل المعرفة بكلام العرب ورواة الشعر منهم , فإنهم ينكرون ذلك ويقولون:','1231','1','0');
INSERT INTO [t13092] VALUES (129,'ذكر خبر آخر من حديث عمر , عن النبي صلى الله عليه وسلم','1237','1','0');
INSERT INTO [t13092] VALUES (130,'القول في علل هذا الخبر وهذا خبر عندنا صحيح سنده , لا علة فيه توهنه , ولا سبب يضعفه؛ لعدالة من بيننا وبين رسول الله صلى الله عليه وسلم من نقلته ورواته , وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح لعلتين: إحداهما: اضطراب نقلته في سنده , فمن راويه','1239','1','0');
INSERT INTO [t13092] VALUES (131,'ذكر اختلاف الرواة في رواية هذا الخبر','1241','1','0');
INSERT INTO [t13092] VALUES (132,'ذكر من وافق عمر في روايته هذا الخبر عن رسول الله صلى الله عليه وسلم من الصحابة','1244','1','0');
INSERT INTO [t13092] VALUES (133,'القول في البيان عما في هذه الأخبار إن سألنا سائل فقال: ما معنى هذه الأخبار , وما وجهها؟ وما الصحيح منها، التي وردت عن النبي صلى الله عليه وسلم بأن طائفة من هذه الأمة لن تزال على الحق ظاهرة على من ناوأها إلى أن تقوم الساعة , أم التي وردت بأنه صلى الله','1259','1','0');
INSERT INTO [t13092] VALUES (134,'القول في البيان عما في هذه الأخبار من الغريب فمن ذلك قول النبي صلى الله عليه وسلم: " لا يضرهم من خالفهم إلا ما أصابهم من لأواء " يعني النبي صلى الله عليه وسلم بالأواء الشدة , إما في المعيشة من جدب وقحط أو حصار , وإما في الأبدان من الأمراض والعلل أو','1272','1','0');
INSERT INTO [t13092] VALUES (135,'ذكر خبر آخر من أخبار عمر , عن رسول الله صلى الله عليه وسلم','1275','1','0');
INSERT INTO [t13092] VALUES (136,'القول في علل هذا الخبر وهذا خبر عندنا صحيح سنده , لا علة فيه توهنه , ولا سبب يضعفه لعدالة من بيننا وبين رسول الله صلى الله عليه وسلم من نقلته , وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح , لعلل: إحداها: اضطراب نقلته في روايته عن عمر. فمن قائل فيه','1277','1','0');
INSERT INTO [t13092] VALUES (137,'ذكر من حدث بهذا الحديث فقال فيه: عن موسى بن طلحة , عن عمر , ولم يدخل بين موسى وبين عمر ابن الحوتكية','1279','1','0');
INSERT INTO [t13092] VALUES (138,'ذكر من حدث بهذا الحديث فجعله عن ابن الحوتكية','1281','1','0');
INSERT INTO [t13092] VALUES (139,'ذكر من حدث بهذا الحديث فجعله عن ابن الحوتكية , عن عمار','1283','1','0');
INSERT INTO [t13092] VALUES (140,'ذكر من قال في هذا الحديث: عن ابن الحوتكية , عن أبي ذر','1285','1','0');
INSERT INTO [t13092] VALUES (141,'ذكر من حدث بهذا الحديث فجعله عن موسى بن طلحة , عن أبي ذر موقوفا عليه , غير مرفوع إلى النبي صلى الله عليه وسلم','1288','1','0');
INSERT INTO [t13092] VALUES (142,'ذكر من وافق عمر في روايته عن رسول الله صلى الله عليه وسلم ما روى في الأرنب','1290','1','0');
INSERT INTO [t13092] VALUES (143,'القول في البيان عما في هذه الأخبار وذكر اختلاف أهل العلم في أكل لحم الأرنب اختلف أهل العلم في أكل لحم الأرنب , فكره أكله جماعة منهم , وأكله منهم جماعة , ولم يروا به بأسا','1298','1','0');
INSERT INTO [t13092] VALUES (144,'ذكر من كره أكله منهم','1299','1','0');
INSERT INTO [t13092] VALUES (145,'ذكر من رخص في أكل لحمه ولم ير به بأسا','1304','1','0');
INSERT INTO [t13092] VALUES (146,'ذكر من كان يختار صوم الأيام البيض من الشهر ويأمر بصومهن','1315','1','0');
INSERT INTO [t13092] VALUES (147,'ذكر الرواية عن من كان يجعل صوم الأيام الثلاثة من كل شهر الاثنين والخميس والخميس','1322','1','0');
INSERT INTO [t13092] VALUES (148,'ذكر من كان يجعل ذلك السبت والأحد والإثنين، ثم في الشهر الذي بعده الثلاثاء والأربعاء والخميس','1324','1','0');
INSERT INTO [t13092] VALUES (149,'ذكر من كان يصوم ذلك من أول الشهر ويأمر به','1326','1','0');
INSERT INTO [t13092] VALUES (150,'ذكر من كان يجعل ذلك في آخر الشهر','1328','1','0');
INSERT INTO [t13092] VALUES (151,'ذكر خبر آخر من أخبار عمر عن النبي صلى الله عليه وسلم','1332','1','0');
INSERT INTO [t13092] VALUES (152,'القول في علل هذا الخبر وهذا خبر عندنا صحيح سنده , لا علة فيه توهنه , ولا سبب يضعفه , لعدالة من بيننا وبين رسول الله صلى الله عليه وسلم من نقلته , وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح , لعلل: إحداها: أن هذا الحديث لا يعرف له مخرج عن عمر , عن','1334','1','0');
INSERT INTO [t13092] VALUES (153,'ذكر من وافق عمر في الذي قال وروى من ذلك , عن رسول الله صلى الله عليه وسلم','1335','1','0');
INSERT INTO [t13092] VALUES (154,'القول في البيان عما في هذه الأخبار من الأحكام إن قال لنا قائل: ما وجه هذا الخبر الذي ذكرت عن زيد بن ثابت , عن رسول الله صلى الله عليه وسلم أنه قال: (الشيخ والشيخة إذا زنيا فارجموهما ألبتة) , وما معنى قول عمر: " لما نزلت أتيت النبي صلى الله عليه وسلم','1344','1','0');
INSERT INTO [t13092] VALUES (155,'ذكر خبر آخر من أخبار عمر , عن رسول الله صلى الله عليه وسلم','1352','1','0');
INSERT INTO [t13092] VALUES (156,'القول في علل هذا الخبر وهذا خبر عندنا صحيح سنده , لا علة فيه توهنه , ولا سبب يضعفه , لعدالة من بيننا وبين رسول الله صلى الله عليه وسلم من نقلته , وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح , لأنه خبر لا يعرف له مخرج عن عمر , عن النبي صلى الله عليه','1357','1','0');
INSERT INTO [t13092] VALUES (157,'ذكر من وافق عمر في روايته ذلك عن رسول الله صلى الله عليه وسلم','1358','1','0');
INSERT INTO [t13092] VALUES (158,'القول فيما في هذا الخبر من الفقه والذي في هذا الخبر من الفقه بيان وقت الوقوف الذي أوجبه الله تعالى ذكره على حجاج بيته الحرام لذكره عند المشعر الحرام بقوله: فإذا أفضتم من عرفات فاذكروا الله عند المشعر , فمن وقف بالمشعر الحرام ذاكرا لله في الوقت الذي وقف','1367','1','0');
INSERT INTO [t13092] VALUES (159,'ذكر البيان عما في هذه الأخبار من الغريب فمن ذلك قول عمر: كان المشركون لا يفيضون من جمع حتى تطلع الشمس , يعني بقوله: لا يفيضون لا يرجعون من المشعر إلى حيث يبدأ المصير إليه من منى حتى تطلع الشمس ولذلك تقول العرب لكل راجع من موضع كان صار إليه من موضع آخر','1368','1','0');
INSERT INTO [t13092] VALUES (160,'ذكر خبر آخر من أخبار عمر , عن رسول الله صلى الله عليه وسلم','1373','1','0');
INSERT INTO [t13092] VALUES (161,'القول في علل هذا الخبر وهذا الخبر عندنا صحيح سنده , لا علة فيه توهنه , ولا سبب يضعفه , لعدالة من بيننا وبين رسول الله صلى الله عليه وسلم من نقلته , وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح لأنه خبر لا يعرف له مخرج عن عمر إلا من هذا الوجه , والخبر','1377','1','0');
INSERT INTO [t13092] VALUES (162,'البيان عن معنى هذا الخبر إن قال لنا قائل: إنك قد ذكرت أن هذا خبر صحيح , فإن كان صحيحا , أفترى العمل به جائزا؟ قيل: نعم , على الصحيح من وجوهه فإن قال: وما الصحيح من وجوهه؟ قيل: أن يكون معنى قول ابن السمط: رأيت عمر يصلي بذي الحليفة ركعتين , أنه رآه','1378','1','0');
INSERT INTO [t13092] VALUES (163,'ذكر الرواية عن بعض من حضرنا ذكره منهم في ذلك','1380','1','0');
INSERT INTO [t13092] VALUES (164,'ذكر الأخبار الواردة عن رسول الله صلى الله عليه وسلم، وعن ورود ذلك عنه من الصحابة والتابعين','1416','1','0');
INSERT INTO [t13092] VALUES (165,'القول في البيان عما في هذه الأخبار من الغريب فمن ذلك قول عثمان: بلغني أن قوما يخرجون إلى سوادهم في تجارة أو جباية أو جشر، يعني بقوله: أو جشر: القوم يخرجون بإبلهم ودوابهم خارج القرية للرعي، يقال من ذلك: أصبح بنو فلان جشرا، إذا كانوا يأوون في الإبل','1439','1','0');
INSERT INTO [t13092] VALUES (166,'ذكر خبر آخر من حديث عمر، عن رسول الله صلى الله عليه وسلم','1441','1','0');
INSERT INTO [t13092] VALUES (167,'القول في البيان عما في هذه الأخبار من الغريب فمن ذلك قول عمر لعبد الرحمن: فهبني صمتا حتى أعهد إلى النفر الذين توفي رسول الله صلى الله عليه وسلم وهو عنهم راض، يعني بقوله: هبني، هب لي، كما يقال: صادني صيدا، بمعنى: صاد لي كما قال نابغة بنى ذبيان:','1457','1','0');
INSERT INTO [t13092] VALUES (168,'ذكر خبر آخر من أخبار عمر رضي الله عنه، عن رسول الله صلى الله عليه وسلم','1462','1','0');
INSERT INTO [t13092] VALUES (169,'القول في علل هذا الخبر وهذا خبر عندنا صحيح سنده، لا علة فيه توهنه، ولا سبب يضعفه، لعدالة من بيننا وبين رسول الله صلى الله عليه وسلم من نقلته، وقد يجب أن يكون على مذهب الآخرين سقيما غير صحيح، لعلل: إحداها: أن المعروف من هذا الخبر من عمر أنه لم يخبر','1464','1','0');
INSERT INTO [t13092] VALUES (170,'ذكر من حدث هذا الحديث عن عمر فوقف بالكلام عليه، ولم يذكر فيه عن رسول الله صلى الله عليه وسلم ولا عن أبي بكر شيئا','1466','1','0');
INSERT INTO [t13092] VALUES (171,'ذكر البيان عما في هذه الأخبار من الفقه وذكر اختلاف العلماء فيه اختلف أهل العلم في الخيل والرقيق، هل فيهما صدقة إذا لم يكونا للتجارة، أم لا صدقة فيهما فقال بعضهم - وهم الأكثرون عددا -: لا صدقة فيهما إذا لم يكونا للتجارة','1489','1','0');
INSERT INTO [t13092] VALUES (172,'ذكر بعض من حضرنا ذكره ممن قال ذلك','1490','1','0');
INSERT INTO [t13092] VALUES (173,'ذكر من قال ذلك قد ذكرنا عن عمر بعض ما روي عنه في ذلك، ونذكر بعد ما لم يمض ذكره عنه في ذلك','1506','1','0');
INSERT INTO [t13092] VALUES (174,'القول في البيان عما في هذه الأخبار من الغريب منه قول النبي صلى الله عليه وسلم: قد عفونا لكم عن الخيل والرقيق، يعني صلى الله عليه وسلم بقوله: قد عفونا لكم، قد تركنا لكم، أن نأخذ الصدقة، مما كان لنا أخذها منه لو أخذناها، فتجاوزنا لكم عنه، على أنها','1512','1','0');
