INSERT INTO [t13104] VALUES (1,'نعد هذا نفاقا','1','0','0');
INSERT INTO [t13104] VALUES (2,'لا يزال هذا الأمر في قريش ما بقي من الناس اثنان','2','0','0');
INSERT INTO [t13104] VALUES (3,'من شرب الخمر فاجلدوه , فإن شربها فاجلدوه قال حماد: أحسبه قال في الخامسة , فإن شربها','3','0','0');
INSERT INTO [t13104] VALUES (4,'لا غش بين المسلمين , ليس منا من غشنا','4','0','0');
INSERT INTO [t13104] VALUES (5,'أم عاصم كان يقال لها عاصية فسماها رسول الله صلى الله عليه وسلم جميلة','5','0','0');
INSERT INTO [t13104] VALUES (6,'لا تبيعوا الثمرة حتى يبدو صلاحها نهى البائع والمبتاع , ونهى عن المحاقلة وعن المزابنة ,','6','0','0');
INSERT INTO [t13104] VALUES (7,'من شاء أن يجعلها عمرة , إلا من كان معه الهدي قالوا: يا رسول الله , يروح أحدنا إلى منى','7','0','0');
INSERT INTO [t13104] VALUES (8,'كل مسكر خمر , وكل مسكر حرام , ولا يطعمها أحد في الدنيا فيطعمها في الآخرة , إلا أن يتوب','8','0','0');
INSERT INTO [t13104] VALUES (9,'من أذن سبع سنين محتسبا كتب الله عز وجل له براءة من النار','9','0','0');
INSERT INTO [t13104] VALUES (10,'نعمت السورتان هما: قل هو الله أحد تعدل ثلث القرآن , وقل يا أيها الكافرون تعدل ربع القرآن','10','0','0');
INSERT INTO [t13104] VALUES (11,'شاهد الزور لا تزول قدماه حتى توجب له النار','11','0','0');
INSERT INTO [t13104] VALUES (12,'والطير يوم القيامة تحت العرش ترفع مناقيرها , وتضرب بأذنابها وتطرح ما في بطونها , وليست','12','0','0');
INSERT INTO [t13104] VALUES (13,'لأبني لنفسي بيتا مع رسول الله صلى الله عليه وسلم , يكنني من المطر ويظلني من الشمس , ما','13','0','0');
INSERT INTO [t13104] VALUES (14,'اشتد غضب الله عز وجل على امرأة تدخل في قوم من ليس منهم , يشركهم في أموالهم , ويطلع على','14','0','0');
INSERT INTO [t13104] VALUES (15,'أمر عائشة أن تناوله الخمرة , فقالت: إني حائض , قال: إنها ليست في يدك','15','0','0');
INSERT INTO [t13104] VALUES (16,'كان في يده خاتم من ذهب , فنزعه واصطنع خاتما من فضة , كان يختم به الصحف','16','0','0');
INSERT INTO [t13104] VALUES (17,'يعتكف العشر الأواخر من رمضان','17','0','0');
INSERT INTO [t13104] VALUES (18,'أقل عيب الرجل أن يكثر الجلوس في البيت','18','0','0');
INSERT INTO [t13104] VALUES (19,'لا تذهب الدنيا حتى تنجلي فراتكم عن جزيرة من ذهب , فتقتتلون عليه , فيقتل من كل مائة تسعة','19','0','0');
INSERT INTO [t13104] VALUES (20,'لولا أنه بدين ما تركتكم حتى تدخلوا وتصيبوا منه','20','0','0');
INSERT INTO [t13104] VALUES (21,'شرف الرجل الصلاة في جوف الليل , وعزه استغناؤه عما في أيدي الناس','21','0','0');
INSERT INTO [t13104] VALUES (22,'المؤمن لا يجزع من ذل الدنيا , ولا ينافس في عزها','22','0','0');
INSERT INTO [t13104] VALUES (23,'إذا أصابك مرض فقل: لا إله إلا الله وحده لا شريك له , له الملك , وله الحمد , يحيى ويميت','23','0','0');
INSERT INTO [t13104] VALUES (24,'صلى خلف أبي بكر رضي الله عنه','24','0','0');
INSERT INTO [t13104] VALUES (25,'لا تسموا باسم نبي فكان رجل يسمى هارون فغير اسمه','25','0','0');
INSERT INTO [t13104] VALUES (26,'ما اختلف الناس في شيء إلا كان الحق مع مضر','26','0','0');
INSERT INTO [t13104] VALUES (27,'أن رجلا أعتق شقيصا من مملوك فأجاز النبي صلى الله عليه وسلم عتقه , وغرمه بقية','27','0','0');
INSERT INTO [t13104] VALUES (28,'أمرت أن أقاتل الناس حتى يشهدوا أن لا إله إلا الله , وأن محمدا رسول الله , ويقيموا الصلاة','28','0','0');
INSERT INTO [t13104] VALUES (29,'ما أفطر هذا منذ كذا وكذا , فقال النبي صلى الله عليه وسلم: ما صام هذا وما أفطر قالوا: يا','29','0','0');
INSERT INTO [t13104] VALUES (30,'آخر آية أنزلت على النبي صلى الله عليه وسلم: {لقد جاءكم رسول من أنفسكم} [التوبة:','30','0','0');
INSERT INTO [t13104] VALUES (31,'الخاتم الذي بين كتفي النبي صلى الله عليه وسلم لحمة ناتئة','31','0','0');
INSERT INTO [t13104] VALUES (32,'انظر ما يؤذي الناس فاعزله عن طريقهم - أو عن طريق الناس -','32','0','0');
INSERT INTO [t13104] VALUES (33,'الله لا يعذب العامة بعمل الخاصة , حتى تكون العامة تستطيع أن تغير على الخاصة , فإذا لم','33','0','0');
INSERT INTO [t13104] VALUES (34,'ناوليني الخمرة قالت: إني حائض , قال: إنها ليست في يدك قال: وسألت عبد الرحمن عن ذلك ,','34','0','0');
INSERT INTO [t13104] VALUES (35,'إياكم والملاعن أن يلقي أحدكم أذاه في الطريق , فلا يمر به أحد إلا لعنه , ويقول: من فعل هذا','35','0','0');
INSERT INTO [t13104] VALUES (36,'الحجر الأسود من الجنة , وكان أشد بياضا من الثلج حتى سودته خطايا أهل','36','0','0');
INSERT INTO [t13104] VALUES (37,'كل يعمل على شاكلته} [الإسراء: ٨٤] قال: على نيته','37','0','0');
INSERT INTO [t13104] VALUES (38,'اللهم أمرت بأمور , ونهيت عن أمور , فتركنا كثيرا مما أمرت به , ووقعنا في كثير مما نهيت عنه','38','0','0');
INSERT INTO [t13104] VALUES (39,'فوعزتي لا أنزع عنه توبتي حتى أنزع منه روحي','39','0','0');
INSERT INTO [t13104] VALUES (40,'مثل الإمام ومثل الناس مثل الفسطاط , لا يقوم إلا بعمود , ولا يقوم العمود إلا بأوتاد , ولا','40','0','0');
INSERT INTO [t13104] VALUES (41,'مثل العلماء كمثل النجوم والأعلام التي يقتدي بها الناس , فإذا توارت ترددوا في','41','0','0');
INSERT INTO [t13104] VALUES (42,'أي الناس أعلم؟ قال: الذي يزداد من علم الناس إلى علمه قيل: أي الناس أغنى؟ قال: الذي يرضى','42','0','0');
INSERT INTO [t13104] VALUES (43,'لا تدعوا ركعتي الفجر وإن طرقتكم الخيل','43','0','0');
INSERT INTO [t13104] VALUES (44,'من سلحك هذه القوس؟ قال: الطفيل بن عمرو الدوسي , أقرأته القرآن , فقال رسول الله صلى الله','44','0','0');
INSERT INTO [t13104] VALUES (45,'يصلي العصر بقدر ما تنحر الجزور , ثم يعضيها لغروب الشمس قال: وكان يصلي الجمعة حين تميل','45','0','0');
INSERT INTO [t13104] VALUES (46,'أبردوا بالصلاة فإن شدة الحر من فيح جهنم','46','0','0');
INSERT INTO [t13104] VALUES (47,'الأيدي ثلاثة: فيد الله العليا , ويد المعطي التي تليها , ويد السائل السفلى , فأعط الفضل','47','0','0');
INSERT INTO [t13104] VALUES (48,'اتقوا الله فإنه لن يموت أحدكم حتى يستكمل رزقه، فلا تستبطئوا الرزق واتقوا الله أيها الناس،','48','0','0');
INSERT INTO [t13104] VALUES (49,'شثن الكفين والقدمين','49','0','0');
INSERT INTO [t13104] VALUES (50,'إذا سجد جافى حتى يرى بياض إبطيه , ويسلم عن يمينه حتى يرى بياض خده , وعن يساره حتى يرى','50','0','0');
INSERT INTO [t13104] VALUES (51,'تقتلك الفئة الباغية','51','0','0');
INSERT INTO [t13104] VALUES (52,'من كذب على نبيه , أو على عينيه , أو على والديه , فلا يرح رائحة الجنة','52','0','0');
INSERT INTO [t13104] VALUES (53,'لكل سهو سجدتان بعدما يسلم','53','0','0');
INSERT INTO [t13104] VALUES (54,'تشهدون أن لا إله إلا الله , وأن محمدا عبده ورسوله , وأن الجنة حق , وأن النار حق , وأن','54','0','0');
INSERT INTO [t13104] VALUES (55,'أما ما أثنيتم عليهم ودعوتم لهم فلا','55','0','0');
INSERT INTO [t13104] VALUES (56,'إذا افتتح الصلاة قال: سبحانك اللهم وبحمدك وتبارك اسمك وتعالى جدك ولا إله','56','0','0');
INSERT INTO [t13104] VALUES (57,'كان باب رسول الله صلى الله عليه وسلم إذا استفتح قرع بالأصابع','57','0','0');
INSERT INTO [t13104] VALUES (58,'الله مع الدائن حتى يقضي دينه , ما لم يكن فيما يكره الله عز وجل','58','0','0');
INSERT INTO [t13104] VALUES (59,'إذا ركب مركبا , أو جاء من سفر لم يدخل بيته حتى يصلي ركعتين في المسجد','59','0','0');
INSERT INTO [t13104] VALUES (60,'الكوثر نهر أعطيه النبي صلى الله عليه وسلم في بطنان الجنة قال: وما بطنان الجنة؟ قالت:','60','0','0');
INSERT INTO [t13104] VALUES (61,'رسول الله جاءني من ربي عز وجل خيرني بين أن يدخل نصف أمتي الجنة وبين الشفاعة , فاخترت','61','0','0');
INSERT INTO [t13104] VALUES (62,'الخاصرة عرق الكلية , فإذا تحركت أذت صاحبها , فداووها بالماء المحرق','62','0','0');
INSERT INTO [t13104] VALUES (63,'قم فاشهد , قال: فشهد أربع مرات , فلما كانت الخامسة , قال رسول الله صلى الله عليه وسلم','63','0','0');
INSERT INTO [t13104] VALUES (64,'أصدع فرق النبي صلى الله عليه وسلم من فوق يأفوخه وأسدل له ناصيته','64','0','0');
INSERT INTO [t13104] VALUES (65,'من اتخذ كلبا ليس بكلب قنص ولا ماشية نقص من أجره كل يوم قيراط','65','0','0');
INSERT INTO [t13104] VALUES (66,'فلما أهلك الله تعالى قومه بما أهلكهم به منعه لمكان الحرم وأنه مات فدفن , ودفن معه غصن من','66','0','0');
INSERT INTO [t13104] VALUES (67,'تخرج عنق من النار يوم القيامة لها لسان تنطق به تقول: إني أمرت بثلاثة: بمن جعل مع الله','67','0','0');
INSERT INTO [t13104] VALUES (68,'من أخذ شيئا من مال أخيه المسلم بيمين فاجرة , فليتبوأ بيتا من النار','68','0','0');
INSERT INTO [t13104] VALUES (69,'لا تضطجع هذه , فإن هذه ضجعة يبغضها الله عز وجل','69','0','0');
INSERT INTO [t13104] VALUES (70,'ما بين العبد والشرك والكفر إلا ترك الصلاة','70','0','0');
INSERT INTO [t13104] VALUES (71,'حجي واشترطي وقولي إن محلي حيث حبستني','71','0','0');
INSERT INTO [t13104] VALUES (72,'أعمر بعض أهله في العشر','72','0','0');
INSERT INTO [t13104] VALUES (73,'فأمرني أن أغتسل بماء وسدر','73','0','0');
INSERT INTO [t13104] VALUES (74,'ينام وهو جنب من غير أن يمس ماء','74','0','0');
INSERT INTO [t13104] VALUES (75,'سبحانك اللهم وبحمدك , اغفر لي , إنك أنت التواب','75','0','0');
INSERT INTO [t13104] VALUES (76,'عليكم بهذه الشجرة المباركة زيت الزيتون فتداووا به , فإن فيه منفعة','76','0','0');
INSERT INTO [t13104] VALUES (77,'أما والله لغير هذا خلقتم , أما والله لغير هذا خلقتم , أما والله لولا أن تكون سنة لضربت','77','0','0');
INSERT INTO [t13104] VALUES (78,'السلام عليكم , - وفيهم ناس من الفرس يبيعون الثياب - فيقول: يا معشر التجار , إياكم والحلف','78','0','0');
INSERT INTO [t13104] VALUES (79,'أتيت بك في سرقة من حرير فقيل لي: هذه امرأتك؟ فكشفت عن وجهك , فقلت: إن يك هذا من عند الله','79','0','0');
INSERT INTO [t13104] VALUES (80,'عليه ما حمل وعليكم ما حملتم وإن تطيعوه تهتدوا} [النور: ٥٤]','80','0','0');
INSERT INTO [t13104] VALUES (81,'إذا التقي الختانان اغتسل','81','0','0');
INSERT INTO [t13104] VALUES (82,'خط خطوطا وخط منها خطا بعيدا وقال: أتدرون ما هذا؟ هذا مثل ابن آدم ومثل التمني , وذاك الخط','82','0','0');
INSERT INTO [t13104] VALUES (83,'كانت الوليدة لتأخذ بيد النبي صلى الله عليه وسلم فتذهب به حيث شاءت من','83','0','0');
INSERT INTO [t13104] VALUES (84,'جنان الفردوس أربع: ثنتين من ذهب , وما فيهما وحليتهما وآنيتهما , واثنتين من فضة , حليتهما','84','0','0');
INSERT INTO [t13104] VALUES (85,'للعبد في الجنة لخيمة من لؤلؤة مجوفة , طولها ستون ميلا للعبد المؤمن , فيها أهلون يطوف','85','0','0');
INSERT INTO [t13104] VALUES (86,'أحسنوا إلى الماعزة , وامسحوا عنها الرعام , فإنها دابة من دواب الجنة , وما من نبي إلا وقد','86','0','0');
INSERT INTO [t13104] VALUES (87,'من قرأ يس في ليلة غفر له','87','0','0');
INSERT INTO [t13104] VALUES (88,'إذا كان آخر الزمان كثرت الصواعق , حتى يحدث بعضهم بعضا','88','0','0');
INSERT INTO [t13104] VALUES (89,'من اجتنب أربعا دخل الجنة: الدماء , والفروج , والأموال , والأشربة , والنساء أربعا: إذا','89','0','0');
INSERT INTO [t13104] VALUES (90,'من دعا على من ظلمه , فقد انتصر','90','0','0');
INSERT INTO [t13104] VALUES (91,'إذا أراد الله أن يتحف عبده , سلط عليه من يظلمه','91','0','0');
INSERT INTO [t13104] VALUES (92,'دعه يحالفونا يستحلون مالنا قال: قلت له: يا أبا جعفر , قم بنا إليه , قال: ربما ذهبت إلى','92','0','0');
INSERT INTO [t13104] VALUES (93,'إني أختاره على العجز','93','0','0');
INSERT INTO [t13104] VALUES (94,'أيها المتصدق على المسكين ترحمه , ارحم الذي ظلمت','94','0','0');
INSERT INTO [t13104] VALUES (95,'الرجل ليظلمني فأرحمه','95','0','0');
INSERT INTO [t13104] VALUES (96,'كره المصل حدثنا يحيى بن محمد الدقاق , حدثنا سريج , حدثنا أزهر , عن ابن عون , عن محمد ,','96','0','0');
