INSERT INTO [t13105] VALUES (126,'إذا أنت رفقت أصبت أو كدت تصيب , وإذا أنت استعجلت أخطأت أو كدت تخطئ','126','0','0');
INSERT INTO [t13105] VALUES (127,'لما حرمت الخمر مشى أصحاب رسول الله صلى الله عليه وسلم بعضهم إلى بعض , فقالوا: حرمت الخمر','127','0','0');
INSERT INTO [t13105] VALUES (128,'سئل عن الوضوء من لحوم الغنم , فرخص في الوضوء منها. وسئل عن الصلاة في مرابضها , فرخص','128','0','0');
INSERT INTO [t13105] VALUES (129,'كل بني آدم خطاء , وخير الخطائين التوابون','129','0','0');
INSERT INTO [t13105] VALUES (130,'كنا نقول: لا صلاة إلا بقراءة','130','0','0');
INSERT INTO [t13105] VALUES (131,'ثلاثة يؤتون أجرهم مرتين: رجل كانت له جارية فأدبها فأحسن تأديبها ثم أعتقها وتزوجها. قال','131','0','0');
INSERT INTO [t13105] VALUES (132,'تقتلك الفئة الباغية','132','0','0');
INSERT INTO [t13105] VALUES (133,'حفظت من النبي صلى الله عليه وسلم عشر ركعات: ركعتين قبل الظهر , وركعتين بعدها , وركعتين','133','0','0');
INSERT INTO [t13105] VALUES (134,'البركة في أوسط طعامكم , فلا تأكلوا من وسطها','134','0','0');
INSERT INTO [t13105] VALUES (135,'لا يغلق الرهن','135','0','0');
INSERT INTO [t13105] VALUES (136,'إذا توضأ خلل لحيته','136','0','0');
