INSERT INTO [t13113] VALUES (1,'لا يموتن أحدكم إلا وهو يحسن الظن بالله عز وجل','1','0','0');
INSERT INTO [t13113] VALUES (2,'أنا عند ظن عبدي بي؛ فليظن ظان ما شاء','2','0','0');
INSERT INTO [t13113] VALUES (3,'أنا عند ظن عبدي بي، وأنا معه حيث يذكرني','3','0','0');
INSERT INTO [t13113] VALUES (4,'لا يموتن أحدكم إلا وهو يحسن الظن بالله؛ عز وجل؛ فإن قوما قد أرداهم سوء ظنهم بالله , فقال','4','0','0');
INSERT INTO [t13113] VALUES (5,'إن لله عز وجل مائة رحمة، فمنها رحمة بها يتراحم الخلق، وتسعة وتسعون ليوم','5','0','0');
INSERT INTO [t13113] VALUES (6,'حسن الظن بالله من حسن العبادة','6','0','0');
INSERT INTO [t13113] VALUES (7,'قدمت بذنوب كثيرة , محاها عني حسن الظن بالله','7','0','0');
INSERT INTO [t13113] VALUES (8,'عليك بمجالس الذكر، وحسن الظن بمولاك , فكفى بهما خيرا','8','0','0');
INSERT INTO [t13113] VALUES (9,'فلم أر مثل حسن الظن بالله عز وجل شيئا','9','0','0');
INSERT INTO [t13113] VALUES (10,'إن شئتم أنبأتكم ما أول ما يقول الله عز وجل للمؤمنين يوم القيامة، وما أول ما يقولون له إن','10','0','0');
INSERT INTO [t13113] VALUES (11,'ما حسن الظن بالله؟ قال: لا يجمعك والفجار في دار واحدة','11','0','0');
INSERT INTO [t13113] VALUES (12,'أن رجلا دعا بعرفات، فقال: لا تعذبنا بالنار بعد أن أسكنت توحيدك قلوبنا , قال: ثم بكى وقال:','12','0','0');
INSERT INTO [t13113] VALUES (13,'سبقت رحمتي غضبي','13','0','0');
INSERT INTO [t13113] VALUES (14,'اللهم لا تشمت من كان يشرك بك بمن كان لا يشرك بك','14','0','0');
INSERT INTO [t13113] VALUES (15,'إذا تلا: {وأقسموا بالله جهد أيمانهم لا يبعث الله من يموت} [النحل: ٣٨] قال: ونحن نقسم','15','0','0');
INSERT INTO [t13113] VALUES (16,'إن لي في ربي جل وعز أملين: أملا أن لا يعذبني بالنار , فإن عذبني لم يخلدني فيها مع من أشرك','16','0','0');
INSERT INTO [t13113] VALUES (17,'اللهم ارحم قوما لم يزالوا مذ خلقتهم على مثل ما كانت عليه السحرة يوم','17','0','0');
INSERT INTO [t13113] VALUES (18,'لله أرحم بعباده من هذه المرأة بولدها','18','0','0');
INSERT INTO [t13113] VALUES (19,'لو يعلم المؤمن ما عند الله من العقوبة ما طمع بجنته أحد , ولو يعلم الكافر ما عند الله من','19','0','0');
INSERT INTO [t13113] VALUES (20,'لله أرحم بعباده من أم الأفراخ بفراخها، اذهب بهن حتى تضعهن من حيث أخذتهن , قال: فذهب بهن','20','0','0');
INSERT INTO [t13113] VALUES (21,'لله أرحم بعبده يوم يأتيه , - أو يوم يلقاه - من أم واحد فرشت له بأرض قر، ثم قامت فلمست','21','0','0');
INSERT INTO [t13113] VALUES (22,'لو لم تذنبوا لجاء الله بقوم يذنبون فيغفر لهم','22','0','0');
INSERT INTO [t13113] VALUES (23,'لو كنتم لا تذنبون لأتى الله عز وجل بقوم يذنبون حتى يغفر لهم','23','0','0');
INSERT INTO [t13113] VALUES (24,'لو أن العباد لم يذنبوا لخلق الله عز وجل عبادا يذنبون فيغفر لهم , إنه هو الغفور','24','0','0');
INSERT INTO [t13113] VALUES (25,'من يحاسب الخلق يوم القيامة؟ قال: الله عز وجل قال: أفلحت ورب الكعبة , إذا يترك حقه , -','25','0','0');
INSERT INTO [t13113] VALUES (26,'لما علمت أن ربي عز وجل يلي محاسبتي زال عني حزني؛ لأن الكريم إذا حاسب عبده','26','0','0');
INSERT INTO [t13113] VALUES (27,'التوكل حسن الظن','27','0','0');
INSERT INTO [t13113] VALUES (28,'من حسن ظنه بالله عز وجل، ثم لا يخاف الله فهو مخدوع','28','0','0');
INSERT INTO [t13113] VALUES (29,'حدثني بالرخص لعلي ألقى الله عز وجل وأنا حسن الظن به','29','0','0');
INSERT INTO [t13113] VALUES (30,'كانوا يستحبون أن يلقنوا العبد محاسن عمله عند موته لكي يحسن ظنه بربه عز','30','0','0');
INSERT INTO [t13113] VALUES (31,'أن رسول الله صلى الله عليه وسلم دخل على شاب وهو في الموت، فقال: كيف تجدك؟ قال: أرجو الله','31','0','0');
INSERT INTO [t13113] VALUES (32,'ابن آدم، إنك ما دعوتني ورجوتني غفرت لك على ما كان منك ولا أبالي، ولو لقيتني بقراب الأرض','32','0','0');
INSERT INTO [t13113] VALUES (33,'لما قضى الخلق كتب عنده في كتابه، فهو عنده فوق العرش؛ إن رحمتي غلبت','33','0','0');
INSERT INTO [t13113] VALUES (34,'إن لي ربا كثير المعروف , فأنا أرجو أن لا يعدمني بعض معروف ربي أن يرحمني قال ثابت فرحمه','34','0','0');
INSERT INTO [t13113] VALUES (35,'لله أرحم بي من والدتي , فقبض الفتى , قال: فخرج عليه عبد الملك بن مروان، فدخلت القبر مع','35','0','0');
INSERT INTO [t13113] VALUES (36,'فإن الله عز وجل أرحم بي منها، فلما مات أنزلته القبر مع غيري , فذهبت أسوي لبنة فاطلعت في','36','0','0');
INSERT INTO [t13113] VALUES (37,'ما أحب أن حسابي جعل إلى والدي؛ ربي خير لي من والدي','37','0','0');
INSERT INTO [t13113] VALUES (38,'كان شاب به رهق فاحتضر , فقالت له أمه: أي بني، توصي بشيء، قال: نعم , خاتمي لا تسلبينيه؛','38','0','0');
INSERT INTO [t13113] VALUES (39,'خرجت يوما أريد الجبان، فإذا بثلاثة نفر يحملون جنازة ومعهم امرأة , قال: فحملت معهم حتى','39','0','0');
INSERT INTO [t13113] VALUES (40,'فما كراهتي أن أذهب إلى من لا أرى الخير إلا منه','40','0','0');
INSERT INTO [t13113] VALUES (41,'ما أخرج من سلطان ربي إلى غيره , ولا نقلني من حال قط إلى حال إلا كان ما نقلني إليه خيرا','41','0','0');
INSERT INTO [t13113] VALUES (42,'مات سلمة بن عباد بن منصور، قال: فاجتمعنا عند أبيه , قال: وحزن له أبوه حزنا شديدا، فقال له','42','0','0');
INSERT INTO [t13113] VALUES (43,'مات لمضر ابن كانت فيه خلال تكره فحزن عليه حزنا شديدا فقلت: هذا من مثلك كثير تحزن على ولد','43','0','0');
INSERT INTO [t13113] VALUES (44,'ألا أخبركم عن عبدين من بني إسرائيل: أما أحدهما فيرى بنو إسرائيل أنه أفضلهما في الدين','44','0','0');
INSERT INTO [t13113] VALUES (45,'كان رجلان في بني إسرائيل فكان أحدهما به رهق والآخر عابدا فكان لا يزال يقول له: ألا تكف','45','0','0');
INSERT INTO [t13113] VALUES (46,'أن رجلا قال: والله لا يغفر الله لفلان وأن الله قال: من ذا الذي تألى علي أن لا أغفر لفلان','46','0','0');
INSERT INTO [t13113] VALUES (47,'قال رجل فيمن مضى: والله لا يغفر الله لفلان أبدا فأوحى الله إلى نبي في زمانه أن أخبره أني','47','0','0');
INSERT INTO [t13113] VALUES (48,'ابنك قد غفر له بحسن ظنه بربه','48','0','0');
INSERT INTO [t13113] VALUES (49,'ما أحب أن لي الدنيا وما فيها بهذه الآية {يا عبادي الذين أسرفوا على أنفسهم لا تقنطوا من','49','0','0');
INSERT INTO [t13113] VALUES (50,'لم تقنط الناس؟ ثم قرأ {يا عبادي الذين أسرفوا على أنفسهم لا تقنطوا من رحمة الله} [الزمر:','50','0','0');
INSERT INTO [t13113] VALUES (51,'أحب آية في القرآن إلي: {إن الله لا يغفر أن يشرك به ويغفر ما دون ذلك لمن يشاء} [النساء:','51','0','0');
INSERT INTO [t13113] VALUES (52,'من أصاب في الدنيا ذنبا فعوقب به فالله أعدل من أن يثني عقوبته على عبده، ومن أذنب في الدنيا','52','0','0');
INSERT INTO [t13113] VALUES (53,'بتنا ليلة مع رجل من العابدين على الساحل بسيراف، فأخذ في البكاء، فلم يزل يبكي حتى خفنا','53','0','0');
INSERT INTO [t13113] VALUES (54,'وإن كان كل ما عصيت به عظيما، فإنه في سعة رحمتك صغير','54','0','0');
INSERT INTO [t13113] VALUES (55,'سبحانك إلهي، إمهالك المذنبين أطمعني لهم في حسن عفوك عنهم، سبحانك إلهي، لم يزل قلبي يشهد','55','0','0');
INSERT INTO [t13113] VALUES (56,'لا تزال المغفرة تحل بالعبد ما لم يرفع الحجاب، قيل: يا نبي الله، وما الحجاب؟ قال: الشرك','56','0','0');
INSERT INTO [t13113] VALUES (57,'أحسنوا أيها الناس برب العالمين الظن؛ فإن الرب عند ظن عبده به','57','0','0');
INSERT INTO [t13113] VALUES (58,'إذا كان يوم القيامة وفرغ الله من قضاء الخلق يبقى رجلان فيؤمر بهما إلى النار فيلتفت أحدهما','58','0','0');
INSERT INTO [t13113] VALUES (59,'إن رجلين ممن دخل النار اشتد صياحهما فقال الرب: أخرجوهما فأخرجا فقال لهما: لأي شيء اشتد','59','0','0');
INSERT INTO [t13113] VALUES (60,'يؤمر بإخراج رجلين من النار فإذا خرجا ووقفا قال الله لهما: كيف وجدتما مقيلكما وسوء','60','0','0');
INSERT INTO [t13113] VALUES (61,'ينصب أو يوضع للأنبياء عليهم السلام منابر من ذهب فيجلسون عليها ويبقى منبري لا أجلس عليه أو','61','0','0');
INSERT INTO [t13113] VALUES (62,'إنا سنرضيك في أمتك ولا نسوؤك','62','0','0');
INSERT INTO [t13113] VALUES (63,'أوحى الله إلى نبيه محمد عليه السلام أتحب أن أجعل أمر أمتك إليك؟ قال: لا يا رب أنت خير لهم','63','0','0');
INSERT INTO [t13113] VALUES (64,'لو علمتم قدر رحمة الله لاتكلتم وما عملتم من عمل ولو علمتم قدر غضبه ما نفعكم','64','0','0');
INSERT INTO [t13113] VALUES (65,'لو يعلم العبد قدر عفو الله ما تورع من حرام ولو يعلم قدر عقوبته لبخع','65','0','0');
INSERT INTO [t13113] VALUES (66,'ليغفرن الله عز وجل يوم القيامة مغفرة لم تخطر على قلب بشر','66','0','0');
INSERT INTO [t13113] VALUES (67,'ما رأيت أحدا كان أعظم رجاء للموحدين من محمد بن سيرين رحمه الله؛ كان يتلو هؤلاء الآيات','67','0','0');
INSERT INTO [t13113] VALUES (68,'من أحسن منكم فليحمد الله، ومن أساء فليستغفر الله، ثم إن عاد فليستغفر الله، ثم إن عاد','68','0','0');
INSERT INTO [t13113] VALUES (69,'ما في القرآن آية أوسع من {قل يا عبادي الذين أسرفوا على أنفسهم لا تقنطوا من رحمة الله إن','69','0','0');
INSERT INTO [t13113] VALUES (70,'أكبر آية في القرآن فرجا آية في سورة الغرف: {قل يا عبادي الذين أسرفوا على أنفسهم} [الزمر:','70','0','0');
INSERT INTO [t13113] VALUES (71,'يخرج من النار - قال أبو عمران: أربعة قال ثابت: رجلان - فيعرضون على ربهم فيأمر بهم إلى','71','0','0');
INSERT INTO [t13113] VALUES (72,'سمعت النبي صلى الله عليه وسلم يقرأ هذه الآية {يا عبادي الذين أسرفوا} [الزمر: ٥٣] على','72','0','0');
INSERT INTO [t13113] VALUES (73,'لا يخرج المؤمن من إيمانه ذنب كما لا يخرج الكافر من كفره إحسان','73','0','0');
INSERT INTO [t13113] VALUES (74,'أهل الجنة عشرون ومائة صف ثمانون منها أمتي','74','0','0');
INSERT INTO [t13113] VALUES (75,'فمن آمن بي وأقر بذنبه غفرت له','75','0','0');
INSERT INTO [t13113] VALUES (76,'لما صبر إسحاق نفسه للذبح أعطي دعوة فدعا لمن قال: لا إله إلا الله أن يدخله الله عز وجل','76','0','0');
INSERT INTO [t13113] VALUES (77,'ليس على أهل لا إله إلا الله وحشة في قبورهم ولا منشرهم وكأني بأهل لا إله إلا الله ينفضون','77','0','0');
INSERT INTO [t13113] VALUES (78,'من أسوأ هذا الجمع حالا؟ قال: الذي يظن أن الله عز وجل لا يغفر لهم','78','0','0');
INSERT INTO [t13113] VALUES (79,'لا تأس على ما فاتك واعلم أنك لو رزقت شيئا لأتاك، ثم قال لي: أبشر فإنك على خير تدري من دعا','79','0','0');
INSERT INTO [t13113] VALUES (80,'إن لآدم عليه السلام من الله موقف في فسح من العرش عليه ثوبان أخضران كأنه نخلة سحوق ينظر','80','0','0');
INSERT INTO [t13113] VALUES (81,'لو أدخلني الله النار فصرت فيها ما أيسته','81','0','0');
INSERT INTO [t13113] VALUES (82,'ما تقولون في رجل يقتل في سبيل الله؟ قالوا: الجنة، قال رسول الله صلى الله عليه وسلم','82','0','0');
INSERT INTO [t13113] VALUES (83,'ما أعطي عبد مؤمن شيئا خيرا من حسن الظن بالله عز وجل والذي لا إله غيره لا يحسن عبد بالله','83','0','0');
INSERT INTO [t13113] VALUES (84,'أنا عند ظن عبدي بي فإن ظن بي خيرا فله الخير فلا تظنوا بالله إلا خيرا','84','0','0');
INSERT INTO [t13113] VALUES (85,'لا تذهب الدنيا حتى يقوم البكاؤون باك يبكي على دينه، وباك يبكي على دنياه فأحسنهم حالا','85','0','0');
INSERT INTO [t13113] VALUES (86,'ما أدري ما أقول لكم إلا أنكم ستعاينون غدا من عفو الله ما لم يكن لكم في حساب قال: ثم ما','86','0','0');
INSERT INTO [t13113] VALUES (87,'إني لأرجو أن ترى من عفو الله عز وجل يوم القيامة ما تخرق له كساءك هذا من','87','0','0');
INSERT INTO [t13113] VALUES (88,'إني قد خلقت ابن آدم لثلاث؛ أخرج منه ذرية يعبدوني وتلا {يخرج الحي من الميت ويخرج الميت من','88','0','0');
INSERT INTO [t13113] VALUES (89,'من أعظم خصلة ترجى للمؤمن أن يكون أشد الناس خوفا على نفسه، وأرجاه لكل','89','0','0');
INSERT INTO [t13113] VALUES (90,'الله عز وجل أوحى إلى بعض أنبيائه: بعيني ما يتحمل المتحملون من أجلي وما يكابدون في طلب','90','0','0');
INSERT INTO [t13113] VALUES (91,'لو كانت المعاصي التي عصيتها طاعة أطعت فيها ما زاد على النعم التي تنيلها، وإنك لتزيد في','91','0','0');
INSERT INTO [t13113] VALUES (92,'من رجا شيئا طلبه، ومن خاف شيئا هرب منه، ما أدري ما حسب رجاء امرئ عرض له بلاء لم يصبر عليه','92','0','0');
INSERT INTO [t13113] VALUES (93,'اللهم ارحم قوما أطاعوك في أحب طاعتك إليك: الإيمان بك والتوكل عليك، وارحم قوما أطاعوك في','93','0','0');
INSERT INTO [t13113] VALUES (94,'ليغفرن الله عز وجل يوم القيامة مغفرة ما خطرت على قلب بشر، والذي نفس محمد بيده ليغفرن الله','94','0','0');
INSERT INTO [t13113] VALUES (95,'إن الذي أدرك منك عدوك بقنوطك من رحمة الله أعظم من ذنبك الذي أذنبت، فقال الرجل: هاه،','95','0','0');
INSERT INTO [t13113] VALUES (96,'خفت نفسي ورجوت ربي فأنا أحب أن أفارق من أخاف إلى من أرجوه','96','0','0');
INSERT INTO [t13113] VALUES (97,'أخرج من بين ظهراني من أخاف فتنته وأقدم علي من لا أشك في رحمته. وأخبرني عبد الله قال: قيل','97','0','0');
INSERT INTO [t13113] VALUES (98,'وإني لأرجو الله حتى كأنني ... أرى بجميل الظن ما الله صانع','98','0','0');
INSERT INTO [t13113] VALUES (99,'ما رأيت أحدا كان أعظم رجاء لهذه الأمة من محمد بن سيرين وأشد خوفا على نفسه','99','0','0');
INSERT INTO [t13113] VALUES (100,'صلى محمد بن المنكدر على رجل من أهل المدينة كان يؤبن بشر وقال: إني لأستحيي من الله عز وجل','100','0','0');
INSERT INTO [t13113] VALUES (101,'وقف الحسن رحمه الله على قبر وكيع بن أبي الأسود، فقال: اللهم ارحم وكيعا؛ فإن رحمتك لن تعجز','101','0','0');
INSERT INTO [t13113] VALUES (102,'لله أحب إلي من عيني هاتين أتراه معذبي بعدها','102','0','0');
INSERT INTO [t13113] VALUES (103,'يا أبا فراس ما أعددت لهذا؟ قال: شهادة ألا إله إلا الله منذ ثمانين سنة فقال: اثبت عليها','103','0','0');
INSERT INTO [t13113] VALUES (104,'نفعتني الكلمة التي راجعت بها الحسن عند القبر','104','0','0');
INSERT INTO [t13113] VALUES (105,'مهما صنعت فلا تقنطن','105','0','0');
INSERT INTO [t13113] VALUES (106,'إن من وراء ابنك ثلاث خلال أما أولها فشهادة ألا إله إلا الله، وأما الثانية فشفاعة رسول','106','0','0');
INSERT INTO [t13113] VALUES (107,'بينا رجل مستلق إذ نظر إلى السماء وإلى النجوم فقال: إني لأعلم أن لك ربا وخلاقا اللهم اغفر','107','0','0');
INSERT INTO [t13113] VALUES (108,'كان رجل يعمل السيئات، وإنه خرج إلى البرية فجمع ترابا فاضطجع عليه مستلقيا فقال: يا رب،','108','0','0');
INSERT INTO [t13113] VALUES (109,'بينما رجل خبيث فتذكر يوما إذ قال: اللهم غفرانك اللهم غفرانك فغفر له','109','0','0');
INSERT INTO [t13113] VALUES (110,'إن عبدا في جهنم ينادي ألف سنة: يا حنان يا منان فيقول الله عز وجل لجبريل عليه السلام: اذهب','110','0','0');
INSERT INTO [t13113] VALUES (111,'اللهم فأقل العثرة وعاف من الزلة وجد بحلمك على جهل من لم يرج غيرك، ولم يثق إلا بك؛ فإنك','111','0','0');
INSERT INTO [t13113] VALUES (112,'إن تناقش يكن نقاشك يا رب، عذابا لا طوق لي بالعذاب، أو تجاوز فأنت رب رحيم عن مسيء ذنوبه','112','0','0');
INSERT INTO [t13113] VALUES (113,'اللهم إن ذنوبي عظمت فجلت عن الصفة، وإنها صغيرة في جنب عفوك فاعف عني','113','0','0');
INSERT INTO [t13113] VALUES (114,'وإني لآتي الذنب أعرف قدره ... وأعلم أن الله يعفو ويغفر','114','0','0');
INSERT INTO [t13113] VALUES (115,'كان عمر بن عبد العزيز رحمه الله يبغض الحجاج فنفس عليه بكلمة قالها عند الموت: اللهم اغفر','115','0','0');
INSERT INTO [t13113] VALUES (116,'إذا كان يوم القيامة دخل أهل الجنة الجنة وأهل النار النار وبقي الذين عليهم الحساب نادى','116','0','0');
INSERT INTO [t13113] VALUES (117,'يجيء المؤمن يوم القيامة قد أخذ صاحب الدين فيقول: ديني على هذا فيقول الله عز وجل: أنا أحق','117','0','0');
INSERT INTO [t13113] VALUES (118,'فاتقوا الله وأصلحوا ذات بينكم؛ فإن الله عز وجل يصلح بين المؤمنين يوم','118','0','0');
INSERT INTO [t13113] VALUES (119,'عود لسانك: اللهم اغفر لي؛ فإن لله ساعات لا يرد فيهن سائلا','119','0','0');
INSERT INTO [t13113] VALUES (120,'التقى رجلان في السوق فقال أحدهما للآخر: يا أخي، تعال حتى تدعو الله في غفلة الناس ففعلا،','120','0','0');
INSERT INTO [t13113] VALUES (121,'ما منتهى الخوف؟ قال: إجلال الله عن مقام السوءات، قال: قلت: فما منتهى الرجاء؟ قال: تأميل','121','0','0');
INSERT INTO [t13113] VALUES (122,'لو لم تذنبوا لجاء الله عز وجل بقوم يذنبون، ثم يغفر لهم','122','0','0');
INSERT INTO [t13113] VALUES (123,'إنما جعلت الرحمة للذنوب','123','0','0');
INSERT INTO [t13113] VALUES (124,'إن أحق من استغفر له المذنب','124','0','0');
INSERT INTO [t13113] VALUES (125,'جاء حبيب أبو محمد إلى خشبة ابن برجان وهو مصلوب فجعل يدعو له ويترحم عليه فقيل له: تدعو','125','0','0');
INSERT INTO [t13113] VALUES (126,'إن الله عز وجل يحب أن يغفر','126','0','0');
INSERT INTO [t13113] VALUES (127,'إني لأرجو ربي وقد صمت له ثمانين رمضان','127','0','0');
INSERT INTO [t13113] VALUES (128,'ما أكره لقاء ربي','128','0','0');
INSERT INTO [t13113] VALUES (129,'إذا فرغ الله عز وجل من القضاء بين خلقه قال: من كان مسلما فليدخل الجنة','129','0','0');
INSERT INTO [t13113] VALUES (130,'كبرت علي خطاي وكثرت حتى لقد آيستني من عظيم عفو الله، ثم قال: وأنى آيس منك وأنت الذي جدت','130','0','0');
INSERT INTO [t13113] VALUES (131,'رأيت أبا عبد الله مسلم بن يسار في منامي بعد موته بسنة فسلمت عليه فلم يرد علي السلام فقلت:','131','0','0');
INSERT INTO [t13113] VALUES (132,'من رجا شيئا طلبه ومن خاف من شيء هرب منه','132','0','0');
INSERT INTO [t13113] VALUES (133,'خف الله خوفا يحول بينك وبين الرجاء، وارجه رجاء يحول بينك وبين الخوف قال: فقال: أي أبه،','133','0','0');
INSERT INTO [t13113] VALUES (134,'لو جيء بميزان تريص فوزن خوف المؤمن ورجاؤه كانا سواء، يذكر رحمة الله فيرجو ويذكر عذاب الله','134','0','0');
INSERT INTO [t13113] VALUES (135,'من أغر الغرة انتظار تمام الأماني، وأنت أيها العبد مقيم على المعاصي. قال: وسمعته يقول: لقد','135','0','0');
INSERT INTO [t13113] VALUES (136,'ابتغ رحمة ربك عند محبته، واحذر نقمته عند معصيته ولا تقطع رجاءك منه في خلال ذلك، ثم ولى','136','0','0');
INSERT INTO [t13113] VALUES (137,'لا يستوي من غدا وراح يعمر عقد الآخرة لنفسه فيقدمها أمامه قبل أن ينزل به الموت حتى يقدم','137','0','0');
INSERT INTO [t13113] VALUES (138,'حسن ظني بحسن عفوك يا ... رب جميل وأنت مالك أمري صنت سري عن القرابة والأهل ... جميعا، وكنت','138','0','0');
INSERT INTO [t13113] VALUES (139,'ما زلت أغرق في الإساءة دائبا ... وتنالني بالعفو والغفران لم تنتقصني إذ أسأت وزدتني ...','139','0','0');
INSERT INTO [t13113] VALUES (140,'إنه ليس له سلطان على الذين آمنوا} [النحل: ٩٩] قال: ليس له سلطان على أن يحملهم على ذنب لا','140','0','0');
INSERT INTO [t13113] VALUES (141,'وأحسنوا إن الله يحب المحسنين} [البقرة: ١٩٥] قال: أحسنوا بالله الظن','141','0','0');
INSERT INTO [t13113] VALUES (142,'إنما سمى نفسه المؤمن؛ لأنه أمنهم من العذاب','142','0','0');
INSERT INTO [t13113] VALUES (143,'وكنتم على شفا حفرة من النار فأنقذكم منها} [آل عمران: ١٠٣] قال: إني لأرجو أن لا يعيدكم','143','0','0');
INSERT INTO [t13113] VALUES (144,'قرأ {وما أرسلناك إلا رحمة للعالمين} [الأنبياء: ١٠٧] فقال: إني لأرجو أن لا يعذبكم الله عز','144','0','0');
INSERT INTO [t13113] VALUES (145,'طوبى لأمتك من قال منهم: لا إله إلا الله وحده لا شريك له','145','0','0');
INSERT INTO [t13113] VALUES (146,'أن شيخا كبيرا أتى النبي صلى الله عليه وسلم وهو يدعم على عصا فقال: يا نبي الله، إن لي','146','0','0');
INSERT INTO [t13113] VALUES (147,'لله عز وجل مائة رحمة، وإنما أنزل منها رحمة واحدة بين الناس والجن والبهائم والهوام فبها','147','0','0');
INSERT INTO [t13113] VALUES (148,'هل جزاء من أنعمت عليه بالإسلام إلا الجنة؟ هل جزاء من قال: لا إله إلا الله إلا','148','0','0');
INSERT INTO [t13113] VALUES (149,'هل جزاء من قال: لا إله إلا الله، إلا الجنة','149','0','0');
INSERT INTO [t13113] VALUES (150,'ثنيا من ربنا على جميع القرآن','150','0','0');
INSERT INTO [t13113] VALUES (151,'ما يسرني بهذه الآية الدنيا وما فيها: قول الله عز وجل: {ما سلككم في سقر} [المدثر: ٤٢]','151','0','0');
INSERT INTO [t13113] VALUES (152,'إن لله عتقاء من النار في كل يوم وليلة، ولكل عبد منهم دعوة مستجابة','152','0','0');
