INSERT INTO [t13131] VALUES (276,'ما جاء في فضل الأضحية سألت محمدا عن حديث أبي المثنى , عن هشام بن عروة , عن أبيه , عن عائشة , عن النبي صلى الله عليه وسلم في الضحايا فقال: هو حديث مرسل. لم يسمع أبو المثنى من هشام بن عروة , قلت له: أبو المثنى ما اسمه؟ قال: سليمان بن يزيد مديني , روى','544','275','0');
