INSERT INTO [t131336] VALUES (3,'أهمية كتاب الخلافيات','7','2','0');
INSERT INTO [t131336] VALUES (4,'ما الذي دفعنا إلى إعادة تحقيق الكتاب؟','9','2','0');
INSERT INTO [t131336] VALUES (5,'منهج البيهقي في الكتاب','11','2','0');
INSERT INTO [t131336] VALUES (6,'وصف النسخ الخطية المعتمدة في تحقيق الكتاب','12','2','0');
INSERT INTO [t131336] VALUES (7,'النسخة (ق)','12','6','0');
INSERT INTO [t131336] VALUES (8,'النسخة (د)','13','6','0');
INSERT INTO [t131336] VALUES (9,'النسخة (س)','14','6','0');
INSERT INTO [t131336] VALUES (10,'النسخة (ع)','14','6','0');
INSERT INTO [t131336] VALUES (11,'النسخة (م)','15','6','0');
INSERT INTO [t131336] VALUES (12,'دراسة حول ما وصلنا من نسخ كتاب الخلافيات','16','2','0');
INSERT INTO [t131336] VALUES (13,'نسخة الشيخ المغربي','16','12','0');
INSERT INTO [t131336] VALUES (14,'البياضات','17','12','0');
INSERT INTO [t131336] VALUES (15,'ظهر الخبر والجزء والمروي والرقاع','17','12','0');
INSERT INTO [t131336] VALUES (16,'نقل وترتيب','17','12','0');
INSERT INTO [t131336] VALUES (17,'إضافات من راوي الكتاب','17','12','0');
INSERT INTO [t131336] VALUES (18,'منهج العمل في تحقيق هذا الكتاب','18','2','0');
INSERT INTO [t131336] VALUES (19,'ترجمة الإمام أبي بكر البيهقي','24','2','0');
INSERT INTO [t131336] VALUES (20,'اسمه ونسبه','24','19','0');
INSERT INTO [t131336] VALUES (21,'مولده ونشأته','24','19','0');
INSERT INTO [t131336] VALUES (22,'رحلته في طلب العلم','26','19','0');
INSERT INTO [t131336] VALUES (23,'مؤلفاته','26','19','0');
INSERT INTO [t131336] VALUES (24,'ثناء العلماء عليه','28','19','0');
INSERT INTO [t131336] VALUES (25,'مذهب البيهقي','29','19','0');
INSERT INTO [t131336] VALUES (26,'وفاته','30','19','0');
INSERT INTO [t131336] VALUES (27,'توثيق صحة نسبة الكتاب إلى البيهقي','31','2','0');
INSERT INTO [t131336] VALUES (28,'رواة الخلافيات','31','2','0');
INSERT INTO [t131336] VALUES (29,'بيان استيعاب النسخ الخطية للكتاب','63','2','0');
INSERT INTO [t131336] VALUES (30,'كتاب الطهارة','66','0','0');
INSERT INTO [t131336] VALUES (31,'مسألة (١): لا تجوز إزالة النجاسات بما سوى الماء من المائعات كالخل واللبن وغيره','67','30','0');
INSERT INTO [t131336] VALUES (32,'مسألة (٢): ولا يجوز الوضوء بنبيذ التمر مطبوخا كان أو نيا','76','30','0');
INSERT INTO [t131336] VALUES (33,'مسألة (٣): وجلد ما لا يؤكل لحمه لا يطهر بالذبح','100','30','0');
INSERT INTO [t131336] VALUES (34,'مسألة (٤): وجلد الكلب لا يطهر بالدباغ','106','30','0');
INSERT INTO [t131336] VALUES (35,'مسألة (٥): وشعر الميتة وصوفها وقرنها وعظمها نجسة','109','30','0');
INSERT INTO [t131336] VALUES (36,'مسألة (٦): ولا يجوز أن تستعمل الآنية المضببة بالفضة تضبيب تزيين لها','120','30','0');
INSERT INTO [t131336] VALUES (37,'مسألة (٧): ولا يجوز الوضوء بغير النية','125','30','0');
INSERT INTO [t131336] VALUES (38,'مسألة (٨): والسنة أن يمسح رأسه ثلاثا','129','30','0');
INSERT INTO [t131336] VALUES (39,'مسألة (٩): الأذنان ليستا من الرأس فيمسحان بماء جديد','139','30','0');
INSERT INTO [t131336] VALUES (40,'مسألة (١٠): وتفريق الوضوء غير جائز في قوله القديم','184','30','0');
INSERT INTO [t131336] VALUES (41,'مسألة (١١): ولا يجوز الوضوء إلا مرتبا','190','30','0');
INSERT INTO [t131336] VALUES (42,'مسألة (١٢): وليس للمحدث مس المصحف','204','30','0');
INSERT INTO [t131336] VALUES (43,'مسألة (١٣): وليس للجنب قراءة القرآن وإن كان أقل من آية','211','30','0');
INSERT INTO [t131336] VALUES (44,'مسألة (١٤): ومن كان في صحراء فأراد أن يقضي حاجته، فلا يجوز له أن يستقبل القبلة ولا أن يستدبرها، وذلك في البناء جائز','220','30','0');
INSERT INTO [t131336] VALUES (45,'ذكر أخبار وردت في تخصيص البناء بالجواز','227','44','0');
INSERT INTO [t131336] VALUES (46,'مسألة (١٥): والاستنجاء واجب لا يجوز تركه، ولا يقع العفو عنه، وإن كانت النجاسة يسيرة','232','30','0');
INSERT INTO [t131336] VALUES (47,'مسألة (١٦): ولا عفو عن قدر الدرهم من النجاسة','242','30','0');
INSERT INTO [t131336] VALUES (48,'مسألة (١٧): وخروج الريح من القبل ينقض الوضوء','246','30','0');
INSERT INTO [t131336] VALUES (49,'مسألة (١٨): ومن استجمع نوم القلب والعين فعليه الوضوء، سواء كان قائما، أو راكعا، أو ساجدا','248','30','0');
INSERT INTO [t131336] VALUES (50,'مسألة (١٩): وملامسة الرجل مع المرأة يوجب الوضوء','263','30','0');
INSERT INTO [t131336] VALUES (51,'مسألة (٢٠): ومس الفرج ببطن الكف ينقض الوضوء','295','30','0');
INSERT INTO [t131336] VALUES (52,'مسألة (٢١): والقيء والرعاف والدم الخارج من غير مخرج الحدث لا ينقض الوضوء','342','30','0');
INSERT INTO [t131336] VALUES (53,'مسألة (٢٢): والقهقهة لا تنقض الوضوء، سواء كان في الصلاة أو خارج الصلاة','371','30','0');
INSERT INTO [t131336] VALUES (54,'مسألة (٢٣): وخروج المني يوجب الاغتسال، سواء خرج دفقا أو خرج سيلا لضعف في البدن','411','30','0');
INSERT INTO [t131336] VALUES (55,'مسألة (٢٤): وإذا توضأ الجنب قبل اغتساله فمن سنته أن يكمل وضوءه قبل اغتساله','413','30','0');
INSERT INTO [t131336] VALUES (56,'مسألة (٢٥): والمضمضة والاستنشاق سنتان في الاغتسال','416','30','0');
INSERT INTO [t131336] VALUES (57,'مسألة (٢٦): ورؤية الماء في الصلاة لا تبطل التيمم، ولا تمنع من إتمام الصلاة به','425','30','0');
INSERT INTO [t131336] VALUES (58,'مسألة (٢٧): ولا يجوز صلاتا فرض بتيمم واحد','429','30','0');
INSERT INTO [t131336] VALUES (59,'مسألة (٢٨): والتيمم عندنا لا يجوز بما لا يعلق باليد منه غبار','432','30','0');
INSERT INTO [t131336] VALUES (60,'مسألة (٢٩): ولا يجوز التيمم بالزرنيخ والنورة','436','30','0');
INSERT INTO [t131336] VALUES (61,'مسألة (٣٠): ولا يجوز التيمم إلا بعد دخول وقت الصلاة','437','30','0');
INSERT INTO [t131336] VALUES (62,'مسألة (٣١): ولا يتيمم لشدة البرد وخوف المرض من استعمال الماء في المصر','439','30','0');
INSERT INTO [t131336] VALUES (63,'مسألة (٣٢): والمريض الذي لا يخاف التلف باستعمال الماء لا يتيمم','443','30','0');
INSERT INTO [t131336] VALUES (64,'مسألة (٣٣): إذا كان بعض أعضائه جريحا غسل ما قدر عليه وتيمم للباقي','446','30','0');
INSERT INTO [t131336] VALUES (65,'مسألة (٣٤): وفي المسح على الجبائر قولان','450','30','0');
INSERT INTO [t131336] VALUES (66,'مسألة (٣٥): ولا يتيمم صحيح في المصر في حال وجود الماء لصلاة جنازة ولا غيرها، وإن خاف فوتها','456','30','0');
INSERT INTO [t131336] VALUES (67,'مسألة (٣٦): وتعجيل الصلاة في أول الوقت بالتيمم أفضل - في أحد القولين - من تأخيرها إلى آخر الوقت رجاء وجود الماء','461','30','0');
INSERT INTO [t131336] VALUES (68,'مسألة (٣٧): وفي الماء المستعمل قولان','464','30','0');
INSERT INTO [t131336] VALUES (69,'مسألة (٣٨): ويغسل الإناء من ولوغ الكلب سبع مرات إحداهن بالتراب، ولا يطهر بدون ذلك','474','30','0');
INSERT INTO [t131336] VALUES (70,'مسألة (٣٩): وأسآر السباع كلها طاهرة سوى الكلب والخنزير وما تفرع منهما','482','30','0');
INSERT INTO [t131336] VALUES (71,'مسألة (٤٠): وما ليس له نفس سائلة إذا مات في الماء القليل نجسه في أحد القولين؛ كالذباب والعقرب','493','30','0');
INSERT INTO [t131336] VALUES (72,'مسألة (٤١): وحد الماء الذي لا ينجس جميعه بما يقع فيه ولا يغيره؛ قلتان','496','30','0');
INSERT INTO [t131336] VALUES (73,'مسألة (٤٢): وإذا غسل إحدى رجليه وأدخلها الخف، ثم غسل الأخرى وأدخلها الخف؛ لم يجز أن يمسح عليهما','520','30','0');
INSERT INTO [t131336] VALUES (74,'مسألة (٤٣): والسنة أن يمسح أعلى الخف وأسفله','522','30','0');
INSERT INTO [t131336] VALUES (75,'مسألة (٤٤): والغسل من غسل الميت سنة مؤكدة','526','30','0');
INSERT INTO [t131336] VALUES (76,'مسألة (٤٥): والتمييز مقدم على العادة في أحد القولين','529','30','0');
INSERT INTO [t131336] VALUES (77,'مسألة (٤٦): وإذا استحيضت المبتدأة ولم تكن مميزة، كان حيضها قدر أقل الحيض في أحد القولين، وقدر غالب حيض نسائها في القول الثاني','533','30','0');
INSERT INTO [t131336] VALUES (78,'مسألة (٤٧): وأقل مدة الحيض يوم وليلة','536','30','0');
INSERT INTO [t131336] VALUES (79,'مسألة (٤٨): وأكثر الحيض خمسة عشر يوما','538','30','0');
INSERT INTO [t131336] VALUES (80,'مسألة (٤٩): وأكثر النفاس ستون يوما','553','30','0');
INSERT INTO [t131336] VALUES (81,'مسألة (٥٠): والمستحاضة تتوضأ لكل صلاة فريضة','566','30','0');
INSERT INTO [t131336] VALUES (82,'كتاب الصلاة','574','0','0');
INSERT INTO [t131336] VALUES (83,'مسألة (٥١): وقت العصر يدخل بمصير ظل كل شيء مثله','575','82','0');
INSERT INTO [t131336] VALUES (84,'مسألة (٥٢): ولصلاة المغرب وقت واحد','584','82','0');
INSERT INTO [t131336] VALUES (85,'مسألة (٥٣): والشفق الذي يدخل بغروبه وقت العشاء هو الحمرة','597','82','0');
INSERT INTO [t131336] VALUES (86,'مسألة (٥٤): وآخر وقت الاختيار في صلاة العشاء لا يتجاوز ثلث الليل في أحد القولين','603','82','0');
INSERT INTO [t131336] VALUES (87,'مسألة (٥٥): والأذان لصلاة الصبح صحيح قبل الفجر','612','82','0');
INSERT INTO [t131336] VALUES (88,'مسألة (٥٦): ودرك قدر التحريمة من الوقت لا يلزم صلاة الوقت على أحد القولين','631','82','0');
INSERT INTO [t131336] VALUES (89,'مسألة (٥٧): ودرك وقت العصر إذا أوجب العصر أوجب معه الظهر','633','82','0');
INSERT INTO [t131336] VALUES (90,'مسألة (٥٨): والمغمى عليه إذا أفاق بعد مضي وقت الضلاة فلا يلزمه قضاء تلك الصلاة','636','82','0');
INSERT INTO [t131336] VALUES (91,'مسألة (٥٩): والترجيع سنة في الأذان','640','82','0');
INSERT INTO [t131336] VALUES (92,'مسألة (٦٠): ويلتوي في: "حي على الصلاة، حي على الفلاح"، ولا يدور في حجرة المنارة','645','82','0');
INSERT INTO [t131336] VALUES (93,'مسألة (٦١): وما فات وقتها من الصلاة أقام لها ولم يؤذن في الصحيح من مذهبه، وله فيه قولان آخران','649','82','0');
INSERT INTO [t131336] VALUES (94,'مسألة (٦٢): قال الإمام أبو الطيب سهل بن محمد بن سليمان - رحمه الله - في الجماعة إذا دخلوا مسجدا قد صلى فيه أهله بالجماعة مرة بالأذان والإقامة: لهم أن يصلوا فيه جماعة تلك الصلاة بالأذان والإقامة','669','82','0');
INSERT INTO [t131336] VALUES (95,'مسألة (٦٣): ويكره أن يؤذن واحد ويقيم آخر','671','82','0');
INSERT INTO [t131336] VALUES (96,'مسألة (٦٤): ومن أذن قاعدا لم يحتسب بأذانه؛ كذا قال الإمام أبو الطيب سهل بن محمد بن سليمان - رحمه الله -','673','82','0');
INSERT INTO [t131336] VALUES (97,'مسألة (٦٥): والإقامة فرادى','676','82','0');
INSERT INTO [t131336] VALUES (98,'حديث عبد الله بن عمر بن الخطاب - رضي الله عنه - في ذلك','717','97','0');
INSERT INTO [t131336] VALUES (99,'حديث أبي جحيفة وهب [بن عبد] الله السوائي - رضي الله عنه -','719','97','0');
INSERT INTO [t131336] VALUES (100,'مسألة (٦٦): وكلمة التثويب قول المؤذن: "الصلاة خير من النوم" مرتين','729','82','0');
INSERT INTO [t131336] VALUES (101,'مسألة (٦٧): وموضع التثويب قبل الفراغ من الأذان','733','82','0');
INSERT INTO [t131336] VALUES (102,'مسألة (٦٨): والتعجيل بالصلوات كلها أفضل إذا لم يكن هناك عذر تتأخر بها الصلاة','737','82','0');
INSERT INTO [t131336] VALUES (103,'مسألة (٦٩): والوتر سنة','777','82','0');
INSERT INTO [t131336] VALUES (104,'مسألة (٧٠): والفرض على كل مصل إصابة عين القبلة','795','82','0');
INSERT INTO [t131336] VALUES (105,'مسألة (٧١): ومن اجتهد فصلى إلى الشرق ثم تيقن أن القبلة إلى الغرب كان عليه إعادة ما صلاه في أصح القولين','800','82','0');
INSERT INTO [t131336] VALUES (106,'مسألة (٧٢): والمراهق إذا افتتح صلاة الوقت بشروط صحتها من طهارة النعل والبدن واللباس، والعقد بالنية، ثم بلغ في خلالها فإنه يتم ما عقده ولا إعادة عليه، وكذلك إن تحلل من هذه الصلاة التي عقدها على استكمال شرائطها ثم بلغ والوقت باق فلا إعادة عليه','808','82','0');
INSERT INTO [t131336] VALUES (107,'مسألة (٧٣): ولا تنعقد الصلاة إلا بقوله: الله أكبر أو الله الأكبر','810','82','0');
INSERT INTO [t131336] VALUES (108,'مسألة (٧٤): وإذا افتتح الصلاة رفع يديه حتى يحاذي كفاه منكبيه','816','82','0');
INSERT INTO [t131336] VALUES (109,'مسألة (٧٥): والسنة أن يضع اليمنى على اليسرى تحت صدره وفوق سرته','820','82','0');
INSERT INTO [t131336] VALUES (110,'مسألة (٧٦): والمختار أن يستفتح بقوله: وجهت وجهي','824','82','0');
INSERT INTO [t131336] VALUES (111,'مسألة (٧٧): بسم الله الرحمن الرحيم آية من كل سورة خاصة من الفاتحة سوى سورة (براءة)، ويجهر بها عند الجهر بالفاتحة','831','82','0');
INSERT INTO [t131336] VALUES (112,'مسألة (٧٨): ويجهر الإمام بالتأمين فيما يجهر بالقراءة فيه','883','82','0');
INSERT INTO [t131336] VALUES (113,'مسألة (٧٩): ورفع اليدين سنة عند الركوع والارتفاع منه','896','82','0');
INSERT INTO [t131336] VALUES (114,'مسألة (٨٠): ومن رفع رأسه من الركوع قال: سمع الله لمن حمده ربنا لك الحمد','956','82','0');
INSERT INTO [t131336] VALUES (115,'مسألة (٨١): وجلسة الاستراحة بعد السجدة الثانية من الركعة الأولى والثالثة سنة، وإذا قام اعتمد بيديه على الأرض','962','82','0');
INSERT INTO [t131336] VALUES (116,'مسألة (٨٢): ويشير بالمسبحة في التشهد','966','82','0');
INSERT INTO [t131336] VALUES (117,'مسألة (٨٣): والقعود في التشهد الأخير يكون بالتورك','972','82','0');
INSERT INTO [t131336] VALUES (118,'مسألة (٨٤): وقراءة السورة سنة في الأخريين في أحد القولين','975','82','0');
INSERT INTO [t131336] VALUES (119,'مسألة (٨٥): والقراءة خلف الإمام فرض','978','82','0');
INSERT INTO [t131336] VALUES (120,'مسألة (٨٦): ويقنت في صلاة الصبح بعد الركوع من الركعة الثانية','1064','82','0');
INSERT INTO [t131336] VALUES (121,'مسألة (٨٧): والمختار من التشهد قوله: التحيات المباركات الصلوات الطيبات لله','1079','82','0');
INSERT INTO [t131336] VALUES (122,'مسألة لم يذكرها الإمام (٨٨): وما جاز للمرء أن يدعو به في غير الصلاة جاز له أن يدعو به في الصلاة','1084','82','0');
INSERT INTO [t131336] VALUES (123,'مسألة (٨٩): ومن ذكر صلاة وهو في فريضة الوقت أتمها، ثم قضى ما تذكر، سواء كان الوقت متسعا أو مضيقا','1088','82','0');
INSERT INTO [t131336] VALUES (124,'مسألة (٩٠): وللمصلي أن يسبح في دفع المار بين يديه','1096','82','0');
INSERT INTO [t131336] VALUES (125,'مسألة (٩١): والسرة عندنا ليست بعورة','1099','82','0');
INSERT INTO [t131336] VALUES (126,'مسألة (٩٢): وإذا سلم على المصلي فإنه يرد بالإشارة، ولا يتكلم','1102','82','0');
INSERT INTO [t131336] VALUES (127,'مسألة (٩٣): وإذا انكشف من عورة الرجل - وهي ما بين السرة والركبتين - والمرأة الحرة - وهي جميع بدنها غير الوجه والكفين - شيء وإن قل، لم تجزئهما صلاتهما','1104','82','0');
INSERT INTO [t131336] VALUES (128,'مسألة (٩٤): وعورة الحرة جميع بدنها غير الوجه والكفين','1109','82','0');
INSERT INTO [t131336] VALUES (129,'مسألة (٩٥): وكلام المخطئ والناسي والجاهل بتحريمه في الصلاة لا يقطع الصلاة','1112','82','0');
INSERT INTO [t131336] VALUES (130,'مسألة (٩٦): إذا نفخ في الصلاة ولم يبن من كلامه حرفان لم تبطل صلاته','1122','82','0');
INSERT INTO [t131336] VALUES (131,'مسألة (٩٧): ومن سبقه الحدث في صلاته استأنف صلاته في الصحيح من المذهب','1125','82','0');
INSERT INTO [t131336] VALUES (132,'مسألة (٩٨): وما أدركه المسبوق فهو أول صلاته','1128','82','0');
INSERT INTO [t131336] VALUES (133,'مسألة (٩٩): ومن صلى فريضة من الفرائض الخمس منفردا، ثم أدرك فيها جماعة، فإنه يصليها في الجماعة','1132','82','0');
INSERT INTO [t131336] VALUES (134,'مسألة (١٠٠): ومن لم يقدر أن يصلي قاعدا، وقدر أن يصلي مستقبلا مستلقيا على قفاه وعلى الجنب صلى على جنبه','1141','82','0');
INSERT INTO [t131336] VALUES (135,'مسألة (١٠١): وأحب إذا قرأ آية رحمة أن يسأل والناس، أو آية عذاب أن يستعيذ والناس','1144','82','0');
INSERT INTO [t131336] VALUES (136,'مسألة (١٠٢): وسجدة التلاوة غير واجبة','1148','82','0');
INSERT INTO [t131336] VALUES (137,'مسألة (١٠٣): وإذا لم يسجد التالي لآية السجدة فلا يسجد لها السامع في أصح الوجهين','1154','82','0');
INSERT INTO [t131336] VALUES (138,'مسألة (١٠٤): وفي (الحج) سجدتان','1159','82','0');
INSERT INTO [t131336] VALUES (139,'مسألة (١٠٥): وسجدة (ص) سجدة شكر، وليست من سجود التلاوة','1163','82','0');
INSERT INTO [t131336] VALUES (140,'مسألة (١٠٦): السجدة إذا كانت في آخر السورة، وكان في الصلاة، فالأولى أن يسجد ثم يقوم فيقرأ سورة أخرى','1167','82','0');
INSERT INTO [t131336] VALUES (141,'مسألة (١٠٧): ويسجد للتلاوة في صلاة يسر فيها بالقراءة، كما يسجد فيما يجهر فيها بالقراءة','1169','82','0');
INSERT INTO [t131336] VALUES (142,'مسألة (١٠٨): ولا تجوز الصلاة على ظهر الكعبة، إذا لم يكن بين يدي المصلي شيء من بناء الكعبة','1170','82','0');
INSERT INTO [t131336] VALUES (143,'مسألة (١٠٩): وعلى المرتد قضاء ما ترك من الصلوات أيام ردته إذا عاد إلى الإسلام','1171','82','0');
INSERT INTO [t131336] VALUES (144,'مسألة (١١٠): إذا أخذ المصحف في صلاته فقرأ منه، فإن كان يتصفح الأوراق متواليا، وزاد على ثلاثة أوراق؛ بطلت صلاته، وإن كان يتصفح ورقة ولم يوال ذلك، أو والى ولكن كان ثلاثة فما دونها؛ لم تبطل صلاته، وكذلك إذا كان مكتوبا على حائط فقرأه في الصلاة؛ لم تبطل صلاته، وإن كان غير قرآن فقرأه في نفسه ولم يتلفظ به؛ لم تبطل صلاته','1173','82','0');
INSERT INTO [t131336] VALUES (145,'مسألة (١١١): من شك في صلاته فلم يدر أثلاثا صلى أم أربعا؟ لم تفسد به صلاته، وإن كان ذلك أول مرة','1174','82','0');
INSERT INTO [t131336] VALUES (146,'مسألة (١١٢): ويبني على ما استيقن إذا وقع له هذا الشك، سواء كان في المرة الأولى والثانية فصاعدا','1176','82','0');
INSERT INTO [t131336] VALUES (147,'مسألة (١١٣): وسجود السهو قبل السلام','1185','82','0');
INSERT INTO [t131336] VALUES (148,'مسألة (١١٤): وإن ذكر أنه في الخامسة - سجد أو لم يسجد، قعد في الرابعة أو لم يقعد - فإنه يجلس للرابعة ويتشهد ويسجد للسهو ثم يسلم','1192','82','0');
INSERT INTO [t131336] VALUES (149,'مسألة (١١٥): ومن أسر فيما يجهر أو جهر فيما يسر فلا سجود عليه','1194','82','0');
INSERT INTO [t131336] VALUES (150,'مسألة (١١٦): وسجود السهو غير واجب','1197','82','0');
INSERT INTO [t131336] VALUES (151,'مسألة (١١٧): وسجود الشكر عند حادث النعمة سنة مؤكدة','1198','82','0');
INSERT INTO [t131336] VALUES (152,'مسألة (١١٨): وتحريمة الصلاة من الصلاة','1209','82','0');
INSERT INTO [t131336] VALUES (153,'مسألة (١١٩): والقيام بقدر فرض القراءة فرض في الصلاة مع القدرة عليه على عموم الأحوال','1211','82','0');
INSERT INTO [t131336] VALUES (154,'مسألة (١٢٠): ولا تصح صلاة من لم يقرأ بفاتحة الكتاب في كل ركعة منها إذا أحسنها','1214','82','0');
INSERT INTO [t131336] VALUES (155,'مسألة (١٢١): والطمأنينة في الركوع فرض، وكذلك الاعتدال من الركوع - حتى يطمئن قائما - فرض، والقعدة بين السجدتين فريضة','1222','82','0');
INSERT INTO [t131336] VALUES (156,'مسألة (١٢٢): ولا يجوز السجود على كور العمامة','1228','82','0');
INSERT INTO [t131336] VALUES (157,'مسألة (١٢٣): ووضع اليدين والركبتين فرض في السجود كوضع الجبهة، على أحد القولين','1238','82','0');
INSERT INTO [t131336] VALUES (158,'مسألة (١٢٤): والقراءة في الركعتين الآخرتين فريضة بمثابتها في الأولتين','1241','82','0');
INSERT INTO [t131336] VALUES (159,'مسألة (١٢٥): وقراءة التشهد الأخير في الصلاة واجب','1245','82','0');
INSERT INTO [t131336] VALUES (160,'مسألة (١٢٦): والصلاة على النبي - صلى الله عليه وسلم - فريضة في التشهد الأخير، وركن من أركان الصلاة','1254','82','0');
INSERT INTO [t131336] VALUES (161,'مسألة (١٢٧): والخروج من الصلاة بالسلام واجب على من أحسنه','1263','82','0');
INSERT INTO [t131336] VALUES (162,'مسألة (١٢٨): من لم يحسن قراءة الفاتحة ولا غيرها من القرآن فإنه يذكر الله تعالى','1289','82','0');
INSERT INTO [t131336] VALUES (163,'مسألة (١٢٩): وترجمة القرآن عندنا ليست بقرآن تجزئ بها الصلاة','1291','82','0');
INSERT INTO [t131336] VALUES (164,'مسألة (١٣٠): وإذا صلى الجنب بقوم ولم يعلموا بجنابته, ثم أخبرهم بها؛ لم تلزمهم الإعادة','1294','82','0');
INSERT INTO [t131336] VALUES (165,'مسألة (١٣١): وبول ما يؤكل لحمه وما لا يؤكل لحمه سواء في النجاسة ووجوب غسل الثوب والبدن منه','1306','82','0');
INSERT INTO [t131336] VALUES (166,'مسألة لم يذكرها الإمام (١٣٢): ويرش على بول الصبي الذي لم يأكل الطعام, ولا يجب غسله','1312','82','0');
INSERT INTO [t131336] VALUES (167,'مسألة (١٣٣): ومني الإنسان طاهر','1320','82','0');
INSERT INTO [t131336] VALUES (168,'مسألة (١٣٤): ويطهر مكان البول من الأرض بصب ذنوب من الماء عليه، سواء كانت الأرض صلبة أو رملة متحللة','1327','82','0');
INSERT INTO [t131336] VALUES (169,'مسألة (١٣٥): ولا تزول نجاسة الأرض من البول بوقوع الشمس عليه وضرب الرياح له','1332','82','0');
INSERT INTO [t131336] VALUES (170,'مسألة (١٣٦): وللجنب أن يمر في المسجد','1335','82','0');
INSERT INTO [t131336] VALUES (171,'مسألة (١٣٧): ويجوز له أن يصلي في الأوقات التي نهي عن الصلاة فيها صلاة لها سبب، ويحتسب بها؛ كقضاء فائتة، أو صلاة جنازة، أو خسوف، أو تحية المسجد، أو ما أشبه ذلك','1339','82','0');
INSERT INTO [t131336] VALUES (172,'مسألة (١٣٨): والنوافل الراتبة على الفرائض الخمسة مقضية بعد فوتها في ظاهر المذهب','1366','82','0');
INSERT INTO [t131336] VALUES (173,'مسألة (١٣٩): وصلاة الصبح لا تفسد على المصلي بطلوع الشمس عليه فيها','1370','82','0');
INSERT INTO [t131336] VALUES (174,'مسألة (١٤٠): الوتر بركعة واحدة صحيح','1376','82','0');
INSERT INTO [t131336] VALUES (175,'مسألة (١٤١): ويقنت في الوتر من طريق السنة في النصف الأخير من شهر رمضان','1395','82','0');
INSERT INTO [t131336] VALUES (176,'مسألة (١٤٢): وموضع القنوت بعد الارتفاع من الركوع','1398','82','0');
INSERT INTO [t131336] VALUES (177,'مسألة (١٤٣): ويقنت بالدعاء الذي علمه رسول الله - صلى الله عليه وسلم - الحسن بن علي - رضي الله عنه -','1411','82','0');
INSERT INTO [t131336] VALUES (178,'مسألة (١٤٤): وسنن الصلاة مثان','1414','82','0');
INSERT INTO [t131336] VALUES (179,'مسألة (١٤٥): ومن حضر صلاة فريضة ووجدها قد أقيمت دخل فيها ولم يشتغل بسنتها قبلها','1426','82','0');
INSERT INTO [t131336] VALUES (180,'مسألة (١٤٦): وتجوز صلاة الفريضة خلف من يصلي النافلة','1432','82','0');
INSERT INTO [t131336] VALUES (181,'مسألة (١٤٧): إذا أخرج المأموم نفسه من صلاة الإمام فصلى لنفسه جاز لعذر، وإن كان لغير عذر فعلى قولين','1439','82','0');
INSERT INTO [t131336] VALUES (182,'مسألة (١٤٨): إذا صلى في بيته بصلاة الإمام في المسجد وبينهما حائل لم يجز','1440','82','0');
INSERT INTO [t131336] VALUES (183,'مسألة (١٤٩): وإمامة الكافر بالمسلمين لا يكون إسلاما منه','1442','82','0');
INSERT INTO [t131336] VALUES (184,'مسألة (١٥٠): وإذا ابتدأ صلاته منفردا ثم دخل في جماعة صحت صلاته في أحد القولين','1444','82','0');
INSERT INTO [t131336] VALUES (185,'مسألة لم يذكرها الإمام (١٥١): والصبي يجوز أن يكون إماما للبالغ في صلاة الفريضة سوى الجمعة','1446','82','0');
INSERT INTO [t131336] VALUES (186,'مسألة لم يذكرها الإمام (١٥٢): ويستحب للنساء الجماعة، وتقف إمامهن وسطهن','1450','82','0');
INSERT INTO [t131336] VALUES (187,'مسألة (١٥٣): وحد السفر القاصد ستة عشر فرسخا، وهو مسيرة يومين بليلتيهما سير الثقل','1454','82','0');
INSERT INTO [t131336] VALUES (188,'مسألة (١٥٤): والقصر في السفر مباح، وليس بفرض','1461','82','0');
INSERT INTO [t131336] VALUES (189,'مسألة (١٥٥): ومن نوى مقام أربع أتم','1474','82','0');
INSERT INTO [t131336] VALUES (190,'مسألة (١٥٦): وإذا نوى مقام أكثر من سبعة عشر أو ثمانية عشر مع الخوف والحرب صار مقيما على أحد القولين','1482','82','0');
INSERT INTO [t131336] VALUES (191,'مسألة لم يذكرها الإمام (١٥٧): والجمع بين الصلاتين جائز بعذر السفر أو المطر','1489','82','0');
INSERT INTO [t131336] VALUES (192,'كتاب الجمعة','1515','0','0');
INSERT INTO [t131336] VALUES (193,'مسألة (١٥٨): والجمعة تجب على أهل القرية إذا بلغوا أربعين، وكانوا مستوطنين','1516','192','0');
INSERT INTO [t131336] VALUES (194,'مسألة لم يذكرها الإمام (١٥٩): وتجب الجمعة على من كان خارج المصر بسماع النداء','1523','192','0');
INSERT INTO [t131336] VALUES (195,'مسألة (١٦٠): والجمعة لا تنعقد بأقل من أربعين','1528','192','0');
INSERT INTO [t131336] VALUES (196,'مسألة (١٦١): تحية المسجد عندنا جائزة في حال ما يخطب الإمام','1537','192','0');
INSERT INTO [t131336] VALUES (197,'مسألة لم يذكرها الإمام (١٦٢): والكلام لا يحرم ما لم يأخذ الإمام في الخطبة','1542','192','0');
INSERT INTO [t131336] VALUES (198,'مسألة (١٦٣): والقيام فرض في حال الخطبة','1545','192','0');
INSERT INTO [t131336] VALUES (199,'مسألة (١٦٤): والجلسة بين الخطبتين فريضة','1548','192','0');
INSERT INTO [t131336] VALUES (200,'مسألة (١٦٥): ولا تصح الخطبة بكلمة واحدة','1550','192','0');
INSERT INTO [t131336] VALUES (201,'مسألة لم يذكرها الإمام (١٦٦): وهل يحل للخطيب والمستمع أن يتكلم بما يعنيه في حال الخطبة أم لا؟ فيه قولان','1560','192','0');
INSERT INTO [t131336] VALUES (202,'مسألة (١٦٧): والجمعة لا تدرك بأقل من ركعة','1570','192','0');
INSERT INTO [t131336] VALUES (203,'مسألة (١٦٨): والجمعة تصح بغير سلطان ولا أحد من جهته','1580','192','0');
INSERT INTO [t131336] VALUES (204,'كتاب صلاة الخوف','1581','0','0');
INSERT INTO [t131336] VALUES (205,'ذكر صلاة رسول الله - صلى الله عليه وسلم - يوم ذات الرقاع','1582','204','0');
INSERT INTO [t131336] VALUES (206,'ذكر صلاة رسول الله - صلى الله عليه وسلم - بعسفان وغيره','1584','204','0');
INSERT INTO [t131336] VALUES (207,'ذكر صلاة رسول الله - صلى الله عليه وسلم - قد قيل: ببطن النخل، وقد قيل: بذات الرقاع','1587','204','0');
INSERT INTO [t131336] VALUES (208,'حديث عبد الله بن عمر بن الخطاب - رضي الله عنهما -','1589','204','0');
INSERT INTO [t131336] VALUES (209,'حديث عبد الله بن عباس - رضي الله عنهما -','1590','204','0');
INSERT INTO [t131336] VALUES (210,'مسألة (١٦٩): والصلاة جائزة مع شدة القتال والتحام الحرب رجالا وركبانا، مستقبلي القبلة وغير مستقبليها بالإيماء، ولا يجوز تأخيرها عن وقتها لأجل ذلك','1592','204','0');
INSERT INTO [t131336] VALUES (211,'مسألة (١٧٠): لبس الحرير، والنوم عليه، والجلوس، والتدثر به محرم','1596','204','0');
INSERT INTO [t131336] VALUES (212,'كتاب صلاة العيدين','1598','0','0');
INSERT INTO [t131336] VALUES (213,'مسألة (١٧١): التكبير في ليلة الفطر ويوم الفطر حتى يخرج الإمام مسنون','1599','212','0');
INSERT INTO [t131336] VALUES (214,'مسألة (١٧٢): ويصلي النوافل قبل صلاة العيد من سبق الإمام إلى محلها','1603','212','0');
INSERT INTO [t131336] VALUES (215,'مسألة (١٧٣): تكبيرات العيد سبع في الأولى سوى تكبيرة الافتتاح، وخمس في الثانية سوى تكبيرة القيام من السجود','1609','212','0');
INSERT INTO [t131336] VALUES (216,'مسألة (١٧٤): والتكبير في الركعة الثانية قبل القراءة كهو في الركعة الأولى','1618','212','0');
INSERT INTO [t131336] VALUES (217,'مسألة (١٧٥): يقف بين كل تكبيرتين يذكر الله تعالى بقدر آية','1622','212','0');
INSERT INTO [t131336] VALUES (218,'مسألة (١٧٦): ومبتدأ تكبير أيام التشريق بعد صلاة الظهر من يوم النحر إلى صلاة الصبح من آخر أيام التشريق','1623','212','0');
INSERT INTO [t131336] VALUES (219,'مسألة (١٧٧): التكبير عندنا ثلاثا نسقا','1633','212','0');
INSERT INTO [t131336] VALUES (220,'مسألة (١٧٨): التكبير مسنون للمقيم والمسافر، وأهل القرى والأمصار، والمنفرد والمصلي في جماعة','1636','212','0');
INSERT INTO [t131336] VALUES (221,'كتاب صلاة الخسوف','1638','0','0');
INSERT INTO [t131336] VALUES (222,'مسألة (١٧٩): صلاة الخسوف ركعتان، في كل ركعة ركوعان','1639','221','0');
INSERT INTO [t131336] VALUES (223,'مسألة (١٨٠): ويخطب لصلاة الكسوف بعد الصلاة','1653','221','0');
INSERT INTO [t131336] VALUES (224,'مسألة لم يذكرها الإمام (١٨١): ويسر بالقراءة في صلاة خسوف الشمس، ويجهر بها في صلاة كسوف القمر','1657','221','0');
INSERT INTO [t131336] VALUES (225,'مسألة (١٨٢): ويصلي الاستسقاء بالجماعة في المصلى كصلاة العيد في التكبيرات والجهر والخطبتين بعدها','1665','221','0');
INSERT INTO [t131336] VALUES (226,'مسألة (١٨٣): يستحب للإمام والمأموم إذا أراد الدعاء أن يقلب رداءه ويحوله','1671','221','0');
INSERT INTO [t131336] VALUES (227,'مسألة (١٨٤): وتارك الصلاة عمدا من غير عذر يقتل','1673','221','0');
INSERT INTO [t131336] VALUES (228,'كتاب الجنائز','1682','0','0');
INSERT INTO [t131336] VALUES (229,'مسألة (١٨٥): يستر غاسل الميت ما بين سرته إلى ركبته، وإن غسله في قميصه كان أحسن','1683','228','0');
INSERT INTO [t131336] VALUES (230,'مسألة (١٨٦): وإذا مات المحرم لم يخمر رأسه ولم يقرب طيبا','1685','228','0');
INSERT INTO [t131336] VALUES (231,'مسألة (١٨٧): الزوج يغسل امرأته','1690','228','0');
INSERT INTO [t131336] VALUES (232,'مسألة (١٨٨): يمضمض الميت وينشق','1693','228','0');
INSERT INTO [t131336] VALUES (233,'مسألة (١٨٩): يضفر شعر المرأة ثلاثة قرون يلقين خلفها','1694','228','0');
INSERT INTO [t131336] VALUES (234,'مسألة (١٩٠): يستحب أن يجعل في الغسلة الأخيرة الكافور','1695','228','0');
INSERT INTO [t131336] VALUES (235,'مسألة (١٩١): ويكفن الرجل في ثلاثة أثواب ليس فيها قميص ولا عمامة','1696','228','0');
INSERT INTO [t131336] VALUES (236,'مسألة (١٩٢): والشهيد لا يغسل، ولا يصلى عليه','1703','228','0');
INSERT INTO [t131336] VALUES (237,'مسألة (١٩٣): ومقتول قطاع الطريق، ومن قتل في دفعه عن نفسه: مغسول مصلى عليه','1715','228','0');
INSERT INTO [t131336] VALUES (238,'مسألة (١٩٤): وحمل الجنازة بين العمودين سنة؛ وهو أن يحملها أربعة من جوانبها الأربعة، ويقف خامس بين العمودين في المقدم، ويحملها على كتفه','1717','228','0');
INSERT INTO [t131336] VALUES (239,'مسألة (١٩٥): إذا وجد بعض من الميت غسل وصلي عليه','1721','228','0');
INSERT INTO [t131336] VALUES (240,'مسألة (١٩٦): والمشي أمام الجنازة أفضل','1722','228','0');
INSERT INTO [t131336] VALUES (241,'مسألة (١٩٧): والولي أولى بالصلاة على الميت من الوالي','1734','228','0');
INSERT INTO [t131336] VALUES (242,'مسألة (١٩٨): وقراءة الفاتحة فريضة في صلاة الجنازة','1738','228','0');
INSERT INTO [t131336] VALUES (243,'مسألة (١٩٩): ويرفع اليدين في التكبيرات على الجنازة','1742','228','0');
INSERT INTO [t131336] VALUES (244,'مسألة (٢٠٠): ومن فاتته صلاة الجنازة، وكان من أهل فرض الكفاية في الصلاة عليها، صلاها على القبر على قرب العهد بالدفن','1744','228','0');
INSERT INTO [t131336] VALUES (245,'مسألة (٢٠١): ويصلى على الغائب بالنية','1748','228','0');
INSERT INTO [t131336] VALUES (246,'مسألة (٢٠٢): والصلاة على الجنازة في المسجد غير مكروهة','1752','228','0');
INSERT INTO [t131336] VALUES (247,'مسألة (٢٠٣): ويسل الميت من قبل رأسه','1756','228','0');
INSERT INTO [t131336] VALUES (248,'كتاب الزكاة','1760','0','0');
INSERT INTO [t131336] VALUES (249,'مسألة (٢٠٤): إذا زادت الإبل على عشرين ومائة، ففي كل أربعين ابنة لبون، وفي كل خمسين حقة','1761','248','0');
INSERT INTO [t131336] VALUES (250,'مسألة (٢٠٥): وجبران ما بين السنين بشاتين أو عشرين درهما لا يزاد عليه','1788','248','0');
INSERT INTO [t131336] VALUES (251,'مسألة (٢٠٦): وفي أربعين من البقر مسنة إلى ستين','1790','248','0');
INSERT INTO [t131336] VALUES (252,'مسألة (٢٠٧): والجذع من الضأن مأخوذ من زكاة الغنم','1795','248','0');
INSERT INTO [t131336] VALUES (253,'مسألة (٢٠٨): والمستفاد من غير نتاج الأصل مزكى بحول نفسه','1798','248','0');
INSERT INTO [t131336] VALUES (254,'مسألة (٢٠٩): وإذا ماتت الأمهات، وبقيت السخال بقيت في حق الزكاة، ويؤخذ منها إذا كانت السخال نصابا','1803','248','0');
INSERT INTO [t131336] VALUES (255,'مسألة (٢١٠): والخلطة في باب الزكاة صحيحة تزيد بها الزكاة مرة وتنقص أخرى','1807','248','0');
INSERT INTO [t131336] VALUES (256,'مسألة (٢١١): والزكاة تجب في مال الصبي والمجنون','1810','248','0');
INSERT INTO [t131336] VALUES (257,'مسألة (٢١٢): ولا تؤخذ قيمة ما يجب من الزكاة مكان الواجب مع القدرة','1818','248','0');
INSERT INTO [t131336] VALUES (258,'مسألة (٢١٣): ومن منع زكاة الأموال الظاهرة، فللإمام أخذها قهرا، وإذا أخذها [قهرا] وقعت موقعها','1826','248','0');
INSERT INTO [t131336] VALUES (259,'مسألة (٢١٤): ولا زكاة في سائمة الخيل','1829','248','0');
INSERT INTO [t131336] VALUES (260,'مسألة (٢١٥): والعشر لا يجب فيما نقص عن خمسة أوسق','1839','248','0');
INSERT INTO [t131336] VALUES (261,'مسألة (٢١٦): ويخرص النخل والكرم إذا بدا صلاحهما','1844','248','0');
INSERT INTO [t131336] VALUES (262,'مسألة (٢١٧): ولا شيء في الخضراوات','1851','248','0');
INSERT INTO [t131336] VALUES (263,'مسألة (٢١٨): والعشر واجب فيما يستنبت في أرض الخراج','1859','248','0');
INSERT INTO [t131336] VALUES (264,'مسألة (٢١٩): والذي يؤخذ من أراضي العراق، فإنما هو كراء أو ثمن لها على ما نذكره في تفصيل المذهب وليس بخراج، وأصل هذا أن أرض السواد فتحت عنوة [ثم] وقفها عمر - رضي الله عنه - على المسلمين؛ فالمأخوذ عنها لجميع المسلمين، ويجري مجرى الأجرة أو الثمن','1862','248','0');
INSERT INTO [t131336] VALUES (265,'مسألة (٢٢٠): والدراهم والدنانير إذا بلغت نصابا، كان فيما زاد على النصاب بحسابه، قلت الزيادة أم كثرت','1866','248','0');
INSERT INTO [t131336] VALUES (266,'مسألة (٢٢١): كل مال تجب الزكاة في عينه إذا نقص عن النصاب في بعض الحول، انقطع حكم الحول، فإذا كمل نصابا استؤنف الحول','1871','248','0');
INSERT INTO [t131336] VALUES (267,'مسألة (٢٢٢): ولا زكاة في الحلي المباح على أحد القولين','1872','248','0');
INSERT INTO [t131336] VALUES (268,'مسألة (٢٢٣): ما يؤخذ من البحر، فلا زكاة فيه، إلا أن يكون ذهبا أو فضة، فيكون كما لو وجده في البر','1883','248','0');
INSERT INTO [t131336] VALUES (269,'مسألة (٢٢٤): اعتبار النصاب في أموال التجارة عند تمام الحول','1885','248','0');
INSERT INTO [t131336] VALUES (270,'مسألة (٢٢٥): وزكاة الفطر واجبة في العبد، وإن كان للتجارة','1887','248','0');
INSERT INTO [t131336] VALUES (271,'مسألة (٢٢٦): والدين لا يمنع وجوب الزكاة في أحد القولين','1890','248','0');
INSERT INTO [t131336] VALUES (272,'مسألة (٢٢٧): ومن له دين على آخر، إلا أنه جحده ثم أقر به لزمه أن يخرج عنه زكاة ما مضى حوله على جحوده إذا أخذه في أحد القولين','1895','248','0');
INSERT INTO [t131336] VALUES (273,'مسألة (٢٢٨): ولا شيء فيما يستخرج من المعادن، إلا في الذهب والفضة','1898','248','0');
INSERT INTO [t131336] VALUES (274,'مسألة (٢٢٩): والنصاب معتبر في المستخرج على أحد القولين. وقال أبو حنيفة: يجب في القليل والكثير','1907','248','0');
INSERT INTO [t131336] VALUES (275,'مسألة (٢٣٠): الحق الواجب في المعدن والركاز، مصرفه مصرف الزكوات، يعتبر بحال الواجد','1909','248','0');
INSERT INTO [t131336] VALUES (276,'مسألة (٢٣١): وتجب زكاة الفطر على من عنده فضل من قوت يومه','1911','248','0');
INSERT INTO [t131336] VALUES (277,'مسألة (٢٣٢): ولا تجب على المسلم صدقة الفطر عن عبده الكافر','1914','248','0');
INSERT INTO [t131336] VALUES (278,'مسألة (٢٣٣): وزكاة فطر المرأة على زوجها','1917','248','0');
INSERT INTO [t131336] VALUES (279,'مسألة (٢٣٤): ولا يجزئ من البر إلا صاع','1920','248','0');
INSERT INTO [t131336] VALUES (280,'مسألة لم يذكرها الإمام (٢٣٥): والصاع خمسة أرطال وثلث','1950','248','0');
INSERT INTO [t131336] VALUES (281,'كتاب الصوم','1962','0','0');
INSERT INTO [t131336] VALUES (282,'مسألة (٢٣٦): لا يصح صوم رمضان بنية النهار','1963','281','0');
INSERT INTO [t131336] VALUES (283,'مسألة (٢٣٧): وصوم التطوع يصح بالنية بعد الزوال في أحد القولين','1972','281','0');
INSERT INTO [t131336] VALUES (284,'مسألة لم يذكرها الإمام (٢٣٨): ويكره صوم يوم الشك','1974','281','0');
INSERT INTO [t131336] VALUES (285,'مسألة (٢٣٩): وشهادة العدل الواحد تقبل في هلال رمضان مع صحو السماء في أظهر القولين','1983','281','0');
INSERT INTO [t131336] VALUES (286,'مسألة (٢٤٠): ولا تقبل شهادة رجل وامرأتين في هلال شوال','1988','281','0');
INSERT INTO [t131336] VALUES (287,'مسألة (٢٤١): وإذا رئي الهلال يوم ثلاثين من شعبان أو يوم الثلاثين من شهر رمضان نهارا فهو لليلة المستقبلة','1993','281','0');
INSERT INTO [t131336] VALUES (288,'مسألة (٢٤٢): وإذا جامع امرأته في نهار رمضان فلا كفارة عليها في ظاهر المذهب','1996','281','0');
INSERT INTO [t131336] VALUES (289,'مسألة (٢٤٣): ومن أكل عامدا في صوم رمضان فعليه القضاء ولا كفارة عليه','2003','281','0');
INSERT INTO [t131336] VALUES (290,'مسألة (٢٤٤): والحامل والمرضع إذا خافتا على ولديهما أفطرتا وعليهما القضاء والفدية','2012','281','0');
INSERT INTO [t131336] VALUES (291,'مسألة (٢٤٥): ومن رأى الهلال وحده وشهد به فردت شهادته كان عليه أن يصوم إجماعا، فإن جامع في يوم ردت شهادته، لزمته الكفارة عندنا','2016','281','0');
INSERT INTO [t131336] VALUES (292,'مسألة (٢٤٦): ومن كان عليه صوم من رمضان فلم يقضه مع القدرة عليه حتى دخل رمضان آخر، قضى وكفر','2019','281','0');
INSERT INTO [t131336] VALUES (293,'مسألة لم يذكرها (٢٤٧): ومن كان عليه صوم فلم يقضه مع القدرة حتى مات صام عنه وليه إن شاء، أو أطعم عنه على قوله في القديم','2022','281','0');
INSERT INTO [t131336] VALUES (294,'مسألة (٢٤٨): ومن نذر صوم يوم النحر أو يوم الفطر لم ينعقد نذره','2031','281','0');
INSERT INTO [t131336] VALUES (295,'مسألة (٢٤٩): ومن سبق ماء المضمضة أو الاستنشاق إلى جوفه لم يفطر على أحد القولين','2035','281','0');
INSERT INTO [t131336] VALUES (296,'مسألة (٢٥٠): والمجنون إذا أفاق في بعض نهار رمضان لا يلزمه قضاء ما مضى من الأيام على الجنون','2038','281','0');
INSERT INTO [t131336] VALUES (297,'مسألة (٢٥١): ويكره السواك للصائم بالعشي','2040','281','0');
INSERT INTO [t131336] VALUES (298,'مسألة (٢٥٢): ومن أفطر في صوم التطوع عامدا لا قضاء عليه','2045','281','0');
INSERT INTO [t131336] VALUES (299,'مسألة (٢٥٥): والاعتكاف بغير صوم يصح','2065','281','0');
INSERT INTO [t131336] VALUES (300,'كتاب الحج','2074','0','0');
INSERT INTO [t131336] VALUES (301,'مسألة (٢٥٦): من حج مسلما ثم ارتد ثم أسلم لم يلزمه الحج ثانيا','2075','300','0');
INSERT INTO [t131336] VALUES (302,'مسألة (٢٥٧): والمعضوب إذا كان واجدا للمال يلزمه الحج، وعليه أن يستأجر من يحج عنه','2078','300','0');
INSERT INTO [t131336] VALUES (303,'مسألة (٢٥٨): ومن مات بعد وجوب الحج عليه قضي عنه، وإن لم يوص به','2085','300','0');
INSERT INTO [t131336] VALUES (304,'مسألة (٢٥٩): ومن لم يحج حجة الإسلام لا يصح حجه عن غيره، وإذا أحرم عن الغير وقع عن فرضه، وكذلك كل من كان عليه حج واجب','2088','300','0');
INSERT INTO [t131336] VALUES (305,'كتاب الفرائض','2092','0','0');
INSERT INTO [t131336] VALUES (306,'مسألة (٣٩٤): ذوو الأرحام لا يرثون إرث ذوي النسب','2095','305','0');
INSERT INTO [t131336] VALUES (307,'مسألة (٣٩٥): ولا يرث من قبل الأب إلا جدة واحدة على أحد القولين، وهي أم الأب وأمهاتها','2117','305','0');
INSERT INTO [t131336] VALUES (308,'مسألة (٣٩٦): والقربى من قبل الأم تحجب البعدى من قبل الأب، والقربى من قبل الأب لا تحجب البعدى من قبل الأم في الصحيح من المذهب، وهو أصح الروايتين عن زيد بن ثابت','2124','305','0');
INSERT INTO [t131336] VALUES (309,'مسألة (٣٩٧): والإخوة والأخوات للأب والأم وللأب يقاسمون الجد ما دامت المقاسمة خيرا له من الثلث','2127','305','0');
INSERT INTO [t131336] VALUES (310,'مسألة (٣٩٨): ومال المرتد إذا مات على ردته أو قتل فيء للمسلمين','2134','305','0');
INSERT INTO [t131336] VALUES (311,'مسألة (٣٩٩): والإخوة والأخوات للأب والأم يشاركون الإخوة والأخوات للأم في ثلثهم في مسألة المشركة','2139','305','0');
INSERT INTO [t131336] VALUES (312,'مسألة (٤٠٠): وإذا مات ولد الملاعنة، ولا وارث له إلا عصبة أمه، فماله لبيت مال المسلمين','2144','305','0');
INSERT INTO [t131336] VALUES (313,'مسألة (٤٠١): إذا أوصى لذوي قرابته دخل فيه من كان من فصيلته ممن يقع عليه اسم القرابة من بني الأعمام وغيرهم','2153','305','0');
INSERT INTO [t131336] VALUES (314,'مسألة (٤٠٢): والوصية للقاتل جائزة في أحد القولين','2156','305','0');
INSERT INTO [t131336] VALUES (315,'مسألة (٤٠٣): إذا أوصى لجيرانه، فحد الجوار عند الشافعي أربعون دارا من جميع الجوانب، يصرف إليهم','2160','305','0');
INSERT INTO [t131336] VALUES (316,'مسألة (٤٠٤): تصح وصية المراهق على أحد القولين، وهو قول مالك','2162','305','0');
INSERT INTO [t131336] VALUES (317,'مسألة (٤٠٥): وإذا قال: أوصيت لفلان بسهم [من] مالي، لم يتقدر ذلك بشيء، والخيار إلى الورثة','2164','305','0');
INSERT INTO [t131336] VALUES (318,'مسألة (٤٠٦): ووصية من لا وارث له بعينه فيما زاد على الثلث ساقطة غير قابلة للإجازة على أحد المذهبين. وفيه وجه آخر أن الإمام لو أجازها جازت','2165','305','0');
INSERT INTO [t131336] VALUES (319,'القسم والفيء','2168','0','0');
INSERT INTO [t131336] VALUES (320,'مسألة (٤٠٧): والسلب للقاتل دون شرط الإمام','2169','319','0');
INSERT INTO [t131336] VALUES (321,'مسألة (٤٠٨): والأراضي المغنومة مقسومة بين الغانمين ليس للإمام أن يردها على المشركين','2190','319','0');
INSERT INTO [t131336] VALUES (322,'مسألة (٤٠٩): إذا شرط الإمام قبل القتال: من أخذ شيئا فهو له، فمن أخذ شيئا يكون غنيمة، وفي قول آخر: إنه جائز','2198','319','0');
INSERT INTO [t131336] VALUES (323,'مسألة (٤١٠): وللإمام أن يمن على البالغين من الأسرى، وأن يفاديهم بأسرى المسلمين','2201','319','0');
INSERT INTO [t131336] VALUES (324,'مسألة (٤١١): المستحب أن تقسم الغنائم في دار الحرب، ما لم يكن عذر يمنع من ذلك','2209','319','0');
INSERT INTO [t131336] VALUES (325,'مسألة (٤١٢): للفارس ثلاثة أسهم: سهمان لفرسه، وسهم له','2211','319','0');
INSERT INTO [t131336] VALUES (326,'مسألة (٤١٣): وسهم ذي القربى ثابت لبني هاشم، وبني المطلب؛ على غناهم وفقرهم','2229','319','0');
INSERT INTO [t131336] VALUES (327,'مسألة (٤١٤): في سهم رسول الله - صلى الله عليه وسلم - بعد وفاته قولان','2242','319','0');
INSERT INTO [t131336] VALUES (328,'كتاب قسم الصدقات','2244','0','0');
INSERT INTO [t131336] VALUES (329,'مسألة (٤١٥): ولا تحل الصدقة المفروضة لمن له كسب بقدر كفايته','2245','328','0');
INSERT INTO [t131336] VALUES (330,'مسألة (٤١٦): لا يجوز صرف شيء من الصدقات الواجبة إلى الكفار','2249','328','0');
INSERT INTO [t131336] VALUES (331,'مسألة (٤١٧): يجوز للرجل أن يتولى دفع صدقة الأموال الظاهرة بنفسه، وتفريقها بنفسه في قوله الجديد','2251','328','0');
INSERT INTO [t131336] VALUES (332,'مسألة (٤١٨): ويعطى سهم الغارمين لمن تحمل حمالة مع غناه','2253','328','0');
INSERT INTO [t131336] VALUES (333,'مسألة (٤١٩): ولا يجوز صرف الصدقات إلى صنف واحد من جماعة الأصناف الثمانية مع وجودهم','2257','328','0');
INSERT INTO [t131336] VALUES (334,'مسألة (٤٢٠): الفقير المذكور في آية الصدقات أمس حاجة من المسكين المذكور فيها','2262','328','0');
INSERT INTO [t131336] VALUES (335,'مسألة (٤٢١): ولا يجوز نقل صدقة بلد إلى بلد آخر مع وجود أهلها على أحد القولين','2270','328','0');
INSERT INTO [t131336] VALUES (336,'مسألة (٤٢٢): وللفقير أن يأخذ من الزكاة ما يقوم بكفايته على الدوام، وإن زاد على مائتي درهم','2280','328','0');
INSERT INTO [t131336] VALUES (337,'مسألة (٤٢٣): يجوز للمرأة أن تصرف زكاتها إلى زوجها إذا كان محتاجا','2284','328','0');
INSERT INTO [t131336] VALUES (338,'مسألة (٤٢٤): إذا دفع رب المال الصدقة إلى من ظاهره الفقر، ثم علم أنه كان غنيا؛ لزمته الإعادة، في أصح القولين','2285','328','0');
INSERT INTO [t131336] VALUES (339,'مسألة (٤٢٥): يجوز للإمام أن يسم نعم الجزية وإبل الصدقة','2289','328','0');
INSERT INTO [t131336] VALUES (340,'كتاب النكاح','2295','0','0');
INSERT INTO [t131336] VALUES (341,'مسألة (٤٢٦): المرأة لا تلي عقد النكاح','2296','340','0');
INSERT INTO [t131336] VALUES (342,'مسألة (٤٢٧): وللأب أن يزوج ابنته البالغ دون رضاها إذا كانت بكرا، كما له ذلك إذا كانت صغيرة، وليس له ذلك إذا كانت ثيبا؛ صغيرة كانت أو بالغا','2328','340','0');
INSERT INTO [t131336] VALUES (343,'مسألة (٤٢٨): لم يذكرها الإمام','2342','340','0');
INSERT INTO [t131336] VALUES (344,'مسألة (٤٢٩): ولا يصح النكاح بشهادة فاسقين، ولا شهادة رجل وامرأتين','2346','340','0');
INSERT INTO [t131336] VALUES (345,'مسألة (٤٣٠): والفاسق المعلن لا يكون وليا في التزويج في أظهر القولين','2353','340','0');
INSERT INTO [t131336] VALUES (346,'مسألة (٤٣١): ولا يزوج البكر الصغيرة إلا أبوها أو جدها','2357','340','0');
INSERT INTO [t131336] VALUES (347,'مسألة (٤٣٢): وإذا كان ولي المرأة ابن عمها فأراد أن يتزوج بها فليس له أن يتولى طرفي العقد بنفسه','2363','340','0');
INSERT INTO [t131336] VALUES (348,'مسألة (٤٣٣): ولا يزوج الابن أمه بحق البنوة، ولا يكون وليا لها','2366','340','0');
INSERT INTO [t131336] VALUES (349,'مسألة (٤٣٤): ولا يزوج الأب ابنته الصغيرة من عبد ولا مجنون، ولا من به إحدى العيوب الأربع، ولا غير كفء','2370','340','0');
INSERT INTO [t131336] VALUES (350,'مسألة (٤٣٥): ولا ينعقد النكاح بلفظ الهبة والبيع، وما أشبههما من ألفاظ عقود التمليك','2371','340','0');
INSERT INTO [t131336] VALUES (351,'مسألة (٤٣٦): والزنا لا يحرم الحلال، ولا يوقع تحريم المصاهرة','2376','340','0');
INSERT INTO [t131336] VALUES (352,'مسألة (٤٣٧): من وجد طول حرة لم يجز له نكاح أمة','2381','340','0');
INSERT INTO [t131336] VALUES (353,'مسألة (٤٣٨): وإذا أسلم وتحته أكثر من أربع نكحهن في عقد واحد، فله أن يختار منهن أربعا','2386','340','0');
INSERT INTO [t131336] VALUES (354,'مسألة (٤٣٩): وإذا أسلم أحدهما بعد الدخول توقف النكاح على انقضاء العدة','2396','340','0');
INSERT INTO [t131336] VALUES (355,'مسألة (٤٤٠): نكاح الشغار باطل','2411','340','0');
INSERT INTO [t131336] VALUES (356,'مسألة (٤٤١): والمنكوحة ترد بالعيوب الخمسة','2415','340','0');
INSERT INTO [t131336] VALUES (357,'مسألة (٤٤٢): وإذا عتقت الأمة تحت حر فلا خيار لها في فسخ','2422','340','0');
INSERT INTO [t131336] VALUES (358,'كتاب الصداق','2434','0','0');
INSERT INTO [t131336] VALUES (359,'مسألة (٤٤٣): أقل المهر لا يتقدر بالعشرة','2435','358','0');
INSERT INTO [t131336] VALUES (360,'مسألة (٤٤٤): ويجوز أخذ الأجرة على تعليم الخير، وذلك مثل تعليم القرآن، وأن يجعل مهرا، وكذلك يجوز أخذ الأجرة على الأذان','2451','358','0');
INSERT INTO [t131336] VALUES (361,'مسألة (٤٤٥): وإن مات زوج المفوضة قبل فرض مهرها والدخول، لم يجب لها مهر المثل مع الميراث في أحد القولين','2462','358','0');
INSERT INTO [t131336] VALUES (362,'مسألة (٤٤٦): والذي بيده عقدة النكاح هو الأب، على قوله القديم','2469','358','0');
INSERT INTO [t131336] VALUES (363,'مسألة (٤٤٧): والخلوة الخالية عن الوطء لا تقرر المهر، ولا توجب العدة على أصح القولين','2475','358','0');
INSERT INTO [t131336] VALUES (364,'مسألة (٤٤٨): وللتي طلقها زوجها بعد الدخول بها المتعة على أحد القولين','2481','358','0');
INSERT INTO [t131336] VALUES (365,'كتاب القسم','2485','0','0');
INSERT INTO [t131336] VALUES (366,'مسألة (٤٤٩): ويقيم الرجل في ابتداء الزفاف عند البكر سبعا، وعند الثيب ثلاثا، ثم لا يقضي قدره لسائر نسائه','2486','365','0');
INSERT INTO [t131336] VALUES (367,'مسألة (٤٥٠): وليس للزوج أن يخرج بواحدة من نسائه إلى سفره بغير القرعة','2492','365','0');
INSERT INTO [t131336] VALUES (368,'كتاب الخلع','2494','0','0');
INSERT INTO [t131336] VALUES (369,'مسألة (٤٥١): الخلع فسخ على أحد القولين','2495','368','0');
INSERT INTO [t131336] VALUES (370,'مسألة (٤٥٢): والمختلعة لا يلحقها الطلاق','2499','368','0');
INSERT INTO [t131336] VALUES (371,'مسألة (٤٥٣): وعقد الطلاق قبل النكاح لا ينعقد بالإضافة إليه','2502','368','0');
INSERT INTO [t131336] VALUES (372,'الطلاق والرجعة [والإيلاء]','2518','0','0');
INSERT INTO [t131336] VALUES (373,'مسألة (٤٥٤): السنة والبدعة في وقت الطلاق دون عدده','2519','372','0');
INSERT INTO [t131336] VALUES (374,'مسألة لم يذكرها (٤٥٥): وإذا قال لامرأته المدخول بها: أنت بت، أو قال: بتلة، أو: خلية، أو: برية، أو: بائن، أو: حرام. وأراد به الطلاق كان رجعيا','2541','372','0');
INSERT INTO [t131336] VALUES (375,'مسألة (٤٥٦): وإذا قال لها: أنت بائن، ونوى به طلقتين؛ وقعتا','2545','372','0');
INSERT INTO [t131336] VALUES (376,'مسألة (٤٥٧): لم يذكرها','2548','372','0');
INSERT INTO [t131336] VALUES (377,'مسألة (٤٥٨): وقول الرجل لامرأته: أنت علي حرام، صريح في التحريم، على أحد القولين، والكفارة فيه تجب بنفس اللفظ، كما لو نوى به التحريم','2556','372','0');
INSERT INTO [t131336] VALUES (378,'مسألة (٤٥٩): وإذا حرم على نفسه الطعام والشراب لم يلزمه كفارة','2560','372','0');
INSERT INTO [t131336] VALUES (379,'مسألة (٤٦٠): ولا يقع طلاق المكره إلا أن يريد وقوعه','2567','372','0');
INSERT INTO [t131336] VALUES (380,'مسألة (٤٦١): وطلاق السكران لا يقع، على أحد القولين','2577','372','0');
INSERT INTO [t131336] VALUES (381,'مسألة (٤٦٢): والمبتوتة في مرض الموت لا ترث على أحد القولين','2583','372','0');
INSERT INTO [t131336] VALUES (382,'مسألة (٤٦٣): وإذا طلق امرأته طلقة أو طلقتين، ثم عادت إليه بعد إصابة زوج عادت بما بقي من عدد الطلاق','2590','372','0');
INSERT INTO [t131336] VALUES (383,'مسألة (٤٦٤): واعتبار عدد الطلاق برق الزوج وحريته، واعتبار قدر العدة برق المرأة وحريتها','2594','372','0');
INSERT INTO [t131336] VALUES (384,'مسألة (٤٦٥): والرجعية محرمة الوطء، وإذا وطئها فالرجعة لا تحصل بالوطء','2603','372','0');
INSERT INTO [t131336] VALUES (385,'مسألة (٤٦٦): وبمضي مدة الإيلاء [لا] يقع الطلاق، لكن يوقف حتى يفيء أو يطلق','2605','372','0');
INSERT INTO [t131336] VALUES (386,'كتاب الظهار','2613','0','0');
INSERT INTO [t131336] VALUES (387,'مسألة (٤٦٧): لو ظاهر من أربعة نسوة بكلمة واحدة يكفيه كفارة واحدة على أحد القولين','2614','386','0');
INSERT INTO [t131336] VALUES (388,'مسألة (٤٦٨): وإذا أمسكها ساعة يمكنه فيها طلاقها بعد كلمة الظهار فقد حصل عائدا','2615','386','0');
INSERT INTO [t131336] VALUES (389,'مسألة (٤٦٩): وإعتاق الكافر في كفارة الظهار غير جائز','2621','386','0');
INSERT INTO [t131336] VALUES (390,'مسألة (٤٧٠): ولو دفع طعام ستين مسكينا إلى مسكين واحد لم يجزئه ذلك','2625','386','0');
INSERT INTO [t131336] VALUES (391,'مسألة (٤٧١): وقدر ما يؤدى للواحد من المساكين من طعام الكفارة مد','2628','386','0');
INSERT INTO [t131336] VALUES (392,'مسألة (٤٧٢): وإذا دفع إلى من ظنه فقيرا، ثم بان أنه كان غنيا، لم يجزئه ذلك على أحد القولين','2638','386','0');
INSERT INTO [t131336] VALUES (393,'كتاب اللعان','2640','0','0');
INSERT INTO [t131336] VALUES (394,'مسألة (٤٧٣): موجب قذف الزوج لزوجته المحصنة الحد، وله درء الحد عن نفسه باللعان','2641','393','0');
INSERT INTO [t131336] VALUES (395,'مسألة (٤٧٤): وللذمي، والعبد، والمحدود في القذف أن يلاعن على قذف امرأته','2647','393','0');
INSERT INTO [t131336] VALUES (396,'مسألة (٤٧٥): والفرقة تقع بلعان الزوج دون تفريق القاضي، والتحريم الواقع به لا يرتفع بتكذيب الزوج الملاعن نفسه','2654','393','0');
INSERT INTO [t131336] VALUES (397,'[مسألة] (٤٧٦): قال الإمام أحمد - رحمه الله -: وإذا التعن الزوج وأبت أن تلتعن، فإنها تحد حد الزنا','2665','393','0');
INSERT INTO [t131336] VALUES (398,'مسألة (٤٧٧): وشهادة الزوج لا تقبل على امرأته بالزنا','2666','393','0');
INSERT INTO [t131336] VALUES (399,'مسألة (٤٧٨): ونسب ولد الأمة لاحق إذا أقر بوطئها دون الدعوة','2668','393','0');
INSERT INTO [t131336] VALUES (400,'مسألة (٤٧٩): وإذا غاب الرجل عن امرأته، فبلغها وفاته فاعتدت، ثم نكحت فولدت أولادا، ثم قدم. فرق بينها وبين زوجها الآخر، وألحق الولد بالآخر','2672','393','0');
INSERT INTO [t131336] VALUES (401,'كتاب العدد','2674','0','0');
INSERT INTO [t131336] VALUES (402,'مسألة (٤٨٠): الأقراء المحتسب بها هي الأطهار','2675','401','0');
INSERT INTO [t131336] VALUES (403,'مسألة (٤٨١): وعدة من تباعد حيضها تنقضي بالأشهر قبل بلوغ سن الإياس، على أحد القولين','2687','401','0');
INSERT INTO [t131336] VALUES (404,'مسألة (٤٨٢): والحامل تحيض على أحد القولين','2697','401','0');
INSERT INTO [t131336] VALUES (405,'مسألة (٤٨٣): وتعتد الأمة الصغيرة والآيسة بشهرين، على أصح القولين، أو بثلاثة أشهر، على القول الثاني','2704','401','0');
INSERT INTO [t131336] VALUES (406,'مسألة (٤٨٤): وله في سكنى المتوفى عنها زوجها قولان','2706','401','0');
INSERT INTO [t131336] VALUES (407,'مسألة (٤٨٥): ولا إحداد على المبتوتة في أحد القولين','2714','401','0');
INSERT INTO [t131336] VALUES (408,'مسألة (٤٨٦): وعلى المتوفى عنها زوجها الإحداد، وإن كانت ذمية أو صغيرة','2716','401','0');
INSERT INTO [t131336] VALUES (409,'مسألة (٤٨٧): والعدتان من رجلين لا يتداخلان','2720','401','0');
INSERT INTO [t131336] VALUES (410,'مسألة لم يذكرها (٤٨٨): وأكثر مدة الحمل أربع سنين','2722','401','0');
INSERT INTO [t131336] VALUES (411,'مسألة لم يذكرها (٤٨٩): وامرأة المفقود لا تعتد ولا تنكح أبدا حتى يأتيها بيقين وفاته','2726','401','0');
INSERT INTO [t131336] VALUES (412,'مسألة (٤٩٠): وأم الولد إذا مات سيدها أو أعتقت تستبرأ بحيضة','2728','401','0');
INSERT INTO [t131336] VALUES (413,'مسألة (٤٩١): وتزويج الأمة التي يطؤها سيدها لا يصح إلا أن يستبرئها أولا بحيضة','2731','401','0');
INSERT INTO [t131336] VALUES (414,'كتاب الرضاع','2733','0','0');
INSERT INTO [t131336] VALUES (415,'مسألة (٤٩٢): لا يحرم أقل من خمس رضعات','2735','414','0');
INSERT INTO [t131336] VALUES (416,'مسألة (٤٩٣): لا رضاع بعد الحولين','2750','414','0');
INSERT INTO [t131336] VALUES (417,'كتاب النفقات','2754','0','0');
INSERT INTO [t131336] VALUES (418,'مسألة (٤٩٤): ونفقة الصغيرة تجب على زوجها في أحد القولين','2755','417','0');
INSERT INTO [t131336] VALUES (419,'مسألة (٤٩٥): والإعسار بنفقة الزوجة يوجب لها خيار الفسخ للنكاح','2756','417','0');
INSERT INTO [t131336] VALUES (420,'مسألة (٤٩٦): ولا نفقة للمبتوتة الحائل','2760','417','0');
INSERT INTO [t131336] VALUES (421,'مسألة (٤٩٧): ويخير الولد بين أبويه إذا بلغ سبع سنين أو ثمان','2773','417','0');
INSERT INTO [t131336] VALUES (422,'كتاب الجراح','2777','0','0');
INSERT INTO [t131336] VALUES (423,'مسألة (٤٩٨): لا يقتل المسلم بالذمي','2778','422','0');
INSERT INTO [t131336] VALUES (424,'مسألة (٤٩٩): لا يقتل حر بعبد','2801','422','0');
INSERT INTO [t131336] VALUES (425,'مسألة (٥٠٠): القصاص يجري فيما بين الرجال والنساء، وبين العبيد في النفس، وفيما دون النفس','2806','422','0');
INSERT INTO [t131336] VALUES (426,'مسألة (٥٠١): وإذا قطع اثنان يد إنسان دفعة واحدة [عمدا] وجب عليهما القصاص','2811','422','0');
INSERT INTO [t131336] VALUES (427,'مسألة (٥٠٢): والقتل بالخشب الكبير والحجر الذي يقتل مثله لا محالة قتل عمد موجب للقصاص','2812','422','0');
INSERT INTO [t131336] VALUES (428,'مسألة (٥٠٣): والواجب بقتل العمد القصاص أو الدية، فيخير بينهما ولي الدم في أحد القولين','2823','422','0');
INSERT INTO [t131336] VALUES (429,'مسألة (٥٠٤): وعلى شريك الأب القصاص في قتل الولد عمدا','2832','422','0');
INSERT INTO [t131336] VALUES (430,'مسألة (٥٠٥): ومن قتل بقطع طرف أو شجاج أو إحراق بالنار كان لولي الدم قتله بمثله إن شاء، والعدول عنه إلى ضرب الرقبة إن شاء','2835','422','0');
INSERT INTO [t131336] VALUES (431,'مسألة (٥٠٦): وأما قطع ما فيه القصاص؛ فللمقطوع طلب القصاص فيه قبل الاندمال','2842','422','0');
INSERT INTO [t131336] VALUES (432,'مسألة (٥٠٧) لم يذكرها: وقاتل العمد إذا التجأ إلى الحرم؛ جاز أن يستوفى منه القصاص في الحرم','2847','422','0');
INSERT INTO [t131336] VALUES (433,'مسألة (٥٠٨): وفي السن السوداء بأصل الخلقة ما في السن البيضاء','2851','422','0');
INSERT INTO [t131336] VALUES (434,'كتاب الديات','2856','0','0');
INSERT INTO [t131336] VALUES (435,'مسألة (٥٠٩): وخمس دية الخطأ بنو اللبون','2857','434','0');
INSERT INTO [t131336] VALUES (436,'مسألة (٥١٠): ودية العمد وعمد الخطأ أثلاث؛ منها أربعون خلفة، وثلاثون حقة، وثلاثون جذعة','2872','434','0');
INSERT INTO [t131336] VALUES (437,'مسألة (٥١١): ودية قتل الخطأ في الحرم، والأشهر الحرم، ولذي الرحم المحرم مغلظة','2878','434','0');
INSERT INTO [t131336] VALUES (438,'مسألة (٥١٢): والأصل في الدية الإبل وحدها، لا يجوز العدول عنها مع وجودها إلى غيرها على قوله في الجديد','2882','434','0');
INSERT INTO [t131336] VALUES (439,'مسألة (٥١٣): فإن جعلنا الدراهم والدنانير أصلين في الدية على القول الثاني، فقدره ألف دينار أو اثنا عشر ألف درهم','2889','434','0');
INSERT INTO [t131336] VALUES (440,'مسألة (٥١٤): ودية الذمي ثلث دية المسلم','2898','434','0');
INSERT INTO [t131336] VALUES (441,'مسألة (٥١٥): وقيمة العبد على القاتل لا تحملها العاقلة في أحد القولين','2909','434','0');
INSERT INTO [t131336] VALUES (442,'مسألة (٥١٦): وأبو القاتل وجده وابنه وابن ابنه ليسوا من جملة العاقلة الذين يتحملون الدية، وكذلك القاتل لا يحمل شيئا من دية قتيله خطأ','2911','434','0');
INSERT INTO [t131336] VALUES (443,'مسألة (٥١٧): ومن في الديوان ومن ليس فيه من العاقلة سواء في تحمل العقل','2917','434','0');
INSERT INTO [t131336] VALUES (444,'مسألة (٥١٨): والبداية في القسامة مع اللوث بأيمان المدعين','2919','434','0');
INSERT INTO [t131336] VALUES (445,'مسألة (٥١٩): قتل العمد يوجب الكفارة','2939','434','0');
INSERT INTO [t131336] VALUES (446,'مسألة (٥٢٠): ونفس السحر ليس بكفر، ولا يقتل ما لم يقتل سحره عمدا','2941','434','0');
INSERT INTO [t131336] VALUES (447,'مسألة (٥٣٤): ولا فرق بين الطعام الرطب واليابس في وجوب القطع إذا آواه الجرين، وبلغ قيمة ربع دينار','2953','434','0');
INSERT INTO [t131336] VALUES (448,'مسألة (٥٣٥): ولو وهبت له السرقة لم يدرأ عنه بذلك الحد','2957','434','0');
INSERT INTO [t131336] VALUES (449,'مسألة (٥٣٦): ويقطع النباش إذا أخرج الكفن وبلغت قيمته نصابا، قال الشافعي: لأنه حرز مثله','2963','434','0');
INSERT INTO [t131336] VALUES (450,'مسألة (٥٣٧): وإذا سرق قطعت يده اليمنى، وإذا سرق الثانية قطعت رجله اليسرى، فإذا سرق الثالثة قطعت يده اليسرى، فإذا سرق الرابعة قطعت رجله اليمنى','2966','434','0');
INSERT INTO [t131336] VALUES (451,'مسألة (٥٣٨): وآية المحاربين نزلت في المسلمين، وترتيبه على ما روي عن ابن عباس - رضي الله عنهما -','2976','434','0');
INSERT INTO [t131336] VALUES (452,'مسألة (٥٣٩): والحدود التي تجب لله تعالى تسقط بالتوبة على أحد القولين','2981','434','0');
INSERT INTO [t131336] VALUES (453,'كتاب الأشربة','2984','0','0');
INSERT INTO [t131336] VALUES (454,'مسألة (٥٤٠): ما أسكر كثيره فقليله حرام، من أي الأجناس كان، من مطبوخ ونيء','2985','453','0');
INSERT INTO [t131336] VALUES (455,'مسألة (٥٤١): وحد الشرب أربعون','3048','453','0');
INSERT INTO [t131336] VALUES (456,'مسألة (٥٤٢): ومن تطلع من صير باب فرمى صاحب الدار بما يدفع بصره فأعمى عينه كان هدرا','3059','453','0');
INSERT INTO [t131336] VALUES (457,'مسألة (٥٤٣): وإذا عزر السلطان إنسانا فتلف من التعزير ضمنه الإمام، إما في مال نفسه، أو في بيت المال، على اختلاف القولين','3065','453','0');
INSERT INTO [t131336] VALUES (458,'مسألة (٥٤٤): الختان واجب','3068','453','0');
INSERT INTO [t131336] VALUES (459,'مسألة (٥٤٥): وما أفسدت البهائم من الزرع بالليل ضمنه أهلها، وما أفسدت بالنهار ولم يكن معها صاحبها فهدر','3074','453','0');
INSERT INTO [t131336] VALUES (460,'مسألة (٥٤٦): ولو وطئت دابته وهو راكبها إنسانا برجلها كان عليه ضمانه؛ لأن عليه منعها في تلك الحال من كل ما يتلف به أحدا','3081','453','0');
INSERT INTO [t131336] VALUES (461,'كتاب السير','3086','0','0');
INSERT INTO [t131336] VALUES (462,'مسألة (٥٤٧): [فإن] أدرك أهل الحرب المسلمين المنصرفين بالغلبة والغنيمة فخاف المسلمون ارتجاع الغنائم [لم] يحل لهم ذبح الحيوان وإتلافه لغير مأكلة','3087','461','0');
INSERT INTO [t131336] VALUES (463,'مسألة (٥٤٨): والغنيمة لمن شهد الوقعة أو كان ردءا لهم، فأما من لم يحضرها ولم يكن ردءا لهم فلا حق له فيها','3090','461','0');
INSERT INTO [t131336] VALUES (464,'مسألة (٥٤٩): ويقتل الشيوخ والرهبان في أحد القولين','3096','461','0');
INSERT INTO [t131336] VALUES (465,'مسألة (٥٥٠): وأمان العبد المسلم جائز قاتل أو لم يقاتل','3103','461','0');
INSERT INTO [t131336] VALUES (466,'مسألة (٥٥١): والطائفة اليسيرة إذا دخلت دار الحرب دون إذن الإمام كان ما يغنمونه غنيمة مخمسة','3105','461','0');
INSERT INTO [t131336] VALUES (467,'مسألة (٥٥٢): وإذا زنى مسلم في دار الحرب لزمه الحد، وكذلك إذا سرق فيها مال مسلم لزمه القطع','3106','461','0');
INSERT INTO [t131336] VALUES (468,'مسألة (٥٥٣): ولا يملك المشركون ما أحرزوه على المسلمين من أموالهم','3109','461','0');
INSERT INTO [t131336] VALUES (469,'مسألة (٥٥٤): ومكة فتحت صلحا','3115','461','0');
INSERT INTO [t131336] VALUES (470,'مسألة (٥٥٥): ومن سبي منهم من الحرائر رقت، وبانت من الزوج؛ كان معها، أو لم يكن معها','3120','461','0');
INSERT INTO [t131336] VALUES (471,'مسألة (٥٥٦): والتفريق بين الأمة وولدها قبل سن التخيير في الملك بالبيع والقسمة باطل','3122','461','0');
INSERT INTO [t131336] VALUES (472,'مسألة (٥٥٧): ومن اشترى من المسلمين أرضا خراجية من أهل الخرجية سقط عنه الخراج','3124','461','0');
INSERT INTO [t131336] VALUES (473,'كتاب الجزية','3125','0','0');
INSERT INTO [t131336] VALUES (474,'مسألة (٥٥٨): ولا تقبل الجزية من أهل الأوثان، وإنما تقبل ممن له كتاب، أو شبه كتاب','3126','473','0');
INSERT INTO [t131336] VALUES (475,'مسألة (٥٥٩): وأقل الجزية دينار، والغني والفقير فيه سواء','3132','473','0');
INSERT INTO [t131336] VALUES (476,'مسألة (٥٦٠): ومن دخل من أهل الحرب دار الإسلام للتجارة، فللإمام أن يضع عليه شيئا يأخذه من أموال تجارته، على ما يؤدي إليه اجتهاده','3134','473','0');
INSERT INTO [t131336] VALUES (477,'كتاب الصيد والذبائح','3135','0','0');
INSERT INTO [t131336] VALUES (478,'مسألة (٥٦١): والكلب المعلم إذا أكل مما اصطاده فلا يحرمه في أحد القولين','3136','477','0');
INSERT INTO [t131336] VALUES (479,'مسألة (٥٦٢): ولو ذبح مسلم ولم يسم حلت ذبيحته','3142','477','0');
INSERT INTO [t131336] VALUES (480,'مسألة (٥٦٣): وإذا رمى بسهم إلى صيد، أو أرسل كلبا على صيد فغابا عنه، ثم وجده مقتولا لم يحل أكله على أحد القولين، وحل في القول الثاني، وإن لم يكن مقتفيا أثره','3150','477','0');
INSERT INTO [t131336] VALUES (481,'مسألة (٥٦٤): وإذا ضرب الصيد فقطعه قطعتين أكل، وإن كانت إحدى القطعتين أقل من الأخرى','3156','477','0');
INSERT INTO [t131336] VALUES (482,'مسألة (٥٦٥): ولا تقع الذكاة بالسن والظفر','3159','477','0');
INSERT INTO [t131336] VALUES (483,'مسألة (٥٦٦): ويجوز أكل السمك الطافي','3163','477','0');
INSERT INTO [t131336] VALUES (484,'كتاب الأضاحي','3174','0','0');
INSERT INTO [t131336] VALUES (485,'مسألة (٥٦٧): الأضحية غير واجبة','3175','484','0');
INSERT INTO [t131336] VALUES (486,'مسألة (٥٦٨): ولا وقت للذبح يوم الأضحى إلا في قدر صلاة النبي - صلى الله عليه وسلم -، وذلك حين حلت الصلاة، وقدر خطبتين خفيفتين، إذا كان هذا فقد حل الذبح لكل أحد حيث كان','3183','484','0');
INSERT INTO [t131336] VALUES (487,'مسألة (٥٦٩): وإذا قطع الحلقوم والمريء فقد حل، وإن لم يقطع الودجين','3186','484','0');
INSERT INTO [t131336] VALUES (488,'مسألة (٥٧٠): ولا يجوز للمضحي أن يبادل بشيء مما ضحى به','3189','484','0');
INSERT INTO [t131336] VALUES (489,'مسألة (٥٧١): والأضحية جائزة يوم النحر، وثلاثة أيام بعده','3190','484','0');
INSERT INTO [t131336] VALUES (490,'مسألة (٥٧٢): ذكاة الجنين ذكاة أمه إذا وجد ميتا','3191','484','0');
INSERT INTO [t131336] VALUES (491,'مسألة (٥٧٣): ولحم الضبع والثعلب حلال يؤكل','3195','484','0');
INSERT INTO [t131336] VALUES (492,'مسألة (٥٧٤): وأكل الضب مباح','3200','484','0');
INSERT INTO [t131336] VALUES (493,'مسألة (٥٧٥): ولحم الفرس مأكول مباح من غير كراهية','3207','484','0');
INSERT INTO [t131336] VALUES (494,'مسألة (٥٧٦): ولا يجوز بيع الزيت النجس','3211','484','0');
INSERT INTO [t131336] VALUES (495,'مسألة (٥٧٧): ومن اضطر إلى الميتة حل له أن يتناول منها مقدار الشبع في أحد القولين','3215','484','0');
INSERT INTO [t131336] VALUES (496,'كتاب السبق والرمي','3217','0','0');
INSERT INTO [t131336] VALUES (497,'مسألة (٥٧٨): والمسابقة جائزة على شرط المال على التفصيل الذي نذكره في المذهب','3218','496','0');
INSERT INTO [t131336] VALUES (498,'كتاب الأيمان والنذور','3223','0','0');
INSERT INTO [t131336] VALUES (499,'مسألة (٥٧٩): ولا كفارة على من حلف باليهودية أو النصرانية أو البراءة من الله أو من الإسلام','3224','498','0');
INSERT INTO [t131336] VALUES (500,'مسألة (٥٨٠): والكفارة واجبة في يمين الغموس','3226','498','0');
INSERT INTO [t131336] VALUES (501,'مسألة (٥٨١): وكفارة اليمين قبل الحنث جائزة واقعة موقعها','3231','498','0');
INSERT INTO [t131336] VALUES (502,'مسألة (٥٨٢): ولو حلف ليقضينه حقه إلى حين، فليس بمعلوم؛ لأنه يقع على مدة الدنيا، ويوم','3238','498','0');
INSERT INTO [t131336] VALUES (503,'مسألة (٥٨٣): ولو حلف لا يأكل خبزا فأكل معه التمر حنث','3240','498','0');
INSERT INTO [t131336] VALUES (504,'مسألة (٥٨٤): ولو حلف ليضربن عبده مائة سوط، فجمعها فضربه بها بحيث يمسه الجميع بر في يمينه','3242','498','0');
INSERT INTO [t131336] VALUES (505,'مسألة (٥٨٥): ولو قال: إن كلمت فلانا فمالي في سبيل الله، أو صدقة، أو لله علي أن أحج، أو ما أشبه ذلك، فعليه كفارة يمين في ظاهر المذهب، وقد قيل: إنه مخير بينهما، وكل واحد منهما كاف مكفر','3244','498','0');
INSERT INTO [t131336] VALUES (506,'مسألة (٥٨٦): ولو قال: إن شفا الله مريضي فلله علي أن أنحر ولدي لم ينعقد نذره','3253','498','0');
INSERT INTO [t131336] VALUES (507,'مسألة (٥٨٧): ومن نذر أن يمشي إلى بيت الله الحرام، لزمه إن قدر على المشي على أحد القولين، وإن لم يقدر ركب وأهراق دما احتياطا','3258','498','0');
INSERT INTO [t131336] VALUES (508,'مسألة (٥٨٨): لو نذر المشي إلى مسجد المدينة، أو بيت المقدس، لزمه الوفاء بما نذر في أحد القولين','3264','498','0');
INSERT INTO [t131336] VALUES (509,'كتاب أدب القاضي','3267','0','0');
INSERT INTO [t131336] VALUES (510,'مسألة (٥٨٩): ويكره القضاء في المسجد','3268','509','0');
INSERT INTO [t131336] VALUES (511,'مسألة (٥٩٠): ولا يجوز الحكم بالتقليد','3271','509','0');
INSERT INTO [t131336] VALUES (512,'مسألة (٥٩١): ولا يجوز أن يكون الفاسق أو المرأة قاضيا','3276','509','0');
INSERT INTO [t131336] VALUES (513,'مسألة (٥٩٢): والقضاء على الغائب جائز','3278','509','0');
INSERT INTO [t131336] VALUES (514,'مسألة (٥٩٣): وللحاكم أن يحكم بعلمه في ظاهر المذهب إلا في الحدود، سواء أحاط علمه بذلك قبل أن ولي القضاء أو بعده، وسواء أحاط علمه به في بلد ولايته أو في غير [بلد] ولايته، وقد قيل: إنه في الحدود بمثابته، وفيه قول آخر أنه لا يحكم بعلمه في الحدود، ولا في غيره','3282','509','0');
INSERT INTO [t131336] VALUES (515,'كتاب الشهادات','3287','0','0');
INSERT INTO [t131336] VALUES (516,'مسألة (٥٩٤): ولا يحل حكم الحاكم الأمور عما هي عليه','3288','515','0');
INSERT INTO [t131336] VALUES (517,'مسألة (٥٩٥): وشهادة الواحدة غير مقبولة','3295','515','0');
INSERT INTO [t131336] VALUES (518,'مسألة (٥٩٦): وشهادة المحدود في القذف مقبولة إذا تاب','3298','515','0');
INSERT INTO [t131336] VALUES (519,'مسألة (٥٩٧): وشهادة الكافر عندنا مردودة في جميع الأحوال','3301','515','0');
INSERT INTO [t131336] VALUES (520,'مسألة (٥٩٨): ويجوز القضاء بشاهد ويمين في الأموال وما يجري مجراها','3304','515','0');
INSERT INTO [t131336] VALUES (521,'مسألة (٥٩٩): ويؤكد اليمين بالمكان والزمان','3322','515','0');
INSERT INTO [t131336] VALUES (522,'مسألة (٦٠٠): واليمين ترد على المدعي بنكول المدعى عليه، ولا يحكم بمجرد النكول','3324','515','0');
INSERT INTO [t131336] VALUES (523,'مسألة (٦٠١): وشهادة العدو على العدو غير مقبولة','3331','515','0');
INSERT INTO [t131336] VALUES (524,'مسألة (٦٠٢): وإذا شهد الشهود بموجب قتل، فقتل المشهود عليه بشهادتهم، ثم رجعوا عن الشهادة، وقالوا: تعمدنا، وجب عليهم القصاص','3333','515','0');
INSERT INTO [t131336] VALUES (525,'مسألة (٦٠٣): وحد الزنا لا يقام على المشهود عليه بشهود الزوايا، وهو أن يشهد كل واحد منهم أنه زنى بها في زاوية أخرى من زوايا البيت','3334','515','0');
INSERT INTO [t131336] VALUES (526,'كتاب الدعوى','3335','0','0');
INSERT INTO [t131336] VALUES (527,'مسألة (٦٠٤): البينتان إذا تعارضتا والشيء في يد ثالث لم يقسم بينهما في أحد الأقوال','3336','526','0');
INSERT INTO [t131336] VALUES (528,'مسألة (٦٠٥): ويرجع في تمييز الأنساب إذا اشتبهت إلى قول القافة','3340','526','0');
INSERT INTO [t131336] VALUES (529,'كتاب العتق والولاء والمدبر والكتابة','3350','0','0');
INSERT INTO [t131336] VALUES (530,'مسألة (٦٠٦): إذا أعتق من عبده جزءا عتق كله','3351','529','0');
INSERT INTO [t131336] VALUES (531,'مسألة (٦٠٧): وإذا أعتق أحد الشريكين نصيبه وهو موسر سرى إلى ملك شريكه، وضمن له','3353','529','0');
INSERT INTO [t131336] VALUES (532,'مسألة (٦٠٨): ومن أعتق ستة أعبد له في مرض موته ولا مال له غيرهم، أقرع بينهم، فيحكم بعتق اثنين، ورق أربعة','3366','529','0');
INSERT INTO [t131336] VALUES (533,'مسألة (٦٠٩): ولا يعتق عليه أخوه','3371','529','0');
INSERT INTO [t131336] VALUES (534,'مسألة (٦١٠): ولا ولاء بغير جهة الإعتاق','3375','529','0');
INSERT INTO [t131336] VALUES (535,'مسألة (٦١١): وبيع المدبر جائز','3381','529','0');
INSERT INTO [t131336] VALUES (536,'مسألة (٦١٢): ولا تجوز الكتابة على أقل من نجمين','3389','529','0');
INSERT INTO [t131336] VALUES (537,'مسألة (٦١٣): ولا يعتق المكاتب ما لم يقل سيده في عقد كتابته: فإذا أديت إلي فأنت حر','3391','529','0');
INSERT INTO [t131336] VALUES (538,'مسألة (٦١٤): وإذا مات المكاتب، وقد بقي عليه شيء من مال الكتابة مات رقيقا، وانفسخت كتابته بموته','3392','529','0');
INSERT INTO [t131336] VALUES (539,'مسألة (٦١٥): وإيتاء المكاتب بعض مال الكتابة أو الحط لبعض مال الكتابة واجب على السيد','3395','529','0');
INSERT INTO [t131336] VALUES (540,'ثبت المصادر والمراجع','3402','0','0');
