INSERT INTO [t13138] VALUES (1,'من قرأ قل هو الله أحد فكأنما قرأ ثلث القرآن، ومن قرأها عشر مرات بنى الله عز وجل له قصرا','1','0','0');
INSERT INTO [t13138] VALUES (2,'من قرأ قل هو الله أحد فكأنما قرأ ثلث القرآن، ومن قرأ قل هو الله أحد مرتين فكأنما قرأ ثلثي','2','0','0');
INSERT INTO [t13138] VALUES (3,'من يقرأ ثلث القرآن في يوم؟ فتعاظم ذلك القوم، فقال: من قرأ قل هو الله أحد فكأنما قرأ ثلث','3','0','0');
INSERT INTO [t13138] VALUES (4,'أيعجز أحدكم أن يقرأ ثلث القرآن في ليلة؟ قالوا: ومن يطيق ذلك يا رسول الله، قال: يقرأ قل هو','4','0','0');
INSERT INTO [t13138] VALUES (5,'من قرأ قل هو الله أحد مرة بورك عليه، ومن قرأها مرتين بورك عليه وعلى أهل بيته، ومن قرأها','5','0','0');
INSERT INTO [t13138] VALUES (6,'قل هو الله أحد تعدل ثلث القرآن','6','0','0');
INSERT INTO [t13138] VALUES (7,'من قرأ قل هو الله أحد مرة فكأنما قرأ ثلث القرآن، ومن قرأها مرتين فكأنما قرأ ثلثي القرآن،','7','0','0');
INSERT INTO [t13138] VALUES (8,'قل هو الله أحد تعدل ثلث القرآن أو هي ثلث القرآن','8','0','0');
INSERT INTO [t13138] VALUES (9,'حبيبي جبريل، ما بلغ معاوية بن معاوية هذه المنزلة، قال: بقراءته قل هو الله أحد كان يقرؤها','9','0','0');
INSERT INTO [t13138] VALUES (10,'من صلى بين المغرب والعشاء عشرين ركعة يقرأ في كل ركعة الحمد وقل هو الله أحد بنى الله عز','10','0','0');
INSERT INTO [t13138] VALUES (11,'من صلى بعد عشاء الآخرة ركعتين يقرأ في كل ركعة قل هو الله أحد خمس عشرة مرة بنى الله له ألف','11','0','0');
INSERT INTO [t13138] VALUES (12,'من قرأ بعد صلاة الجمعة قل هو الله أحد وقل أعوذ برب الفلق وقل أعوذ برب الناس سبع مرات','12','0','0');
INSERT INTO [t13138] VALUES (13,'من صلى يوم الجمعة أربع ركعات يقرأ في كل ركعة قل هو الله أحد مرة فقد أدى حق الجمعة كما أدت','13','0','0');
INSERT INTO [t13138] VALUES (14,'من قرأ في ليلة النصف من شعبان قل هو الله أحد ألف مرة في مائة ركعة لم يمت حتى يرى في منامه','14','0','0');
INSERT INTO [t13138] VALUES (15,'من قرأ قل هو الله أحد حرم لحمه على النار','15','0','0');
INSERT INTO [t13138] VALUES (16,'ينزلون من الجنة حيث شاءوا: الشهيد، ورجل قرأ في كل يوم قل هو الله أحد مائتي','16','0','0');
INSERT INTO [t13138] VALUES (17,'من قرأ قل هو الله أحد ألف مرة كانت أحب إلى الله عز وجل من ألف فرس ملجمة مسرجة في','17','0','0');
INSERT INTO [t13138] VALUES (18,'من قرأ قل هو الله أحد ثلاث مرات، ثم قال: لا إله إلا الله واحدا لا نعبد إلا إياه مخلصين له','18','0','0');
INSERT INTO [t13138] VALUES (19,'يقرأ في الركعتين قبل الفجر قل يا أيها الكافرون وقل هو الله أحد','19','0','0');
INSERT INTO [t13138] VALUES (20,'رمقت النبي صلى الله عليه وسلم خمسا وعشرين ليلة في ركعتي الفجر يقرأ قل هو الله أحد وقل يا','20','0','0');
INSERT INTO [t13138] VALUES (21,'قرأت بكم ثلث القرآن وربعه','21','0','0');
INSERT INTO [t13138] VALUES (22,'أيعجز أحدكم أن يقرأ ثلث القرآن في ليلة الله الواحد الصمد','22','0','0');
INSERT INTO [t13138] VALUES (23,'قل هو الله أحد ثلث القرآن','23','0','0');
INSERT INTO [t13138] VALUES (24,'أيعجز أحدكم أن يقرأ في ليلة بثلث القرآن فشق عليهم فقالوا: من يطيق؟ فقال: يقرأ قل هو الله','24','0','0');
INSERT INTO [t13138] VALUES (25,'من قرأ قل هو الله أحد مرة فكأنما قرأ ثلث القرآن وكتب له من الحسنات عدد من أشرك بالله وآمن','25','0','0');
INSERT INTO [t13138] VALUES (26,'من قرأ قل هو الله أحد فلا يقرأ معها شيئا من القرآن استقلالا بها؛ لأنها نسبة الرحمن عز وجل','26','0','0');
INSERT INTO [t13138] VALUES (27,'من أراد أن ينام، فنام على يمينه، ثم قرأ قل هو الله أحد مائة مرة إذا كان يوم القيامة يقول','27','0','0');
INSERT INTO [t13138] VALUES (28,'من قرأ قل هو الله أحد في ركعتين ثنتي عشرة مرة في كل ركعة ست مرات بعد أم القرآن، يحسن','28','0','0');
INSERT INTO [t13138] VALUES (29,'يا محمد {قل هو الله أحد} [الإخلاص: ١]- ليس له عروق تتشعب - {الله الصمد} [الإخلاص: ٢]- ليس','29','0','0');
INSERT INTO [t13138] VALUES (30,'أي سورة في القرآن أعظم؟ قال: قل هو الله أحد قال: فأي آية في القرآن أعظم؟ قال: آية الكرسي','30','0','0');
INSERT INTO [t13138] VALUES (31,'من قرأ قل هو الله أحد مائة مرة في خلاء لا يخبر بها أحدا غفر له ذنوب خمسين سنة إلا الدماء','31','0','0');
INSERT INTO [t13138] VALUES (32,'استشفوا بما حمد الله به نفسه قبل أن يحمده خلقه وبما مدح به نفسه قلنا: وماذا بأبي وأمي يا','32','0','0');
INSERT INTO [t13138] VALUES (33,'قرأ في ركعتي الطواف بسورتي الإخلاص، وهي قل يا أيها الكافرون وقل هو الله','33','0','0');
INSERT INTO [t13138] VALUES (34,'يقرأ في المغرب بقل يا أيها الكافرون وقل هو الله أحد','34','0','0');
INSERT INTO [t13138] VALUES (35,'كان يقرأ على المنبر قل يا أيها الكافرون وقل هو الله أحد','35','0','0');
INSERT INTO [t13138] VALUES (36,'يقرأ في ركعتي الفجر في الأولى بالحمد وقل يا أيها الكافرون وفي الثانية بالحمد وقل هو الله','36','0','0');
INSERT INTO [t13138] VALUES (37,'من صلى ركعتين يقرأ في كل ركعة بأم الكتاب وست مرار قل هو الله أحد يحسن ركوعهما وسجودهما','37','0','0');
INSERT INTO [t13138] VALUES (38,'من قرأ قل هو الله أحد إحدى وعشرين ألف مرة فقد اشترى نفسه من الله عز وجل وهو من خاصة الله','38','0','0');
INSERT INTO [t13138] VALUES (39,'الأرضين السبع أسست على قل هو الله أحد','39','0','0');
INSERT INTO [t13138] VALUES (40,'من واظب على قراءة قل هو الله أحد وآية الكرسي عشر مرات في ليل أو نهار استوجب رضوان الله','40','0','0');
INSERT INTO [t13138] VALUES (41,'من قرأ قل هو الله أحد إلى آخرها مائة مرة بعد صلاة الغداة قبل أن يكلم أحدا رفع له ذلك','41','0','0');
INSERT INTO [t13138] VALUES (42,'من قرأ قل هو الله أحد ثلاثين مرة كتب الله له براءة من النار، وأمانا من العذاب، والأمان','42','0','0');
INSERT INTO [t13138] VALUES (43,'من قرأ في صلاة الصبح قل هو الله أحد إحدى عشرة مرة لم يصل إليه في ذلك اليوم ذنب وإن جهد','43','0','0');
INSERT INTO [t13138] VALUES (44,'من قرأ قل هو الله أحد اثنتي عشرة مرة فكأنما ختم القرآن أربع مرات، وكان خير أهل الأرض إذا','44','0','0');
INSERT INTO [t13138] VALUES (45,'من أتى منزله، فقرأ الحمد وقل هو الله أحد نفى الله عنه الفقر، وكثر خير بيته حتى يفيض به','45','0','0');
INSERT INTO [t13138] VALUES (46,'من صلى بعد المغرب ثنتي عشرة ركعة يقرأ في كل ركعة قل هو الله أحد أربعين مرة صافحته','46','0','0');
INSERT INTO [t13138] VALUES (47,'أخبروه أن الله عز وجل يحبه','47','0','0');
INSERT INTO [t13138] VALUES (48,'إذا نقس بالناقوس اشتد غضب الرحمن عز وجل، فتنزل الملائكة فيأخذون بأقطار الأرض فلا يزالون','48','0','0');
INSERT INTO [t13138] VALUES (49,'من قرأ قل هو الله أحد في مرضه الذي يموت فيه لم يفتن في قبره، وأمن من ضغطة القبر، وحملته','49','0','0');
INSERT INTO [t13138] VALUES (50,'من قرأ قل هو الله أحد حين يدخل منزله مرة واحدة باعد ذلك به الفقر ونفع','50','0','0');
INSERT INTO [t13138] VALUES (51,'من قال: لا إله إلا الله، وحده لا شريك له، أحدا صمدا، لم يلد ولم يولد، ولم يكن له كفوا أحد','51','0','0');
INSERT INTO [t13138] VALUES (52,'ثلاث من جاء بهن مع إيمان دخل من أي أبواب الجنة شاء، وزوج من حور العين حيث شاء: من أدى','52','0','0');
INSERT INTO [t13138] VALUES (53,'من مر على المقابر وقرأ قل هو الله أحد إحدى عشرة مرة، ثم وهب أجره للأموات أعطي من الأجر','53','0','0');
INSERT INTO [t13138] VALUES (54,'يوتر بثلاث، يقرأ في الأولى بالحمد وقل هو الله أحد وفي الثانية بالحمد وقل هو الله أحد وفي','54','0','0');
INSERT INTO [t13138] VALUES (55,'لعن الله العقرب لا يدع مصليا، ولا غيره. ثم دعا بماء وملح، وجعل يمسح عليها ويقرأ قل هو','55','0','0');
INSERT INTO [t13138] VALUES (56,'من قرأ آية الكرسي في دبر كل صلاة مكتوبة كان في ذمة الله إلى الصلاة','56','0','0');
INSERT INTO [t13138] VALUES (57,'ما حافظ عليها إلا نبي أو صديق أو شهيد','57','0','0');
INSERT INTO [t13138] VALUES (58,'أعيذك بالأحد الصمد الذي لم يلد ولم يولد ولم يكن له كفوا أحد من شر ما تجد رددها سبع مرات،','58','0','0');
