INSERT INTO [t13141] VALUES (1,'كلكم راع، وكلكم مسئول عن رعيته، فالإمام راع وهو مسئول عن رعيته','3','0','0');
INSERT INTO [t13141] VALUES (2,'كلكم راع، وكلكم مسئول عن رعيته، فالأمير الذي على الناس راع، وهو مسئول عن رعيته رواه بسر','4','0','0');
INSERT INTO [t13141] VALUES (3,'كلكم راع، وكلكم مسئول عن رعيته، والأمير راع ومسئول عن رعيته، فأعدوا لتلك المسائل جوابا','5','0','0');
INSERT INTO [t13141] VALUES (4,'ما من أمير عشرة إلا يؤتى به يوم القيامة مغلولا حتى يفكه عنه العدل أو يوبقه','6','0','0');
INSERT INTO [t13141] VALUES (5,'ما من أمير عشرة إلا يؤتى به يوم القيامة ويده مغلولة إلى عنقه فيفكه عدله أو يهلكه جوره قال','7','0','0');
INSERT INTO [t13141] VALUES (6,'من ولي من أمر المسلمين شيئا فأمر عليهم أحدا محاباة له لا يقبل الله منه صرفا ولا عدلا، ومن','8','0','0');
INSERT INTO [t13141] VALUES (7,'من ولى ذا قرابة له محاباة وهو يجد خيرا منه لم يرح رائحة الجنة','9','0','0');
INSERT INTO [t13141] VALUES (8,'من ولى واليا فبلغه عنه ظلم لرعيته فلم يعزله، فقد خان الله ورسوله','10','0','0');
INSERT INTO [t13141] VALUES (9,'أيما وال لقي الله وهو غاش لرعيته حرم الله عليه أن يدخل الجنة قال أبو نعيم: وتقرير','11','0','0');
INSERT INTO [t13141] VALUES (10,'من ولي من أمر أمتي شيئا فحسنت سريرته رزق الهيبة، وإذا بسط يده بالمعروف رزق المحبة، وإذا','12','0','0');
INSERT INTO [t13141] VALUES (11,'من ولي من أمر الناس ولاية، وكانت نيته الحق وكل به ملكان يوفقانه ويرشدانه، ومن ولي من أمر','13','0','0');
INSERT INTO [t13141] VALUES (12,'عدل ساعة خير من عبادة ستين سنة، قيام ليلها، وصيام نهارها، وجور ساعة في حكم أشد وأعظم من','14','0','0');
INSERT INTO [t13141] VALUES (13,'يوم من إمام عادل أفضل من عبادة ستين سنة، وحد يقام في الأرض أزكى فيها من قطر أربعين','15','0','0');
INSERT INTO [t13141] VALUES (14,'لعمل العادل في رعيته يوما واحدا أفضل من عمل العابد في أهله مائة عام أو خمسين عاما الشك من','16','0','0');
INSERT INTO [t13141] VALUES (15,'الوالي العادل المتواضع ظل الله ورمحه في الأرض، فمن نصحه في نفسه وفي عباد الله حشره الله','17','0','0');
INSERT INTO [t13141] VALUES (16,'أحب الناس إلى الله وأقربهم منه مجلسا يوم القيامة إمام عادل، وأبغض الناس إلى الله يوم','18','0','0');
INSERT INTO [t13141] VALUES (17,'المقسطون على منابر من نور، هم الذين يعدلون في حكمهم وأهلهم وما ولوا','19','0','0');
INSERT INTO [t13141] VALUES (18,'المقسطين في الدنيا على منابر من لؤلؤ يوم القيامة بين يدي الرحمن بما أقسطوا في','20','0','0');
INSERT INTO [t13141] VALUES (19,'أفضل الشهداء عند الله المقسطون','21','0','0');
INSERT INTO [t13141] VALUES (20,'ثلاثة لا ترد دعوتهم: الإمام العادل، والصائم حتى يفطر، ودعوة المظلوم تحمل على الغمام،','22','0','0');
INSERT INTO [t13141] VALUES (21,'ثلاثة لا يرد دعاؤهم: الذاكر لله كثيرا، ودعوة المظلوم، ودعوة الإمام','23','0','0');
INSERT INTO [t13141] VALUES (22,'في الجنة درجة لا يبلغها إلا إمام عادل، أو ذو رحم وصول، أو ذو عيال صبور، فقال له علي: يا','24','0','0');
INSERT INTO [t13141] VALUES (23,'أهل الجنة ثلاثة: ذو سلطان مقسط مصدق موفق، ورجل رحيم القلب بكل ذي قربى ومسلم، ورجل فقير','25','0','0');
INSERT INTO [t13141] VALUES (24,'في الجنة قصرا، حوله البروج والمروج، له خمسة آلاف باب، لا يدخله أو لا يسكنه إلا نبي أو','26','0','0');
INSERT INTO [t13141] VALUES (25,'يقال للإمام العادل في قبره: أبشر، فإنك رفيق محمد صلى الله عليه وسلم قال أبو نعيم: ومن','27','0','0');
INSERT INTO [t13141] VALUES (26,'ما من الناس أحد أعظم أجرا من وزير صالح يكون مع إمام فيأمره بذات الله','28','0','0');
INSERT INTO [t13141] VALUES (27,'ما من أحد ولي من أمر المسلمين أمرا، فأراد الله به خيرا، إلا جعل معه وزيرا صالحا إن نسي','29','0','0');
INSERT INTO [t13141] VALUES (28,'السلطان ظل الله في أرضه من نصحه هدي، ومن غشه ضل','30','0','0');
INSERT INTO [t13141] VALUES (29,'السلطان ظل الله في الأرض، فمن نصحهم ودعا لهم اهتدى، ومن غشهم ودعا عليهم ضل قال أبو نعيم:','31','0','0');
INSERT INTO [t13141] VALUES (30,'سبعة يظلهم الله يوم القيامة في ظله يوم لا ظل إلا ظله: إمام عادل وشاب نشأ في عبادة','32','0','0');
INSERT INTO [t13141] VALUES (31,'سبعة يظلهم الله في ظل عرشه يوم القيامة: إمام مقسط ورجل يتصدق بيمينه يخفيها عن','33','0','0');
INSERT INTO [t13141] VALUES (32,'سبعة في ظل العرش يوم لا ظل إلا ظله: إمام مقسط ورجل دعته امرأة ذات حسب وجمال إلى نفسها','34','0','0');
INSERT INTO [t13141] VALUES (33,'اثنان من الناس إذا صلحا صلح الناس، وإذا فسدا فسد الناس: العلماء','35','0','0');
INSERT INTO [t13141] VALUES (34,'لن يزالوا بخير ما استقامت لهم ولاتهم وهداتهم','36','0','0');
INSERT INTO [t13141] VALUES (35,'لن تهلك الرعية وإن كانت ظالمة سيئة إذا كانت الولاة هادية مهدية، ولكن تهلك الرعية وإن كانت','37','0','0');
INSERT INTO [t13141] VALUES (36,'الإسلام والسلطان أخوان توأم، لا يصلح واحد منهما إلا بصاحبه، فالإسلام أس والسلطان حارس،','38','0','0');
INSERT INTO [t13141] VALUES (37,'ظل الله في الأرض، فإن أحسنوا فلهم الأجر وعليكم الشكر، وإن أساءوا فعليكم الصبر وعليهم','39','0','0');
INSERT INTO [t13141] VALUES (38,'لا تسبوا السلطان فإنه ظل الله في الأرض، به يقيم الله الحق، ويظهر الدين، وبه يرفع الله','40','0','0');
INSERT INTO [t13141] VALUES (39,'الإمام جنة يقاتل من ورائه، ويتقى به، فإن أمر بتقوى الله وعدل فإن له بذلك أجرا، وإن أمر','41','0','0');
INSERT INTO [t13141] VALUES (40,'سيليكم أمراء يفسدون وما يصلح الله بهم أكثر، فمن عمل منهم بطاعة الله فلهم الأجر وعليكم','42','0','0');
INSERT INTO [t13141] VALUES (41,'ويل لديان من في الأرض من ديان من في السماء يوم يلقونه إلا من أمر بالعدل وقضى بالحق ولم','43','0','0');
INSERT INTO [t13141] VALUES (42,'مثلك مثل رجل استأجر أجيرا فولاه ماشيته وجعل له الأجر على أن يحسن الرعية، ويوفر جزازها','44','0','0');
INSERT INTO [t13141] VALUES (43,'أعطاك الدنيا بكمالها. فاشتر نفسك منه ببعضها، واعلم أنك واقف بين يديه، وسائلك عن مثاقيل','45','0','0');
INSERT INTO [t13141] VALUES (44,'من جلس على وسادة الأمير فقد وجبت عليه النصيحة لله ولرسوله ولجماعة','46','0','0');
INSERT INTO [t13141] VALUES (45,'ابن آدم وعاء، فمن جعل فيه شيء كان، ولو كانت لي دعوة مستجابة جعلتها في الإمام زادني غيره:','47','0','0');
INSERT INTO [t13141] VALUES (46,'إذا ظلم أو هم بظلم ذهبت البركة، قال الملك: أنى عرفت ذلك؟ قال: هو ذاك كما قلت لك، قال:','48','0','0');
