INSERT INTO [t13169] VALUES (1,'صحبت رسول الله صلى الله عليه وسلم في الحضر والسفر، فكان في الحضر إذا صلى الظهر صلى أربعا،','1','0','0');
INSERT INTO [t13169] VALUES (2,'يتوضأ وضوءه للصلاة، وكان ابن عمر إذا أراد أن يطعم يتوضأ','2','0','0');
INSERT INTO [t13169] VALUES (3,'صليت مع رسول الله صلى الله عليه وسلم في الحضر والسفر، فصليت معه في الحضر الظهر أربعا،','3','0','0');
INSERT INTO [t13169] VALUES (4,'من اقتنى كلبا إلا كلب صيد، أو كلب ماشية، أو كلب مخافة، نقص من عمله كل يوم قيراطان، قيل:','4','0','0');
INSERT INTO [t13169] VALUES (5,'مثنى مثنى، فإن خشي الصبح فواحدة','5','0','0');
INSERT INTO [t13169] VALUES (6,'لا تمسح من هذه الأركان إلا الركن اليماني والركن الذي فيه الحجر، قال: رأيت رسول الله صلى','6','0','0');
INSERT INTO [t13169] VALUES (7,'لا تبيعوا الثمرة حتى يبدو صلاحها','7','0','0');
INSERT INTO [t13169] VALUES (8,'نهى رسول الله صلى الله عليه وسلم عن الجر والدباء؟ قال: نعم','8','0','0');
INSERT INTO [t13169] VALUES (9,'تشهد أن لا إله إلا الله وحده لا شريك له، وأن محمدا رسوله، قال: فإذا فعلت ذلك فأنا مسلم،','9','0','0');
INSERT INTO [t13169] VALUES (10,'يعلمنا التشهد في الصلاة كما يعلم المكتب الولدان','10','0','0');
INSERT INTO [t13169] VALUES (11,'توضئوا من لحوم الإبل، ولا توضئوا من لحوم الغنم، وتوضئوا من ألبان الإبل، ولا توضئوا من','11','0','0');
INSERT INTO [t13169] VALUES (12,'إن أهل السماء لا يسمعون شيئا من أهل الأرض إلا الأذان','12','0','0');
INSERT INTO [t13169] VALUES (13,'المؤذنون أطول الناس أعناقا يوم القيامة، وهم أول من يكسى','13','0','0');
INSERT INTO [t13169] VALUES (14,'أبغض الحلال إلى الله عز وجل الطلاق','14','0','0');
INSERT INTO [t13169] VALUES (15,'سماهم الله الأبرار لأنهم بروا الآباء والأبناء، كما أن لوالديك عليك حقا كذلك لولدك عليك','15','0','0');
INSERT INTO [t13169] VALUES (16,'ما زال رسول الله صلى الله عليه وسلم يوصي بالصلاة وما ملكت أيمانكم حتى انكسر','16','0','0');
INSERT INTO [t13169] VALUES (17,'إن مسحهما يحطان الخطايا حطا','17','0','0');
INSERT INTO [t13169] VALUES (18,'النميمة وهي الكذب، والسخيمة والحمية في النار، فلا يجتمعان في صدر مسلم','18','0','0');
INSERT INTO [t13169] VALUES (19,'لعن النائحة والمستمعة، والحالقة والصالقة، والواشمة والموشومة وقال: ليس للنساء في اتباع','19','0','0');
INSERT INTO [t13169] VALUES (20,'النميمة والشتيمة والحمية في النار، فلا يجتمعان في صدر مؤمن','20','0','0');
INSERT INTO [t13169] VALUES (21,'إذا ضن الناس بالدينار والدرهم، واتبعوا أذناب البقر، وتركوا الجهاد في سبيل الله، وتبايعوا','21','0','0');
INSERT INTO [t13169] VALUES (22,'لا يتعار ساعة من الليل إلا أجرى السواك على فيه','22','0','0');
INSERT INTO [t13169] VALUES (23,'إن أمتي هذه توفي سبعين أمة نحن آخرها وخيرها','23','0','0');
INSERT INTO [t13169] VALUES (24,'من أذن فهو أحق أن يقيم','24','0','0');
INSERT INTO [t13169] VALUES (25,'يدنو المؤمن من ربه يوم القيامة كأنه بذج - قال أبو النضر: قال قتادة: البذج: السخلة - فيضع','25','0','0');
INSERT INTO [t13169] VALUES (26,'من خلع الطاعة لقي الله يوم القيامة لا حجة له، ومن مات لا طاعة عليه مات ميتة جاهلية، قال','26','0','0');
INSERT INTO [t13169] VALUES (27,'يقتل هذا مظلوما، فنظرت إليه فإذا هو عثمان بن عفان رحمه الله','27','0','0');
INSERT INTO [t13169] VALUES (28,'لا تساكنوا الأنباط في بلادهم، فإن نازعوكم الكلام واختبئوا في الأقنية فالهرب الهرب، ولا','28','0','0');
INSERT INTO [t13169] VALUES (29,'ليبلغ شاهدكم غائبكم، لا صلاة بعد طلوع الفجر إلا ركعتين','29','0','0');
INSERT INTO [t13169] VALUES (30,'ما رأيت رسول الله صلى الله عليه وسلم مفطرا قط، يعني يوم الجمعة','30','0','0');
INSERT INTO [t13169] VALUES (31,'يبلغ العرق من بني آدم، فقال أحدهما: إلى شحمة أذنه، وقال الآخر: يلجمه','31','0','0');
INSERT INTO [t13169] VALUES (32,'يقتل الغراب، والحدأة، والفأرة، والكلب العقور، والعقرب، فقلت لنافع: فالحيات؟ قال: لا يختلف','32','0','0');
INSERT INTO [t13169] VALUES (33,'من ابتاع نخلا قد أبرت - سألته، فقال: لقحت - فثمره للبائع، إلا أنه يشترط','33','0','0');
INSERT INTO [t13169] VALUES (34,'قطع في مجن ثمنه ثلاثة دراهم','34','0','0');
INSERT INTO [t13169] VALUES (35,'نهى عن المزابنة: أن يبيع الرجل ثمر أرضه بكيل إن زاد فلا، وإن نقص فعلى','35','0','0');
INSERT INTO [t13169] VALUES (36,'رخص في بيع العرايا بخرصها','36','0','0');
INSERT INTO [t13169] VALUES (37,'إن عبد الله رجل صالح','37','0','0');
INSERT INTO [t13169] VALUES (38,'إن العبد إذا أحسن عبادة ربه ونصح لسيده يؤتى أجره مرتين','38','0','0');
INSERT INTO [t13169] VALUES (39,'ما تركت استلام الحجر في رخاء ولا شدة منذ رأيت رسول الله صلى الله عليه وسلم','39','0','0');
INSERT INTO [t13169] VALUES (40,'إذا راح أحدكم إلى الجمعة فليغتسل','40','0','0');
INSERT INTO [t13169] VALUES (41,'من قتل مؤمنا متعمدا فقد كفر بالله','41','0','0');
INSERT INTO [t13169] VALUES (42,'كل مسكر خمر','42','0','0');
INSERT INTO [t13169] VALUES (43,'أهللنا مع رسول الله صلى الله عليه وسلم بالحج مفردا','43','0','0');
INSERT INTO [t13169] VALUES (44,'إن الرجل ليكون من أهل الصلاة والزكاة والحج والعمرة والصيام والجهاد، حتى ذكر سهام الخير،','44','0','0');
INSERT INTO [t13169] VALUES (45,'على المرء المسلم السمع والطاعة فيما أحب وكره','45','0','0');
INSERT INTO [t13169] VALUES (46,'بادروا الصبح بالوتر','46','0','0');
INSERT INTO [t13169] VALUES (47,'لا يلبس القميص، ولا العمامة، ولا السراويلات، ولا البرانس، ولا الخفاف، إلا أن لا يجد نعلين','47','0','0');
INSERT INTO [t13169] VALUES (48,'أمرنا رسول الله صلى الله عليه وسلم في أطراف المدينة أن نقتل الكلاب، فلقد رأيتنا نقتل','48','0','0');
INSERT INTO [t13169] VALUES (49,'لا يحلبن أحدكم ماشية أحد إلا بإذن أهله، أيحب أحدكم أن تؤتى خزانته فتكسر فينتثل ما فيها،','49','0','0');
INSERT INTO [t13169] VALUES (50,'المحرم لا ينكح ولا ينكح','50','0','0');
INSERT INTO [t13169] VALUES (51,'في أربع وعشرين من الإبل خمس شياه إلى أن تبلغ خمسا وعشرين، فإذا بلغت خمسا وعشرين ففيها','51','0','0');
INSERT INTO [t13169] VALUES (52,'كانت في كل أربعين شاة شاة إلى عشرين ومائة، فإذا زادت فشاتان إلى مائتين، فإذا زادت فثلاث','52','0','0');
INSERT INTO [t13169] VALUES (53,'كل مسكر خمر، وكل مسكر حرام','53','0','0');
INSERT INTO [t13169] VALUES (54,'كان يوما يعظمه أهل الجاهلية','54','0','0');
INSERT INTO [t13169] VALUES (55,'ما حق امرئ مسلم له شيء يوصي فيه يبيت ليلتين إلا وعنده وصية','55','0','0');
INSERT INTO [t13169] VALUES (56,'إذا كان في السفر في الليلة الباردة أو الليلة المطيرة، فينادي بالصلاة صلاة العشاء، ثم','56','0','0');
INSERT INTO [t13169] VALUES (57,'من خلع يدا من طاعة لقي الله لا حجة له، ومن مات ليس في رقبته بيعة مات موتة','57','0','0');
INSERT INTO [t13169] VALUES (58,'رجم يهوديا ويهودية','58','0','0');
INSERT INTO [t13169] VALUES (59,'يجمع بين صلاة المغرب والعشاء الآخرة في السفر وهو على ظهر، ويقول ابن عمر: كان رسول الله','59','0','0');
INSERT INTO [t13169] VALUES (60,'من ترك صلاة يعني العصر، حتى تفوته فكأنما وتر أهله وماله، وكان ابن عمر يصليها والشمس بيضاء','60','0','0');
INSERT INTO [t13169] VALUES (61,'صلاة الليل ركعتين ركعتين، فإن خفت الصبح فأوتر بواحدة','61','0','0');
INSERT INTO [t13169] VALUES (62,'من حمل علينا السلاح فليس منا','62','0','0');
INSERT INTO [t13169] VALUES (63,'رأيت رسول الله صلى الله عليه وسلم يبرز بين لبنتين وهو مستقبل القبلة وهو على ظهر بيت، قال','63','0','0');
INSERT INTO [t13169] VALUES (64,'مثنى مثنى، فإذا خفت الصبح فأوتر بواحدة توتر لك صلاتك','64','0','0');
INSERT INTO [t13169] VALUES (65,'إني قد اتخذت خاتما، وإني نقشت فيه محمد رسول الله صلى الله عليه وسلم فلا ينقش أحد','65','0','0');
INSERT INTO [t13169] VALUES (66,'نهى عن تلقي السلع حتى يهبط بها الأسواق، ونهى عن التناجش','66','0','0');
INSERT INTO [t13169] VALUES (67,'نهى عن البرنس؟','67','0','0');
INSERT INTO [t13169] VALUES (68,'اللهم بارك لنا في مكتنا، وبارك لنا في مدينتنا، وبارك لنا في شامنا، وبارك لنا في يمننا،','68','0','0');
INSERT INTO [t13169] VALUES (69,'سيكون بعدي أمراء، فمن صدقهم بكذبهم وأعانهم على ظلمهم فليس مني، ولست منه، ولا يرد على','69','0','0');
INSERT INTO [t13169] VALUES (70,'لعل هوام رأسك آذتك؟ قال: نعم، قال: احلق رأسك وافتد قال: فافتديت ببقرة وقلدتها','70','0','0');
INSERT INTO [t13169] VALUES (71,'ما الإسلام؟ قال: شهادة أن لا إله إلا الله وأن محمدا رسول الله صلى الله عليه وسلم، وتصلي','71','0','0');
INSERT INTO [t13169] VALUES (72,'يا ابن آدم، اثنتان لم يكن لك واحدة منهما: جعلت لك نصيبا من مالك حين أخذت بكظمك لأطهرك به','72','0','0');
INSERT INTO [t13169] VALUES (73,'لا يحل لامرأة تؤمن بالله واليوم الآخر، وتؤمن بالله ورسوله أن تحد على ميت فوق ثلاثة أيام','73','0','0');
INSERT INTO [t13169] VALUES (74,'لبيك اللهم لبيك، لا شريك لك لبيك، إن الحمد والنعمة لك والملك لا شريك','74','0','0');
INSERT INTO [t13169] VALUES (75,'الليل مثنى مثنى، فإذا خشيت الصبح فواحدة توتر ما قبلها','75','0','0');
INSERT INTO [t13169] VALUES (76,'فص خاتم رسول الله صلى الله عليه وسلم في كفه','76','0','0');
INSERT INTO [t13169] VALUES (77,'اتخذ خاتما من ذهب فلبسه ثلاثة أيام، ففشت الخواتيم على أصحابه، فرمى به واتخذ خاتما من ورق','77','0','0');
INSERT INTO [t13169] VALUES (78,'البيعان بالخيار ما لم يتفرقا، إلا أن يكونا اشترطا الخيار','78','0','0');
INSERT INTO [t13169] VALUES (79,'إن من الفطرة قص الشارب وحلق العانة','79','0','0');
INSERT INTO [t13169] VALUES (80,'ينحر يوم الأضحى بالمدينة، وكان إذا لم ينحر يذبح','80','0','0');
INSERT INTO [t13169] VALUES (81,'لا يلبس القميص، ولا العمامة، ولا البرنس، ولا السراويل، ولا الخفين، إلا أن لا يجد نعلين،','81','0','0');
INSERT INTO [t13169] VALUES (82,'لا يأكل أحدكم من أضحيته فوق ثلاثة أيام، فكان عبد الله إذا غابت الشمس من اليوم الثالث لا','82','0','0');
INSERT INTO [t13169] VALUES (83,'خمس يقتلهن المحرم: الفأرة، والكلب العقور، والحدأة، والغراب، والعقرب','83','0','0');
INSERT INTO [t13169] VALUES (84,'إذا أعجله السير أخر المغرب حتى يجمع بينهما وبين العشاء الآخرة، وقد فعله ابن عمر وأنا','84','0','0');
INSERT INTO [t13169] VALUES (85,'بينما ثلاثة يمشون إذ أخذتهم السماء فأووا إلى غار في جبل فوقعت عليهم صخرة وذكر الحديث','85','0','0');
INSERT INTO [t13169] VALUES (86,'نهى عن قتل النساء والولدان يوم فتح مكة','86','0','0');
INSERT INTO [t13169] VALUES (87,'من السنة أن تأكل قبل أن تصلي','87','0','0');
INSERT INTO [t13169] VALUES (88,'من شرب الخمر في الدنيا لم يشربها في الآخرة، إلا أن يتوب','88','0','0');
INSERT INTO [t13169] VALUES (89,'يصلي راكبا، كذلك ويوتر راكبا، وقد زعم موسى، عن نافع، أن ابن عمر قال: إنه رأى النبي صلى','89','0','0');
INSERT INTO [t13169] VALUES (90,'أسامة أحب الناس إلي، ما حاشا فاطمة، ولا غيرها','90','0','0');
INSERT INTO [t13169] VALUES (91,'يدخل أهل الجنة الجنة، ويدخل أهل النار النار، فيقوم مؤذن بينهم فيقول: يا أهل الجنة لا موت،','91','0','0');
INSERT INTO [t13169] VALUES (92,'أيما عبد تزوج بغير إذن مواليه فهو زان','92','0','0');
INSERT INTO [t13169] VALUES (93,'نهى أن ينبذ البسر والرطب جميعا، والبسر والتمر جميعا','93','0','0');
INSERT INTO [t13169] VALUES (94,'من عمر ميسرة المسجد كان له كفلان من الأجر','94','0','0');
INSERT INTO [t13169] VALUES (95,'لغة النبي صلى الله عليه وسلم: أتحسب ويحسبون','95','0','0');
INSERT INTO [t13169] VALUES (96,'لبيك اللهم لبيك، لبيك لا شريك لك لبيك، إن الحمد والنعمة لك والملك لا شريك لك. قال:','96','0','0');
