INSERT INTO [t13171] VALUES (1,'مره فليراجعها، ثم يمسكها حتى تطهر من حيضتها، إن شاء طلقها وإن شاء أمسكها عنده، وهي واحدة','1','0','0');
INSERT INTO [t13171] VALUES (2,'ارتجعها حتى تطهر ثم تحيض، فإنها العدة التي أمر الله بها','2','0','0');
INSERT INTO [t13171] VALUES (3,'مر عبد الله فليراجعها ثم ليمسكها حتى تطهر من حيضتها هذه، ثم تحيض حيضة أخرى، فإذا طهرت فإن','3','0','0');
INSERT INTO [t13171] VALUES (4,'مره فليراجعها فإذا طهرت ثم حاضت، ثم طهرت، فإن بدا له أن يمسكها فليمسكها، وإن بدا له أن','4','0','0');
INSERT INTO [t13171] VALUES (5,'يرجعها ثم يمسكها حتى تطهر ثم تحيض ثم تطهر ثم تحيض فإذا طهرت طلقها إن شاء، فتلك العدة التي','5','0','0');
INSERT INTO [t13171] VALUES (6,'فأمره أن يراجعها، ثم يمسكها حتى تطهر، ثم يمهلها حتى تحيض عنده حيضة، ثم يمهلها حتى تطهر،','6','0','0');
INSERT INTO [t13171] VALUES (7,'مره فليراجعها، ثم يمسكها حتى تطهر ثم تحيض ثم تطهر، ثم إن شاء طلق','7','0','0');
INSERT INTO [t13171] VALUES (8,'فليراجعها ثم ليمسكها حتى تطهر ثم تحيض ثم إن شاء أمسك بعد ذلك، وإن شاء طلق قبل أن يمس،','8','0','0');
INSERT INTO [t13171] VALUES (9,'يراجعها ثم يمسكها حتى تطهر ثم تحيض حيضة أخرى، ثم يمهلها حتى تطهر من حيضتها فإن أراد أن','9','0','0');
INSERT INTO [t13171] VALUES (10,'يراجعها ويمسكها حتى تطهر، ثم تحيض عنده حيضة أخرى، ثم يمهلها حتى تطهر من حيضتها، فإن أراد','10','0','0');
INSERT INTO [t13171] VALUES (11,'أمر أن يراجعها في طلاق بقي له، فأنت لم يبق لك ما ترجع به امرأتك','11','0','0');
INSERT INTO [t13171] VALUES (12,'بئس ما صنع، مره فليراجعها فإذا طهرت فليطلقها طاهرا في غير جماع','12','0','0');
INSERT INTO [t13171] VALUES (13,'فأمره أن يراجعها، ثم يمسكها حتى تطهر فإن بدا له أن يطلقها قبل أن','13','0','0');
INSERT INTO [t13171] VALUES (14,'التقى آدم وموسى، فقال له موسى: أنت آدم الذي خلقك الله بيده، وأسجد لك ملائكته، وأدخلك','14','0','0');
INSERT INTO [t13171] VALUES (15,'لو لم يكن الحجر من البيت ما طفت','15','0','0');
INSERT INTO [t13171] VALUES (16,'لا تتخذوا من وراء الروحا مالا ولا ترتدوا على أعقابكم بعد الهجرة، ولا تزوجوا طلقاء مكة','16','0','0');
INSERT INTO [t13171] VALUES (17,'من كان له بخيبر نصيب فليحضر حتى نقسمها بينكم، فإنهم قد فعلوا وفعلوا، وغشوا المسلمين','17','0','0');
INSERT INTO [t13171] VALUES (18,'ما بلت قائما منذ أسلمت','18','0','0');
INSERT INTO [t13171] VALUES (19,'لا تمنعوا إماء الله مساجد الله عز وجل','19','0','0');
INSERT INTO [t13171] VALUES (20,'لا تمنعوا إماء الله مساجد الله','20','0','0');
INSERT INTO [t13171] VALUES (21,'لا تمنعوا إماء الله مساجد الله لم يذكر عمر','21','0','0');
INSERT INTO [t13171] VALUES (22,'فرض عمر لأسامة أكثر مما فرض لي، فقلت له: هجرتي وهجرته واحدة، قال: فإنه كان أحب إلى رسول','22','0','0');
INSERT INTO [t13171] VALUES (23,'لأن أسامة كان أحب إلى رسول الله صلى الله عليه وسلم منك','23','0','0');
INSERT INTO [t13171] VALUES (24,'خيرت: {استغفر لهم أو لا تستغفر لهم} [التوبة: ٨٠] ، قال: فنزلت: {ولا تصل على أحد منهم مات}','24','0','0');
INSERT INTO [t13171] VALUES (25,'خيرني فقال: إن شئت {استغفر لهم أو لا تستغفر لهم إن تستغفر لهم سبعين مرة} [التوبة: ٨٠]','25','0','0');
INSERT INTO [t13171] VALUES (26,'أسهم يوم خيبر للفارس ثلاثة أسهم، له سهم ولفرسه سهمان','26','0','0');
INSERT INTO [t13171] VALUES (27,'سابق بين الخيل المضمرة من ثنية الوداع إلى الحفياء، وأرسل التي لم تضمر إلى مسجد بني','27','0','0');
INSERT INTO [t13171] VALUES (28,'أوضع في وادي محسر قال: حسين رفعه مرة وأحيانا لم يرفعه','28','0','0');
INSERT INTO [t13171] VALUES (29,'ضرب لليهود والنصارى والمجوس أجلا: ثلاثة أيام بالمدينة، يبيعون فيها، فزعم أن عمر أجلاهم من','29','0','0');
INSERT INTO [t13171] VALUES (30,'لا يجتمع دينان في جزيرة العرب','30','0','0');
INSERT INTO [t13171] VALUES (31,'لو بلغنا ثلاثمائة، لقد أخرجناكم منها، قال ابن عمر، فقلت لعمر: من الرجل يا أبت الذي دخل؟','31','0','0');
INSERT INTO [t13171] VALUES (32,'تعلموا من النجوم ما تهتدوا به في ظلمات البر والبحر، ثم انتهوا، وتعلموا من الأنساب قدر ما','32','0','0');
INSERT INTO [t13171] VALUES (33,'أرأيت الرجل يكون مع الرجل وليدا أو يكون معه غلاما أو يكون معه يافعا ويكون معه شبابا','33','0','0');
INSERT INTO [t13171] VALUES (34,'في خمس من الإبل شاة، وفي عشر شاتان، وفي خمس عشرة ثلاث، وفي عشرين أربع، وفي خمس وعشرين','34','0','0');
INSERT INTO [t13171] VALUES (35,'إن شئت حبست أصلها وتصدقت بها، فتصدق بها عمر، أنه لا يباع أصلها، ولا يوهب ولا يورث قال:','35','0','0');
INSERT INTO [t13171] VALUES (36,'حبس أصله وسبل ثمره','36','0','0');
INSERT INTO [t13171] VALUES (37,'احبس الأصل، وسبل الثمرة','37','0','0');
INSERT INTO [t13171] VALUES (38,'احبس أصلها، وسبل ثمرها','38','0','0');
INSERT INTO [t13171] VALUES (39,'احبس أصلها، واجعل ثمرها صدقة','39','0','0');
INSERT INTO [t13171] VALUES (40,'أصبت مالا نفيسا أتصدق به قال: فجعلها صدقة لا تباع ولا توهب ولا تورث، يليها ذوو الرأي من','40','0','0');
INSERT INTO [t13171] VALUES (41,'عبد الله بن عمر كان يهدي إلى عبد الله بن صفوان بن أمية، ٥٢ - حدثنا الحسن بن علي، ثنا','41','0','0');
INSERT INTO [t13171] VALUES (42,'تصدق بثمره وحبس أصله، لا يباع ولا يورث،','42','0','0');
INSERT INTO [t13171] VALUES (43,'لا يأكل أحدكم بشماله ولا يشرب بشماله، فإن الشيطان يأكل بشماله، ويشرب بشماله ٥٧ - حدثنا','43','0','0');
INSERT INTO [t13171] VALUES (44,'لأعلم أنك حجر، ولكني رأيت رسول الله صلى الله عليه وسلم يقبلك لفظ','44','0','0');
INSERT INTO [t13171] VALUES (45,'لأقبلك، وإني لأعلم أنك حجر، ولكني رأيت رسول الله صلى الله عليه وسلم','45','0','0');
INSERT INTO [t13171] VALUES (46,'أعتق رسول الله صلى الله عليه وسلم أسارى حنين','46','0','0');
INSERT INTO [t13171] VALUES (47,'صلوا في الرحال','47','0','0');
INSERT INTO [t13171] VALUES (48,'أريد أن أزيد في قبلتها ما زدت، قال العمري: فزاد ما بين المنبر والمقصورة لفظ','48','0','0');
INSERT INTO [t13171] VALUES (49,'ينهاكم أن تحلفوا بآبائكم، ليحلف حالف بالله أو ليصمت','49','0','0');
INSERT INTO [t13171] VALUES (50,'أينام أحدنا وهو جنب؟ قال: نعم، إذا توضأ','50','0','0');
INSERT INTO [t13171] VALUES (51,'لا تجعلوا صدقة النساء أكثر من أربع مائة','51','0','0');
INSERT INTO [t13171] VALUES (52,'فرض للحسن والحسين رضي الله عنهما كما فرض للمهاجرين','52','0','0');
INSERT INTO [t13171] VALUES (53,'إذا جعلنا شيئا في سبيل الله أتينا به النبي صلى الله عليه وسلم فيضعه حيث','53','0','0');
INSERT INTO [t13171] VALUES (54,'إن استخلفت فقد استخلف من هو خير مني، وإن أترك فقد ترك من هو خير مني','54','0','0');
INSERT INTO [t13171] VALUES (55,'سبع مواطن لا يجوز فيها الصلاة: ظهر بيت الله عز وجل، والمقبرة، والمزبلة، وأعطان الإبل،','55','0','0');
INSERT INTO [t13171] VALUES (56,'ما أسكر كثيره فقليله حرام','56','0','0');
INSERT INTO [t13171] VALUES (57,'لتتوضأ ليس فيه ابن عمر','57','0','0');
INSERT INTO [t13171] VALUES (58,'كل مسكر حرام، كل مسكر خمر ليس فيه عمر','58','0','0');
INSERT INTO [t13171] VALUES (59,'ما أسكر كثيره فقليله حرام','59','0','0');
INSERT INTO [t13171] VALUES (60,'لا يخلون رجل بامرأة، فإن الشيطان ثالثهما','60','0','0');
INSERT INTO [t13171] VALUES (61,'إذا صلى أحدكم فليصل في ثوبين؛ فإن الله أحق من تزين له، وإن لم يكن إلا ثوب واحد فليتزر به،','61','0','0');
INSERT INTO [t13171] VALUES (62,'لما اجتمعنا للهجرة اتعدت أنا وعياش بن أبي ربيعة، وهشام بن العاص بن وائل، فقلنا: الميعاد','62','0','0');
INSERT INTO [t13171] VALUES (63,'من ابتاع نخلا مؤبرا فثمرته للبائع، إلا أن يشترط المبتاع','63','0','0');
INSERT INTO [t13171] VALUES (64,'إن الميت يعذب ببكاء أهله عليه فلا يبك أحد منكم علينا','64','0','0');
INSERT INTO [t13171] VALUES (65,'عامل يهود أهل خيبر على أن يجليهم إذا شاء، وقد عدوا على عبد الله بن عمر مع عدوهم على','65','0','0');
INSERT INTO [t13171] VALUES (66,'يحب الصوت الخفيض، ويبغض الصوت الرفيع','66','0','0');
