INSERT INTO [t13172] VALUES (1,'مسند عمر بن الخطاب ليعقوب بن شيبة بسم الله الرحمن الرحيم توكلت على الله وحده أخبرنا أبو عمر عبد الواحد بن محمد بن عبد الله بن محمد بن مهدي قراءة عليه , قال: أخبرنا أبو بكر محمد بن أحمد بن يعقوب بن شيبة قراءة عليه , قال: ثنا جدي , قال','1','0','0');
INSERT INTO [t13172] VALUES (2,'وأما الحديث البصري فرواه خالد الحذاء , عن بركة عن ابن عباس , عن النبي صلى الله عليه وسلم رواه عنه بشر بن المفضل. ولم يذكر فيه عمر , مخالفا فيه أهل مكة , وأهل الكوفة. وبركة هذا هو أبو العريان المجاشعي، ولا نحفظ أحدا روى عن هذا الشيخ غير خالد الحذاء هذا','2','0','0');
INSERT INTO [t13172] VALUES (3,'فأما ما سمعنا في، رواية ابن عيينة , وحماد بن زيد , عن عمرو بن دينار ثنا محمد , قال: ثنا جدي , قال: فحدثني أحمد بن العباس، قال: قلت لأبي زكريا يحيى بن معين: أيهما أحفظ , ابن عيينة , أو حماد بن زيد؟ فقال: جميعا سواء ثنا محمد , قال: ثنا جدي , قال:','3','0','0');
INSERT INTO [t13172] VALUES (4,'وأما حديث أهل مكة حديث عمرو بن دينار الذي رواه سفيان بن عيينة فأسنده','4','0','0');
INSERT INTO [t13172] VALUES (5,'وأما حديث حماد بن زيد، عن عمرو الذي نقص من إسناده ابن عباس','7','0','0');
INSERT INTO [t13172] VALUES (6,'وأما حديث أهل الكوفة الذي تفرد به شيبان عن الأعمش ووافق أهل مكة، فقال: عن ابن عباس، عن عمر، عن النبي صلى الله عليه وسلم','10','0','0');
INSERT INTO [t13172] VALUES (7,'وأما الحديث الآخر الذي رواه شيبان عن الأعمش عن جامع بن شداد، عن كلثوم، عن أسامة الذي تابعه عليه عمار بن رزيق','12','0','0');
INSERT INTO [t13172] VALUES (8,'وأما حديث عمار بن رزيق بمتابعته ما روى شيبان بن عبد الرحمن عن الأعمش','14','0','0');
INSERT INTO [t13172] VALUES (9,'وحديثه في يوم حنين أن فلانا قتل شهيدا وقد ذكر الغلول حديث حسن الإسناد رواه عكرمة بن عمار، عن أبي زميل سماك الحنفي، عن ابن عباس، عن عمر، رضي الله عنه، عن النبي صلى الله عليه وسلم، قال علي بن المديني في هذا الحديث بعينه: لا يحفظ هذا الحديث في الغلول','16','0','0');
INSERT INTO [t13172] VALUES (10,'وحديثه في حاطب بن أبي بلتعة حين كتب إلى أهل مكة حديث حسن الإسناد رواه أيضا عكرمة بن عمار عن سماك أبي زميل، عن ابن عباس، عن عمر، رضي الله عنه، قال علي بن المديني في هذا الحديث بعينه: لا نعلمه روي عن عمر، عن النبي صلى الله عليه وسلم إلا من هذا الوجه','18','0','0');
INSERT INTO [t13172] VALUES (11,'وحديثه أن النبي صلى الله عليه وسلم صالح أهل مكة يوم الحديبية حديث حسن الإسناد وهو أيضا مما تفرد بروايته عكرمة بن عمار، وما قل أيضا من رواه عن عكرمة','20','0','0');
INSERT INTO [t13172] VALUES (12,'وحديثه في قصة الأسرى يوم بدر ومشاورة النبي صلى الله عليه وسلم بعض أصحابه فيهم هو حديث حسن الإسناد ولا نحفظه عن عمر إلا من هذا الطريق، رواه عكرمة بن عمار، عن أبي زميل، عن ابن عباس، عن عمر، ورواه عن عكرمة أبو حذيفة وعبد الله بن المبارك، وعمر بن يونس','22','0','0');
INSERT INTO [t13172] VALUES (13,'وأما حديث أبي حذيفة، عن عكرمة بن عمار الذي اختصره وجعله، عن عمر، عن النبي صلى الله عليه وسلم','24','0','0');
INSERT INTO [t13172] VALUES (14,'وأما حديث عبد الله بن المبارك الذي وافق فيه أبا حذيفة وجاء به أتم','26','0','0');
INSERT INTO [t13172] VALUES (15,'وأما حديث عمر بن يونس الذي فصله وبينه، فجعل بعضه عن عمر، عن النبي صلى الله عليه وسلم، وبعضه عن ابن عباس، عن النبي صلى الله عليه وسلم','28','0','0');
INSERT INTO [t13172] VALUES (16,'وأما حديث قراد بموافقته أبا حذيفة، وابن المبارك في الإسناد وزيادته عليهما وعلى عمر بن يونس ذكر يوم أحد','30','0','0');
INSERT INTO [t13172] VALUES (17,'وحديثه في اعتزال النبي صلى الله عليه وسلم نساءه هو حديث حسن الإسناد وقد أخرجناه بطوله فيما تقدم قبل هذا بأحاديث يسيرة، ونأتي به هاهنا مختصرا إن شاء الله','32','0','0');
INSERT INTO [t13172] VALUES (18,'وحديثه عن النبي صلى الله عليه وسلم، " أتاني آت من ربي عز وجل، فأمرني أن أصلي في الوادي المبارك " حديث حسن الإسناد، وهو صحيح رواه علي بن المبارك، والأوزاعي جميعا، عن يحيى بن أبي كثير، عن عكرمة، عن ابن عباس، عن عمر، عن النبي صلى الله عليه وسلم،','34','0','0');
INSERT INTO [t13172] VALUES (19,'فأما ما رواه يحيى بن أبي كثير، عن الأوزاعي','35','0','0');
INSERT INTO [t13172] VALUES (20,'وأما ما رواه مالك بن أنس، عنه','37','0','0');
INSERT INTO [t13172] VALUES (21,'وأما ما رواه عنه الثوري','39','0','0');
INSERT INTO [t13172] VALUES (22,'فأما حديث علي بن المبارك ثنا محمد , قال: ثنا جدي , قال: ثناه حجاج بن نصير الفساطيطي. سألت عنه يحيى بن معين، فقال لي صاحب الفساطيط: كان شيخا صدوقا، ولكنهم أخذوا عليه أشياء في حديث شعبة، كان لا بأس به، يعني أنه أخطأ في أحاديث من أحاديث شعبة','41','0','0');
INSERT INTO [t13172] VALUES (23,'وأما حديث عبد الرحمن الأوزاعي ثنا محمد , قال: ثنا جدي , قال: ثناه إبراهيم بن موسى الصغير، قال أبو يوسف، وهو ثبت مسلم','43','0','0');
INSERT INTO [t13172] VALUES (24,'حديثه أن النبي صلى الله عليه وسلم قال: " إني ممسك بحجزكم عن النار " هو حديث حسن الإسناد غير أن في إسناده رجلا مجهولا رواه يعقوب القمي، عن حفص بن حميد، عن عكرمة عن ابن عباس، عن عمر رضي الله عنه، عن النبي صلى الله عليه وسلم. وحفص بن حميد هذا لا نعلم','46','0','0');
INSERT INTO [t13172] VALUES (25,'فأما حديث يعقوب القمي','47','0','0');
INSERT INTO [t13172] VALUES (26,'وأما ما رواه أهل المدينة فيه','50','0','0');
INSERT INTO [t13172] VALUES (27,'وأما حديث ابن عباس أخرجناه مختصرا، حتى نأتي به في موضعه إن شاء الله','54','0','0');
INSERT INTO [t13172] VALUES (28,'وأما حديث أم سلمة','57','0','0');
INSERT INTO [t13172] VALUES (29,'وأما حديث أسماء ابنة أبي بكر','60','0','0');
INSERT INTO [t13172] VALUES (30,'وحديثه في ليلة القدر حديث إسناده وسط، ليس بالثبت ولا الساقط، هو صالح رواه عاصم بن كليب، عن أبيه، عن خاله الفلتان بن عاصم، عن النبي صلى الله عليه وسلم، ورواه أيضا، عن أبيه عن ابن عباس، عن عمر عن النبي صلى الله عليه وسلم، قال علي بن المديني:','63','0','0');
INSERT INTO [t13172] VALUES (31,'فأما حديث زائدة بن قدامة','64','0','0');
INSERT INTO [t13172] VALUES (32,'وأما حديث عبد الله بن إدريس','66','0','0');
INSERT INTO [t13172] VALUES (33,'فأما حديث صالح بن عمر','68','0','0');
INSERT INTO [t13172] VALUES (34,'وأما حديث عبد الواحد بن زياد','70','0','0');
INSERT INTO [t13172] VALUES (35,'وحديثه في المال الذي كان بين يديه فقال: أما كان هذا عند الله عز وجل إذ محمد صلى الله عليه وسلم وأصحابه يأكلون القد حديث صالح الإسناد وسط رواه أيضا عاصم بن كليب، عن أبيه عن ابن عباس، عن عمر رضي الله عنه','72','0','0');
INSERT INTO [t13172] VALUES (36,'وحديثه في العاني حديث صالح الإسناد أيضا غير أنه لم يحك فيه عمر رضي الله عنه عن النبي صلى الله عليه وسلم كلاما وفي الحديث ذكر قضية النبي صلى الله عليه وسلم مجملة، رواه عبد الله بن إدريس، عن عاصم بن كليب، عن أبيه، عن عمر رضي الله عنه ولم يرو هذا الحديث','76','0','0');
INSERT INTO [t13172] VALUES (37,'فأما من رواه عن ابن إدريس، عن عاصم، عن أبيه، عن عمر رضي الله عنه','77','0','0');
INSERT INTO [t13172] VALUES (38,'وأما حديث حسين بن عبد الأول الذي زاد في إسناده رجلا','79','0','0');
INSERT INTO [t13172] VALUES (39,'وحديثه عن النبي صلى الله عليه وسلم في الصلاة بعد العصر وبعد الصبح حديث حسن الإسناد ثبت رواه قتادة، عن أبي العالية، عن ابن عباس، عن عمر رضي الله عنه عن النبي صلى الله عليه وسلم، ورواية قتادة، عن أبي العالية، مرسلة كلها إلا أربعة أحاديث سمعها من أبي','81','0','0');
