INSERT INTO [t13183] VALUES (1,'اللهم إني آمنت بك، وهاجرت إلى رسولك، فإذا أنزلت بي شدة شديدة دعوتك، ففرجتها، فأسألك اللهم','1','0','0');
INSERT INTO [t13183] VALUES (2,'مولاي أرحم بي، لا يأخذ مني ابني، وأنا صماء عمياء مقعدة، ليس لي أحد، مولاي أرحم بي من ذاك،','2','0','0');
INSERT INTO [t13183] VALUES (3,'أقبلوا على أميركم واسمعوا وأطيعوا، فمن تولى فلا يعهدن دما، كان أمر الله قدرا مقدورا، الله','3','0','0');
INSERT INTO [t13183] VALUES (4,'حضرت الوفاة رجلا من الأنصار فمات، فسجوه، ثم تكلم، فقال: أبو بكر القوي في أمر الله الضعيف','4','0','0');
INSERT INTO [t13183] VALUES (5,'مضت اثنتان وغبر أربع، فأكل غنيهم فقيرهم، فانفضوا فلا نظام لهم، أبو بكر لين رحيم','5','0','0');
INSERT INTO [t13183] VALUES (6,'محمد رسول الله صلى الله عليه وسلم الأمي، خاتم النبيين، لا نبي بعده كان ذلك في الكتاب','6','0','0');
INSERT INTO [t13183] VALUES (7,'محمد رسول الله صلى الله عليه وسلم، أبو بكر الصديق، عثمان، اللين،','7','0','0');
INSERT INTO [t13183] VALUES (8,'إني لقيت ربي عز وجل فلقيني بروح وريحان، ورب غير غضبان، وإنه كساني ثيابا خضرا من سندس','8','0','0');
INSERT INTO [t13183] VALUES (9,'يتكلم رجل من أمتي بعد الموت من خيار التابعين','9','0','0');
INSERT INTO [t13183] VALUES (10,'لم يزل متبسما على سريره ونحن نغسله حتى فرغنا منه','10','0','0');
INSERT INTO [t13183] VALUES (11,'اللهم لا تمتني حتى ترزقني غزوا في سبيلك، قال: فعاش بعد ذلك حتى قتل مع','11','0','0');
INSERT INTO [t13183] VALUES (12,'أبشروا فإني وجدت الأمر أيسر مما كنتم تخوفوني، ووجدت لا يدخل الجنة قاطع رحم، ولا مدمن خمر،','12','0','0');
INSERT INTO [t13183] VALUES (13,'فلم أرني استغفرت من ذنب إلا غفر لي، ولم أر ذنبا لم أستغفر منه إلا وجدته كما هو، قال: حتى','13','0','0');
INSERT INTO [t13183] VALUES (14,'غروني، أهلكوني، النار، أهلكوني، النار فقلنا له: قل لا إله إلا الله، قال: لا أستطيع أن','14','0','0');
INSERT INTO [t13183] VALUES (15,'قوم مخضبة لحاهم في هذا المسجد يعني: مسجد المدائن يلعنون أبا بكر وعمر رضي الله عنهما','15','0','0');
INSERT INTO [t13183] VALUES (16,'النار النار فقلت: قل لا إله إلا الله، قال: إنها ليست بنافعتي، لعن الله مشيخة بالكوفة،','16','0','0');
INSERT INTO [t13183] VALUES (17,'وثب الميت وثبة ندرت اللبنة عن بطنه، وهو ينادي بالويل والثبور، فلما رأى ذلك أصحابه تصدع','17','0','0');
INSERT INTO [t13183] VALUES (18,'أمك هبل ... ألا ترى حفرتك تنتثل وقد كادت أمك تثكل ... أرأيت إن حولناها عنك بمحول وقذفنا','18','0','0');
INSERT INTO [t13183] VALUES (19,'ولدته أمه وهي ميتة، قال: ويحك وكيف ذاك؟ قال: خرجت في بعث كذا وكذا وتركتها حاملا، وقلت:','19','0','0');
INSERT INTO [t13183] VALUES (20,'ما هذا النهيق؟ قالوا: هذا رجل كان عندنا، كانت أمه تكلمه بشيء، فيقول لها: انهقي نهيقك،','20','0','0');
INSERT INTO [t13183] VALUES (21,'ما أنت إلا حمار، ثم نهق في وجهها وقال: ها ها ها، فمات يوم مات فدفناه في تلك الحفيرة، فما','21','0','0');
INSERT INTO [t13183] VALUES (22,'إذا كلمته أمه نهق في وجهها ثلاثا، ثم قال لها: إنما أنت حمار، فمات فكان يخرج من قبره كل','22','0','0');
INSERT INTO [t13183] VALUES (23,'اللهم إني جئت من الدثينة مجاهدا في سبيلك وابتغاء مرضاتك، وإني أشهد أنك تحيي الموتى وتبعث','23','0','0');
INSERT INTO [t13183] VALUES (24,'ومنا الذي أحيا الإله حماره ... وقد مات منه كل عضو ومفصل','24','0','0');
INSERT INTO [t13183] VALUES (25,'غزونا الروم فعسكرنا، فخرج منا ناس يطلبون أثر العدو وانفرد منهم رجلان قالا: فبينا نحن كذلك','25','0','0');
INSERT INTO [t13183] VALUES (26,'رجل قد خرج من القبر يتأجج نارا، في عنقه سلسلة من نار، ومعي إداوة من ماء، فلما رآني قال:','26','0','0');
INSERT INTO [t13183] VALUES (27,'رجل في عنقه سلسلة تشتعل نارا، والسلسلة في يد شخص، فلما رأته الراحلة نفرت فجعل ينادي: يا','27','0','0');
INSERT INTO [t13183] VALUES (28,'ما هذه الريح؟ فأذن له فتكلم، فقال: قد وليت القضاء فيكم أربعين سنة فما رابني شيء إلا رجلين','28','0','0');
INSERT INTO [t13183] VALUES (29,'اللهم اغفر لعبادك الشعث الغبر الذين جاءوا من كل فج عميق، قال: فأجابه ملك آخر بأن قد غفر','29','0','0');
INSERT INTO [t13183] VALUES (30,'مرضت مرضا شديدا، حتى ظننت أنه الموت، فكان باب بيتي قبالة باب حجرتي، وكان باب حجرتي قبالة','30','0','0');
INSERT INTO [t13183] VALUES (31,'قدم علينا رجل من أهل المدينة يقال له: زياد، فغزونا سقلية من أرض الروم، قال: فحاصرنا','31','0','0');
INSERT INTO [t13183] VALUES (32,'كان فيما مضى فتية يخرجون إلى أرض الروم ويصيبون منهم، فقضي عليهم الأسر، فأخذوا جميعا، فأتى','32','0','0');
INSERT INTO [t13183] VALUES (33,'كنا في غزاة لنا، فلقينا العدو، فلما تفرقنا فقدنا رجلا من أصحابنا، فطلبناه فوجدناه في أجمة','33','0','0');
INSERT INTO [t13183] VALUES (34,'ركبت يوما إلى قبور الشهداء وكانت لا تزال تأتيهم قالت: فنزلت عند قبر حمزة، فصليت ما شاء','34','0','0');
INSERT INTO [t13183] VALUES (35,'مات أخي فلما ألحد وانصرف الناس وضعت رأسي على قبره، فسمعت صوتا ضعيفا أعرف أنه صوت أخي وهو','35','0','0');
INSERT INTO [t13183] VALUES (36,'بصوت من داخل القبر يقول: من ربك؟ ومن نبيك؟ فسمعت صوت أخي وعرفته وعرفت صفته، فقال: الله','36','0','0');
INSERT INTO [t13183] VALUES (37,'دعا بطست ودعا به، فذبحه فبدرت قطرة من دمه على الأرض فلم تزل تغلي حتى بعث الله بختنصر','37','0','0');
INSERT INTO [t13183] VALUES (38,'تلك تنزيل السجدة وهي تسع وعشرون آية سطع أولها من رأسي وأوسطها من وسطي وآخرها من رجلي، وقد','38','0','0');
INSERT INTO [t13183] VALUES (39,'النور الذي خرج من رأسي: فأربع عشرة آية من أول الم تنزيل السجدة، وأما النور الذي خرج من','39','0','0');
INSERT INTO [t13183] VALUES (40,'أنا ابن آدم، أنا أول من سفك دما في الأرض','40','0','0');
INSERT INTO [t13183] VALUES (41,'تلك طير في حواصلها أرواح آل فرعون تعرض على النار، فتلفحها فيسود ريشها، ثم يلقى ذلك الريش','41','0','0');
INSERT INTO [t13183] VALUES (42,'جزاكما الله خيرا، فإن ربي قد حولني إلى موضع كذا وكذا من الجنة حيث قضي عني','42','0','0');
INSERT INTO [t13183] VALUES (43,'واختار موسى قومه سبعين رجلا} [الأعراف: ١٥٥] قال: اختار من صالحيهم سبعين رجلا، ثم خرج بهم','43','0','0');
INSERT INTO [t13183] VALUES (44,'ألم تر إلى الذين خرجوا من ديارهم وهم ألوف حذر الموت} [البقرة: ٢٤٣] قال: كان أناس من بني','44','0','0');
INSERT INTO [t13183] VALUES (45,'أعلم أن الله على كل شيء قدير} [البقرة: ٢٥٩]','45','0','0');
INSERT INTO [t13183] VALUES (46,'جاء شابا وأولاده شيوخ','46','0','0');
INSERT INTO [t13183] VALUES (47,'فقام الشيخ ينفض رأسه يقول: قتلني ابن أخي طال عليه عمري وأراد أخذ مالي،','47','0','0');
INSERT INTO [t13183] VALUES (48,'رجلا من رجال الجاهلية ولم يكن يرى للضيف حقا','48','0','0');
INSERT INTO [t13183] VALUES (49,'رب أرني كيف تحيي الموتى قال: أولم تؤمن قال بلى، ولكن ليطمئن قلبي} [البقرة: ٢٦٠] قال: فقيل','49','0','0');
INSERT INTO [t13183] VALUES (50,'خرجت رفقة مرة يسيرون في الأرض فمروا بمقبرة، فقال بعضهم لبعض: لو صلينا ركعتين ثم دعونا','50','0','0');
INSERT INTO [t13183] VALUES (51,'فهتف نبي الله فخرج أشمط قالوا: يا روح الله وكلمته، نبئنا أنه مات وهو شاب فما هذا البياض؟','51','0','0');
INSERT INTO [t13183] VALUES (52,'شهد جنازة امرأة فلما انتهى بها إلى القبر تحركت، قال: فردت فعاشت بعد ذلك دهرا','52','0','0');
INSERT INTO [t13183] VALUES (53,'أن امرأة، من بني إسرائيل كانت حسنة التبعل لزوجها فتردى ابنان لها في بئر فماتا، فأمرت بهما','53','0','0');
INSERT INTO [t13183] VALUES (54,'الشاب من أحسنهم بلاء، ثم إنه قتل فقام رأسه واستقبل أهل المركب وهو يتلو: {تلك الدار الآخرة','54','0','0');
INSERT INTO [t13183] VALUES (55,'أبشر بالنار قتلت نفسا مؤمنة بغير حق، حتى دخل من تحت رجليه فخرج من عند رأسه وهو ينادي: ثم','55','0','0');
INSERT INTO [t13183] VALUES (56,'مت وذهب بي إلى قبري فإذا إنسان حسن الوجه طيب الريح قد وضعني في لحدي وطواه بالقراطيس، إذ','56','0','0');
