INSERT INTO [t132412] VALUES (2,'تنبيه','7','0','0');
INSERT INTO [t132412] VALUES (3,'(١) النسبة: الآبري، نسبة إلى آبر: بفتح الهمزة وسكون الألف وضم الباء الموحدة وراء: قرية من قرى سجستان.','10','0','0');
INSERT INTO [t132412] VALUES (4,'المنسوب: أبو الحسن محمد بن الحسين بن إبراهيم بن عاصم الآبري','10','3','0');
INSERT INTO [t132412] VALUES (5,'(٢) النسبة: الأبرينقي، نسبة إلى أبرينق: بفتح الهمزة وسكون الباء، وكسر الراء، وياء ساكنة، ونون مفتوحة، وقاف، من قرى مرو.','10','0','0');
INSERT INTO [t132412] VALUES (6,'والمنسوب: أبو الحسن علي بن محمد الدهان الأبرينقي، كان فقيها صالحا.','10','5','0');
INSERT INTO [t132412] VALUES (7,'(٣) النسبة: الآبسكوني، نسبة إلى آبسكون: بفتح الهمزة، وسكون الألف، وفتح الباء الموحدة، والسين المهملة ساكنة، وكاف مضمومة، وواو ساكنة، ونون، بليدة على ساحل بحر طبرستان، بينها وبين جرجان ثلاثة أيام.','10','0','0');
INSERT INTO [t132412] VALUES (8,'والمنسوب: أبو العلاء احمد بن صالح بن محمد بن صالح التميمي الآبسكوني، كان ينزل بصور على ساحل بحر الشام.','10','7','0');
INSERT INTO [t132412] VALUES (9,'(٤) النسبة: الآبلي، نسبة إلى آبل القمح: قرية من نواحي بانياس، من أعمال دمشق بين دمشق والساحل.','11','0','0');
INSERT INTO [t132412] VALUES (10,'والمنسوب: أبو طاهر الحسين بن محمد بن الحسين بن عامر بن أحمد، يعرف بابن خراشة الأنصاري، الخزرجي، المقري الآبلي، إمام جامع دمشق.','11','9','0');
INSERT INTO [t132412] VALUES (11,'(٥) النسبة: الآبي، نسبة إلى آبه: بالباء الموحدة، من قرى أصبهان، وقيل: إن آبه قرية من قرى ساوه، قال ياقوت رحمه الله: أما آبه، بليدة تقابل ساوه تعرف بين العامة بآوه، فلا شك فيها، وأهلها شيعة، وأهل ساوه سنية، لا تزال الحروب بين البلدين قائمة على المذهب.','11','0','0');
INSERT INTO [t132412] VALUES (12,'والمنسوب: جرير بن عبد الحميد الآبي سكن الري، نسب إلى آبة أصبهان. والوزير أبو سعد منصور بن الحسين الآبي.','11','11','0');
INSERT INTO [t132412] VALUES (13,'(٦) النسبة: الآجري، نسبة إلى الآجر: بضم الجيم، وتشديد الراء: بلغة أهل مصر الطوب، وبلغة أهل الشام القرميد. درب الآجر: محلة كانت ببغداد من محال نهر طابق بالجانب الغربي، سكنها غير واحد من أهل العلم.','12','0','0');
INSERT INTO [t132412] VALUES (14,'والمنسوب: أبو بكر محمد بن الحسين بن عبد الله، الآجري، الفقيه، الشافعي.','12','13','0');
INSERT INTO [t132412] VALUES (15,'(٧) النسبة: الآجنقاني، نسبة إلى آجنقان: بالجيم المكسورة، والنون الساكنة، وقاف، وألف، ونون: قرية من قرى سرخس، والعجم يسمونها آجنكان.','12','0','0');
INSERT INTO [t132412] VALUES (16,'والمنسوب: أبو الفضل محمد بن عبدالواحد الآجنقاني.','12','15','0');
INSERT INTO [t132412] VALUES (17,'(٨) النسبة: الآخري، نسبة إلى آخر: بضم الخاء المعجمة، والراء: قصبة ناحية دهستان، بين جرجان وخوارزم، وقيل: آخر قرية بدهستان نسب.','12','0','0');
INSERT INTO [t132412] VALUES (18,'والمنسوب: أبو الفضل العباس بن أحمد بن الفضل الزاهد، وكان إمام المسجد العتيق بدهستان، وأبو الفضل خزيمة بن علي بن عبدالرحمن الآخري الدهستاني، المعتزلي، كان فقيها، أديبا، لغويا، سمع بدهستان.','12','17','0');
INSERT INTO [t132412] VALUES (19,'(٩) النسبة: الأذرمي، نسبة إلى آذرمة: من أعمال نصيبي.','13','0','0');
INSERT INTO [t132412] VALUES (20,'المنسوب: أبو عبد الرحمن عبدالله بن محمد ابن إسحاق الآذرمي النصيبيني.','13','19','0');
INSERT INTO [t132412] VALUES (21,'(١٠) النسبة: الآذيوخاني، نسبة إلى آذيوخان: بكسر الذال المعجمة، وياء ساكنة، وواو مفتوحة، وخاء معجمة، وألف، ونون: قرية من قرى نهاوند.','13','0','0');
INSERT INTO [t132412] VALUES (22,'والمنسوب: أبو سعيد الفضل بن عبد الله بن علي بن عمر بن عبد الله بن يوسف الآذيوخاني.','13','21','0');
INSERT INTO [t132412] VALUES (23,'(١١) النسبة: الآزاذاني، نسبة إلى آزاذان: بالزاي، والذال المعجمة، وألف، ونون: قرى هراة، بها قبر الشيخ أبي الوليد أحمد بن أبي رجا شيخ البخاري، وقرية من قرى أصبهان.','13','0','0');
INSERT INTO [t132412] VALUES (24,'والمنسوب: أبو عبد الرحمن قتيبة بن مهران المقري الآزاذاني، من الأصبهانية.','13','23','0');
INSERT INTO [t132412] VALUES (25,'(١٢) النسبة: الآزاذواري، نسبة إلى آزاذوار: بعد الألف زاي، وألف، وذال معجمة، وواو، وألف، وراء: بليدة في أول كورة جوين، من جهة قومس، من أعمال نيسابور.','13','0','0');
INSERT INTO [t132412] VALUES (26,'والمنسوب: إبراهيم بن عبد الرحمن بن سهل، الآزاذواري، يكنى أبا موسى.','14','25','0');
INSERT INTO [t132412] VALUES (27,'(١٣) النسبة: الآغزوني، نسبة إلى آغزون: الغين معجمة ساكنة، يلتقي معها ساكنان، والزاي معجمة مضمومة، والواو ساكنة، ونون: من قرى بخارى.','14','0','0');
INSERT INTO [t132412] VALUES (28,'والمنسوب: أبو عبد الله عبد الواحد بن محمد بن عبد الله بن أيمن بن عبد الله بن مرة بن الأحنف بن قيس التميمي الآغزوني. وليس الأغذوني بالذال.','14','27','0');
INSERT INTO [t132412] VALUES (29,'(١٤) النسبة: الآفراني، نسبة إلى آفران: بضم الفاء وآخره نون: قرية بينها وبين نسف فرسخان "١١ كم" بما وراء النهر، أخرجت طائفة من أهل العلم قديما وحديثا.','14','0','0');
INSERT INTO [t132412] VALUES (30,'والمنسوب: أبو موسى الوثير بن المنذر بن جنك بن زمانة الآفراني النسفي.','14','29','0');
INSERT INTO [t132412] VALUES (31,'(١٥) النسبة: الآلوزاني، نسبة إلى آلوزان: بضم اللام، وسكون الواو، وزاي، وألف، ونون: من قرى سرخس.','14','0','0');
INSERT INTO [t132412] VALUES (32,'والمنسوب: سورة بن الحسن الآلوزاني.','14','31','0');
INSERT INTO [t132412] VALUES (33,'(١٦) النسبة: الآليني، نسبة إلى آلين: بكسر اللام، وياء ساكنة، ونون: من قرى مرو على أسفل نهر خارقان.','14','0','0');
INSERT INTO [t132412] VALUES (34,'والمنسوب: فرات بن النضر الآليني، وأبو سداد الآليني.','14','33','0');
INSERT INTO [t132412] VALUES (35,'(١٧) النسبة: الآمدي، نسبة إلى آمد: بكسر الميم: لها في العربية أصل حسن لأن الأمد الغاية، وينسب إلى آمد خلق من أهل العلم في كل فن.','14','0','0');
INSERT INTO [t132412] VALUES (36,'والمنسوب: أبو القاسم الحسن بن بشر الآمدي الأديب، كان بالبصرة يكتب بين يدي القضاة بها، وله تصانيف في الأدب مشهورة، منها كتاب المؤتلف والمختلف في أسماء الشعراء، وكتاب الموازنة بين أبي تمام والبحتري، وغير ذلك، ومات في سنة (٣٧٠) ومن المتأخرين أبو المكارم محمد بن الحسين الآمدي، شاعر بغدادي مكثر مجيد، مدح جمال الدين الأصبهاني وزير الموصل، مات أبو المكارم','14','35','0');
INSERT INTO [t132412] VALUES (37,'(١٨) النسبة: الآملي، نسبة إلى آمل: بضم الميم، واللام: اسم أكبر مدينة بطبرستان في السهل، لأن طبرستان سهل وجبل.','15','0','0');
INSERT INTO [t132412] VALUES (38,'والمنسوب: أحمد بن هارون الآملي.','15','37','0');
INSERT INTO [t132412] VALUES (39,'(١٩) النسبة: الأبي، نسبة إلى أب: بالفتح والتشديد: الزرع، في قوله تعالى: ... {وفاكهة وأبا} وهي بليدة باليمن، يكسرون الهمزة.','16','0','0');
INSERT INTO [t132412] VALUES (40,'والمنسوب: أبو محمد عبد الله بن الحسن بن الفياض الهاشمي.','16','39','0');
INSERT INTO [t132412] VALUES (41,'(٢٠) النسبة: الأبدي، نسبة إلى أبدة: بالضم، ثم الفتح والتشديد: اسم مدينة بالأندلس من كورة جيان، تعرف بأبدة العرب.','16','0','0');
INSERT INTO [t132412] VALUES (42,'والمنسوب: أبو العباس أحمد بن البني الأبدي بجزيرة ميورقة.','17','41','0');
INSERT INTO [t132412] VALUES (43,'(٢١) النسبة: الأبرقوهي، أبرقوه: بفتح أوله، وثانيه، وسكون الراء، وضم القاف، والواو ساكنة، وهاء محضة: وأهل فارس يسمونها وركوه، ومعناه: فوق الجبل، بليدة بنواحي أصبهان على عشرين فرسخا "١١١ كم تقريبا" منها.','17','0','0');
INSERT INTO [t132412] VALUES (44,'والمنسوب: أبو الحسن هبة الله بن الحسن بن محمد، الأبرقوهي، الفقيه.','17','43','0');
INSERT INTO [t132412] VALUES (45,'(٢٢) النسبة: الأبزاري، نسبة إلى أبزار: بفتح الهمزة، وسكون الباء، وزاي، وألف، وراء: قرية بينها وبين نيسابور فرسخان "١١ كم، و ٨٠ م" نسب إليها قوم من أهل العلم.','17','0','0');
INSERT INTO [t132412] VALUES (46,'والمنسوب: حامد بن موسى الأبزاري.','17','45','0');
INSERT INTO [t132412] VALUES (47,'(٢٣) النسبة: الأبغري، نسبة إلى أبغر: بالفتح، ثم السكون، والغين المعجمة مفتوحة، وراء: من قرى سمرقند، وقيل هي ناحية بسمرقند ذات قرى متصلة.','18','0','0');
INSERT INTO [t132412] VALUES (48,'والمنسوب: أبو يزيد خالد بن كردة الأبغري، السمرقندي، وأبو عبد الله محمد بن محمد بن عمران الأبغري، كاتب الإنشاء في أيام دولة السامانية، وكان من البلغاء.','18','47','0');
INSERT INTO [t132412] VALUES (49,'(٢٤) النسبة: الأبلي، نسبة إلى الأبلة: بضم أوله، وثانيه، وتشديد اللام وفتحها، بلدة على شاطئ دجل نسب إليها جماعة من رواة العلم.','18','0','0');
INSERT INTO [t132412] VALUES (50,'والمنسوب: شيبان بن فروخ الأبلي.','18','49','0');
INSERT INTO [t132412] VALUES (51,'(٢٥) النسبة: الأبي، نسبة إلى أبة: بضم أوله، وتشديد ثانيه، والهاء: اسم مدينة بإفريقية، بينها وبين القيروان ثلاثة أيام، وهي من ناحية الأربس، موصوفة بكثرة الفواكه وإنبات الزعفران.','18','0','0');
INSERT INTO [t132412] VALUES (52,'والمنسوب: أبو القاسم عبد الرحمن بن عبد المعطي بن أحمد الأنصاري الأبي.','18','51','0');
INSERT INTO [t132412] VALUES (53,'(٢٦) النسبة: البويطي، نسبة إلى أبويط: بالفتح، ثم السكون، وفتح الواو، وياء ساكنة، وطاء مهملة: قرية قرب بردنيس في شرقي النيل، من أعمال الصعيد الأدنى، من كورة الأسيوطية، وأكثر ما يقال بغير همزة.','19','0','0');
INSERT INTO [t132412] VALUES (54,'والمنسوب: البويطي الفقيه، نذكره في باب الباء، إن شاء الله تعالى.','19','53','0');
INSERT INTO [t132412] VALUES (55,'(٢٧) النسبة: الأبياري، نسبة إلى أبيار: بفتح أوله، وسكون ثانيه بلفظ جمع البئر، مخفف الهمزة: اسم قرية بجزيرة بني نصر بين مصر والاسكندرية.','19','0','0');
INSERT INTO [t132412] VALUES (56,'والمنسوب: أبو الحسن علي بن إسماعيل بن أسد الربعي الأبياري.','19','55','0');
INSERT INTO [t132412] VALUES (57,'(٢٨) النسبة: الأبيوردي، نسبة إلى أبيورد: بفتح أوله، وكسر ثانيه، وياء ساكنة، وفتح الواو، وسكون الراء، ودال مهملة: مدينة بخراسان بين سرخس ونسا.','19','0','0');
INSERT INTO [t132412] VALUES (58,'والمنسوب: الأديب أبو المظفر محمد بن أحمد بن محمد بن أحمد الأموي المعاوي الشاعر، وأصله من كوفن، قرية من قرى أبيورد، كان إماما في كل فن من العلوم، عارفا بالنحو واللغة والنسب والأخبار، ويده باسطة في البلاغة والإنشاء، وله تصانيف في جميع ذلك، وشعره سائر مشهور، مات بأصبهان في العشرين من شهر ربيع الأول سنة (٥٠٧).','19','57','0');
INSERT INTO [t132412] VALUES (59,'(٢٩) النسبة: الأثاربي، نسبة إلى الأثارب: كأنه جمع أثرب، من الثرب، وهو الشحم الذي قد غشي الكرش، يقال: أثرب الكبش إذا زاد شحمه، وهي قلعة معروفة بين حلب وأنطاكية، بينها وبين حلب نحو ثلاثة فراسخ "١٦ كم، و ٦٣٢ م".','20','0','0');
INSERT INTO [t132412] VALUES (60,'المنسوب: أبو الفتح أحمد بن عبدالرحمن بن الحسين، الأحصبي، الوراق، نزل الأحصبين.','20','59','0');
INSERT INTO [t132412] VALUES (61,'(٣٠) النسبة: الأجدابي، نسبة إلى أجدابية: بالفتح، ثم السكون، ودال مهملة، وبعد الألف، باء موحدة، وياء خفيفة، وهاء، بلد بين برقة وطرابلس الغرب، بينه وبين زويلة نحو شهر سيرا.','20','0','0');
INSERT INTO [t132412] VALUES (62,'والمنسوب: أبو المعالي محمد بن هياج بن مبادر بن علي الأثاربي الأنصاري.','20','61','0');
INSERT INTO [t132412] VALUES (63,'(٣١) النسبة: الأحصبي، نسبة إلى الأحصبان: تثنية الأحصب، من الأرض الحصباء، وهي الحصى الصغار، موضع باليمن.','20','0','0');
INSERT INTO [t132412] VALUES (64,'والمنسوب: أبو إسحاق إبراهيم بن إسماعيل بن أحمد بن عبد الله، الطرابلسي، يعرف بابن الأجدابي، كان أديبا فاضلا، له تصانيف حسنة، منها كفاية المتحفظ، وهو مختصر في اللغة مشهور، مستعمل جيد، وكتاب الأنواء وغير ذلك.','20','63','0');
INSERT INTO [t132412] VALUES (65,'(٣٢) النسبة: الأحصي، نسبة إلى الأحص: بالفتح، وتشديد الصاد المهملة، يقال: رجل أحص، بين الحصص أي قليل شعر الرأس، وبنجد موضعان يقال لهما: الأحص وشبيث، وبالشام من نواحي حلب موضعان يقال لهما: الأحص وشبيث.','20','0','0');
INSERT INTO [t132412] VALUES (66,'والمنسوب إلى أحص حلب، شاعر يعرف بالناشي الأحصي، كان في أيام سيف الدولة أبي الحسن علي بن حمدان، قال ياقوت رحمه الله: له خبر ظريف، أنا','20','65','0');
INSERT INTO [t132412] VALUES (67,'(٣٣) النسبة: الأخسيكثي، نسبة إلى أخسيكث: بالفتح، ثم السكون، وكسر السين المهملة، وياء ساكنة، وكاف وثاء مثلثة، وبعضهم يقوله بالتاء المثناة، وهو الأولى، لأن المثلثة ليست من حروف العجم: اسم مدينة بما وراء النهر، هي قصبة ناحية فرغانة، على شاطئ نهر الشاش على أرض مستوية، بينها وبين الجبال نحو من فرسخ "٢٤ كم" على شمالي النهر، وقد خرج منها جماعة من أهل العلم والأدب.','21','0','0');
INSERT INTO [t132412] VALUES (68,'والمنسوب أبو الوفاء محمد بن محمد بن القاسم الأخسيكثي، كان إماما في اللغة','21','67','0');
INSERT INTO [t132412] VALUES (69,'(٣٤) النسبة: الأدفوي، نسبة إلى أدفو: بضم الهمزة، وسكون الدال، وضم الفاء، وسكون الوأو اسم قرية بصعيد مصر الأعلى، بين أسوان وقوص.','22','0','0');
INSERT INTO [t132412] VALUES (70,'والمنسوب: أبو بكر محمد بن علي الأدفوي، الأديب المقري صاحب النحاس، له كتاب في تفسير القرآن المجيد في خمسة مجلدات كبار، وله غير ذلك من كتب الأدب، راجع معجم الأدباء.','22','69','0');
INSERT INTO [t132412] VALUES (71,'(٣٥) النسبة: الأذرعي، نسبة إلى أذرعات: بالفتح، ثم السكون، وكسر الراء، وعين مهملة، وألف وتاء. كأنه جمع أذرعة، جمع ذراع جمع قلة: وهو بلد في أطراف الشام، يجاور أرض البلقاء وعمان، خرج منها طائفة من أهل العلم.','22','0','0');
INSERT INTO [t132412] VALUES (72,'والمنسوب: إسحاق بن إبراهيم الأذرعي بن هشام بن يعقوب بن إبراهيم بن عمرو بن هاشم بن أحمد، ويقال: ابن إبراهيم بن زامل أبو يعقوب النهدي، أحد الثقات من عباد الله الصالحين، رحل وحدث.','22','71','0');
INSERT INTO [t132412] VALUES (73,'(٣٦) النسبة: الأذني، أذنة: بفتح أوله، وثانيه، ونون، بوزن حسنة نسب إليها جماعة من أهل العلم.','23','0','0');
INSERT INTO [t132412] VALUES (74,'والمنسوب: أبو بكر محمد بن علي بن أحمد بن داود، الكتاني، الأذني.','23','73','0');
INSERT INTO [t132412] VALUES (75,'(٣٧) النسبة: الأذوني، نسبة إلى أذون: بالفتح، ثم الضم، وسكون الواو، وآخره نون: قرية من نواحي الري.','24','0','0');
INSERT INTO [t132412] VALUES (76,'المنسوب: أبو العباس أحمد بن الحسين بن بابا الزيدي، سمع منه أبو سعد.','24','75','0');
INSERT INTO [t132412] VALUES (77,'(٣٨) النسبة: الأراني، نسبة إلى أران: بالفتح وتشديد الراء وألف ونون: اسم لولاية واسعة وبلاد كثيرة، وبين أذربيجان وأران نهر يقال له: الرس، كل ما جاوره من ناحية المغرب والشمال، فهو من أران، وما كان من جهة المشرق فهو من أذربيجان.','24','0','0');
INSERT INTO [t132412] VALUES (78,'والمنسوب: الفقيه عبدالخالق بن أبي المعالي بن محمد الأراني الشافعي، قدم الموصل وتفقه على أبي حامد بن يونس، وكان كثيرا ما ينشد قول أبي المعالي الجويني الإمام','24','77','0');
INSERT INTO [t132412] VALUES (79,'(٣٩) النسبة: الأربسي، نسبة إلى الأربس: بالضم، ثم السكون، والباء الموحدة مضمومة، وسين مهملة: مدينة وكورة بافريقية.','25','0','0');
INSERT INTO [t132412] VALUES (80,'والمنسوب: أبو طاهر الأربسي الشاعر من أهل مصر.','25','79','0');
INSERT INTO [t132412] VALUES (81,'(٤٠) النسبة: الأربقي، نسبة إلى أربق بالفتح، ثم السكون، وباء مفتوحة موحدة، وقد تضم، وقاف، ويقال: بالكاف مكان القاف، من نواحي رامهرمز من نواحي خوزستان.','25','0','0');
INSERT INTO [t132412] VALUES (82,'والمنسوب: أبو طاهر علي بن أحمد بن الفضل الرامهرمزي الأربقي. وأبو الحسن أحمد بن الحسن الأربقي، وكان رجلا فاضلا، قاضي البلد، وخطيبه وإمامه في شهر رمضان.','25','81','0');
INSERT INTO [t132412] VALUES (83,'(٤١) النسبة: الإربلي، نسبة إلى إربل: بالكسر، ثم السكون، وباء موحدة مكسورة، ولام، بوزن إثمد، قلعة حصينة، ومدينة كبيرة، في فضاء من الأرض واسع، نسب إليها جماعة من أهل العلم والحديث.','25','0','0');
INSERT INTO [t132412] VALUES (84,'والمنسوب: أبو احمد القاسم بن المظفر الشهرزوري الشيباني الإربلي وغيره.','25','83','0');
INSERT INTO [t132412] VALUES (85,'(٤٢) النسبة: الأربنجني، أربنجن: بالفتح، ثم السكون، وكسر الباء الموحدة، وسكون النون، وفتح الجيم، وآخره نون: بليدة من نواحي الصغد، ثم من أعمال سمرقند.','25','0','0');
INSERT INTO [t132412] VALUES (86,'والمنسوب: أبو بكر أحمد بن محمد بن موسى بن رجاء الأربنجني، كان فقيها حنفيا، مات سنة (٣٦٩).','25','85','0');
INSERT INTO [t132412] VALUES (87,'(٤٣) النسبة: الأرتاحي، نسبة إلى أرتاح: بالفتح، ثم السكون، وتاء فوقها نقطتان، وألف، وحاء مهملة: اسم حصن منيع، كان من العواصم من أعمال حلب.','25','0','0');
INSERT INTO [t132412] VALUES (88,'والمنسوب: الحسين بن عبد الله الأرتاحي.','26','87','0');
INSERT INTO [t132412] VALUES (89,'(٤٤) النسبة: الأرجاني، نسبة إلى أرجان: بفتح أوله، وتشديد الراء، وجيم وألف ونون، وعامة العجم يسمونها أرغان، مدينة كبيرة كثيرة الخير، وهي برية بحرية، سهلية جبلية، بينها وبين البحر مرحلة "٩٦ كم" وبينها وبين شيراز ستون فرسخا "٣٣٢ كم" ينسب إلى أرجان جماعة كثيرة من أهل العلم.','26','0','0');
INSERT INTO [t132412] VALUES (90,'والمنسوب: أبو سهل أحمد بن سهل الأرجاني.','26','89','0');
INSERT INTO [t132412] VALUES (91,'(٤٥) النسبة: الأرجذوني، نسبة إلى أرجذونة: بالضم، ثم السكون، وضم الجيم، والذال المعجمة، وسكون الواو، وفتح النون، وهاء: مدينة بالأندلس.','27','0','0');
INSERT INTO [t132412] VALUES (92,'والمنسوب: عمرو بن حفصويه الخارج على بني أمية.','27','91','0');
INSERT INTO [t132412] VALUES (93,'(٤٦) النسبة: الأرخسي، نسبة إلى أرخس: بضم أوله، وثانيه، وسكون الخاء المعجمة، وسين مهملة: قرية من ناحية شاوذار من نواحي سمرقند عند الجبال، بينها وبين سمرقند أربعة فراسخ"٢٢ كم"','27','0','0');
INSERT INTO [t132412] VALUES (94,'والمنسوب: العباس بن عبدالله الأرخسي، ويقال: الرخسي.','27','93','0');
INSERT INTO [t132412] VALUES (95,'(٤٧) النسبة: الأردبيلي، نسبة إلى أردبيل: بالفتح، ثم السكون، وفتح الدال، وكسر الباء، وياء ساكنة، ولام: من أشهر مدن أذربيجان ينسب إليها خلق كثير من أهل العلم في كل فن.','27','0','0');
INSERT INTO [t132412] VALUES (96,'والمنسوب: حفص بن عمر الأردبيلي: قال: جلس سعيد بن عمرو البرذعي في منزله وأغلق بابه وقال: ما أحدث الناس فإن الناس قد تغيروا، فاستعان عليه أصحاب الحديث بمحمد بن مسلم بن وارة الرازي، فدخل عليه وسأله أن يحدثهم، فقال: ما أفعل، فقال: بحقي عليك إلا حدثتهم، فقال: وأي حق لك علي؟ فقال: أخذت يوما بركابك، فقال: قضيت حقا لله عليك وليس لك علي حق، فقال: إن قوما اغتابوك فرددت عنك، فقال: هذا أيضا يلزمك لجماعة المسلمين، قال: فإني عبرت بك يوما في ضيعتك فتعلقت بي إلى طعامك فأدخلت على قلبك سرورا، فقال: أما هذه فنعم، فأجابه إلى ما أراد.','27','95','0');
INSERT INTO [t132412] VALUES (97,'(٤٨) النسبة: الأردستاني، نسبة إلى أردستان: بالفتح، ثم السكون، وكسر الدال المهملة، وسكون السين المهملة، وتاء مثناة من فوقها، وألف، ونون، مدينة بين قاشان وأصبهان، بينها وبين أصبهان ثمانية عشر فرسخا "١٠٠" تقريبا، ينسب إليها طائفة كثيرة من أهل العلم في كل فن.','28','0','0');
INSERT INTO [t132412] VALUES (98,'والمنسوب: القاضي أبو طاهر زيد بن عبد الوهاب بن محمد الأردستاني الأديب الشاعر، قدم نيسابور وسمع من أصحاب الأصم.','28','97','0');
INSERT INTO [t132412] VALUES (99,'(٤٩) النسبة: الأردني، نسبة إلى الأردن: بالضم ثم السكون، وضم الدال المهملة، وتشديد النون، هو البلد المعروف اليوم، نسب إليه جماعة وافرة من العلماء.','28','0','0');
INSERT INTO [t132412] VALUES (100,'والمنسوب: الوليد بن مسلمة الأردني.','28','99','0');
INSERT INTO [t132412] VALUES (101,'(٥٠) النسبة: الأرزكاني، نسبة إلى أرزكان: بالفتح، ثم السكون، وفتح الزاي، وكاف، وألف، ونون: من قرى فارس.','29','0','0');
INSERT INTO [t132412] VALUES (102,'والمنسوب: أبو عبد الرحمن عبد الله بن جعفر بن أبي جعفر الأرزكاني.','29','101','0');
INSERT INTO [t132412] VALUES (103,'(٥١) النسبة: الأرزناني، نسبة إلى أرزنان: بالفتح، ثم السكون، وضم الزاي، ونون، وألف، ونون أخرى: من قرى أصبهان.','30','0','0');
INSERT INTO [t132412] VALUES (104,'والمنسوب: أبو القاسم الحسن بن أحمد بن محمد، الأرزناني، المعلم الأعمى، مات سنة (٤٥٣) وأبو جعفر محمد بن عبد الرحمن بن زياد الأصبهاني الأرزناني الحافظ الثبت، توفي سنة (٣١٧) وجده سمع بالشام، ورأس عين، سليمان بن المعافى، وبصور أبا ميمون محمد بن أبي نصر، وبمصر يحيى بن عثمان بن صالح، وبكر بن صالح الدمياطي، وبأصبهان أحمد بن مهران بن خالد، وبالري الحسن بن علي ابن زياد السري، وبخوزستان عبد الوارث بن ابراهيم، وبمكة علي بن عبد العزيز، وبالعراق هشام بن علي وغيره، وبدامغان أبا بكر محمد بن ابراهيم بن احمد بن ناصح، وبطرسوس أبا الدرداء عبد الله بن محمد ابن الأشعث. وروى عنه أبو الشيخ عبد الله بن محمد بن جعفر، وأبو بكر أحمد بن الحسين بن مهران المقرئ وجماعة كثيرة، وكان موصوفا بالعلم والثقة والإتقان والزهد والورع، رحمه الله تعالى.','30','103','0');
INSERT INTO [t132412] VALUES (105,'(٥٢) النسبة: الأرزني، نسبة إلى أرزن: بالفتح ثم السكون، وفتح الزاي، ونون، مدينة مشهورة قرب خلاط، ولها قلعة حصينة، وكانت من أعمر نواحي ارمينية، وقد نسب إليها قوم من أهل العلم.','30','0','0');
INSERT INTO [t132412] VALUES (106,'والمنسوب: أبو غسان عياش بن إبراهيم الأرزني.','30','105','0');
INSERT INTO [t132412] VALUES (107,'(٥٣) النسبة: الأرسابندي، نسبة إلى أرسابند بالفتح، ثم السكون، وسين مهملة، وألف، وباء موحدة مفتوحة، ونون ساكنة، ودال مهملة: قرية بينها وبين مرو فرسخان "١١ كم" خرج منها طائفة من أئمة العلماء.','31','0','0');
INSERT INTO [t132412] VALUES (108,'والمنسوب: محمد بن عمران الأرسابندي، وأبو الفضل محمد بن الفضل الأرسابندي، والقاضي محمد بن الحسين الأرسابندي الحنفي القاضي مرو، وكان من أجلاء الرجال، ملكا في صورة عالم.','31','107','0');
INSERT INTO [t132412] VALUES (109,'(٥٤) النسبة: الأرضيطي، نسبة إلى أرضيط: بالفتح، ثم السكون، والضاد معجمة مكسورة، وياء ساكنة، وطاء، من قرى مالقة.','31','0','0');
INSERT INTO [t132412] VALUES (110,'والمنسوب: أبو الحسن سليمان بن محمد بن الطراوة السبائي النحوي المالقي الأرضيطي ولد بها، وهو شيخ الأندلسيين في زمانه.','31','109','0');
INSERT INTO [t132412] VALUES (111,'(٥٥) النسبة: الأرعنزي، نسبة إلى أرعنز: بالفتح، ثم السكون، وفتح العين المهملة، ونون ساكنة، وزاي: لعله موضع بديار بكر.','31','0','0');
INSERT INTO [t132412] VALUES (112,'والمنسوب: أحمد بن أحمد بن أحمد أبو العباس أحد طلاب الحديث، سمع ببغداد مع أبي الحسن علي بن أحمد العلوي الزيدي صاحب وقف الكتب بدار دينار ببغداد من جماعة وافرة، وخرج من بغداد وغاب خبره.','31','111','0');
INSERT INTO [t132412] VALUES (113,'(٥٦) النسبة: الأرغياني، نسبة إلى أرغيان: بالفتح، ثم السكون، وكسر الغين المعجمة، وياء، وألف، ونون: كورة من نواحي نيسابور، قيل إنها تشتمل على إحدى وسبعين قرية، ينسب إليها جماعة من أهل العلم والأدب.','31','0','0');
INSERT INTO [t132412] VALUES (114,'والمنسوب: الحاكم أبو الفتح سهل بن أحمد بن علي الأرغياني، توفي في مستهل المحرم سنة (٤٩٩).','31','113','0');
INSERT INTO [t132412] VALUES (115,'(٥٧) النسبة: الأرفودي، نسبة إلى الأرفود: بالفتح، ثم السكون، وضم الفاء، وسكون الواو، ودال مهملة: من قرى كرمينية من أعمال سمرقند على طريق بخارى.','31','0','0');
INSERT INTO [t132412] VALUES (116,'والمنسوب: أبو أحمد محمد بن محفوظ الأرفودي، توفي قرابة سنة (٣٨٠).','32','115','0');
INSERT INTO [t132412] VALUES (117,'(٥٨) النسبة: الأرمنازي، نسبة إلى أرمناز: بالفتح ثم السكون، وفتح الميم والنون، وألف، وزاي: بليدة قديمة من نواحي حلب، بينهما نحو خمسة فراسخ "٢٨ كم" تقريبا.','32','0','0');
INSERT INTO [t132412] VALUES (118,'والمنسوب: أبو الحسن علي بن عبد السلام الأرمنازي، كان من الفضلاء المشهورين والشعراء.','32','117','0');
INSERT INTO [t132412] VALUES (119,'(٥٩) النسبة: الأرموي، نسبة إلى أرمية: بالضم، ثم السكون، وياء مفتوحة خفيفة، وهاء، ويجوز في قياس العربية تخفيف الياء وتشديدها، اسم مدينة عظيمة قديمة بأذربيجان بينها وبين البحيرة نحو ثلاثة أميال أو أربعة، ينسب إليها جماعة من العلماء.','33','0','0');
INSERT INTO [t132412] VALUES (120,'والمنسوب: أبو عبد الله الحسين بن عبد الله بن محمد بن الشويخ الأرموي، نزل مصر وتوفي بها سنة (٤٦٠) وأبو الفضل محمد بن عمر بن يوسف الأرموي البغدادي.','33','119','0');
INSERT INTO [t132412] VALUES (121,'(٦٠) النسبة: الأرمني، الأرمني، نسبة إلى إرمينية: بكسر أوله ويفتح، وسكون ثانيه، وكسر الميم، وياء ساكنة، وكسر النون، وياء خفيفة مفتوحة: اسم لصقع عظيم واسع في جهة الشمال من خراسان، وقد نسب بهذه النسبة قوم من أهل العلم.','33','0','0');
INSERT INTO [t132412] VALUES (122,'والمنسوب: أبو عبد الله عيسى بن مالك بن شمر الأرمني، سافر إلى مصر والمغرب.','33','121','0');
INSERT INTO [t132412] VALUES (123,'(٦١) النسبة: الأرواوي، نسبة إلى أروى: بالفتح، ثم السكون، وفتح الواو، والقصر، وهو في الأصل جمع أروية: وهو الأنثى من الوعل، قرية من قرى مرو','33','0','0');
INSERT INTO [t132412] VALUES (124,'والمنسوب: أبو العباس أحمد بن محمد بن عميرة بن عمرو بن يحيى بن سليم الأرواوي.','34','123','0');
INSERT INTO [t132412] VALUES (125,'(٦٢) النسبة: الأريولي، نسبة إلى أريول: بالفتح، ثم السكون، وياء مضمومة، وواو ساكنة، ولام: مدينة بشرق الأندلس من ناحية تدمير.','34','0','0');
INSERT INTO [t132412] VALUES (126,'والمنسوب: أبو بكر عتيق بن أحمد بن عبد الرحمن، الأزدي، الأندلسي، الأريولي، قدم الاسكندرية ولقيه بها أبو طاهر أحمد بن سلفة الحافظ، ثم مضى إلى مكة فجاور بها سنين يؤذن للمالكية، ثم رجع إلى المغرب وكان آخر العهد به.','34','125','0');
INSERT INTO [t132412] VALUES (127,'(٦٣) النسبة: الأزاذواري، نسبة إلى أزاذوار: الذال معجمة، يلتقي عندها ساكنان، وواو، وألف، وراء: اسم بليدة، هي قصبة كورة جوين من أعمال نيسابور، ينسب إليه جماعة من أهل العلم.','34','0','0');
INSERT INTO [t132412] VALUES (128,'والمنسوب: أبو عبدالله محمد بن حفص بن محمد بن يزيد، الشعراني، النيسابوري، الأزاذواري، شيخ ثقة.','34','127','0');
INSERT INTO [t132412] VALUES (129,'(٦٤) النسبة: الأزجاهي، نسبة إلى أزجاه: بالفتح، ثم السكون، وجيم، وألف، وهاء محضة: قرية من قرى خابران، ثم من نواحي سرخس.','35','0','0');
INSERT INTO [t132412] VALUES (130,'والمنسوب: أبو بكر أصرم بن محمد بن أصرم الأزجاهي، المقرئ، كان صالحا ورعا.','35','129','0');
INSERT INTO [t132412] VALUES (131,'(٦٥) النسبة: الأزجي، نسبة إلى الأزج: بالتحريك، والجيم، باب الأزج: محلة كبيرة ذات أسواق كثيرة ومحال كبار في شرقي بغداد، نسب إليها أهل العلم وغيرهم كثير جدا.','35','0','0');
INSERT INTO [t132412] VALUES (132,'والمنسوب: عبد العزيز بن علي الأزجي، روى عن سعيد بن أحمد بن الحسين أبو بكر الصريفيني.','35','131','0');
INSERT INTO [t132412] VALUES (133,'(٦٦) النسبة: الأزمي، نسبة إلى أزم: بفتحتين: ناحية من نواحي سيراف.','35','0','0');
INSERT INTO [t132412] VALUES (134,'والمنسوب: بحر بن يحيى بن بحر الأزمي، الفارسي.','35','133','0');
INSERT INTO [t132412] VALUES (135,'(٦٧) النسبة: الأزناوي، نسبة إلى أزنأو بالفتح، ثم السكون، ونون، وألف، وواو، ويقال: أزناوه، بالهاء: قلعة من ناحية الأجم من نواحي همذان.','36','0','0');
INSERT INTO [t132412] VALUES (136,'والمنسوب: أبو الفضل عبد الكريم بن أحمد الأزناوي، المعروف بالبئاري، فقيه شافعي.','36','135','0');
INSERT INTO [t132412] VALUES (137,'(٦٨) النسبة: الأزواري، نسبة إلى أزوارة: بالضم، ثم السكون، وواو، وألف، وراء، وهاء: بليدة بنواحي أصبهان على طرف البرية.','36','0','0');
INSERT INTO [t132412] VALUES (138,'والمنسوب: أبو نصر أحمد بن علي الأزواري.','36','137','0');
