INSERT INTO [t132412] VALUES (210,'(١٠٥) النسبة: الإشتيخني، نسبة إلى إشتيخن: بالكسر، ثم السكون، وكسر التاء المثناة، وياء ساكنة، وخاء معجمة مفتوحة، ونون: من قرى صغد سمرقند، بينها وبين سمرقند سبعة فراسخ "٣٩ كم" تقريبا، ينسب إليها جماعة وافرة من أهل العلم.','50','0','0');
INSERT INTO [t132412] VALUES (211,'والمنسوب: أبو بكر محمد ابن أحمد بن مت الإشتيخني، كان من أئمة أصحاب الشافعي.','50','210','0');
INSERT INTO [t132412] VALUES (212,'(١٠٦) النسبة: الأشرقي، نسبة إلى ذو أشرق: بالقاف مضاف إليه ذو، فيقال ذو أشرق: بلدة باليمن قرب ذي جبلة.','50','0','0');
INSERT INTO [t132412] VALUES (213,'والمنسوب: أحمد بن محمد الأشرقي، الشاعر يمدح الملك المعز إسماعيل بن سيف الإسلام طغتكين بن أيوب بقصيدة أولها','50','212','0');
INSERT INTO [t132412] VALUES (214,'(١٠٧) النسبة: الأشروسني، نسبة إلى أشروسنة: بالضم، ثم السكون، وضم الراء، وواو ساكنة، وسين مهملة مفتوحة، ونون، وهاء، بلدة كبيرة بما وراء النهر من بلاد الهياطلة بين سيحون وسمرقند، وبينها وبين سمرقند ستة وعشرون فرسخا "١٤٤ كم" ينسب إلى أشروسنة أمم من أهل العلم.','51','0','0');
INSERT INTO [t132412] VALUES (215,'والمنسوب: أبو طلحة حكيم بن نصر بن خالج بن جندبك، وقيل: جند لك الأشروسني.','51','214','0');
INSERT INTO [t132412] VALUES (216,'(١٠٨) النسبة: الأشفورقاني، نسبة إلى أشفورقان: من قرى مرو الروذ والطالقان.','52','0','0');
INSERT INTO [t132412] VALUES (217,'والمنسوب: عثمان بن أحمد بن أبي الفضل أبو عمرو الأشفورقاني، الحصري، كان إماما فاضلا حسن السيرة جميل الأمر، وكان إمام جامع أشفورقان.','52','216','0');
