INSERT INTO [t132412] VALUES (219,'والمنسوب: أبو العباس يوسف بن محمد بن فارو الإشكربي، ولد بأشكرب، ونشأ بجيان، فانتسب إلى مسقط رأسه، وسافر إلى خراسان، وأقام ببلخ إلى أن مات بها في سنة (٥٤٨).','52','218','0');
INSERT INTO [t132412] VALUES (220,'(١١٠) النسبة: الأشكوراني، أشكوران: بالفتح، وضم الكاف، وواو ساكنة، وراء، وألف، ونون: من قرى أصبهان.','52','0','0');
INSERT INTO [t132412] VALUES (221,'والمنسوب: محمد أبو بكر محمد بن الحسن بن محمد بن إبراهيم بن إبروية الأشكوراني: قال أبو طاهر: قدم علينا أصبهان، وقرأت عليه وسألته عن مولده، فقال: سنة (٤١٧) وتوفي سنة (٤٩٣).','52','220','0');
