INSERT INTO [t132412] VALUES (226,'(١١٣) النسبة: الأشموني، نسبة إلى أشمون: بالنون، وأهل مصر يقولون الأشمونين: مدينة قديمة أزلية عامرة آهلة، وهي كورة من كور الصعيد الأدنى غربي النيل، ينسب إليها جماعة.','53','0','0');
INSERT INTO [t132412] VALUES (227,'والمنسوب: أبو إسماعيل ضمام بن إسماعيل بن مالك المعافري الأشموني، مات بالإسكندرية سنة (١٨٥).','53','226','0');
INSERT INTO [t132412] VALUES (228,'(١١٤) النسبة: الأشميوني، نسبة إلى أشميون: الميم مكسورة، وياء مضمومة، وواو ساكنة، ونون: من قرى بخارى، وقيل محلة.','53','0','0');
INSERT INTO [t132412] VALUES (229,'والمنسوب: أبو عبد الله حاتم بن قديد الأشميوني، من شيوخ الإمام محمد بن إسماعيل البخاري.','53','228','0');
INSERT INTO [t132412] VALUES (230,'(١١٥) النسبة: الأشناذجردي، نسبة إلى أشناذجرد: نون، وألف، وذال معجمة ساكنة، وجيم مكسورة، وراء، ودال مهملة: لعلها من قرى نهاوند، والله أعلم.','53','0','0');
INSERT INTO [t132412] VALUES (231,'والمنسوب: أبو العباس أحمد بن الحسن بن محمد بن علي الأشناذجردي.','53','230','0');
INSERT INTO [t132412] VALUES (232,'(١١٦) النسبة: الأشنانبرتي، نسبة إلى أشنانبوت: الألف، والنون الثانية ساكنتان، وباء موحدة مكسورة، وراء ساكنة، وتاء مثناة: من قرى بغداد.','54','0','0');
INSERT INTO [t132412] VALUES (233,'والمنسوب: أبو طاهر إسحاق بن هبة الله بن الحسن الأشنانبرتي الضرير.','54','232','0');
INSERT INTO [t132412] VALUES (234,'(١١٧) النسبة: الأشناني، نسبة إلى قنطرة الأشنان: بالضم: محلة كانت ببغداد.','54','0','0');
INSERT INTO [t132412] VALUES (235,'والمنسوب: محمد بن يحيى الأشناني.','54','234','0');
INSERT INTO [t132412] VALUES (236,'(١١٨) النسبة، أشناني، أو الأشنهي، أو الأشنائي، على غير قياس، كلها نسبة إلى أشنه: بالضم، ثم السكون، وضم النون، وهاء محضة: بلد في طرف أذربيجان من جهة إربل، نسب المحدثون إليها جماعة من الرواة على ثلاثة.','54','0','0');
