INSERT INTO [t132412] VALUES (238,'(١١٩) النسبة: الأشوقي، نسبة إلى أشوقة: بالضم، ثم الضم، وسكون الواو، وقاف، وهاء: بلدة بالأندلس.','55','0','0');
INSERT INTO [t132412] VALUES (239,'والمنسوب: أحمد بن محمد بن مرحب أبو بكر الأشوقي، فقيه مفت.','55','238','0');
INSERT INTO [t132412] VALUES (240,'(١٢٠) النسبة: الأشوني، نسبة على أشونة: بالنون مكان القاف: حصن بالأندلس من نواحي إستجة.','55','0','0');
INSERT INTO [t132412] VALUES (241,'والمنسوب: الأديب غانم بن الوليد المخزومي الأشوني، وهو الذي يقول','55','240','0');
INSERT INTO [t132412] VALUES (242,'(١٢١) النسبة: الأشيري، نسبة إلى أشير: بكسر ثانيه، وياء ساكنة، وراء: مدينة في جبال البربر بالمغرب في طرف إفريقية الغربي مقابل بجاية في البر.','55','0','0');
INSERT INTO [t132412] VALUES (243,'والمنسوب: الشيخ الفاضل أبو محمد عبدالله بن محمد الأشيري، إمام أهل الحديث والفقه والأدب بحلب خاصة وبالشام عامة، استدعاه الوزير عون الدين أبو المظفر يحيى بن محمد بن هبيرة وزير المقتفي والمستنجد، وطلبه من الملك العادل نور الدين محمود بن زنكي فسيره إليه، وقرأ كتاب ابن هبيرة الذي صنفه وسماه الإيضاح في شرح معاني الصحاح، بحضوره، وجرت له مع الوزير منافرة في شيء اختلف فيه، أغضب كل واحد منهما صاحبه، وردف ذلك اعتذار من الوزير وبره برا وافرا، ثم سار من بغداد إلى مكة ثم عاد إلى الشام، فمات في بقاع بعلبك في سنة (٥٦١).','55','242','0');
INSERT INTO [t132412] VALUES (244,'(١٢٢) النسبة: الأصبهاني، نسبة إلى أصبهان: منهم من يفتح الهمزة، وهم الأكثر، وكسرها آخرون، مدينة عظيمة مشهورة من أعلام المدن وأعيانها، وهي في إيران اليوم، ومن نسب إلى أصبهان من العلماء لا يحصون.','56','0','0');
INSERT INTO [t132412] VALUES (245,'والمنسوب: الحافظ الإمام أبو نعيم أحمد بن عبدالله بن أحمد بن إسحاق بن موسى بن مهران سبط محمد ابن موسى البناء الحافظ المشهور صاحب التصانيف، منها: حلية الأولياء، وغير ذلك، مات يوم الاثنين العشرين من محرم سنة (٤٣٠) ودفن بمردبان، ومولده في رجب سنة (٣٣٠).','56','244','0');
INSERT INTO [t132412] VALUES (246,'(١٢٣) النسبة: الإصطخري، وإصطخرزي بزيادة الزاي، نسبة إلى إصطخر: بالكسر، وسكون الخاء المعجمة، بلدة بفارس، من أقدم مدن فارس وأشهرها، وأهل إصطخر أكرم الناس أحسابا ملوك وأبناء ملوك.','56','0','0');
INSERT INTO [t132412] VALUES (247,'والمنسوب: أبو سعيد الحسن بن أحمد بن يزيد بن عيسى بن الفضل الإصطخري القاضي، أحد الأئمة الشافعية، وصاحب قول فيهم، مولده سنة (٢٤٤) ووفاته في جمادى الآخرة سنة (٣٢٨).','56','246','0');
INSERT INTO [t132412] VALUES (248,'(١٢٤) النسبة: الأصيلي، نسبة إلى أصيل: ياء ساكنة، ولام: بلد بالأندلس.','57','0','0');
INSERT INTO [t132412] VALUES (249,'والمنسوب: أبو محمد عبد الله بن إبراهيم الأصيلي، محدث متقن فاضل معتبر، تفقه بالأندلس فانتهت اليه الرياسة، وصنف كتاب الآثار والدلائل في الخلاف، ثم مات بالأندلس في نحو سنة (٣٩٠) قدم قرطبة سنة (٣٤٢) من المغرب، فوالده ابراهيم كان أديبا شاعرا، له شعر في أهل فاس.','57','248','0');
INSERT INTO [t132412] VALUES (250,'(١٢٥) النسبة: الأضاخي، نسبة إلى أضاخ: بالضم، وآخره خاء معجمة: من قرى اليمامة لبني نمير.','57','0','0');
INSERT INTO [t132412] VALUES (251,'والمنسوب: الحافظ أبو القاسم محمد بن زكرياء أبو غانم النجدي، اليمامي، الأضاخي.','57','250','0');
INSERT INTO [t132412] VALUES (252,'(١٢٦) النسبة: الأطرابلسي، نسبة إلى أطرابلس: بضم الباء الموحدة واللام، والسين مهملة: مدينة مشهورة على ساحل بحر الشام الأبيض المتوسط، بين اللاذقية وعكا، وهي طرابلس بدون همزة، من مدن لبنان اليوم، خرج من أطرابلس هذه خلق من أهل العلم.','58','0','0');
INSERT INTO [t132412] VALUES (253,'والمنسوب: معاوية بن يحيى الأطرابلسي، يكنى أبا مطيع.','58','252','0');
INSERT INTO [t132412] VALUES (254,'(١٢٧) النسبة الأطرابلسي، نسبة إلى أطرابلس الغرب: مدينة في آخر أرض برقة وأول أرض إفريقية، وهي المعروفة بطرابلس الغرب، عاصمة ليبيا اليوم.','60','0','0');
