INSERT INTO [t132412] VALUES (256,'(١٢٨) النسبة: الأعلمي، نسبة إلى الأعلم: بلفظ الأعلم المشقوق الشفة: اسم كورة كبيرة بين همذان وزنجان من نواحي الجبال.','61','0','0');
INSERT INTO [t132412] VALUES (257,'والمنسوب: عبد الغفار بن محمد بن عبد الواحد أبو سعد الأعلمي القومساني، فقيه مقيم بالموصل، روى شيئا من الحديث.','61','256','0');
INSERT INTO [t132412] VALUES (258,'(١٢٩) النسبة: الأغذوني، نسبة إلى أغذون: بفتح الهمزة، وسكون الغين، وضم الذال المعجمة، وسكون الواو، ونون: من قرى بخارى.','61','0','0');
INSERT INTO [t132412] VALUES (259,'والمنسوب: أبو عبد الرحمن حاشد بن عبدالله القصير بن عبدالله بن عبد الواحد ابن محمد بن عبدالله بن أيمن الأغذوني، توفي سنة (٢٥٠) وكان يزعم أنه من ولد الأحنف بن قيس، وقد ذكر المدائني أن الأحنف لم يكن له ولد غير بحر وأنه لا عقب له.','61','258','0');
INSERT INTO [t132412] VALUES (260,'(١٣٠) النسبة: الأغزوني، نسبة إلى أغزون: بالزاي: من قرى بخارى.','61','0','0');
INSERT INTO [t132412] VALUES (261,'والمنسوب: أبو عبد الله عبد الواحد بن محمد بن عبد الله بن أيمن بن عبد الله بن مرة بن الأحنف بن قيس الأغزوني، جد أبي عبد الرحمن حاشد المذكور قبل في أغذون، بالذال المعجمة، توفي في حدود سنة مائتين.','61','260','0');
INSERT INTO [t132412] VALUES (262,'(١٣١) النسبة: الأغماتي، نسبة إلى أغمات: ناحية في بلاد البربر من أرض المغرب قرب مراكش.','61','0','0');
INSERT INTO [t132412] VALUES (263,'والمنسوب: أبو هارون موسى بن عبد الله بن إبراهيم بن محمد بن سنان بن عطاء الأغماتي، المغربي، رحل إلى الشرق وأوغل حتى بلغ سمرقند، وكان فاضلا وله شعر حسن منه','62','262','0');
INSERT INTO [t132412] VALUES (264,'(١٣٢) النسبة: الأفراني، نسبة إلى أفران: بفتح الهمزة، وسكون الفاء، وراء، وألف، ونون: قرية من قرى نخشب.','62','0','0');
INSERT INTO [t132412] VALUES (265,'والمنسوب: أبو بكر محمد بن أحمد الأفراني، الحامدي.','62','264','0');
INSERT INTO [t132412] VALUES (266,'(١٣٣) النسبة: الأفرخشى، نسبة إلى أفرخش: بفتح الهمزة، وسكون الفاء، وفتح الراء، وسكون الخاء المعجمة، والشين معجمة: من قرى بخارى.','62','0','0');
INSERT INTO [t132412] VALUES (267,'والمنسوب: أبو بكر أحمد بن محمد بن إسماعيل بن إسحاق بن إبراهيم الأفرخشى، البخاري، كان رئيس العلماء ومقدمهم، ويعرف بالإسماعيلي، توفي في شهر رمضان سنة (٣٨٤).','62','266','0');
INSERT INTO [t132412] VALUES (268,'(١٣٤) النسبة: الإفريقي، نسبة إلى إفريقية: بكسر الهمزة: وهو اسم لبلاد واسعة ومملكة كبيرة، خرج منها من العلماء والأئمة والأدباء ما لا يحصى عددهم.','62','0','0');
INSERT INTO [t132412] VALUES (269,'والمنسوب: أبو خالد عبد الرحمن بن زياد بن أنعم الإفريقي قاضيها، وهو أول مولود ولد في الإسلام بإفريقية.','62','268','0');
INSERT INTO [t132412] VALUES (270,'(١٣٥) النسبة: الأفشواني، نسبة إلى أفشوان: بفتح الهمزة، وسكون الفاء، وفتح الشين، وواو، وألف، ونون: من قرى بخارى على أربعة فراسخ "٢٧ كم" منها.','63','0','0');
