INSERT INTO [t132412] VALUES (604,'والمنسوب: أبو شعيب البراثي، العابد، كان أول من سكن براثا في كوخ يتعبد فيه، فمرت بكوخه جارية من أبناء الكتاب الكبار وأبناء الدنيا كانت ربيت في القصور فنظرت إلى أبي شعيب فاستحسنت حاله وما كان عليه فصارت كالأسير له، فجاءت إلى أبي شعيب وقالت: أريد أن أكون لك خادمة، فقال لها: إن أردت ذلك فتعري من هيئتك وتجردي عما أنت فيه حتى تصلحي لما أردت، فتجردت عن كل ما تملكه ولبست لبسة النساك وحضرته فتزوجها، فلما دخلت الكوخ رأت قطعة خصاف كانت في مجلس أبي شعيب تقيه من الندى، فقالت: ما أنا بمقيمة عندك حتى تخرج ما تحتك، لأني سمعتك تقول: إن الأرض تقول يا ابن آدم تجعل بيني وبينك حجابا وأنت غدا في بطني، فرماها أبو شعيب، ومكثت عنده سنين يتعبدان أحسن عبادة، وتوفيا على ذلك.','119','603','0');
