INSERT INTO [t132412] VALUES (1479,'(٧٢٠) النسبة: الجيخني، نسبة إلى جيخن: بالكسر ثم السكون، وفتح الخاء المعجمة، ونون: من قرى مرو على أربعة فراسخ "٢٢ كم" منها.','299','0','0');
