INSERT INTO [t132412] VALUES (1481,'(٧٢١) النسبة: الجيذاني، نسبة إلى جيذا: بالكسر، والذال معجمة، مقصور: من قرى واسط.','299','0','0');
INSERT INTO [t132412] VALUES (1482,'والمنسوب: إبراهيم بن ثابت الجيذاني.','299','1481','0');
INSERT INTO [t132412] VALUES (1483,'(٧٢٢) النسبة: الجيراخشتي، نسبة إلى جيراخشت: بالكسر ثم السكون، وراء، وألف، وخاء معجمة مفتوحة، وشين معجمة ساكنة، والتاء فوقها نقطتان: من قرى بخارى.','300','0','0');
INSERT INTO [t132412] VALUES (1484,'والمنسوب: أبو مسلم عمر بن علي بن أحمد بن الليث البخاري الليثي، الجيراخشتي.','300','1483','0');
INSERT INTO [t132412] VALUES (1485,'(٧٢٣) النسبة: الجيراني، نسبة إلى جيران: بالفتح ثم السكون، وراء، وألف، ونون: قرية بينها وبين مدينة أصبهان فرسخان "١١ كم".','300','0','0');
