INSERT INTO [t132412] VALUES (1489,'(٧٢٥) النسبة: الجيرمزداني، نسبة إلى جيرمزدان: بالكسر ثم السكون، وفتح الراء والميم، وسكون الزاي، ودال مهملة، وألف، ونون: من قرى مرو.','301','0','0');
