INSERT INTO [t132412] VALUES (1491,'(٧٢٦) النسبة: الجيرنجي، نسبة إلى جيرنج: بالكسر، وبعد الراء المفتوحة نون ساكنة، وجيم: بليدة من نواحي مرو على نهرها ذات جانبين.','301','0','0');
INSERT INTO [t132412] VALUES (1492,'والمنسوب: أبو بكر أحمد بن محمد الجيرنجي.','301','1491','0');
INSERT INTO [t132412] VALUES (1493,'(٧٢٧) النسبة: الجيروني، نسبة إلى جيرون: بالفتح، قال ابن الفقيه: ومن بنائهم جيرون عند باب دمشق من بناء سليمان بن داود، عليه السلام، يقال: إن الشياطين بنته، وهي سقيفة مستطيلة على عمد وسقائف وحولها مدينة تطيف بها، والمعروف أن بابا من أبواب الجامع بدمشق، وهو بابه الشرقي، يقال له: باب جيرون، وقيل: جيرون هي دمشق نفسها، نسب إليها بعض الرواة.','302','0','0');
INSERT INTO [t132412] VALUES (1494,'والمنسوب: هبة الله بن أحمد بن عبد الله بن علي بن طاووس المقرئ، الجيروني.','302','1493','0');
INSERT INTO [t132412] VALUES (1495,'(٧٢٨) النسبة: الجيزاباذي، نسبة إلى جيزاباذ: بالكسر ثم السكون، وزاي، وألف، وباء موحدة، وألف، وذال معجمة، أو راء: محلة بنيسابور، على الظن.','302','0','0');
INSERT INTO [t132412] VALUES (1496,'والمنسوب: أحمد بن إسمعيل بن أبي سعد عبد الحميد بن محمد الجيزاباذي، أو الجيراباذي.','302','1495','0');
INSERT INTO [t132412] VALUES (1497,'(٧٢٩) النسبة: الجيزي، نسبة إلى الجيزة: بالكسر، والجيزة في لغة العرب الوادي أو أفضل موضع فيه، وهي: بليدة في غربي فسطاط مصر قبالتها، معروفة اليوم، نسب إليها قوم من العلماء.','302','0','0');
INSERT INTO [t132412] VALUES (1498,'والمنسوب: الربيع بن سليمان بن داود الجيزي.','302','1497','0');
INSERT INTO [t132412] VALUES (1499,'(٧٣٠) النسبة: الجيشاني، نسبة إلى جيشان: بالفتح ثم السكون، وشين معجمة، وألف، ونون، مخلاف جيشان: باليمن كان ينزلها جيشان بن غيدان بن حجر بن ذي رعي.','303','0','0');
