INSERT INTO [t132412] VALUES (1501,'(٧٣١) النسبة: الجيشبري، نسبة إلى جيشبر: بالكسر ثم السكون، وشين معجمة، وضم الباء الموحدة، وراء: من قرى مرو.','303','0','0');
INSERT INTO [t132412] VALUES (1502,'والمنسوب: أبو يحيى محمد بن أبي علوية بن شداد الجيشبري، كان كثير السماع.','303','1501','0');
INSERT INTO [t132412] VALUES (1503,'(٧٣٢) النسبة: الجيلاني، والجيلي جيلان: بالكسر: اسم لبلاد كثيرة من وراء بلاد طبرستان، ينسب إليها جيلاني وجيلي، وقد فرق قوم فقيل: إذا نسب إلى البلاد','303','0','0');
