INSERT INTO [t132412] VALUES (1525,'(٧٤٣) النسبة: الحجاجي، نسبة إلى حجاج: بالفتح والتشديد، وآخره جيم: من قرى بيهق من أعمال نيسابور.','307','0','0');
INSERT INTO [t132412] VALUES (1526,'والمنسوب: أبو سعيد إسماعيل بن محمد بن أحمد الحجاجي.','307','1525','0');
INSERT INTO [t132412] VALUES (1527,'(٧٤٤) النسبة: الحجاري، نسبة إلى الحجارة: جمع الحجر: كورة بالأندلس يقال لها وادي الحجارة، ينسب إليها بالحجاري جماعة.','307','0','0');
INSERT INTO [t132412] VALUES (1528,'والمنسوب: محمد بن إبراهيم بن حيون، وسعيد بن مسعدة الحجاري، محدث، مات سنة (٤٢٧).','307','1527','0');
INSERT INTO [t132412] VALUES (1529,'(٧٤٥) النسبة: الحجري، نسبة إلى حجر: بالضم: قرية باليمن من مخاليف بدر، كذا قال ابن الفقيه، وبدر هذه التي باليمن غير بدر صاحبة غزوة بدر، قال أبو سعد: حجر، بالضم، اسم موضع باليمن.','308','0','0');
