INSERT INTO [t132412] VALUES (1535,'(٧٤٨) النسبة: الحدادي، نسبة إلى الحدادة: بالفتح، والتشديد، وبعد الألف دال أخرى: قرية كبيرة بين دامغان وبسطام من أرض قومس.','308','0','0');
INSERT INTO [t132412] VALUES (1536,'والمنسوب: محمد بن زياد الحدادي، ويقال له: القومسي.','308','1535','0');
INSERT INTO [t132412] VALUES (1537,'(٧٤٩) النسبة: الحداني، نسبة إلى حدان: بالضم: إحدى محال البصرة القديمة يقال لها: بنو حدان، سميت باسم قبيلة، وهو حدان بن شمس بن عمرو بن غنم بن غالب بن عثمان بن نصر بن زهران بن كعب بن الحارث بن كعب بن عبد الله بن مالك بن نصر بن الأزد، وسكنها جماعة من أهل العلم ونسبوا إليها.','309','0','0');
