INSERT INTO [t132412] VALUES (1539,'(٧٥٠) النسبة: الحدثي، نسبة إلى الحدث: بالتحريك، وآخره ثاء مثلثة: قلعة حصينة بين ملطية وسميساط ومرعش من الثغور.','309','0','0');
