INSERT INTO [t132412] VALUES (1546,'والمنسوب: أحمد بن محمد بن أحمد بن جعفر أبو العباس الأكار النهربيني، أخو أبي عبد الله المقري من سواد بغداد سكن الحديثة هذه.','312','1545','0');
INSERT INTO [t132412] VALUES (1547,'(٧٥٤) النسبة: الحديلي، نسبة إلى حديلة: مصغر أيضا، واشتقاقه من الذي قبله: وهي مدينة باليمن، سميت بذي حديلة، واسم حديلة معاوية بن عمرو بن مالك بن النجار، وقيل: أمه حديلة بنت مالك بن زيد مناة.','313','0','0');
