INSERT INTO [t132412] VALUES (1555,'والمنسوب: إبراهيم بن إسحاق الحربي.','314','1554','0');
INSERT INTO [t132412] VALUES (1556,'(٧٥٨) النسبة: الحربوي، نسبة على حربى: مقصور: بليدة في أقصى دجيل بين بغداد وتكريت مقابل الحظيرة، نسب إليها قوم من أهل العلم والنباهة.','314','0','0');
